INSERT INTO [t934] VALUES (242,'قوله: ونؤمن بالملائكة والنبيين، والكتب المنزلة على المرسلين، ونشهد أنهم كانوا على الحق المبين','301','0','0');
INSERT INTO [t934] VALUES (243,'[المسألة الأولى: أن الإيمان بأركان الإيمان الستة معناه التصديق الجازم، وأنه ينقسم إلى قسم واجب، وقسم مستحب]','302','242','0');
INSERT INTO [t934] VALUES (244,'قوله: ونؤمن بالملائكة','303','0','0');
INSERT INTO [t934] VALUES (245,'[المسألة الأولى: معنى الملائكة لغة واصطلاحا]','303','244','0');
INSERT INTO [t934] VALUES (246,'[المسألة الثانية: الملائكة درجات وطبقات، وهم يختلفون في قربهم من الله U، وأيضا يختلفون في وظائفهم وما وكلوا به.]','304','244','0');
INSERT INTO [t934] VALUES (247,'[المسألة الثالثة: الملائكة خلقوا من نور وملؤوا السماء، وكونهم ملؤوا السماء ليس ملأ أجسام تحول دون العبور في السماء؛ بل هذه أجسام نور، الله أعلم كيف تكوينها وكيف صفاتها على وجه الكمال]','305','244','0');
INSERT INTO [t934] VALUES (248,'[المسألة الرابعة: أن من لم يؤمن بالملائكة فلم يؤمن بالله وهو كافر، وبيان مراتب الإيمان الوجبه والمستحبه]','306','244','0');
INSERT INTO [t934] VALUES (249,'[المسألة الخامسة: الإيمان بالملائكة تبع للعلم، وكلما زاد العلم بالعقيدة وبالنصوص زاد الإيمان بالملائكة لمن وفقه الله]','307','244','0');
INSERT INTO [t934] VALUES (250,'الأسئلة','309','0','0');
INSERT INTO [t934] VALUES (251,'قوله: والنبيين','310','0','0');
INSERT INTO [t934] VALUES (252,'[المسألة الأولى: تعريف النبي والرسول، وذكر الفرق بين النبي والرسول]','311','251','0');
INSERT INTO [t934] VALUES (253,'[المسألة الثانية: الأنبياء والرسل درجات في الفضل والمنزلة عند الله]','313','251','0');
INSERT INTO [t934] VALUES (254,'[المسألة الثالثة: الأنبياء يعطيهم الله آيات، فنؤمن بالأنبياء ونؤمن بآيات الأنبياء.]','314','251','0');
INSERT INTO [t934] VALUES (255,'[المسألة الرابعة: معنى الإيمان بالأنبياء، وذكر مراتب الإيمان بالأنبياء]','315','251','0');
INSERT INTO [t934] VALUES (256,'[المسألة الخامسة: من كذب برسول بعد العلم به فإنه مكذب بجميع الأنبياء والمرسلين]','316','251','0');
INSERT INTO [t934] VALUES (257,'والكتب المنزلة على المرسلين','317','0','0');
INSERT INTO [t934] VALUES (258,'[المسألة الأولى: وحي الله عز وجل بكتبه إلى أنبيائه يكون من الله مباشرة أو يكون بواسطة الملك]','318','257','0');
INSERT INTO [t934] VALUES (259,'[المسألة الثانية: الكتب السماوية من جهة التوحيد متفقة، ومن جهة الشرائع فهي مختلفة]','319','257','0');
INSERT INTO [t934] VALUES (260,'[المسألة الثالثة: الإيمان بالكتب ينقسم إلى إيمان إجمالي وإيمان تفصيلي]','320','257','0');
INSERT INTO [t934] VALUES (261,'[المسألة الرابعة: اختلف العلماء في الكتب التي أنزلها الله على المرسلين هل يدخل فيها الصحف، أم أن الكتب غير الصحف؟]','321','257','0');
INSERT INTO [t934] VALUES (262,'[المسألة الخامسة: يدخل في الكلام على الكتب الكلام على القرآن، وعلى إعجاز القرآن، وقد سبق ذكره]','322','257','0');
INSERT INTO [t934] VALUES (263,'الأسئلة','323','0','0');
INSERT INTO [t934] VALUES (264,'قوله: ونسمي أهل قبلتنا مسلمين مؤمنين، ما داموا بما جاء به النبي صلى الله عليه وسلم معترفين، وله بكل ما قال وأخبر مصدقين','325','0','0');
INSERT INTO [t934] VALUES (265,'[المسألة الأولى: (أهل القبلة) يشمل كل أهل الأهواء، كل الفرق الثلاث والسبعين]','326','264','0');
INSERT INTO [t934] VALUES (266,'[المسألة الثانية: المشركون الذين يعبدون مع الله غيره ويدعون غير الله ويستغيثون بغير الله ويذبحون لغير الله ويعبدون غير الله، هل يصدق عليهم اسم أنهم من أهل القبلة أم لا يصدق عليهم.]','327','264','0');
INSERT INTO [t934] VALUES (267,'[المسألة الثالثة: أن قول الطحاوي (مسلمين مؤمنين) لا يلزم منه أنه يسوي ما بين المسلم والمؤمن]','328','264','0');
INSERT INTO [t934] VALUES (268,'[المسألة الرابعة: أن إسم أهل القبلة واسم المسلم والمؤمن لابد من بقاء ما دل عليه ما داموا بما جاء به النبي معترفين، وله بكل ما قال وأخبر مصدقين]','329','264','0');
INSERT INTO [t934] VALUES (269,'[المسألة الخامسة: باب الإيمان والخروج من اسم الإسلام واسم الإيمان هذا من المواضع التي تزل فيها الأقدام]','330','264','0');
INSERT INTO [t934] VALUES (270,'قوله: ولا نخوض في الله، ولا نماري في دين الله','331','0','0');
INSERT INTO [t934] VALUES (271,'[المسألة الأولى: الخوض في ذات الله محرمة، وكذلك التفكر في ذات الله أيضا منهي عنه، لكن المأمور أن يفكر المرء في آلاء الله]','332','270','0');
INSERT INTO [t934] VALUES (272,'[المسألة الثانية: المراء مذموم وضابطه هو أن يورد الشيء بقصد الانتصار للنفس أو إضعاف من أمامه]','334','270','0');
INSERT INTO [t934] VALUES (273,'من قوله (ولا نجادل في القرآن) إلى (ولا نخالف جماعة المسلمين)','335','0','0');
INSERT INTO [t934] VALUES (274,'[المسألة الأولى: معنى المجادلة، وذكر أقسامها، والفرق بينها وبين الجدل، وتوضيح معنى المجادلة في القرآن]','336','273','0');
INSERT INTO [t934] VALUES (275,'[المسألة الثانية: المراد بكون القرآن محكم كله، ومتشابه كله، ومنه ما هو محكم ومنه ما هو متشابه]','337','273','0');
INSERT INTO [t934] VALUES (276,'[المسألة الثالثة: عقيدة أهل السنة في القرآن]','339','273','0');
INSERT INTO [t934] VALUES (277,'[المسألة الرابعة: الروح الأمين هو جبريل عليه السلام]','340','273','0');
INSERT INTO [t934] VALUES (278,'[المسألة الخامسة: لماذا كان القرآن معجزا؟]','341','273','0');
INSERT INTO [t934] VALUES (279,'[المسألة السادسة: أن معتقد الصحابة ومعتقد التابعين وتبع التابعين وأئمة الإسلام أن القرآن كلام الله منزل غير مخلوق منه بدأ وإليه يعود]','342','273','0');
INSERT INTO [t934] VALUES (280,'[المسألة السابعة: ذكر شبهة من قال بخلق القرآن]','343','273','0');
INSERT INTO [t934] VALUES (281,'[المسألة الثامنة: ذكر معنى قول الطحاوي (جماعة المسلمين) ، مع ذكر سبيل النجاة من الفرق الضالة]','344','273','0');
INSERT INTO [t934] VALUES (282,'الأسئلة','346','0','0');
INSERT INTO [t934] VALUES (283,'الأسئلة٢','347','0','0');
INSERT INTO [t934] VALUES (284,'قوله: ولا نكفر أحدا من أهل القبلة بذنب، ما لم يستحله، ولا نقول: لا يضر مع الإيمان ذنب لمن عمله','349','0','0');
INSERT INTO [t934] VALUES (285,'[المسألة الأولى: الأدلة من القرآن والسنة والنظر على أن مرتكب الكبيرة لا يكفر، وأن الذنوب تؤثر في الإيمان]','350','284','0');
INSERT INTO [t934] VALUES (286,'[المسألة الثانية: التكفير بلا علم أيضا حرام، وقد يكون من كبائر الذنوب مع ذكر الأدلة]','352','284','0');
INSERT INTO [t934] VALUES (287,'[المسألة الثالثة: أن الأمة في مسألة التكفير اختلفت إلى ثلاث طوائف: أهل السنة والخوارج والمرجئة]','353','284','0');
INSERT INTO [t934] VALUES (288,'[المسألة الرابعة: أن الناس ثلاثة أصناف لا رابع لهم، وهم: المؤمنون، الكفار، المنافقون، فمن كان من أهل الإيمان: فإنه ليس كل ذنب يخرجه من الإيمان]','354','284','0');
INSERT INTO [t934] VALUES (289,'[المسألة الخامسة: من أصول أهل السنة والجماعة في هذا الباب أنهم فرقوا بين التكفير المطلق وما بين التكفير المعين]','355','284','0');
INSERT INTO [t934] VALUES (290,'[المسألة السادسة: في قول الطحاوي (ولا نكفر أحدا من أهل القبلة بذنب) أخذ على الطحاوي أنه قال (بذنب) وهذا يفيد أنه لا يكفر بأي ذنب]','356','284','0');
INSERT INTO [t934] VALUES (291,'[المسألة السابعة: تعريف الإستحلال المكفر]','357','284','0');
INSERT INTO [t934] VALUES (292,'[المسألة الثامنة: ذكر الفرق بين الاستحلال وبين الجحد وبين التكذيب]','358','284','0');
INSERT INTO [t934] VALUES (293,'[المسألة التاسعة: أن أهل البدع لا يكفرون بإطلاق بل قد يكون مذنبا، وقد يكون مخطئا أو متأولا]','359','284','0');
INSERT INTO [t934] VALUES (294,'[المسألة العاشرة: أن تكفير المعين يشترط فيه إقامة الحجة، ولايشترط فهم الحجة]','360','284','0');
INSERT INTO [t934] VALUES (295,'[المسألة الحادية عشرة: قوله (ولا نقول: لا يضر مع الإيمان ذنب لمن عمله) هذا فيه مخالفة للمرجئة]','361','284','0');
INSERT INTO [t934] VALUES (296,'[المسألة الثانية عشرة: ما خالف فيه أهل السنة الخوارج والمرجئة فرع لأصل ومثال لقاعدة؛ وهي قاعدة الوسطية لأهل السنة والجماعة]','362','284','0');
INSERT INTO [t934] VALUES (297,'الأسئلة','363','0','0');
INSERT INTO [t934] VALUES (298,'الأسئلة٢','365','0','0');
INSERT INTO [t934] VALUES (299,'نرجو للمحسنين من المؤمنين أن يعفو عنهم، ويدخلهم الجنة برحمته، ولا نأمن عليهم، ولا نشهد لهم بالجنة، ونستغفر لمسيئهم، ونخاف عليهم، ولا نقنطهم','367','0','0');
INSERT INTO [t934] VALUES (300,'[المسألة الأولى: أن الرجاء للمحسن بالعفو وعدم الأمن والاستغفار للمسيء والخوف عليه، هذا عقيدة يتعامل بها المرء مع نفسه وكذلك مع المؤمنين]','368','299','0');
INSERT INTO [t934] VALUES (301,'[المسألة الثانية: الرجاء للمحسن من المؤمنين بالعفو هذا يشمل كل أحد حتى من لم يعرف لنفسه ذنبا]','369','299','0');
INSERT INTO [t934] VALUES (302,'[المسألة الثالثة: الجمع ما بين الرجاء للمحسن والاستغفار للمسيء هذا تبع لأصل عظيم وهو الجمع في العبادة ما بين الخوف والرجاء]','370','299','0');
INSERT INTO [t934] VALUES (303,'[المسألة الرابعة: الخوف والرجاء هل يجب تساويهما أم يرجح أحدهما على الآخر]','371','299','0');
INSERT INTO [t934] VALUES (304,'[المسألة الخامسة: الأسباب التي يكفر الله عز وجل بها الخطايا أو يمحو بها أثر السيئات]','372','299','0');
INSERT INTO [t934] VALUES (305,'[المسألة السادسة: لا نشهد لأحد من أهل القبلة بجنة ولا نار إلا من شهد له رسول الله]','376','299','0');
INSERT INTO [t934] VALUES (306,'[المسألة السابعة: كيف يتعامل المسلم مع إخوانه إن كانوا مفرطين أو كانوا تائبين]','377','299','0');
INSERT INTO [t934] VALUES (307,'[المسألة الثامنة: لن يدخل أحد الجنة بعمله بل ما ثم إلا عفو الله ورحمته]','378','299','0');
INSERT INTO [t934] VALUES (308,'الأسئلة','379','0','0');
INSERT INTO [t934] VALUES (309,'الأسئلة٢','382','0','0');
INSERT INTO [t934] VALUES (310,'الأسئلة٣','386','0','0');
INSERT INTO [t934] VALUES (311,'قوله: والأمن والإياس ينقلان عن ملة الإسلام، وسبيل الحق بينهما لأهل القبلة','387','0','0');
INSERT INTO [t934] VALUES (312,'قوله: ولا يخرج العبد من الإيمان إلا بجحود ما أدخله فيه','389','0','0');
INSERT INTO [t934] VALUES (313,'[المسألة الأولى: أجمع أهل السنة والجماعة على أن من دخل في الإيمان بيقين فإنه لا يخرج منه إلا بيقين]','390','312','0');
INSERT INTO [t934] VALUES (314,'[المسألة الثانية: الحصر في قوله (إلا بجحود ما أدخله فيه) ليس مرادا في أنه لا يخرج أحد من الإيمان إلا بالجحد]','391','312','0');
INSERT INTO [t934] VALUES (315,'[المسألة الثالثة: تعريف الجحد عند أهل السنة والفرق بينهم وبين الخوارج في تعريفه]','392','312','0');
INSERT INTO [t934] VALUES (316,'[المسألة الرابعة: أهل السنة والجماعة خالفوا الخوارج والمرجئة في إخراجهم الواحد من أهل القبلة من الإيمان، فعندهم المخرجات من الإيمان منها التكذيب والجحد والإعراض والشك]','393','312','0');
INSERT INTO [t934] VALUES (317,'قوله: والإيمان: هو الإقرار باللسان، والتصديق بالجنان','394','0','0');
INSERT INTO [t934] VALUES (318,'[المسألة الأولى: الإيمان في اللغة هو التصديق الجازم الذي يكون معه عمل يأمن معه]','395','317','0');
INSERT INTO [t934] VALUES (319,'[المسألة الثانية: أنه لا يطلق لفظ مصدقا في اللغة على من صدق حتى يعمل]','397','317','0');
INSERT INTO [t934] VALUES (320,'[المسألة الثالثة: الضابط لما جاء في القرآن من استعمال الإيمان في الحقيقة اللغوية والعرفية والشرعية]','398','317','0');
INSERT INTO [t934] VALUES (321,'[المسألة الرابعة: الرد على أدلة المرجئة في إخراج العمل عن مسمى الإيمان]','400','317','0');
INSERT INTO [t934] VALUES (322,'من قوله (والإيمان: هو الإقرار باللسان) إلى قوله (وملازمة الأولى)','402','317','0');
INSERT INTO [t934] VALUES (323,'[المسألة الخامسة: ذكر أقوال الفرق في الإيمان]','404','317','0');
INSERT INTO [t934] VALUES (324,'[المسألة السادسة: الفرق بين قول مرجئة الفقهاء وبين قول أهل السنة والجماعة في الإيمان]','405','317','0');
INSERT INTO [t934] VALUES (325,'[المسألة السابعة: مسألة زيادة الإيمان ونقصانه وأقوال الفرق فيها]','407','317','0');
INSERT INTO [t934] VALUES (326,'[المسألة الثامنة: الفرق ما بين (التصديق بالجنان) و (الإعتقاد بالجنان) ]','408','317','0');
INSERT INTO [t934] VALUES (327,'قوله: وجميع ما صح عن رسول الله صلى الله عليه وسلم من الشرع والبيان كله حق','409','0','0');
INSERT INTO [t934] VALUES (328,'قوله: والإيمان واحد وأهله في أصله سواء، والتفاضل بينهم بالخشية والتقى، ومخالفة الهوى، وملازمة الأولى','410','0','0');
INSERT INTO [t934] VALUES (329,'[المسألة الأولى: الرد على الطحاوي في قوله (وأهله في أصله سواء) ]','411','328','0');
INSERT INTO [t934] VALUES (330,'[المسألة الثانية: أن أصل الإيمان إذا قلنا هو التصديق، فإنه يتفاوت، مع ذكر أساب زيادة التصديق ونقصه]','412','328','0');
INSERT INTO [t934] VALUES (331,'[المسألة الثالثة: أن قوله (والتفاضل بينهم بالخشية والتقى، ومخالفة الهوى، وملازمة الأولى) هذا صحيح؛ لكنه وجه تفاضل وليس كل أوجه التفاضل]','414','328','0');
INSERT INTO [t934] VALUES (332,'قوله: والمؤمنون كلهم أولياء الرحمن، وأكرمهم عند الله أطوعهم وأتبعهم للقرآن','415','0','0');
INSERT INTO [t934] VALUES (333,'[المسألة الأولى: تعريف الولي لغة واصطلاحا]','416','332','0');
INSERT INTO [t934] VALUES (334,'[المسألة الثانية: الدليل على هذا التعريف للولي]','417','332','0');
INSERT INTO [t934] VALUES (335,'[المسألة الثالثة: الله عز وجل ولي للعبد، والعبد أيضا ولي لله عز وجل]','418','332','0');
INSERT INTO [t934] VALUES (336,'[المسألة الرابعة: الأولياء قسمان فيما دلت عليه الأدلة: مقتصدون وسابقون مقربون]','419','332','0');
INSERT INTO [t934] VALUES (337,'[المسألة الخامسة: الإرتباط ما بين مسألة الولاية ومسألة الكرامة]','420','332','0');
INSERT INTO [t934] VALUES (338,'[المسألة السادسة: من أعظم مظاهر التقوى في المؤمنين المتقين عدم تزكية النفس]','421','332','0');
INSERT INTO [t934] VALUES (339,'[المسألة السابعة: لشيخ الإسلام مصنف مهم في الفرق ما بين أولياء الرحمن وأولياء الشيطان سماه (الفرقان ما بين أولياء الرحمن وأولياء الشيطان) يحسن مطالعته]','422','332','0');
INSERT INTO [t934] VALUES (340,'[المسألة الثامنة: أولياء كل أمة شاهدون لأنبيائها ولرسلها وهذا أصل مهم يقضي بأن الولي لا يخرج عن طاعة النبي الذي اتبعه]','423','332','0');
INSERT INTO [t934] VALUES (341,'الأسئلة','424','0','0');
INSERT INTO [t934] VALUES (342,'قوله: والإيمان: هو الإيمان بالله، وملائكته، وكتبه، ورسله، واليوم الآخر، والقدر خيره وشره، وحلوه ومره من الله تعالى.','426','0','0');
INSERT INTO [t934] VALUES (343,'[المسألة الأولى: كلمة الأركان سواء أركان الإسلام أو أركان الإيمان أو غير ذلك هي تسمية اصطلاحية، لم يأت بها الدليل أن هذا ركن]','427','342','0');
INSERT INTO [t934] VALUES (344,'[المسألة الثانية: خلاصة الكلام على الإيمان بأركان الإيمان الستة]','428','342','0');
INSERT INTO [t934] VALUES (345,'[المسألة الثالثة: أركان الإيمان كلها أمور اعتقادية بحتة، فأين العمل في هذه الأركان الستة؟]','430','342','0');
INSERT INTO [t934] VALUES (346,'قوله: ونحن مؤمنون بذلك كله، لا نفرق بين أحد من رسله، ونصدقهم كلهم على ما جاءوا به','431','0','0');
INSERT INTO [t934] VALUES (347,'[المسألة الأولى: الرسل دينهم واحد ولكن الشرائع تختلف، ومن الباطل قول القائل الأديان السماوية]','432','346','0');
INSERT INTO [t934] VALUES (348,'[المسألة الثانية: شرائع الرسل تختلف، وذكر الفرق ما بين الدين العام والشريعة]','433','346','0');
INSERT INTO [t934] VALUES (349,'[المسألة الثالثة: أننا (لا نفرق بين أحد من رسله) خلافا لكل أهل الملل والديانات]','434','346','0');
INSERT INTO [t934] VALUES (350,'قوله: وأهل الكبائر من أمة محمد صلى الله عليه وسلم في النار لا يخلدون، إذا ماتوا وهم موحدون','435','0','0');
INSERT INTO [t934] VALUES (351,'[المسألة الأولى: تعريف أهل الكبائر، وضابط الكبيرة]','436','350','0');
INSERT INTO [t934] VALUES (352,'[المسألة الثانية: هل الإصرار على الصغيرة يصيرها كبيرة أم لا؟]','437','350','0');
INSERT INTO [t934] VALUES (353,'الأسئلة','438','350','0');
INSERT INTO [t934] VALUES (354,'[المسألة الثالثة: أن عدم الخلود في النار لأهل الكبائر ليس خاصا بأمة محمد بل هو عام لهذه الأمة ولغيرها]','439','350','0');
INSERT INTO [t934] VALUES (355,'[المسألة الرابعة: أن مرتكب الكبيرة من أهل الوعيد إلا في حالات، مع ذكر هذه الحالات]','440','350','0');
INSERT INTO [t934] VALUES (356,'[المسألة الخامسة: من لم يغفر له ممن لم يتب فإنه يشترط لعدم خلوده في النار شرطان]','441','350','0');
INSERT INTO [t934] VALUES (357,'[المسألة السادسة: الخلود في النار نوعان: خلود أمدي إلى أجل، وخلود أبدي]','442','350','0');
INSERT INTO [t934] VALUES (358,'[المسألة السابعة: قوله (لا يخلدون، إذا ماتوا وهم موحدون، وإن لم يكونوا تائبين) هذه الجملة معروفة أصلا لأن التائب من الذنب كمن لا ذنب له فهي من باب التأكيد وليست إشارة لخلاف ولا إشارة لشرط ونحو ذلك]','443','350','0');
INSERT INTO [t934] VALUES (359,'[المسألة الثامنة: التعقيب على تعقب ابن أبي العز للطحاوي في قوله (لقوا الله عارفين) ]','444','350','0');
INSERT INTO [t934] VALUES (360,'[المسألة التاسعة: أن أهل الكبائر إذا ماتوا غير تائبين تحت المشيئة]','445','350','0');
INSERT INTO [t934] VALUES (361,'[المسألة العاشرة: أن ولاية الله لعباده المؤمنين تتبعض]','446','350','0');
INSERT INTO [t934] VALUES (362,'قوله: اللهم يا ولي الإسلام وأهله ثبتنا على الإسلام حتى نلقاك به','447','0','0');
INSERT INTO [t934] VALUES (363,'قوله: ونرى الصلاة خلف كل بر وفاجر من أهل القبلة، وعلى من مات منهم','448','0','0');
INSERT INTO [t934] VALUES (364,'[المسألة الأولى: الصلاة خلف الإمام الأعظم أو الأمير الخاص هذه سنة ماضية دل عليها سنة النبي، ودل عليها عمل السلف الصالح]','449','363','0');
INSERT INTO [t934] VALUES (365,'[المسألة الثانية: أن الصلاة نراها ونفعلها خلف كل إمام بر أو فاجر أو أيضا ممن نجهل عقيدته]','450','363','0');
INSERT INTO [t934] VALUES (366,'[المسألة الثالثة: قوله (خلف كل بر وفاجر من أهل القبلة) هذا إذا كان إماما مرتبا، ولم يكن بوسع المرء أن يختار الأمثل]','451','363','0');
INSERT INTO [t934] VALUES (367,'[المسألة الرابعة: التفصيل في الصلاة على المؤمن الصالح والمؤمن الفاجر بمعاص مختلفة، والمؤمن الفاجر بمعاص نصض عليها في الشرع والمنافق]','452','363','0');
INSERT INTO [t934] VALUES (368,'الأسئلة','453','0','0');
INSERT INTO [t934] VALUES (369,'الأسئلة٢','454','0','0');
INSERT INTO [t934] VALUES (370,'قوله: ولا ننزل أحدا منهم جنة ولا نارا، ولا نشهد عليهم بكفر ولا بشرك ولا بنفاق، ما لم يظهر منهم شيء من ذلك، ونذر سرائرهم إلى الله تعالى','456','0','0');
INSERT INTO [t934] VALUES (371,'[المسألة الأولى: أن قوله (ولا ننزل أحدا منهم جنة ولا نارا، ولا نشهد عليهم بشرك ونفاق) هذا في أهل لقبلة فقط ولايدخل فيه الكافر]','457','370','0');
INSERT INTO [t934] VALUES (372,'[المسألة الثانية: ذكر أقوال العلماء في الشهادة للمسلمين للجنس والمعين من المسلمين بالجنة والنار]','458','370','0');
INSERT INTO [t934] VALUES (373,'[المسألة الثالثة: أننا نشهد للجنس والنوع بالجنة والنار دون تنزيله على معين]','459','370','0');
INSERT INTO [t934] VALUES (374,'[المسألة الرابعة: أننا مع ذلك كله فإننا نرجو للمحسن ونخاف على المسيء]','460','370','0');
INSERT INTO [t934] VALUES (375,'[المسألة الخامسة: الشهادة بما يدل على الشهادة بالجنة مثل أن يقال فلان شهيد، ممنوعة]','461','370','0');
INSERT INTO [t934] VALUES (376,'قوله: ولا نشهد عليهم بكفر ولا بشرك ولا بنفاق، ما لم يظهر منهم شيء من ذلك، ونذر سرائرهم إلى الله تعالى','462','0','0');
INSERT INTO [t934] VALUES (377,'[المسألة الأولى: المعين من أهل القبلة قد يجتمع فيه إيمان وكفر، ويجتمع فيه إسلام وشرك، ويجتمع فيه طاعة وإسلام وإيمان ونفاق]','463','376','0');
INSERT INTO [t934] VALUES (378,'[المسألة الثانية: أن قوله (ولا نشهد عليهم ما لم يظهر منهم) يعني أنه إذا ظهر منهم فإننا قد نشهد عليهم وجواز ذلك منوط بالمصلحة]','464','376','0');
INSERT INTO [t934] VALUES (379,'[المسألة الثالثة: من خرج من الإسلام بكفر أكبر أو بشرك أكبر أو بردة وقامت عليه الحجة في ذلك فإنه يشهد عليه بعينه]','465','376','0');
INSERT INTO [t934] VALUES (380,'قوله: ولا نرى السيف على أحد من أمة محمد إلا من وجب عليه السيف','466','0','0');
INSERT INTO [t934] VALUES (381,'[المسألة الأولى: (ولا نرى السيف) هذه الكلمة مصطلح شائع عند العلماء يوصف به يحبذ الخروج ولو لم يدخل فيه بفعله وإنما يستحسنه لفظا ويؤيد من يفعله]','467','380','0');
INSERT INTO [t934] VALUES (382,'[المسألة الثانية: أدلة عدم جواز قتل من لم يجب عليه القتل]','468','380','0');
INSERT INTO [t934] VALUES (383,'[المسألة الثالثة: ولي الأمر هو الذي بيده أن يسفك الدم وليس لآحاد الناس من العلماء أو من العامة هذا الأمر]','469','380','0');
INSERT INTO [t934] VALUES (384,'الأسئلة','470','0','0');
INSERT INTO [t934] VALUES (385,'الأسئلة٢','472','0','0');
INSERT INTO [t934] VALUES (386,'قوله: ولا نرى الخروج على أئمتنا وولاة أمورنا وإن جاروا','473','0','0');
INSERT INTO [t934] VALUES (387,'[المسألة الأولى: لفظ الأئمة وولاة الأمور مما جاء به الكتاب والسنة]','474','386','0');
INSERT INTO [t934] VALUES (388,'[المسألة الثانية: الأصل أن ولي الأمر يجمع ما بين حسن التدبير في أمور الناس والعلم بأحكام الشريعة بما يناسب]','475','386','0');
INSERT INTO [t934] VALUES (389,'[المسألة الثالثة: الذي عليه الصحابة جميعا وعامة التابعين وأئمة الإسلام أن الخروج على ولي الأمر محرم وكبيرة من الكبائر، ومن خرج على ولي الأمر فليس من الله في شيء]','476','386','0');
INSERT INTO [t934] VALUES (390,'[المسألة الرابعة: الأدلة من القرآن والسنة على وجوب طاعة الولاة وتحريم الخروج عليهم]','477','386','0');
INSERT INTO [t934] VALUES (391,'[المسألة الخامسة: الخروج على الولاة يكون بالسيف وكذلك باعتقاد جواز الخروج عليهم]','478','386','0');
INSERT INTO [t934] VALUES (392,'[المسألة السادسة: أسباب الخروج على الولاة]','479','386','0');
INSERT INTO [t934] VALUES (393,'[المسألة السابعة: الأمور الواجب طاعة ولاة الأمور فيها]','480','386','0');
INSERT INTO [t934] VALUES (394,'[المسألة الثامنة: أن الطاعة لا تتقيد بأنها لولي الأمر العدل بل وإن كان منه جور فإنه يطاع]','481','386','0');
INSERT INTO [t934] VALUES (395,'قوله: ولا ندعو عليهم','482','0','0');
INSERT INTO [t934] VALUES (396,'ولا ننزع يدا من طاعتهم، ونرى طاعتهم من طاعة الله فريضة','483','0','0');
INSERT INTO [t934] VALUES (397,'الأسئلة','484','0','0');
INSERT INTO [t934] VALUES (398,'قوله: ونتبع السنة والجماعة، ونجتنب الشذوذ والخلاف والفرقة','485','0','0');
INSERT INTO [t934] VALUES (399,'[المسألة الأولى: حكم التقليد في العقائد عند أهل السنة والجماعة]','487','398','0');
INSERT INTO [t934] VALUES (400,'[المسألة الثانية: في قوله (ونتبع السنة) السنة يراد بها العلم الموروث عن النبي في مسائل الاعتقاد]','488','398','0');
INSERT INTO [t934] VALUES (401,'[المسألة الثالثة: لفظ (أهل السنة والجماعة) إنما يدخل فيه أهل الحديث والأثر الذين لم ينحرفوا في مسائل الاعتقاد]','489','398','0');
INSERT INTO [t934] VALUES (402,'[المسألة الرابعة: معنى الشذوذ في العلم والعقيدة، وحكم من شذ فيهما]','490','398','0');
INSERT INTO [t934] VALUES (403,'[المسألة الخامسة: أقسام الخلاف في الشريعة وصوره.]','491','398','0');
INSERT INTO [t934] VALUES (404,'[المسألة السادسة: معنى الإفتراق وأنواعه]','492','398','0');
INSERT INTO [t934] VALUES (405,'قوله: ونحب أهل العدل والأمانة، ونبغض أهل الجور والخيانة','493','0','0');
INSERT INTO [t934] VALUES (406,'[المسألة الأولى: تعريف أهل العدل وأهل الجور]','494','405','0');
INSERT INTO [t934] VALUES (407,'[المسألة الثانية: تعريف أهل الأمانة وأهل الخيانة]','495','405','0');
INSERT INTO [t934] VALUES (408,'قوله: ونقول: الله أعلم فيما اشتبه علينا علمه','496','0','0');
INSERT INTO [t934] VALUES (409,'[المسألة الأولى: المراد بقول الطحاوي (الله أعلم) ]','497','408','0');
INSERT INTO [t934] VALUES (410,'[المسألة الثانية: الاشتباه في الأمور المشتبهة قد يكون اشتباها في الدليل، وقد يكون اشتباها في المدلول، وهدي أهل السنة والجماعة في المشتبهات]','498','408','0');
INSERT INTO [t934] VALUES (411,'الأسئلة','499','0','0');
INSERT INTO [t934] VALUES (412,'قوله: ونرى المسح على الخفين في السفر والحضر، كما جاء في الأثر','501','0','0');
INSERT INTO [t934] VALUES (413,'[المسألة الأولى: الذي يرى هنا في قوله (نرى) المقصود بهم أهل السنة]','502','412','0');
INSERT INTO [t934] VALUES (414,'[المسألة الثانية: (المسح على الخفين) جاء في الأثر عن النبي، وهو متواتر]','503','412','0');
INSERT INTO [t934] VALUES (415,'[المسألة الثالثة: الاستدلال بقول الله {وامسحوا برءوسكم وأرجلكم إلى الكعبين} على المسح على الخفين فيه نظر]','504','412','0');
INSERT INTO [t934] VALUES (416,'[المسألة الرابعة: قراءة الجر قيل إنها دليل على إبطال المسح على الخفين مع الرد]','505','412','0');
INSERT INTO [t934] VALUES (417,'قوله: والحج والجهاد ماضيان مع أولي الأمر من المسلمين، برهم وفاجرهم إلى قيام الساعة، لا يبطلهما شيء ولا ينقضهما','506','0','0');
INSERT INTO [t934] VALUES (418,'[المسألة الأولى: المخالف في هذا الأصل هم الروافض والخوارج أو من شابه الخوارج]','507','417','0');
INSERT INTO [t934] VALUES (419,'[المسألة الثانية: قوله (إلى قيام الساعة) هذا المقصود منه إلى قرب قيام الساعة]','508','417','0');
INSERT INTO [t934] VALUES (420,'[المسألة الثالثة: لا يبطل الحج شيء من معصية الولاة ولا ينقض الحج والجهاد مع ولاة الأمر شيء من فجورهم أو نقصهم]','509','417','0');
INSERT INTO [t934] VALUES (421,'قوله: ونؤمن بالكرام الكاتبين، فإن الله قد جعلهم علينا حافظين','510','0','0');
INSERT INTO [t934] VALUES (422,'[المسألة الأولى: إيضاح كون الملائكة حفظة، وكتبة، ويعلمون ما تفعلون]','511','421','0');
INSERT INTO [t934] VALUES (423,'[المسألة الثانية: أن الملائكة الكتبة غير الحفظة]','512','421','0');
INSERT INTO [t934] VALUES (424,'[المسألة الثالثة: الكتابة التي في صحف الملائكة هذه هي التي تجمع على العبد، وهي كتابه الذي يجمع معه في عنقه إذا أدخل القبر]','513','421','0');
INSERT INTO [t934] VALUES (425,'قوله: ونؤمن بملك الموت، الموكل بقبض أرواح العالمين','514','0','0');
INSERT INTO [t934] VALUES (426,'[المسألة الأولى: ملك الموت له أعوان وجنود فهو كالأمير لهم]','515','425','0');
INSERT INTO [t934] VALUES (427,'[المسألة الثانية: الملائكة تقبض الروح بأمر مجدد من الله]','516','425','0');
INSERT INTO [t934] VALUES (428,'[المسألة الثالثة: الله عز وجل خلق الملائكة وجعل لهم هذه المهمة وغيرها من المهام للتعبد لا لنقص في ملكوت]','517','425','0');
INSERT INTO [t934] VALUES (429,'[المسألة الرابعة: ذكر الشارح الكلام على الأرواح والروح وحقيقتها والنفس والفرق بينها وبين الروح، وهل الروح مخلوقة الآن، فيرجع إليه]','518','425','0');
INSERT INTO [t934] VALUES (430,'[المسألة الخامسة: في قوله (أرواح العالمين) لفظ (العالمين) يريد به هنا من له روح من المكلفين]','519','425','0');
INSERT INTO [t934] VALUES (431,'قوله: ونؤمن ... وبعذاب القبر لمن كان له أهلا]','520','0','0');
INSERT INTO [t934] VALUES (432,'[المسألة الأولى: عذاب القبر اسم لما بعد الموت، وقيل عنه عذاب القبر تغليبا، وقد يكون عذابا في القبر وقد يكون عذابا في غير القبر]','522','431','0');
INSERT INTO [t934] VALUES (433,'[المسألة الثانية: عذاب القبر يتناول الروح والجسد معا]','523','431','0');
INSERT INTO [t934] VALUES (434,'[المسألة الثالثة: من المنتسبين للسنة من العلماء من يقول العذاب على الروح والنعيم للروح وأما البدن فإنه لا يعذب ولا ينعم]','524','431','0');
INSERT INTO [t934] VALUES (435,'[المسألة الرابعة: الروح والبدن ذكر العلماء أن لها أربعة أنواع من التعلق]','525','431','0');
INSERT INTO [t934] VALUES (436,'[المسألة الخامسة: نعيم غير المكلفين تبع لحال آبائهم]','526','431','0');
INSERT INTO [t934] VALUES (437,'قوله: ونؤمن بسؤال منكر ونكير في قبره عن ربه ودينه ونبيه','527','0','0');
INSERT INTO [t934] VALUES (438,'[المسألة الأولى: أن سؤال الملكين يقع عن ثلاثة أشياء: أولا: عن ربه، ثانيا: عن دينه، ثالثا: عن نبيه]','528','437','0');
INSERT INTO [t934] VALUES (439,'[المسألة الثانية: هذا السؤال لهذه الأمة ولجميع الأمم]','529','437','0');
INSERT INTO [t934] VALUES (440,'[المسألة الثالثة: سؤال منكر ونكير، هل يكون للكافر أم لمن أجاب النبي ظاهرا]','530','437','0');
INSERT INTO [t934] VALUES (441,'قوله: والقبر روضة من رياض الجنة، أو حفرة من حفر النيران','531','0','0');
INSERT INTO [t934] VALUES (442,'قوله: ونؤمن بالبعث وجزاء الأعمال يوم القيامة، والعرض والحساب، وقراءة الكتاب، والثواب والعقاب، والصراط والميزان','532','0','0');
INSERT INTO [t934] VALUES (443,'[المسألةالأولى: ذكر بعض ما يحدث في البعث]','533','442','0');
INSERT INTO [t934] VALUES (444,'[المسألة الثانية: جزاء الأعمال لا يكون بعد البعث مباشرة؛ بل يكون متأخرا]','534','442','0');
INSERT INTO [t934] VALUES (445,'[المسألة الثالثة: العرض معناه: أن يعرض المكلف وأن يعرض عمل المكلف]','535','442','0');
INSERT INTO [t934] VALUES (446,'[المسألة الرابعة: الحساب من حيث هو تقرير للعمل مع الجزاء والعقاب، وهو يحصل في سرعة خاطفة]','536','442','0');
INSERT INTO [t934] VALUES (447,'[المسألة الخامسة: في قوله (وقراءة الكتاب) يعني بالكتاب الصحف التي كتبت فيها أعماله]','537','442','0');
INSERT INTO [t934] VALUES (448,'[المسألة السادسة: في قوله (والثواب والعقاب) الثواب والعقاب يكون بعد الوزن]','538','442','0');
INSERT INTO [t934] VALUES (449,'[المسألة السابعة: الصراط وذكر صفته]','539','442','0');
INSERT INTO [t934] VALUES (450,'[المسألة الثامنة: الميزان حقيقة وليس هو العدل كما تقوله المعتزلة، وهي موازين متعددة وليست ميزان واحد، وذكر ما يوزن فيها]','540','442','0');
INSERT INTO [t934] VALUES (451,'[المسألة التاسعة: ترتيب ما يحصل يوم القيامة]','541','442','0');
INSERT INTO [t934] VALUES (452,'قوله: والجنة والنار مخلوقتان، لا تفنيان أبدا ولا تبيدان','543','0','0');
INSERT INTO [t934] VALUES (453,'[المسألة الأولى: قوله (الجنة والنار مخلوقتان) يعني به أن خلقهما قد تم، ليس موقوفا على قيام الساعة، وإنما خلقهما الله قبل خلق الخلق]','544','452','0');
INSERT INTO [t934] VALUES (454,'[المسألة الثانية: الجنة خلقت للبقاء والنار خلقت للبقاء، وأهلهما خالدين فيها أبدا، وذكر أقوال المخالفين]','545','452','0');
