INSERT INTO [t934] VALUES (572,'قوله: ونؤمن بأشراط الساعة: من خروج الدجال، ونزول عيسى ابن مريم عليه السلام من السماء، ونؤمن بطلوع الشمس من مغربها، وخروج دابة الأرض من موضعها','687','0','0');
INSERT INTO [t934] VALUES (573,'[المسألة الأولى: تعريف أشراط الساعة]','688','572','0');
INSERT INTO [t934] VALUES (574,'[المسألة الثانية: أقسام أشراط الساعة]','689','572','0');
INSERT INTO [t934] VALUES (575,'[المسألة الثالثة: لا يدل كون الحدث من أشراط الساعة على مدحه أو ذمه]','690','572','0');
INSERT INTO [t934] VALUES (576,'[المسألة الرابعة: ذكر أشراط الساعة الكبرى مع التفصيل]','691','572','0');
INSERT INTO [t934] VALUES (577,'[المسألة الخامسة: أشراط الساعة لا يتعرض لها بمجاز ولا بما ينفي حقيقتها ولا بالتأويل الذي يصرفها عن ظواهرها]','694','572','0');
INSERT INTO [t934] VALUES (578,'[المسألة السادسة: عيسى ابن مريم عليه السلام إذا نزل فإنه ينزل تابعا لشريعة النبي محمد]','695','572','0');
INSERT INTO [t934] VALUES (579,'[المسألة السابعة: التحذير من تنزيل أشراط الساعة على الواقع دون تحقيق]','696','572','0');
