INSERT INTO [t934] VALUES (581,'الأسئلة٢','699','0','0');
INSERT INTO [t934] VALUES (582,'قوله: ولا نصدق كاهنا ولا عرافا، ولا من يدعي شيئا يخالف الكتاب والسنة وإجماع الأمة','700','0','0');
INSERT INTO [t934] VALUES (583,'[المسألة الأولى: سبب ادعاء الكهان أو العرافين لعلم الغيب هو أن الشياطين تمدهم بالمعلومات]','701','582','0');
INSERT INTO [t934] VALUES (584,'[المسألة الثانية: الفرق بين الكاهن والعراف]','702','582','0');
INSERT INTO [t934] VALUES (585,'[المسألة الثالثة: حكم الكاهن والعراف، وحكم من يأتيهما]','703','582','0');
INSERT INTO [t934] VALUES (586,'[المسألة الرابعة: التفصيل لاستراق الشياطين السمع قبل البعثة، وبعد البعثة، وبعد عهد النبي]','705','582','0');
INSERT INTO [t934] VALUES (587,'[المسألة الخامسة: بعض صور الكهانة والعرافة القديمة والحديثة، ومنها التنويم المغناطيسي]','706','582','0');
INSERT INTO [t934] VALUES (588,'الأسئلة','707','0','0');
INSERT INTO [t934] VALUES (589,'قوله: ولا من يدعي شيئا يخالف الكتاب والسنة وإجماع الأمة','709','0','0');
INSERT INTO [t934] VALUES (590,'[المسألة الأولى: أن مخالفة الكتاب والسنة وإجماع الأمة مذمومة وضلال وقد تصل بصاحبها إلى الكفر في باب الاعتقاد، ولماذا جمع الطحاوي بينها وبين تصديق الكهان]','710','589','0');
INSERT INTO [t934] VALUES (591,'[المسألة الثانية: أقسام الذين نسبوا إلى الولاية وعدوا من الأولياء وأهل الزهادة]','711','589','0');
INSERT INTO [t934] VALUES (592,'[المسألة الثالثة: الواجب على كل مسلم أن يعتقد أن علم الغيب مختص بالله، وأنه قد يعطي بعض علم الغيب لرسول]','712','589','0');
INSERT INTO [t934] VALUES (593,'[المسألة الرابعة: ذكر ابن أبي العز أحوالا متنوعة فيمن ادعى أشياء مخالفة للكتاب والسنة وإجماع الأمة، وأضاف عليها الشيخ صالح (الطائفة الملامكية) ]','713','589','0');
INSERT INTO [t934] VALUES (594,'قوله: ونرى الجماعة حقا وصوابا، والفرقة زيغا وعذابا','714','0','0');
INSERT INTO [t934] VALUES (595,'[المسألة الأولى: كلمة (نرى) في كتب عقائد أهل السنة إذا جاءت بصيغة الجمع فإنه يراد بها ما قرره أئمة أهل السنة والجماعة في عقائدهم دون خلاف بينهم]','715','594','0');
INSERT INTO [t934] VALUES (596,'[المسألة الثانية: توضيح معنى الجماعة وأنواعها]','716','594','0');
INSERT INTO [t934] VALUES (597,'[المسألة الثالثة: من دعا إلى الدين والاجتماع عليه وتحقيق التوحيد ونبذ البدع ووسائل الشرك والبدع وإحلال الحلال وتحريم الحرام والأمر بما أوجب الله والنهي عن ضد ذلك أن هذا في الحقيقة يدعو إلى الاجتماع في الأبدان]','717','594','0');
INSERT INTO [t934] VALUES (598,'[المسألة الرابعة: توضيح قول الطحاوي (الجماعة حقا وصوابا) ]','718','594','0');
INSERT INTO [t934] VALUES (599,'قوله: والفرقة زيغا وعذابا','719','0','0');
INSERT INTO [t934] VALUES (600,'[المسألة الأولى: ذكر أنواع الفرقة]','719','599','0');
INSERT INTO [t934] VALUES (601,'[المسألة الثانية: الفرقة التي حصلت في الأمة في الدين على مراتب]','720','599','0');
INSERT INTO [t934] VALUES (602,'[المسألة الثالثة: تفصيل الكلام في مسألة الخلاف الفقهي]','721','599','0');
INSERT INTO [t934] VALUES (603,'الأسئلة','723','0','0');
INSERT INTO [t934] VALUES (604,'الأسئلة٢','726','0','0');
INSERT INTO [t934] VALUES (605,'قوله: ودين الله في الأرض والسماء واحد، وهو دين الإسلام','727','0','0');
INSERT INTO [t934] VALUES (606,'[المسألة الأولى: أن الإسلام ينقسم إلى إسلام عام وإسلام خاص، وتعريف كل قسم]','728','605','0');
INSERT INTO [t934] VALUES (607,'[المسألة الثانية: من الغلط قول القائل: الأديان السماوية الثلاثة]','729','605','0');
INSERT INTO [t934] VALUES (608,'[المسألة الثالثة: قول الطحاوي (دين الله) ، هنا إضافة الدين إلى الرب ليست إضافة إلى الفاعل، بل هي إضافة إلى الآمر بها]','730','605','0');
INSERT INTO [t934] VALUES (609,'[المسألة الرابعة: ذكر أقسام الإسلام من عدة إعتبارات]','731','605','0');
INSERT INTO [t934] VALUES (610,'قوله: وهو بين الغلو والتقصير، وبين التشبيه والتعطيل، وبين الجبر والقدر، وبين الأمن واليأس','732','0','0');
INSERT INTO [t934] VALUES (611,'[المسألة الأولى: وسطية الإسلام بين الغلو والتقصير]','733','610','0');
