INSERT INTO [t934] VALUES (618,'من قوله (ونسأل الله تعالى أن يثبتنا على الإيمان) إلى (وبالله العصمة والتوفيق)','743','0','0');
INSERT INTO [t934] VALUES (619,'[المسألة الأولى: عظم شأن الدعاء]','746','618','0');
INSERT INTO [t934] VALUES (620,'[المسألة الثانية: الثبات على الإيمان يكون بالثبات على أصله وعلى كماله]','747','618','0');
INSERT INTO [t934] VALUES (621,'[المسألة الثالثة: عمل الأكياس وعمل الصالحين أنهم يستعدون للخاتمة، وذكر كيفية الإستعداد لها]','748','618','0');
