INSERT INTO [t934] VALUES (624,'[المسألة السادسة: قول الطحاوي (خالفوا السنة والجماعة) ، هذا مما يؤكد لك أن قصده بالثبات على الإيمان والعصمة من الأهواء هي موافقة الجماعة، وذكر كيفية مخالفة السنة والجماعة]','753','618','0');
INSERT INTO [t934] VALUES (625,'[المسألة السابعة: الواجب على المسلم أن يتبرأ جملة وتفصيلا من القول ومن المذاهب الردية ومن أصحابها، ولا يقول أتبرأ من العمل دون صاحب العمل]','754','618','0');
INSERT INTO [t934] VALUES (626,'[المسألة الثامنة: في قوله (وبالله التوفيق) التوفيق هو الهداية إلى طريق الرشاد والإعانة على سلوك هذا الطريق جملة وتفصيلا]','755','618','0');
INSERT INTO [t934] VALUES (627,'الأسئلة','756','0','0');
