INSERT INTO [t9351] VALUES (448,'ذكر حمل مصعب لواء رسول الله صلى الله عليه وسلم','3608','381','0');
INSERT INTO [t9351] VALUES (449,'سويبط بن سعد بن حرملة بن مالك - وكان مالك شاعرا - ابن عميلة بن السباق بن عبد الدار بن قصي، وأمه هنيدة بنت خباب أبي سرحان بن منقذ بن سبيع بن جعثمة بن سعد بن مليح من خزاعة، وكان سويبط من مهاجرة الحبشة','3616','381','0');
