INSERT INTO [t9351] VALUES (1023,'ناجية بن جندب الأسلمي من بني سهم بطن من أسلم. شهد مع رسول الله صلى الله عليه وسلم الحديبية , واستعمله رسول الله صلى الله عليه وسلم على هديه حين توجه إلى الحديبية , وأمره أن يقدمها إلى ذي الحليفة','6688','850','0');
