INSERT INTO [t9351] VALUES (1032,'حجاج بن عمرو الأسلمي وهو أبو حجاج الذي , روى عنه عروة بن الزبير، وقد روى حجاج بن حجاج عن أبي هريرة','6703','850','0');
INSERT INTO [t9351] VALUES (1033,'عمرو بن عبد نهم الأسلمي خرج مع رسول الله صلى الله عليه وسلم إلى الحديبية وهو كان دليله على طريق ثنية ذات الحنظل. انطلق أمام رسول الله صلى الله عليه وسلم بأمره حتى وقف به عليها، فقال رسول الله صلى الله عليه وسلم: " والذي نفسي بيده ما مثل هذه الثنية','6706','850','0');
