INSERT INTO [t9351] VALUES (1051,'عبد الله ابن بحينة وبحينة أمه , وهي ابنة الأرت , وهو الحارث بن المطلب بن عبد مناف بن قصي. وأبوه مالك بن القشب , وهو جندب بن نضلة بن عبد الله بن رافع بن محضب بن مبشر بن صعب بن دهمان بن نصر بن زهران بن كعب بن الحارث بن عبد الله بن نصر بن الأزد. غضب على','6804','850','0');
INSERT INTO [t9351] VALUES (1052,'وأخوه لأبيه وأمه جبير بن مالك وأمه بحينة بنت الحارث بن عبد المطلب. صحب النبي صلى الله عليه وسلم , وقتل يوم اليمامة شهيدا سنة اثنتي عشرة في خلافة أبي بكر الصديق','6805','850','0');
