INSERT INTO [t9351] VALUES (1070,'أبو عبد الرحمن الجهني أسلم , وصحب النبي صلى الله عليه وسلم , وروى عنه','6832','850','0');
INSERT INTO [t9351] VALUES (1071,'عبد الله بن خبيب الجهني أسلم , وصحب النبي صلى الله عليه وسلم , وروى عنه','6835','850','0');
INSERT INTO [t9351] VALUES (1072,'الحارث بن عبد الله الجهني','6837','850','0');
INSERT INTO [t9351] VALUES (1073,'عوسجة بن حرملة بن جذيمة بن سبرة بن خديج بن مالك بن المحرث بن مازن بن سعد بن مالك بن رفاعة بن نصر بن غطفان بن قيس بن جهينة قال محمد بن سعد: هكذا نسبه لي هشام بن محمد بن السائب الكلبي. وذكر هشام أن رسول الله صلى الله عليه وسلم عقد لعوسجة بن حرملة على ألف','6839','850','0');
INSERT INTO [t9351] VALUES (1074,'بنة الجهني قال محمد بن سعد: أخبرت عن الوليد بن مسلم، عن ابن لهيعة، عن أبي الزبير، عن جابر بن عبد الله، عن بنة الجهني قال: قال رسول الله صلى الله عليه وسلم: " لا يتعاطى السيف مسلولا "','6840','850','0');
INSERT INTO [t9351] VALUES (1075,'ابن حديدة الجهني وكان له صحبة , وهو الذي أدركه عمر بن الخطاب , فقال: أين تريد؟ ، قال: أردت صلاة العصر، فقال: أسرع فإنك قد طفقت','6841','850','0');
INSERT INTO [t9351] VALUES (1076,'رفاعة بن عرادة الجهني قال بعضهم: ابن عرابة، وابن عرابة أسلم , وصحب النبي صلى الله عليه وسلم','6842','850','0');
