INSERT INTO [t9351] VALUES (1079,'أبو الشموس البلوي وكان ينزل حبقا، أسلم , وصحب النبي صلى الله عليه وسلم','6845','850','0');
INSERT INTO [t9351] VALUES (1080,'طلحة بن البراء بن عمير بن وبرة بن ثعلبة بن غنم بن سري بن سلمة بن أنيف بن جشم بن تميم بن عوذ مناة بن ناج بن تيم بن أراشة بن عامر بن عبيلة بن قسميل بن فران بن بلي. وله حلف في بني عمرو بن عوف من الأنصار. وهو الذي قال له النبي صلى الله عليه وسلم: " اللهم','6846','850','0');
INSERT INTO [t9351] VALUES (1081,'أبو أمامة بن ثعلبة البلوي بن عم أبي بردة بن نيار. خال البراء بن عازب','6847','850','0');
INSERT INTO [t9351] VALUES (1082,'عبد الله بن صيفي بن وبرة بن ثعلبة بن غنم بن سري بن سلمة بن أنيف. وهو في بني عمرو بن عوف. وشهد الحديبية مع رسول الله صلى الله عليه وسلم , وبايع تحت الشجرة بيعة الرضوان. قال: أخبرني بذلك هشام بن محمد بن السائب الكلبي عن أبيه','6849','850','0');
INSERT INTO [t9351] VALUES (1083,'ومن بني عذرة بن سعد بن زيد بن ليث بن سود بن أسلم بن الحاف بن قضاعة','6850','850','0');
INSERT INTO [t9351] VALUES (1084,'خالد بن عرفطة بن أبرهة بن سنان بن صيفي بن الهائلة بن عبد الله بن غيلان بن أسلم بن حزاز بن كاهل بن عذرة. وهو حليف لبني زهرة بن كلاب، صحب النبي صلى الله عليه وسلم وروى عنه، وكان سعد بن أبي وقاص ولاه القتال يوم القادسية , وهو الذي قتل الخوارج يوم النخيلة','6851','850','0');
INSERT INTO [t9351] VALUES (1085,'جمرة بن النعمان بن هوذة بن مالك بن سنان بن البياع بن دليم بن عدي بن حزاز بن كاهل بن عذرة. وكان سيد عذرة، وهو أول أهل الحجاز , قدم على النبي صلى الله عليه وسلم بصدقة بني عذرة، فأقطعه رسول الله صلى الله عليه وسلم رمية سوطه، وحضر فرسه من وادي القرى ,','6852','850','0');
INSERT INTO [t9351] VALUES (1086,'أبو خزامة العذري كان يسكن الجناب , وهي أرض عذرة وبلي. أسلم , وصحب النبي صلى الله عليه وسلم , وروى عنه','6853','850','0');
INSERT INTO [t9351] VALUES (1087,'ومن الأشعريين وهم بنو الأشعر , واسمه نبت بن آدد بن زيد بن يشجب بن عريب بن زيد بن كهلان بن سبأ بن يشجب بن يعرب بن قحطان','6854','850','0');
INSERT INTO [t9351] VALUES (1088,'أبو بردة بن قيس بن سليم بن حضار بن حرب بن عامر بن عنز بن بكر بن عامر بن عذر بن وائل بن ناجية بن الجماهر بن الأشعر وهو أخو أبي موسى الأشعري، أسلم , وهاجر من بلاد قومه , فوافق قدومه المدينة مع من هاجر من الأشعريين، ويقال: كانوا خمسين رجلا قدوم أهل','6855','850','0');
INSERT INTO [t9351] VALUES (1089,'أبو عامر الأشعري وكان ممن قدم من الأشعريين على رسول الله صلى الله عليه وسلم , وشهد معه فتح مكة وحنين، وبعثه رسول الله صلى الله عليه وسلم يوم حنين في آثار من توجه إلى أوطاس من المشركين من هوازن، وعقد له رسول الله صلى الله عليه وسلم لواء، فانتهى إلى','6856','850','0');
