INSERT INTO [t9351] VALUES (1093,'ومن الحضارمة وهم من اليمن','6865','850','0');
INSERT INTO [t9351] VALUES (1094,'العلاء بن الحضرمي واسم الحضرمي عبد الله بن ضماد بن سلمى بن أكبر , من حضرموت من اليمن، وكان حليفا لبني أمية بن عبد شمس بن عبد مناف. وأخوه ميمون بن الحضرمي صاحب البئر التي بأعلى مكة بالأبطح يقال لها: بئر ميمون مشهورة على طريق أهل العراق. وكان حفرها في','6866','850','0');
