INSERT INTO [t9351] VALUES (2136,'زيد بن وهب الجهني أحد بني حسل بن نصر بن مالك بن عدي بن الطول بن عوف بن غطفان بن قيس بن جهينة. من قضاعة , ويكنى زيد أبا سليمان. وروى زيد، عن عمر، وعلي، وعبد الله، وحذيفة، وشهد مع علي بن أبي طالب مشاهده','9694','2121','0');
INSERT INTO [t9351] VALUES (2137,'عبد الله بن سخبرة الأزدي ويكنى أبا معمر روى عن عمر وعلي وعبد الله وخباب وأبي مسعود وعلقمة , وقد روى من حديث إسرائيل عن أبي معمر أنه سمع أبا بكر الصديق يقول: كفر بالله ادعاء نسب لا يعرف وليس ذلك عندي بثبت','9698','2121','0');
INSERT INTO [t9351] VALUES (2138,'يزيد بن شريك التيمي وهو أبو إبراهيم التيمي روى عن عمر وعلي وعبد الله بن مسعود وسعد بن أبي وقاص وحذيفة وأبي ذر , وكان عريف قومه وكان ثقة وله أحاديث','9701','2121','0');
INSERT INTO [t9351] VALUES (2139,'أبو عمرو الشيباني واسمه سعد بن إياس شهد القادسية وروى عن عمر وعلي وعبد الله وحذيفة وأبي مسعود الأنصاري وكان كبيرا له سن عالية وكان ثقة وله أحاديث','9702','2121','0');
INSERT INTO [t9351] VALUES (2140,'زر بن حبيش الأسدي أحد بني غاضرة بن مالك بن ثعلبة بن دودان بن أسد بن خزيمة , ويكنى أبا مريم , روى عن عمر وعلي وعبد الله وعبد الرحمن بن عوف وأبي بن كعب وحذيفة وأبي وائل','9705','2121','0');
INSERT INTO [t9351] VALUES (2141,'عمرو بن شرحبيل وهو أبو ميسرة الهمداني ثم الوادعي روى عن عمر وعلي وعبد الله','9714','2121','0');
INSERT INTO [t9351] VALUES (2142,'عبد الرحمن بن أبي ليلى , واسمه يسار بن بلال بن بليل بن أحيحة بن الجلاح بن الحريش بن جحجبا بن كلفة بن عوف بن عمرو بن عوف. من الأوس. قال: ويكنى عبد الرحمن أبا عيسى. روى عن عمر , وعلي , وعبد الله , وأبي بن كعب , وسهل بن حنيف , وخوات بن جبير , وحذيفة ,','9740','2121','0');
INSERT INTO [t9351] VALUES (2143,'عبد الله بن عكيم الجهني ويكنى أبا معبد روى عن عمر , وعثمان , وعلي , وعبد الله , وكان كبيرا قد أدرك الجاهلية','9762','2121','0');
INSERT INTO [t9351] VALUES (2144,'عبد الله بن أبي الهذيل العنزي من ربيعة , ويكنى أبا المغيرة. روى عن عمر , وعلي , وعبد الله بن مسعود , وعمار بن ياسر , وابن عباس , وعبد الله بن عمرو , وأبي زرعة بن عمرو بن جرير','9774','2121','0');
INSERT INTO [t9351] VALUES (2145,'حارثة بن مضرب العبدي روى عن عمر , وعلي , وعبد الله , وعمار , وأبي موسى الأشعري , وفرات بن حيان العجلي , والوليد بن عقبة','9778','2121','0');
INSERT INTO [t9351] VALUES (2146,'عبد الله بن سلمة الجملي من مراد. روى عن عمر , وعلي , وعبد الله وسعد بن أبي وقاص , وعمار بن ياسر , وسلمان قال: أخبرنا إسحاق بن منصور، عن زهير، عن أبي إسحاق عن أبي العالية، وهو عبد الله بن سلمة','9780','2121','0');
INSERT INTO [t9351] VALUES (2147,'مرة بن شراحيل الهمداني وهو مرة الخير , ومرة الطيب. روى عن عمر , وعلي , وعبد الله','9782','2121','0');
INSERT INTO [t9351] VALUES (2148,'عبيد بن نضيلة الخزاعي ويكنى أبا معاوية روى عن عمر , وعبد الله وروى عن علي في الفريضة وقال يحيى بن آدم: عن الحسن بن صالح، قال: قرأ يحيى بن وثاب على عبيد بن نضيلة , وقرأ عبيد بن نضيلة على علقمة , وقرأ علقمة على عبد الله , فأي قراءة أصح من هذه؟ وقال غير','9784','2121','0');
INSERT INTO [t9351] VALUES (2149,'ومن هذه الطبقة , ممن روى عن عمر بن الخطاب , وعبد الله بن مسعود , ولم يرو عن علي بن أبي طالب','9785','2121','0');
INSERT INTO [t9351] VALUES (2150,'عمرو بن ميمون الأودي أود بن صعب بن سعد العشيرة، من مذحج , روى عن عمر , وعبد الله , وسمع من معاذ باليمن في حياة رسول الله صلى الله عليه وسلم , وروى عن أبي مسعود الأنصاري , وعبد الله بن عمرو , وسلمان بن ربيعة , والربيع بن خثيم','9786','2121','0');
INSERT INTO [t9351] VALUES (2151,'المعرور بن سويد الأسدي أحد بني سعد بن الحارث بن ثعلبة بن دودان بن أسد روى عن عمر , وعبد الله , وأبي ذر , قال أبو نعيم: بلغ المعرور بن سويد عشرين ومائة سنة','9789','2121','0');
INSERT INTO [t9351] VALUES (2152,'همام بن الحارث النخعي روى عن عمر , وعبد الله , وأبي مسعود الأنصاري وأبي الدرداء وعدي بن حاتم , وجرير بن عبد الله , وعائشة وتوفي بالكوفة في ولاية الحجاج','9791','2121','0');
