INSERT INTO [t9351] VALUES (2838,'منصور بن أبي الأسود مولى لبني ليث وكان تاجرا وكان كثير الحديث','11347','2804','0');
INSERT INTO [t9351] VALUES (2839,'وأخوه صالح بن أبي الأسود وكان أيضا يحدث','11348','2804','0');
INSERT INTO [t9351] VALUES (2840,'عبد الرحمن بن حميد الرؤاسي وهو أبو حميد بن عبد الرحمن وكان ثقة وله أحاديث','11349','2804','0');
INSERT INTO [t9351] VALUES (2841,'وأخوه إبراهيم بن حميد الرؤاسي صاحب إسماعيل بن أبي خالد وقد أكثر الرواية عن إسماعيل','11350','2804','0');
INSERT INTO [t9351] VALUES (2842,'مسلمة بن جعفر','11351','2804','0');
INSERT INTO [t9351] VALUES (2843,'جعفر بن زياد الأحمر مولى مزاحم بن زفر من تيم الرباب، سمعت أبا نعيم قال: مات جعفر بالكوفة سنة سبع وسبعين ومائة في خلافة هارون','11352','2804','0');
INSERT INTO [t9351] VALUES (2844,'عمرو بن أبي المقدام العجلي توفي في خلافة هارون واسم أبي المقدام: ثابت. وليس عمرو عندهم في الحديث بشيء ومنهم من لا يكتب حديثه لضعفه ورأيه وكان متشيعا مفرطا','11353','2804','0');
INSERT INTO [t9351] VALUES (2845,'سلمة بن صالح الأحمر الجعفي ويكنى أبا إسحاق وكان قد طلب الحديث ثم اضطرب عليه حفظه فضعفه الناس وولي قضاء واسط ثم عزل وتوفي ببغداد سنة ثمان وثمانين ومائة في خلافة هارون','11354','2804','0');
INSERT INTO [t9351] VALUES (2846,'حشرج بن نباتة ويكنى أبا مكرم روى عن سعيد بن جمهان','11355','2804','0');
INSERT INTO [t9351] VALUES (2847,'القاسم بن معن بن عبد الرحمن بن عبد الله بن مسعود الهذلي حليف بني زهرة من قريش ويكنى القاسم أبا عبد الله ولي قضاء الكوفة ولم يرتزق عليه شيئا حتى مات وكان ثقة عالما بالحديث , والفقه , والشعر وأيام الناس وكان يقال له شعبي زمانه , وكان سخيا','11356','2804','0');
INSERT INTO [t9351] VALUES (2848,'أبو شيبة واسمه إبراهيم بن عثمان العبسي من ولد أبي سعدة وقد روى عن أبي سعدة الحديث وروى أبو سعدة عن ابن عباس وكان أبو شيبة قد ولي قضاء واسط وتوفي في خلافة هارون وهو ضعيف الحديث وقد روى عنه يزيد بن هارون','11357','2804','0');
INSERT INTO [t9351] VALUES (2849,'أبو المحياة واسمه يحيى بن يعلى بن حرملة بن الجليد بن عمار بن أرطأة بن زهير بن أمية بن جشم بن عدي بن الحارث بن تيم الله بن ثعلبة، مات بالكوفة سنة ثمان ومائة في خلافة هارون وهو ابن ست وتسعين سنة','11358','2804','0');
INSERT INTO [t9351] VALUES (2850,'المبارك بن سعيد بن مسروق أخو سفيان الثوري توفي بالكوفة في أول سنة ثمانين ومائة وكانت عنده أحاديث','11359','2804','0');
INSERT INTO [t9351] VALUES (2851,'إسماعيل بن إبراهيم بن المهاجر البجلي','11360','2804','0');
INSERT INTO [t9351] VALUES (2852,'حمزة الزيات بن عمارة ويكنى أبا عمارة مولى لآل عكرمة بن ربعي التيمي، وكان يجلب الزيت من الكوفة إلى حلوان ويجلب من حلوان الجبن والجوز إلى الكوفة وكان صاحب قراءة القرآن وصاحب فرائض قال محمد بن سعد: أخبرت أن سفيان بن سعيد الثوري قال له: يا ابن عمارة، أما','11361','2804','0');
INSERT INTO [t9351] VALUES (2853,'محمد بن أبان بن صالح بن عمير بن عبيد مولى عبد الله بن خالد بن أسيد بن أبي العيص بن أمية بن عبد شمس ويكنى أبا عمرو، وكانت له رواية للحديث ومات يوم الرءوس يوم الأحد لإحدى عشرة ليلة خلت من ذي الحجة سنة خمس وسبعين ومائة في خلافة هارون وهو ابن إحدى وثمانين','11362','2804','0');
INSERT INTO [t9351] VALUES (2854,'الطبقة السابعة','11363','0','0');
INSERT INTO [t9351] VALUES (2855,'أبو بكر بن عياش مولى واصل بن حيان الأحدب الأسدي وهو من الطبقة التي قبل هذه الطبقة ولكنه بقي وعمر حتى كتب عنه الأحداث وكان من العباد قال: وقال وكيع، ونظر إليه يصلي يوم الجمعة حين يسلم الإمام إلى العصر فقال: أعرف هذا الشيخ بهذه الصلاة منذ أربعين سنة','11364','2854','0');
INSERT INTO [t9351] VALUES (2856,'سعير بن الخمس من بني عمرو بن سعد بن زيد مناة بن تميم، وكان رجلا شريفا يجتمع إليه أصحابه، وكان مألفا، وكان صاحب سنة وجماعة وكانت عنده أحاديث','11365','2854','0');
INSERT INTO [t9351] VALUES (2857,'عبد السلام بن حرب الملائي، ويكنى أبا بكر، توفي بالكوفة سنة سبع وثمانين ومائة في خلافة هارون وكان به ضعف في الحديث وكان عسرا','11366','2854','0');
INSERT INTO [t9351] VALUES (2858,'المطلب بن زياد بن أبي زهير القرشي، ويكنى أبا محمد وكان نازلا في ثقيف وهو مولى جابر بن سمرة السوائي , وجابر حليف لبني زهرة من قريش ولذلك قيل للمطلب بن زياد: القرشي وكان ضعيفا في الحديث جدا توفي بالكوفة سنة خمس وثمانين ومائة في خلافة هارون','11367','2854','0');
INSERT INTO [t9351] VALUES (2859,'سيف بن هارون البرجمي من بني تميم من أنفسهم وقد روي عنه','11368','2854','0');
INSERT INTO [t9351] VALUES (2860,'وأخوه سنان بن هارون وقد روي عنه أيضا','11369','2854','0');
INSERT INTO [t9351] VALUES (2861,'عمر بن عبيد الطنافسي ويكنى أبا حفص مولى لإياد بن نزار بن معد، توفي بالكوفة سنة خمس وثمانين ومائة في خلافة هارون , وكان شيخا قديما , وكان ثقة إن شاء الله','11370','2854','0');
INSERT INTO [t9351] VALUES (2862,'زفر بن الهذيل العنبري من أنفسهم ويكنى أبا الهذيل وكان قد سمع الحديث ونظر في الرأي فغلب عليه ونسب إليه ومات بالبصرة وأوصى إلى خالد بن الحارث وعبد الواحد بن زياد، وكان أبوه الهذيل على أصبهان وكان أخوه صباح بن الهذيل على صدقة بني تميم , ولم يكن زفر في','11371','2854','0');
INSERT INTO [t9351] VALUES (2863,'عمار بن محمد ابن أخت سفيان الثوري توفي في المحرم سنة اثنتين وثمانين ومائة في خلافة هارون وكان ثقة وقد روي عنه','11372','2854','0');
INSERT INTO [t9351] VALUES (2864,'علي بن مسهر ويكنى أبا الحسن من عائذة قريش , من أنفسهم وكان قد ولي القضاء بالموصل وكان ثقة كثير الحديث','11373','2854','0');
INSERT INTO [t9351] VALUES (2865,'مسعود بن سعد الجعفي وقد روي عنه','11374','2854','0');
INSERT INTO [t9351] VALUES (2866,'عمر بن شبيب المسلي من مذحج وقد روي عنه أيضا','11375','2854','0');
INSERT INTO [t9351] VALUES (2867,'عمار بن سيف الضبي وإليه أوصى سفيان الثوري رحمه الله ووضع كتبه عنده وقال له: ادفنها إذا مت','11376','2854','0');
INSERT INTO [t9351] VALUES (2868,'محمد بن الفضيل بن غزوان الضبي مولى لهم ويكنى أبا عبد الرحمن','11377','2854','0');
INSERT INTO [t9351] VALUES (2869,'عبد الله بن إدريس بن يزيد بن عبد الرحمن الأودي من مذحج ويكنى أبا محمد','11379','2854','0');
INSERT INTO [t9351] VALUES (2870,'موسى بن محمد الأنصاري وقد روي عنه','11381','2854','0');
INSERT INTO [t9351] VALUES (2871,'حفص بن غياث بن طلق بن معاوية بن مالك بن الحارث بن ثعلبة بن عامر بن ربيعة بن جشم بن وهبيل بن سعد بن مالك بن النخع من مذحج','11382','2854','0');
INSERT INTO [t9351] VALUES (2872,'إبراهيم بن حميد بن عبد الرحمن الرؤاسي ويكنى أبا إسحاق , مات سنة ثمان وسبعين ومائة في خلافة هارون','11384','2854','0');
INSERT INTO [t9351] VALUES (2873,'القاسم بن مالك المزني ويكنى أبا جعفر وكان ثقة صالح الحديث','11385','2854','0');
INSERT INTO [t9351] VALUES (2874,'عبد الرحمن بن عبد الملك بن أبجر الكناني من أنفسهم مات سنة إحدى وثمانين ومائة في خلافة هارون وهو صلى على سفيان الثوري بالبصرة وكان خيرا فاضلا صاحب سنة','11386','2854','0');
INSERT INTO [t9351] VALUES (2875,'عبدة بن سليمان بن حاجب بن زرارة بن عبد الرحمن بن صرد بن سمير بن مليل بن عبد الله بن أبي بكر بن كلاب , والذي أدرك الإسلام وأسلم صرد , ويكنى عبدة أبا محمد وكان اسمه عبد الرحمن فلقب عبدة فغلب عليه ومات بالكوفة لثلاث خلون من رجب سنة ثمان وثمانين ومائة في','11387','2854','0');
INSERT INTO [t9351] VALUES (2876,'أبو خالد الأحمر سليمان بن حيان مولى لبني جعفر بن كلاب توفي بالكوفة في شوال سنة تسع وثمانين ومائة في خلافة هارون وكان ثقة كثير الحديث','11388','2854','0');
INSERT INTO [t9351] VALUES (2877,'يحيى بن اليمان العجلي من أنفسهم ويكنى أبا زكريا توفي بالكوفة في رجب سنة تسع وثمانين ومائة في خلافة هارون وكان كثير الحديث كثير الغلط لا يحتج به إذا خولف','11389','2854','0');
INSERT INTO [t9351] VALUES (2878,'أبو شهاب الحناط واسمه عبد ربه بن نافع وكان ثقة كثير الحديث','11390','2854','0');
INSERT INTO [t9351] VALUES (2879,'عبيد الله بن عبد الرحمن الأشجعي من أنفسهم وكان ثقة','11391','2854','0');
INSERT INTO [t9351] VALUES (2880,'علي بن غراب مولى الوليد بن صخر الفزاري الذي روى عنه إسماعيل بن رجاء حديث الأعمش في عثمان , ويكنى أبا الحسن توفي بالكوفة في أول سنة أربع وثمانين ومائة في خلافة هارون وكان علي صدوقا وفيه ضعف وصحب يعقوب بن داود فتركه الناس','11392','2854','0');
INSERT INTO [t9351] VALUES (2881,'أبو مالك الجبني واسمه عمرو بن هاشم، كان صدوقا ولكنه كان يخطيء كثيرا','11393','2854','0');
INSERT INTO [t9351] VALUES (2882,'علي بن هاشم بن البريد توفي بالكوفة في رجب أو شعبان سنة إحدى وثمانين ومائة في خلافة هارون وهو صالح الحديث صدوق','11394','2854','0');
INSERT INTO [t9351] VALUES (2883,'عبد الرحمن بن محمد المحاربي ويكنى أبا محمد توفي بالكوفة سنة خمس وتسعين ومائة في خلافة محمد بن هارون وكان شيخا ثقة كثير الغلط','11395','2854','0');
INSERT INTO [t9351] VALUES (2884,'عثام بن علي من بني الوحيد ويكنى أبا علي، توفي بالكوفة سنة خمس وتسعين ومائة في خلافة محمد بن هارون وكان ثقة','11396','2854','0');
INSERT INTO [t9351] VALUES (2885,'أبو معاوية الضرير واسمه محمد بن خازم مولى لبني عمرو بن سعد بن زيد مناة بن تميم , رهط سعير بن الخمس وكان ثقة كثير الحديث , يدلس وكان مرجيا توفي بالكوفة سنة خمس وتسعين ومائة فلم يشهده وكيع','11397','2854','0');
INSERT INTO [t9351] VALUES (2886,'عبد الرحمن بن سليمان الداري وكان أصله من الري ولكنه نشأ بالكوفة وسمع الحديث ويكنى أبا علي ومات بالكوفة سنة أربع وثمانين ومائة وكان مولى لبني كنانة وكان يعرف بالخلقاني وقد روي عنه','11398','2854','0');
INSERT INTO [t9351] VALUES (2887,'يحيى بن عبد الملك بن أبي غنية ويكنى أبا زكريا، وكان نازلا في بني سعد بن همام، توفي بالكوفة سنة ست أو سبع وثمانين ومائة في خلافة هارون وكان ثقة صالح الحديث','11399','2854','0');
INSERT INTO [t9351] VALUES (2888,'يحيى بن زكرياء بن أبي زائدة ويكنى أبا سعيد توفي بالمدائن وهو قاضيها سنة ثلاث وثمانين ومائة في خلافة هارون وكان ثقة إن شاء الله , وكان استقضاه هارون أمير المؤمنين','11400','2854','0');
INSERT INTO [t9351] VALUES (2889,'أسباط بن محمد القرشي , ويكنى أبا محمد توفي بالكوفة في المحرم سنة مائتين في خلافة عبد الله المأمون , وكان ثقة صدوقا إلا أن فيه بعض الضعف وقد حدثوا عنه','11401','2854','0');
INSERT INTO [t9351] VALUES (2890,'محمد بن بشر بن الفرافصة العبدي ويكنى أبا عبد الله توفي بالكوفة في جمادى الأولى سنة ثلاث ومائتين في خلافة المأمون وكان ثقة كثير الحديث','11402','2854','0');
INSERT INTO [t9351] VALUES (2891,'عبد الله بن نمير بن عبد الله بن أبي حية بن سرح بن سلمة بن سعد بن الحكم بن سلمان بن مالك وهو خارف بن عبد الله بن كثير بن مالك بن جشم بن حاشد من همدان , الهمداني ثم الخارفي , ويكنى أبا هشام , توفي بالكوفة في شهر ربيع الأول سنة تسع وتسعين ومائة وصلى عليه','11403','2854','0');
INSERT INTO [t9351] VALUES (2892,'وكيع بن الجراح بن مليح بن عدي بن الفرس بن سفيان بن الحارث بن عمرو بن عبيد بن رؤاس بن كلاب بن ربيعة بن عامر بن صعصعة ويكنى أبا سفيان , حج سنة ست وتسعين ومائة ثم انصرف من الحج فمات بفيد في المحرم سنة سبع وتسعين ومائة في خلافة محمد بن هارون , وكان ثقة','11404','2854','0');
INSERT INTO [t9351] VALUES (2893,'أبو أسامة واسمه حماد بن أسامة بن زيد بن سليمان بن زياد وهو المعتق مولى الحسن بن سعد مولى الحسن بن علي بن أبي طالب عليهما السلام. قال: وسمعت من يذكر أن زيادا المعتق مولى الحسن بن علي بن أبي طالب عليهما السلام نفسه , وكانوا يسكنون مع آل الحسن بن سعد في','11405','2854','0');
INSERT INTO [t9351] VALUES (2894,'الحسن بن ثابت من بني تغلب من أنفسهم وكان يعرف بابن الروزكار ويكنى أبا علي , وكان من أصحاب عبد الله بن إدريس ونظرائه روى عن الأعمش وغيره , ثم امتنع من الحديث فلم يحدث حتى مات , وكان معروفا بالحديث','11407','2854','0');
INSERT INTO [t9351] VALUES (2895,'عقبة بن خالد السكوني من أنفسهم روى عن الأعمش وإسماعيل بن أبي خالد وعبد الملك بن أبي سليمان وهشام بن عروة وعبيد الله بن عمر وموسى بن محمد بن إبراهيم , ومات بالكوفة سنة ثمان وثمانين ومائة في خلافة هارون','11408','2854','0');
INSERT INTO [t9351] VALUES (2896,'زياد بن عبد الله بن الطفيل البكائي من بني عامر بن صعصعة ويكنى أبا محمد، سمع من منصور بن المعتمر ومغيرة والأعمش وإسماعيل بن أبي خالد , ورجال أهل الكوفة وسمع الفرائض من محمد بن سالم وسمع المغازي من محمد بن إسحاق وقدم بغداد فحدثهم بها , وبالفرائض وغير ذلك','11409','2854','0');
INSERT INTO [t9351] VALUES (2897,'أحمد بن بشير ويكنى أبا بكر مولى لبني شيبان، روى عن الأعمش وهشام بن عروة وإسماعيل بن أبي خالد وعبد الملك بن أبي سليمان وغيرهم','11410','2854','0');
INSERT INTO [t9351] VALUES (2898,'جعفر بن عون بن جعفر بن عمرو بن حريث المخزومي ويكنى أبا عون، توفي بالكوفة يوم الاثنين لإحدى عشرة ليلة خلت من شعبان سنة تسع ومائتين في خلافة المأمون وكان ثقة كثير الحديث','11411','2854','0');
INSERT INTO [t9351] VALUES (2899,'حسين بن علي الجعفي ويكنى أبا عبد الله، كان هو وأخ له يقال له: محمد توأمين ولدا في بطن فتزوج محمد وولد له أولاد ولم يتزوج حسين قط ولم يتسر وأذن في مسجد جعفي ستين سنة وكان عابدا ناسكا له فضل قارئا للقرآن , يقرئ الناس وقد روى عن ليث بن أبي سليم وموسى','11412','2854','0');
INSERT INTO [t9351] VALUES (2900,'عائذ بن حبيب بياع الهروي ويكنى أبا أحمد وهو مولى لبني عبس وكان جار عبيد الله بن موسى لزيق داره , وكان ثقة إن شاء الله','11413','2854','0');
INSERT INTO [t9351] VALUES (2901,'يعلى بن عبيد بن أبي أمية الطنافسي، ويكنى أبا يوسف مولى لإياد أخبرنا طلق بن غنام النخعي قال: ولد يعلى بن عبيد سنة سبع عشرة ومائة في خلافة هشام بن عبد الملك وتوفي بالكوفة يوم الأحد لخمس ليال خلون من شوال سنة تسع ومائتين في خلافة المأمون، وكان ثقة كثير','11414','2854','0');
INSERT INTO [t9351] VALUES (2902,'وأخوه محمد بن عبيد بن أبي أمية الطنافسي ويكنى أبا عبد الله وكان قد نزل بغداد دهرا ثم رجع إلى الكوفة فمات بها قبل يعلى في سنة أربع ومائتين في خلافة المأمون وكان ثقة كثير الحديث وكان صاحب سنة وجماعة','11415','2854','0');
INSERT INTO [t9351] VALUES (2903,'عمران بن عيينة أخو سفيان بن عيينة ويكنى أبا إسحاق توفي سنة تسع وتسعين ومائة في خلافة المأمون وقد روى عن أبي حيان التيمي وغيره','11416','2854','0');
INSERT INTO [t9351] VALUES (2904,'يحيى بن سعيد بن أبان بن سعيد بن العاص بن سعيد بن العاص بن أمية بن عبد شمس ويكنى أبا أيوب، روى عن الأعمش وهشام بن عروة ويحيى بن سعيد , وإسماعيل بن أبي خالد وغيرهم. وروى المغازي عن محمد بن إسحاق , وتحول فنزل بغداد فمات بها','11417','2854','0');
INSERT INTO [t9351] VALUES (2905,'وأخوه عبد الملك بن سعيد وكان أديبا عالما بالنجوم وأيام الناس','11418','2854','0');
INSERT INTO [t9351] VALUES (2906,'محاضر بن المورع الهمداني ثم اليامي من أنفسهم ويكنى أبا المورع , كان يسكن جبانة كندة. روى عن الأعمش وهشام بن عروة وغيرهما وكان ثقة صدوقا ممتنعا بالحديث ثم حدث بعد ذلك وتوفي بالكوفة في شوال سنة ست ومائتين في خلافة المأمون','11419','2854','0');
INSERT INTO [t9351] VALUES (2907,'حميد بن عبد الرحمن بن حميد الرؤاسي ويكنى أبا عوف , وكان إمام مسجد وكيع بن الجراح , وروى عن الأعمش وروى عن الحسن بن صالح رواية كثيرة وتوفي بالكوفة سنة سبعين ومائة في خلافة هارون وكان ثقة كثير الحديث ولم يكتب الناس كل ما عنده','11420','2854','0');
INSERT INTO [t9351] VALUES (2908,'محمد بن ربيعة ويكنى أبا عبد الله توفي ببغداد وقد روي عنه','11421','2854','0');
INSERT INTO [t9351] VALUES (2909,'سعيد بن محمد الثقفي الوراق ويكنى أبا الحسن توفي ببغداد وكان ضعيفا وقد كتبوا عنه','11422','2854','0');
INSERT INTO [t9351] VALUES (2910,'قران بن تمام الأسدي ويكنى أبا تمام وكان. . . . فقدم بغداد فمات بها وكانت عنده أحاديث ومنهم من يستضعفه','11423','2854','0');
INSERT INTO [t9351] VALUES (2911,'يونس بن بكير مولى بني شيبان ويكنى أبا بكر، وهو صاحب محمد بن إسحاق صاحب المغازي , توفي بالكوفة سنة تسع وتسعين ومائة في خلافة المأمون','11424','2854','0');
INSERT INTO [t9351] VALUES (2912,'عبد الحميد بن عبد الرحمن الحماني ويكنى أبا يحيى وكان ضعيفا','11425','2854','0');
INSERT INTO [t9351] VALUES (2913,'عبيد الله بن موسى بن المختار العبسي ويكنى أبا محمد , قرأ على عيسى بن عمر وعلى علي بن صالح بن حي , وكان يقرئ القرآن في مسجده , وروى عن الأعمش وهشام بن عروة وإسماعيل بن أبي خالد وزكرياء بن أبي زائدة وعثمان بن الأسود ومحمد بن عبد الرحمن بن أبي ليلى وغيرهم،','11426','2854','0');
INSERT INTO [t9351] VALUES (2914,'أبو نعيم الفضل بن دكين بن حماد بن زهير مولى لآل طلحة بن عبيد الله التيمي روى عن الأعمش وزكرياء بن أبي زائدة ومسعر بن كدام وجعفر بن برقان وغيرهم , وتوفي بالكوفة ليلة الثلاثاء ودفن يوم الثلاثاء لانسلاخ شعبان سنة تسع عشرة ومائتين','11427','2854','0');
INSERT INTO [t9351] VALUES (2915,'محمد بن القاسم الأسدي ويكنى أبا إبراهيم وكان يبيع الحمر والإبل بالكناسة روى عن الأوزاعي وغيره وتوفي بالكوفة , وكانت عنده أحاديث','11430','2854','0');
INSERT INTO [t9351] VALUES (2916,'محمد بن عبد الأعلى بن كناسة الأسدي من أنفسهم وهو ابن أخت إبراهيم بن أدهم الزاهد روى عن الأعمش وهشام بن عروة وغيرهما , وكان عالما بالعربية وأيام الناس والشعر توفي بالكوفة لثلاث ليال خلون من شوال سنة تسع ومائتين في خلافة المأمون','11431','2854','0');
INSERT INTO [t9351] VALUES (2917,'علي بن ظبيان العبسي ويكنى أبا الحسن ولي قضاء الشرقية ببغداد ثم ولاه هارون أمير المؤمنين القضاء معه في عسكره حيث كان , فكان يجلس في المسجد الذي ينسب إلى الخلد للقضاء وخرج مع هارون حين توجه إلى خراسان فمات بقرماسين سنة اثنتين وتسعين ومائة وقد روى علي عن','11432','2854','0');
INSERT INTO [t9351] VALUES (2918,'الطبقة الثامنة','11433','0','0');
INSERT INTO [t9351] VALUES (2919,'يحيى بن آدم بن سليمان ويكنى أبا زكريا مولى لخالد بن خالد بن عمارة بن عقبة بن أبي معيط توفي بفم الصلح في النصف من شهر ربيع الأول سنة ثلاث ومائتين في خلافة المأمون، وقد روى عن سفيان الثوري وغيره وكان ثقة','11434','2918','0');
INSERT INTO [t9351] VALUES (2920,'زيد بن الحباب العكلي مولى لهم ويكنى أبا الحسين توفي بالكوفة في ذي الحجة سنة ثلاث ومائتين في خلافة المأمون','11435','2918','0');
INSERT INTO [t9351] VALUES (2921,'أبو أحمد الزبيري واسمه محمد بن عبد الله بن الزبير مولى لبني أسد وهو ابن أخي فضيل الرماني توفي بالأهواز في جمادى الأولى سنة ثلاث ومائتين في خلافة المأمون وكان صدوقا كثير الحديث','11436','2918','0');
INSERT INTO [t9351] VALUES (2922,'أبو داود الحفري واسمه عمر بن سعد وكان أبوه مؤدبا , وكان أبو داود عمر بن سعد ناسكا له فضل وتواضع , زاهدا وكان من أصحاب سفيان الثوري , توفي بالكوفة في جمادى الآخرة سنة ثلاث ومائتين في خلافة المأمون','11437','2918','0');
INSERT INTO [t9351] VALUES (2923,'قبيصة بن عقبة ويكنى أبا عامر من بني سواءة بن عامر بن صعصعة توفي بالكوفة في صفر سنة خمس عشرة ومائتين في خلافة المأمون وكان ثقة صدوقا كثير الحديث عن سفيان الثوري','11438','2918','0');
INSERT INTO [t9351] VALUES (2924,'عمرو بن محمد العنقزي، كان يبيع متاعا يقال له: العنقز وكان مولى لآل زياد بن أبي سفيان وكانت عنده أحاديث الأنبياء وغيرهم , وكان جارا لأبي داود الحفري بالكوفة يصليان في مسجد منزلهما في حفر السبيع','11439','2918','0');
INSERT INTO [t9351] VALUES (2925,'معاوية بن هشام القصار مولى بني أسد ويكنى أبا الحسن توفي بالكوفة وكان صدوقا كثير الحديث','11440','2918','0');
INSERT INTO [t9351] VALUES (2926,'عبد العزيز بن أبان القرشي من ولد سعيد بن العاص ويكنى أبا خالد، وكان قد ولي قضاء واسط ثم عزل فقدم إلى بغداد فنزلها وتوفي بها يوم الأربعاء لأربع عشرة ليلة خلت من رجب سنة سبع ومائتين في خلافة المأمون وكان كثير الرواية عن سفيان ثم خلط بعد ذلك فأمسكوا عن','11441','2918','0');
INSERT INTO [t9351] VALUES (2927,'علي بن قادم ويكنى أبا الحسن، وتوفي بالكوفة سنة ثلاث عشرة ومائتين في خلافة المأمون وكان ممتنعا منكر الحديث شديد التشيع','11442','2918','0');
INSERT INTO [t9351] VALUES (2928,'ثابت بن محمد الكناني ويكنى أبا إسماعيل وكان عابدا ناسكا روى عن مسعر بن كدام وغيره وتوفي بالكوفة في ذي الحجة سنة خمس عشرة ومائتين في خلافة المأمون','11443','2918','0');
INSERT INTO [t9351] VALUES (2929,'هشام بن المقدام','11444','2918','0');
INSERT INTO [t9351] VALUES (2930,'أبو غسان واسمه مالك بن إسماعيل بن زياد بن درهم، مولى كليب بن عامر النهدي أحد بني خزيمة , وأم أبي غسان ابنة إسماعيل بن حماد بن أبي سليمان , وحماد بن أبي سليمان خال إسماعيل بن أبي غسان، وتوفي أبو غسان بالكوفة في غرة شهر ربيع الآخر سنة تسع عشرة ومائتين في','11445','2918','0');
INSERT INTO [t9351] VALUES (2931,'أحمد بن عبد الله بن يونس ويكنى أبا عبد الله، مولى لبني يربوع من بني تميم، مات بالكوفة يوم الجمعة لخمس ليال بقين من شهر ربيع الآخر سنة سبع وعشرين ومائتين وكان ثقة صدوقا صاحب سنة وجماعة','11446','2918','0');
INSERT INTO [t9351] VALUES (2932,'طلق بن غنام بن طلق بن معاوية بن مالك بن الحارث بن ثعلبة بن عامر بن ربيعة بن عامر بن جشم بن وهبيل بن سعد بن مالك بن النخع من مذحج ويكنى طلق أبا محمد وهو ابن عم حفص بن غياث القاضي لحا وكان كاتبه على القضاء','11447','2918','0');
INSERT INTO [t9351] VALUES (2933,'إسحاق بن منصور السلولي مولى لهم مات سنة خمس ومائتين بالكوفة في خلافة المأمون','11449','2918','0');
INSERT INTO [t9351] VALUES (2934,'بكر بن عبد الرحمن بن عبد الله بن عيسى بن عبد الرحمن بن أبي ليلى الأنصاري، سمع من عيسى بن المختار بن عبد الله بن أبي ليلى مصنف محمد بن عبد الرحمن بن أبي ليلى، وكان يحدث به عنه، وولي بكر قضاء الكوفة بضع عشرة سنة ثم عزل وتوفي بعد ذلك بالكوفة','11450','2918','0');
INSERT INTO [t9351] VALUES (2935,'خالد بن مخلد القطواني وينتمي إلى بجيلة ويكنى أبا الهيثم وكانت عنده أحاديث عن رجال أهل المدينة وكان متشيعا توفي بالكوفة في النصف من المحرم سنة ثلاث عشرة ومائتين في خلافة المأمون وكان منكر الحديث في التشيع مفرطا وكتبوا عنه ضرورة','11451','2918','0');
INSERT INTO [t9351] VALUES (2936,'إسحاق بن منصور بن حيان بن الحصين بن مالك ابن أخي أبي الهياج الأسدي وكان خيرا فاضلا روى عن أبي كدينة وشريك وأبي الأحوص','11452','2918','0');
INSERT INTO [t9351] VALUES (2937,'عبيد بن سعيد بن أبان بن سعيد بن العاص بن سعيد بن العاص بن أمية، روى عن سفيان وغيره','11453','2918','0');
INSERT INTO [t9351] VALUES (2938,'وأخوه عنبسة بن سعيد بن أبان بن سعيد بن العاص بن سعيد بن العاص ويكنى أبا خالد، وكان ثقة كثير الرواية عن عبد الله بن المبارك وغيره','11454','2918','0');
INSERT INTO [t9351] VALUES (2939,'رباح بن خالد ويكنى أبا علي، روى عن زهير وحسن بن صالح وقيس وشريك وكان كثير الحديث وتوفي بالكوفة قبل أن يكتب عنه','11455','2918','0');
INSERT INTO [t9351] VALUES (2940,'نوفل بن. . . . . ويكنى أبا مسعود الضبي من أنفسهم روى نوفل عن زهير وأبي الأحوص وشريك وابن المبارك وغيرهم وكان كثير الحديث وتوفي بالكوفة قبل أن يكتب عنه','11456','2918','0');
INSERT INTO [t9351] VALUES (2941,'عبد الرحيم بن عبد الرحمن بن محمد المحاربي ويكنى أبا زياد، روى عن زائدة بن قدامة وغيره توفي بالكوفة في شعبان سنة إحدى عشرة ومائتين في خلافة المأمون وكان ثقة صدوقا','11457','2918','0');
INSERT INTO [t9351] VALUES (2942,'زكرياء بن عدي ويكنى أبا يحيى، مولى لبني تيم الله وتوفي ببغداد في جمادى الأولى سنة اثنتي عشرة ومائتين في خلافة المأمون وكان زكرياء رجلا صالحا صدوقا','11458','2918','0');
INSERT INTO [t9351] VALUES (2943,'عبد الرحمن بن مصعب المعني ويكنى أبا يزيد وكان عابدا ناسكا وكانت عنده أحاديث','11459','2918','0');
INSERT INTO [t9351] VALUES (2944,'علي بن عبد الحميد المعني من الأزد وكان أيضا فاضلا خيرا وهو ابن عم عبد الرحمن بن مصعب وكانت عنده أحاديث','11460','2918','0');
INSERT INTO [t9351] VALUES (2945,'عون بن سلام مولى قريش ويكنى أبا محمد، روى عن إسرائيل وزهير وأسباط بن نصر ومنصور بن أبي الأسود وعيسى بن عبد الرحمن السلمي وغيرهم','11461','2918','0');
INSERT INTO [t9351] VALUES (2946,'سويد بن عمرو الكلبي','11462','2918','0');
INSERT INTO [t9351] VALUES (2947,'يحيى بن يعلى بن الحارث المحاربي توفي بالكوفة سنة ست عشرة ومائتين في خلافة المأمون','11463','2918','0');
INSERT INTO [t9351] VALUES (2948,'عمرو بن حماد بن طلحة القناد ويكنى أبا محمد صاحب تفسير أسباط بن نصر عن السدي توفي بالكوفة في شهر ربيع الأول سنة اثنتين وعشرين ومائتين , قال: وكان أصله من أصبهان وصار جده إلى الكوفة ووالى همدان ونزل فيهم عند شهار سوج همدان، توفي في خلافة أبي إسحاق وكان','11464','2918','0');
INSERT INTO [t9351] VALUES (2949,'محمد بن الصلت ويكنى أبا جعفر مولى لبني أسد بن خزيمة','11465','2918','0');
INSERT INTO [t9351] VALUES (2950,'إسماعيل بن أبان الوراق ويكنى أبا إسحاق مولى لكندة','11466','2918','0');
INSERT INTO [t9351] VALUES (2951,'الحسن بن الربيع ويكنى أبا علي، وهو أخو مطير صاحب البواري وكان الحسن من أصحاب عبد الله بن المبارك وشهده حين مات بهيت وهو ولي تغميضه وتوفي الحسن بالكوفة يوم السبت في غرة شهر رمضان سنة إحدى وعشرين ومائتين في خلافة أبي إسحاق','11467','2918','0');
INSERT INTO [t9351] VALUES (2952,'عبد الحميد بن صالح ويكنى أبا محمد وكان ينزل في بني شيطان بالكوفة وقد روى عن زهير وهريم','11468','2918','0');
INSERT INTO [t9351] VALUES (2953,'الحسن بن بشر بن سلم بن المسيب البجلي ويكنى أبا علي','11469','2918','0');
INSERT INTO [t9351] VALUES (2954,'أحمد بن المفضل مولى قريش وهو ابن عم عمرو العنقزي مات في ذي القعدة سنة خمس عشرة ومائتين في خلافة المأمون وكان راوية عن أسباط بن نصر','11470','2918','0');
INSERT INTO [t9351] VALUES (2955,'عثمان بن حكيم الأودي روى عن شريك وغيره وكان ثقة','11471','2918','0');
INSERT INTO [t9351] VALUES (2956,'وأخوه علي بن حكيم الأودي ويكنى أبا الحسن، روى عن شريك وغيره','11472','2918','0');
INSERT INTO [t9351] VALUES (2957,'شهاب بن عباد العبدي مات بالكوفة يوم السبت لليلتين خلتا من جمادى الأولى سنة أربع وعشرين ومائتين في خلافة أبي إسحاق بن هارون','11473','2918','0');
INSERT INTO [t9351] VALUES (2958,'الهيثم بن عبيد الله المفتي من قريش ويكنى أبا محمد','11474','2918','0');
INSERT INTO [t9351] VALUES (2959,'يحيى بن عبد الحميد بن عبد الرحمن الحماني ويكنى أبا زكرياء مات بسامراء في شهر رمضان سنة ثلاثين ومائتين','11475','2918','0');
INSERT INTO [t9351] VALUES (2960,'يوسف بن البهلول ويكنى أبا يعقوب، من بني أبان بن دارم من بني تميم من أنفسهم، وهو صاحب المغازي سمعها من عبد الله بن إدريس عن محمد بن إسحاق , توفي بالكوفة في شهر ربيع الآخر أو جمادى الأولى سنة ثماني عشرة ومائتين في خلافة المأمون','11476','2918','0');
INSERT INTO [t9351] VALUES (2961,'سعيد بن شرحبيل الكندي ويكنى أبا عثمان وكان سعيد قد أتى مصر فكتب عن ابن لهيعة وغيره','11477','2918','0');
INSERT INTO [t9351] VALUES (2962,'عثمان بن زفر بن الهذيل مات بالكوفة في شهر ربيع الآخر أو جمادى الأولى سنة ثماني عشرة ومائتين في خلافة المأمون','11478','2918','0');
INSERT INTO [t9351] VALUES (2963,'يحيى بن بشر بن كثير ويكنى أبا زكرياء الأسدي الحريري ومنزله قرب مسجد سماك وكان تاجرا قدم دمشق فسمع من سعيد بن عبد العزيز وسعيد بن بشير ومعاوية بن سلام صاحب يحيى بن أبي كثير وتوفي بالكوفة في جمادى الأولى سنة تسع وعشرين ومائتين في خلافة هارون الواثق','11479','2918','0');
INSERT INTO [t9351] VALUES (2964,'الطبقة التاسعة','11480','0','0');
INSERT INTO [t9351] VALUES (2965,'إسماعيل بن موسى ابن بنت إسماعيل بن عبد الرحمن السدي ويكنى أبا محمد روى عن شريك بن عبد الله وغيره','11481','2964','0');
