INSERT INTO [t9351] VALUES (2968,'عثمان بن محمد بن إبراهيم بن عثمان العبسي ويكنى أبا الحسن من ولد أبي سعدة وقد روى عن أبي سعدة الحديث وروى أبو سعدة عن ابن عباس , وابن الزبير وذكر عثمان بن أبي شيبة أنه روى عن النبي صلى الله عليه وسلم , وروى عثمان عن شريك , وأبي الأحوص , وعلي بن مسهر ,','11484','2964','0');
INSERT INTO [t9351] VALUES (2969,'وأخوه عبد الله بن محمد بن أبي شيبة ويكنى أبا بكر روى عن شريك وعلي بن مسهر والكوفيين، ورحل إلى البصرة فكتب عمن أدرك من مشيختها','11485','2964','0');
INSERT INTO [t9351] VALUES (2970,'أحمد بن أسد بن عاصم بن مغول البجلي وهو ابن ابنة مالك بن مغول ويكنى أبا عاصم. مات بالكوفة في صفر سنة تسع وعشرين ومائتين في خلافة هارون الواثق بالله','11486','2964','0');
