INSERT INTO [t9351] VALUES (2973,'محمد بن عبد الله بن نمير الهمداني ثم الخارفي ويكنى أبا عبد الرحمن توفي بالكوفة سنة أربع وثلاثين ومائتين','11489','2964','0');
