INSERT INTO [t9351] VALUES (2978,'ليث بن هارون العكلي من أنفسهم ويكنى أبا عتبة وكان زيد بن الحباب مولى لهم توفي بالكوفة في آخر سنة ثمان وعشرين ومائتين في خلافة هارون بن أبي إسحاق','11494','2964','0');
INSERT INTO [t9351] VALUES (2979,'فروة بن أبي المغراء','11495','2964','0');
