INSERT INTO [t9351] VALUES (3582,'إبراهيم بن محمد بن عرعرة بن البرند توفي ببغداد في شهر رمضان سنة إحدى وثلاثين ومائتين، وكان مرض بعسكر الخليفة بسامراء، فقدم به إلى بغداد، فتوفي بها','13202','3571','0');
INSERT INTO [t9351] VALUES (3583,'علي بن بري وقد كتب عنه الحديث، وتوفي بالبصرة سنة أربع وثلاثين ومائتين','13203','3571','0');
INSERT INTO [t9351] VALUES (3584,'سليمان بن الشاذكوني وكان حافظا للحديث، وتوفي بالبصرة سنة أربع وثلاثين ومائتين آخر البصريين','13204','3571','0');
INSERT INTO [t9351] VALUES (3585,'تسمية من كان بواسط من الفقهاء والمحدثين','13205','3571','0');
INSERT INTO [t9351] VALUES (3586,'أبو هاشم الرماني واسمه يحيى بن دينار، وكان ثقة','13206','3571','0');
INSERT INTO [t9351] VALUES (3587,'يعلى بن عطاء مولى عبد الله بن عمرو بن العاص، وكان ثقة، وكان من أهل الطائف، وكان قدم واسط، وأقام بها في آخر سلطان بني أمية، سمع منه شعبة بن الحجاج، وأبو عوانة، وهشيم، وأصحابهم','13207','3571','0');
INSERT INTO [t9351] VALUES (3588,'أبو عقيل الذي روى عنه شعبة، واسمه: هاشم بن سلال ويقال: سلام، وكان ثقة إن شاء الله، وكان من أهل الشام، فقدم واسط، وكان قاضيا بها','13208','3571','0');
INSERT INTO [t9351] VALUES (3589,'أبو خالد الدالاني واسمه: يزيد بن عبد الرحمن، وكان منكر الحديث','13209','3571','0');
INSERT INTO [t9351] VALUES (3590,'القاسم بن أبي أيوب وكان ثقة قليل الحديث','13210','3571','0');
INSERT INTO [t9351] VALUES (3591,'أبو بلج واسمه: يحيى بن أبي سليم الفزاري، وكان ثقة إن شاء الله، روى عنه شعبة، وهشيم، وأبو عوانة','13211','3571','0');
INSERT INTO [t9351] VALUES (3592,'منصور بن زاذان صاحب الحسن، وهو الذي روى عنه هشيم وأصحابه، وكان ثقة، ثبتا، سريع القراءة، وكان يريد يترسل فلا يستطيع، وكان يختم في الضحى، وكان يعرف ذلك منه بسجود القرآن، وكان قد تحول، فنزل المبارك على تسعة فراسخ من واسط قال يزيد بن هارون: ومات','13213','3571','0');
INSERT INTO [t9351] VALUES (3593,'العوام بن حوشب بن يزيد بن رؤيم وكان ثقة قال يزيد بن هارون: وكان يكنى أبا عيسى، وكان صاحب أمر بالمعروف، ونهي عن المنكر، مات سنة ثمان وأربعين ومائة','13214','3571','0');
INSERT INTO [t9351] VALUES (3594,'سفيان بن حسين السلمي مولى لهم قال يزيد بن هارون: ويكنى سفيان أبا الحسن وقال غيره: يكنى أبا محمد وكان ثقة، يخطئ في حديثه كثيرا، وكان مؤدبا مع المهدي أمير المؤمنين، ومات بالري في خلافة المهدي','13215','3571','0');
INSERT INTO [t9351] VALUES (3595,'أبو العلاء القصاب واسمه: أيوب بن أبي مسكين، وكان ثقة قال: سمعت يزيد بن هارون يقول: مات سنة أربعين ومائة','13216','3571','0');
INSERT INTO [t9351] VALUES (3596,'يزيد بن عطاء البزاز مولى أبي عوانة من فوق، وكان ضعيف الحديث','13217','3571','0');
INSERT INTO [t9351] VALUES (3597,'أصبغ بن زيد الوراق مولى لجهينة، وكان يكتب المصاحف، وكان ضعيفا في الحديث، ويكنى أصبغ أبا عبد الله، مات سنة تسع وخمسين ومائة في خلافة المهدي','13218','3571','0');
INSERT INTO [t9351] VALUES (3598,'خلف بن خليفة ويكنى أبا أحمد، مولى لأشجع، كان من أهل واسط، فتحول إلى بغداد، وكان ثقة، ثم أصابه الفالج قبل أن يموت حتى ضعف وتغير لونه واختلط، ومات ببغداد قبل هشيم في سنة إحدى وثمانين ومائة وهو يومئذ ابن تسعين سنة أو نحوها','13219','3571','0');
INSERT INTO [t9351] VALUES (3599,'هشيم بن بشير ويكنى أبا معاوية، مولى لبني سليم، وكان ثقة، كثير الحديث، ثبتا، يدلس كثيرا، فما قال في حديثه أخبرنا فهو حجة، وما لم يقل فيه أخبرنا فليس بشيء','13220','3571','0');
INSERT INTO [t9351] VALUES (3600,'خالد بن عبد الله الطحان مولى لمزينة، وكان ثقة، توفي بواسط سنة اثنتين وثمانين ومائة','13222','3571','0');
INSERT INTO [t9351] VALUES (3601,'علي بن عاصم بن صهيب مولى بني تميم، ويكنى أبا الحسن، ولد سنة تسع ومائة، وتوفي بواسط في جمادى الأولى سنة إحدى ومائتين، وهو ابن اثنتين وتسعين سنة وأشهر','13223','3571','0');
INSERT INTO [t9351] VALUES (3602,'عبد الحكيم بن منصور مولى لخزاعة، وكان ضعيفا في الحديث','13224','3571','0');
INSERT INTO [t9351] VALUES (3603,'محمد بن يزيد الكلاعي ويكنى أبا سعيد، وكان ثقة، توفي بواسط سنة ثمان وثمانين ومائة في خلافة هارون','13225','3571','0');
INSERT INTO [t9351] VALUES (3604,'أبو سفيان الحميري الحذاء وكان شيخا ضعيفا، عنده أحاديث قليلة، توفي بواسط يوم الأربعاء لسبع ليال بقين من شعبان سنة اثنتين ومائتين','13226','3571','0');
INSERT INTO [t9351] VALUES (3605,'قرة بن عيسى وقد روى عن الأعمش','13227','3571','0');
INSERT INTO [t9351] VALUES (3606,'يزيد بن هارون ويكنى أبا خالد، مولى لبني سليم، وكان ثقة كثير الحديث، ولد سنة ثماني عشرة ومائة، وقال: طلبت الحديث وحصين حي، كان بالمبارك، وكان يقرأ عليه، وكان قد نسي قال: وربما ابتدأني الجريري بالحديث، وكان قد أنكر قال يزيد: في شوال سنة تسع','13228','3571','0');
INSERT INTO [t9351] VALUES (3607,'إسحاق بن يوسف الأزرق ويكنى أبا محمد، وكان ثقة، وربما خلط، مات بواسط سنة خمس وتسعين ومائة في خلافة محمد بن هارون','13229','3571','0');
INSERT INTO [t9351] VALUES (3608,'محمد بن الحسن وكان من أهل الشام، وولي القضاء بواسط، وكان ثقة','13230','3571','0');
INSERT INTO [t9351] VALUES (3609,'الفضل بن عنبسة الخزاز ويكنى أبا الحسن، وكان ثقة، معروفا، روى عن يزيد بن إبراهيم التستري، وحماد بن سلمة وغيرهما','13231','3571','0');
INSERT INTO [t9351] VALUES (3610,'صلة بن سليمان وكان معروفا','13232','3571','0');
INSERT INTO [t9351] VALUES (3611,'سرور بن المغيرة بن زاذان ابن أخي منصور بن زاذان، وكان يروي التفسير عن عباد بن منصور، عن الحسن، وكان معروفا','13233','3571','0');
INSERT INTO [t9351] VALUES (3612,'رحمة بن مصعب','13234','3571','0');
INSERT INTO [t9351] VALUES (3613,'بشر بن مبشر','13235','3571','0');
INSERT INTO [t9351] VALUES (3614,'عاصم بن علي بن عاصم كان يروي عن شعبة، وسليمان بن المغيرة، وليث بن سعد، والمسعودي وغيرهم، وكان ثقة، وليس بالمعروف بالحديث، ويكثر الخطأ فيما حدث به، وتوفي بواسط يوم الاثنين للنصف من رجب سنة إحدى وعشرين ومائتين في خلافة أبي إسحاق، وصلى عليه المطلب','13236','3571','0');
INSERT INTO [t9351] VALUES (3615,'عمرو بن عون بن أوس ويكنى أبا عثمان، توفي بواسط سنة خمس وعشرين ومائتين في خلافة أبي إسحاق بن هارون','13237','3571','0');
INSERT INTO [t9351] VALUES (3616,'وكان بالمدائن من أصحاب رسول الله صلى الله عليه وسلم','13238','3571','0');
INSERT INTO [t9351] VALUES (3617,'حذيفة بن اليمان رضي الله عنه، وهو ابن حسيل بن جابر بن ربيعة بن عمرو بن جروة، وهو اليمان بن الحارث بن قطيعة بن عبس، وأمه الرباب بنت كعب بن عدي بن كعب بن عبد الأشهل','13239','3571','0');
INSERT INTO [t9351] VALUES (3618,'سلمان الفارسي','13242','3571','0');
INSERT INTO [t9351] VALUES (3619,'وكان بالمدائن من المحدثين والفقهاء','13250','3571','0');
INSERT INTO [t9351] VALUES (3620,'أبو جعفر المدائني واسمه عبد الله بن المسور بن محمد بن جعفر بن أبي طالب، وكان معروفا، قليل الحديث','13251','3571','0');
INSERT INTO [t9351] VALUES (3621,'عاصم الأحول بن سليمان ويكنى أبا عبد الرحمن، مولى لبني تميم، وكان ثقة، وكان من أهل البصرة، وكان يتولى الولايات، فكان بالكوفة على الحسبة في المكايل والأوزان، فكان قاضيا بالمدائن في خلافة أبي جعفر، ومات سنة إحدى أو اثنتين وأربعين ومائة','13252','3571','0');
INSERT INTO [t9351] VALUES (3622,'هلال بن خباب كان أصله من أهل البصرة، ثم نزل المدائن، ومات بها في آخر سنة أربع وأربعين ومائة','13253','3571','0');
INSERT INTO [t9351] VALUES (3623,'الهذيل بن بلال الفزاري وكان ضعيفا في الحديث','13254','3571','0');
INSERT INTO [t9351] VALUES (3624,'نعيم بن حكيم ولم يكن بذلك في الحديث','13255','3571','0');
INSERT INTO [t9351] VALUES (3625,'نصر بن حاجب القرشي من بني الحارث بن لؤي، ويكنى أبا يحيى، أصله من خراسان، ونزل المدائن، ومات بها سنة خمس وأربعين ومائة، وهو ابن بضع وخمسين سنة','13256','3571','0');
INSERT INTO [t9351] VALUES (3626,'شبابة بن سوار الفزاري مولى لهم، ويكنى أبا عمرو، وكان ثقة صالح الأمر في الحديث، وكان مرجيا','13257','3571','0');
INSERT INTO [t9351] VALUES (3627,'شعيب بن حرب ويكنى أبا صالح، وكان من أبناء خراسان من أهل بغداد، فتحول إلى المدائن، فنزلها واعتزل بها، وكان ثقة، له فضل، ثم خرج إلى مكة، فنزلها إلى أن مات بها','13258','3571','0');
INSERT INTO [t9351] VALUES (3628,'علي بن حفص','13259','3571','0');
INSERT INTO [t9351] VALUES (3629,'وكان ببغداد من الفقهاء والمحدثين ممن نزلها وقدمها فمات بها','13260','3571','0');
INSERT INTO [t9351] VALUES (3630,'إسماعيل بن سالم الأسدي الذي روى عنه هشيم وأصحابه، كان ثقة ثبتا، وكان أصله من أهل الكوفة، ثم تحول فسكن بغداد قبل أن تبنى وتسكن، وكان ببغداد لهشام بن عبد الملك وغيره من الخلفاء خمس مائة فارس رابطة يغيرون على الخوارج إذا خرجوا في ناحيتهم قبل أن يضعف','13261','3571','0');
INSERT INTO [t9351] VALUES (3631,'هشام بن عروة بن الزبير بن العوام بن خويلد بن أسد ويكنى أبا المنذر، وأمه أم ولد، وكان ثقة، ثبتا، كثير الحديث، حجة، وقد سمع من عبد الله بن الزبير، ووفد على أبي جعفر المنصور بالكوفة، ولحق به ببغداد، فمات بها في سنة ست وأربعين ومائة، ودفن في مقبرة','13262','3571','0');
INSERT INTO [t9351] VALUES (3632,'محمد بن إسحاق بن يسار مولى قيس بن مخرمة بن المطلب بن عبد مناف بن قصي، ويكنى محمد أبا عبد الله، وكان جده يسار من سبي عين التمر، وكان محمد ثقة، وقد روى الناس عنه، روى عنه الثوري، وشعبة، وسفيان بن عيينة، ويزيد بن زريع، وإبراهيم بن سعد، وإسماعيل','13263','3571','0');
INSERT INTO [t9351] VALUES (3633,'أبو حنيفة واسمه النعمان بن ثابت، مولى بني تيم الله بن ثعلبة، وهو ضعيف في الحديث، وكان صاحب رأي، وقدم بغداد، فمات بها في رجب أو شعبان سنة خمسين ومائة، وهو ابن سبعين سنة، ودفن في مقابر الخيزران','13264','3571','0');
INSERT INTO [t9351] VALUES (3634,'أبو معاوية النحوي واسمه شيبان بن عبد الرحمن، مولى لبني تميم، وكان مؤدبا لولد داود بن علي وغيرهم، وكان ثقة في الحديث، ومات ببغداد سنة أربع وستين ومائة في خلافة المهدي، ودفن في مقابر قريش بباب التبن','13265','3571','0');
INSERT INTO [t9351] VALUES (3635,'إبراهيم بن سعد بن إبراهيم بن عبد الرحمن بن عوف الزهري ويكنى أبا إسحاق، وكان ثقة كثير الحديث، وربما أخطأ في الحديث، وقدم بغداد فنزلها هو وعياله وولده، وولي بها بيت المال لهارون أمير المؤمنين، ومات ببغداد سنة ثلاث وثمانين ومائة، ودفن في مقابر باب','13266','3571','0');
INSERT INTO [t9351] VALUES (3636,'عبد العزيز بن عبد الله بن أبي سلمة الماجشون ويكنى أبا عبد الله، مولى لآل الهدير التيميين، وكان ثقة كثير الحديث، وأهل العراق أروى عنه من أهل المدينة، وكان قد قدم بغداد، فأقام بها إلى أن توفي في خلافة المهدي، فحضره المهدي، وصلى عليه، ودفنه في مقابر','13267','3571','0');
INSERT INTO [t9351] VALUES (3637,'عبد الملك بن محمد بن أبي بكر بن محمد بن عمرو بن حزم بن زيد بن لوذان بن عمرو بن عبيد بن عوف بن مالك بن النجار، وأمه أمة الوهاب بنت عبد الله بن عبد الله بن حنظلة بن أبي عامر الغسيل، وكان قدم بغداد فأقام بها، واستقضاه هارون أمير المؤمنين على عسكر المهدي','13268','3571','0');
INSERT INTO [t9351] VALUES (3638,'محمد بن عبد الله بن علاثة الكلابي ويكنى أبا اليسير، وكان ثقة إن شاء الله، وكان من أهل حران، فقدم بغداد، فولاه المهدي القضاء بعسكر المهدي، ثم ولى عافية بن يزيد الأودي أيضا القضاء معه فأخبرني علي بن الجعد قال: رأيتهما جميعا يقضيان في المسجد الجامع','13269','3571','0');
INSERT INTO [t9351] VALUES (3639,'زياد بن عبد الله بن علاثة الكلابي فكان خليفة أخيه محمد بن عبد الله بن علاثة على القضاء مع المهدي','13270','3571','0');
INSERT INTO [t9351] VALUES (3640,'إسماعيل بن عمر ويكنى أبا المنذر، روى عن سفيان الثوري، ومالك بن أنس','13271','3571','0');
INSERT INTO [t9351] VALUES (3641,'عبيد بن أبي قرة','13272','3571','0');
INSERT INTO [t9351] VALUES (3642,'محمد بن سابق ويكنى أبا جعفر، مولى بني تميم، وكان من أهل الكوفة، ونزل بغداد في قطيعة الربيع وتجر بها، ومات ببغداد','13273','3571','0');
INSERT INTO [t9351] VALUES (3643,'سعيد بن عبد الرحمن بن جهبل الجمحي ولي القضاء ببغداد في عسكر المهدي، ومات بها','13274','3571','0');
INSERT INTO [t9351] VALUES (3644,'عبد الرحمن بن أبي الزناد ويكنى أبا محمد، قدم بغداد في حاجة له، فسمع منه البغداديون، وكان كثير الحديث، وكان يضعف لروايته عن أبيه، ومات ببغداد سنة أربع وسبعين ومائة في خلافة هارون، ودفن في مقابر باب التبن','13275','3571','0');
INSERT INTO [t9351] VALUES (3645,'وابنه محمد بن عبد الرحمن بن أبي الزناد ويكنى أبا عبد الله، وكان قد لقي عامة رجال أبيه، وكان ثقة، عنده علم كثير، فمات قبل أن يسمع الناس منه، مات ببغداد بعد أبيه بإحدى وعشرين ليلة في سنة أربع وسبعين ومائة، وهو ابن أربع وخمسين سنة، ودفن في مقابر','13276','3571','0');
INSERT INTO [t9351] VALUES (3646,'هشيم بن بشير الواسطي ويكنى أبا معاوية، نزل بغداد، ومات بها يوم الثلاثاء في شعبان سنة ثلاث وثمانين ومائة في خلافة هارون، وكان ثقة يدلس','13277','3571','0');
INSERT INTO [t9351] VALUES (3647,'إسماعيل بن إبراهيم بن مقسم مولى عبد الرحمن بن قطبة الأسدي أسد خزيمة من أهل الكوفة، وكان مقسم من سبي القيقانية ما بين خراسان وزابلستان، وكان إبراهيم بن مقسم تاجرا من أهل الكوفة، وكان يقدم البصرة بتجارته، فيبيع ويرجع، فتخلف فتزوج علية بنت حسان مولاة','13278','3571','0');
INSERT INTO [t9351] VALUES (3648,'إسماعيل بن زكريا بن مرة مولى لبني سواءة بن الحارث بن ثعلبة بن دودان بن أسد بن خزيمة، ويكنى أبا زياد، وكان تاجرا في الطعام وغيره، وهو من أهل الكوفة، ونزل بغداد في ربض حميد بن قحطبة، ومات بها في أول سنة ثلاث وسبعين ومائة، وهو ابن خمس وسبعين سنة','13280','3571','0');
INSERT INTO [t9351] VALUES (3649,'عنبسة بن عبد الواحد القرشي','13281','3571','0');
INSERT INTO [t9351] VALUES (3650,'أبو سعيد المؤدب واسمه محمد بن مسلم بن أبي الوضاح، كان من حي من قضاعة من أنفسهم، وكان أصله جزريا، فلما تولى أبو جعفر المنصور على الجزيرة ضم أبا سعيد إلى المهدي، والمهدي يومئذ ابن عشر سنين أو نحوها، فقدم معه إلى بغداد، ثم ضم أبو جعفر المنصور إلى','13282','3571','0');
INSERT INTO [t9351] VALUES (3651,'أبو إسماعيل المؤدب واسمه: إبراهيم بن سليمان','13284','3571','0');
INSERT INTO [t9351] VALUES (3652,'عباد بن عباد بن حبيب بن المهلب بن أبي صفرة العتكي ويكنى أبا معاوية، وكان ثقة، وربما غلط، روى عن أبي حمزة، وعن واصل مولى أبي عيينة، وكان من أهل البصرة، فقدم بغداد فنزلها ومات بها','13285','3571','0');
INSERT INTO [t9351] VALUES (3653,'الفرج بن فضالة ويكنى أبا فضالة، وكان من أهل الشام من أهل حمص، فقدم بغداد، وولي بيت المال في أول خلافة هارون، وكان يسكن مدينة أبي جعفر المنصور، ومات بها سنة ست وسبعين ومائة، وكان ضعيفا في الحديث، وقد روي عنه','13286','3571','0');
INSERT INTO [t9351] VALUES (3654,'إسماعيل بن جعفر بن أبي كثير المدني وكان ثقة، وهو صاحب الخمسمائة الحديث التي سمعها منه الناس، وكان من أهل المدينة، فقدم بغداد، فلم يزل بها حتى مات','13287','3571','0');
INSERT INTO [t9351] VALUES (3655,'عبيد الله بن عبيد الرحمن الأشجعي ويكنى أبا عبد الرحمن، روى كتب الثوري على وجهها، وروى عنه الجامع، وكان من أهل الكوفة، فقدم بغداد، فلم يزل بها حتى مات','13288','3571','0');
INSERT INTO [t9351] VALUES (3656,'عمار بن محمد ويكنى أبا اليقظان، وهو ابن أخت سفيان بن سعيد الثوري، وكان ثقة، روى عن عطاء بن السائب وغيره من الكوفيين، وكان من أهل الكوفة، فقدم بغداد، فلم يزل بها حتى مات','13289','3571','0');
INSERT INTO [t9351] VALUES (3657,'طلحة بن يحيى الأنصاري وكان ينزل ربض الأنصار، روى عن يونس بن يزيد الأيلي، وسمع منه عباد بن موسى سماعا كثيرا','13290','3571','0');
INSERT INTO [t9351] VALUES (3658,'مروان بن شجاع وكان يقال له: الخصيفي، وكان من أهل الجزيرة من أهل حران، وكان راوية لخصيف، فقدم بغداد، فكان مؤدبا لولد موسى أمير المؤمنين، فلم يزل ببغداد حتى مات','13291','3571','0');
INSERT INTO [t9351] VALUES (3659,'عبيدة بن حميد التيمي ويكنى أبا عبد الرحمن، وكان ثقة صالح الحديث، وكان من أهل الكوفة، صاحب نحو وعربية وقراءة للقرآن، فقدم بغداد أيام هارون أمير المؤمنين، فصيره مع ابنه محمد بن هارون، فلم يزل معه حتى مات ببغداد','13292','3571','0');
INSERT INTO [t9351] VALUES (3660,'أبو حفص الأبار واسمه: عمر بن عبد الرحمن الأسدي، وكان ثقة، روى عن منصور بن المعتمر وغيره، وكان من أهل الكوفة، فقدم بغداد، فلم يزل بها حتى مات','13293','3571','0');
INSERT INTO [t9351] VALUES (3661,'أبو عبيدة الحداد واسمه: عبد الواحد','13294','3571','0');
INSERT INTO [t9351] VALUES (3662,'مروان بن معاوية بن الحارث بن أسماء بن خارجة بن حصن بن حذيفة بن بدر الفزاري، ويكنى أبا عبد الله، كان من أهل الكوفة، ثم أتى الثغر، فأقام به، ثم قدم بغداد، فأقام بها ونزلها، وسمع منه البغداديون، وكان ثقة، ثم خرج إلى مكة، فأقام بها، فمات بها في','13295','3571','0');
INSERT INTO [t9351] VALUES (3663,'عباد بن العوام ويكنى أبا سهل، كان من أهل واسط، وكان يتشيع، فأخذه هارون أمير المؤمنين فحبسه زمانا ثم خلى عنه، وأقام ببغداد، وسمع منه البغداديون، وكان ثقة، وكان ينزل بالكرخ على نهر البزارين، وتوفي سنة خمس وثمانين ومائة، في خلافة هارون أمير المؤمنين','13296','3571','0');
INSERT INTO [t9351] VALUES (3664,'علي بن ثابت ويكنى أبا الحسن، مولى العباس بن محمد الهاشمي، وكان أصله من أهل الجزيرة، وقدم بغداد فنزلها إلى أن مات بها، وكان ثقة صدوقا','13297','3571','0');
INSERT INTO [t9351] VALUES (3665,'أبو يوسف القاضي واسمه: يعقوب بن إبراهيم بن حبيب بن سعد بن بجير بن معاوية بن قحافة بن نفيل بن سدوس بن عبد مناف بن أبي أسامة بن سحمة بن سعد بن عبد الله بن قرادة بن ثعلبة بن معاوية بن زيد بن الغوث بن بجيلة، وأم سعد بن بجير حبتة بنت مالك من بني عمرو بن عوف','13298','3571','0');
INSERT INTO [t9351] VALUES (3666,'الحسين بن حسن بن عطية بن سعد بن جنادة العوفي ويكنى أبا عبد الله، وكان من أهل الكوفة، وقد سمع سماعا كثيرا، وكان ضعيفا في الحديث، ثم قدم به بغداد، فولوه قضاء الشرقية بعد حفص بن غياث، ثم نقل من الشرقية، فولي قضاء عسكر المهدي في خلافة هارون، ثم عزل،','13300','3571','0');
INSERT INTO [t9351] VALUES (3667,'أسد بن عمرو البجلي من أنفسهم، ويكنى أبا المنذر، وكان عنده حديث كثير، وهو ثقة إن شاء الله، وكان قد صحب أبا حنيفة وتفقه، وكان من أهل الكوفة، فقدم به بغداد، فولي قضاء مدينة الشرقية بعد العوفي','13301','3571','0');
INSERT INTO [t9351] VALUES (3668,'عافية بن يزيد الأودي وكان من أصحاب أبي حنيفة أيضا، وولي القضاء للمهدي ببغداد في عسكر المهدي','13302','3571','0');
INSERT INTO [t9351] VALUES (3669,'عصمة بن محمد الأنصاري وكان إمام مسجد الأنصار الكبير ببغداد، روى عن سهل بن أبي أفلح، ويحيى بن سعيد، وعبيد الله بن عمر، وكان عندهم ضعيفا في الحديث','13303','3571','0');
INSERT INTO [t9351] VALUES (3670,'المسيب بن شريك ويكنى أبا سعيد، وهو من بني شقرة تميم، وولد بخراسان، ونشأ بالكوفة، وسمع الحديث من الأعمش، وإسماعيل بن أبي خالد، وعبد الملك بن أبي سليمان، وغيرهم، وكان ضعيفا في الحديث، لا يحتج به ثم قدم بغداد، فنزلها، وولي بيت المال لهارون أمير','13304','3571','0');
INSERT INTO [t9351] VALUES (3671,'أبو البختري القاضي واسمه وهب بن وهب بن وهب بن كثير بن عبد الله بن زمعة بن الأسود بن المطلب بن أسد بن عبد العزى بن قصي، كان من أهل المدينة، ثم خرج منها، فنزل الشام، ثم قدم بغداد، فولاه هارون أمير المؤمنين القضاء بعسكر المهدي، ثم عزله فولاه مدينة','13305','3571','0');
INSERT INTO [t9351] VALUES (3672,'الحجاج بن محمد الأعور ويكنى أبا محمد، مولى سليمان بن مجالد مولى أبي جعفر المنصور، ولم يزل ببغداد من أهلها، ثم تحول إلى المصيصة بولده وعياله، فأقام بها سنتين، ثم قدم بغداد في حاجة، فلم يزل بها حتى مات بها في شهر ربيع الأول سنة ست ومائتين، وكان ثقة','13306','3571','0');
INSERT INTO [t9351] VALUES (3673,'عبد الوهاب بن عطاء العجلي الخفاف ويكنى أبا نصر، وهو من أهل البصرة، ولزم سعيد بن أبي عروبة، وعرف بصحبته، وكتب كتبه، وقد روى عن يونس بن عبيد، وخالد الحذاء، وحميد الطويل، وعوف الأعرابي، وابن عون، وداود بن أبي هند، وعمران بن حدير، وغيرهم، وكان','13307','3571','0');
INSERT INTO [t9351] VALUES (3674,'أبو بدر واسمه شجاع بن الوليد بن قيس السكوني روى عن الأعمش، وهشام بن عروة، وخصيف وغيرهم، وكانت له سن قد جاوز التسعين، وكان كثير الصلاة، ورعا، وتوفي ببغداد سنة أربع ومائتين، وذلك في شهر رمضان في خلافة المأمون','13308','3571','0');
INSERT INTO [t9351] VALUES (3675,'وابنه أبو همام واسمه: الوليد بن شجاع بن الوليد روى عن بقية، وإسماعيل بن عياش، والوليد بن مسلم وغيرهم','13309','3571','0');
INSERT INTO [t9351] VALUES (3676,'عبد الله بن بكر السهمي بطن من باهلة، وهو من أهل البصرة، وكان ثقة صدوقا، نزل بغداد، فنزل على سعيد بن مسلم، وسمع منه البغداديون، ولم يزل بها حتى مات بها ليلة الثلاثاء لثلاث عشرة ليلة بقيت من المحرم سنة ثمان ومائتين في خلافة المأمون','13310','3571','0');
INSERT INTO [t9351] VALUES (3677,'كثير بن هشام ويكنى أبا سهل، وهو صاحب جعفر بن برقان، نزل بغداد باب الكرخ في السوق، فكان يجهز على التجار إلى الرقة وغيرها من الجزيرة والشام، وكان ثقة صدوقا، ثم خرج إلى الحسن بن سهل وهو بفم الصلح، فمات هناك في شعبان سنة سبع ومائتين','13311','3571','0');
INSERT INTO [t9351] VALUES (3678,'بكر بن الطويل','13312','3571','0');
INSERT INTO [t9351] VALUES (3679,'محمد بن عمر بن واقد الأسلمي مولى عبد الله بن بريدة الأسلمي، ويكنى أبا عبد الله، وكان من أهل المدينة، فقدم بغداد في سنة ثمانين ومائة في دين لحقه، فلم يزل بها، وخرج إلى الشام والرقة، ثم رجع إلى بغداد، فلم يزل بها إلى أن قدم المأمون من خراسان، فولاه','13313','3571','0');
INSERT INTO [t9351] VALUES (3680,'هاشم بن القاسم الكناني ويكنى أبا النضر، وكان من بني ليث من أنفسهم، وهو من أهل خراسان، ونزل بغداد، وكان ثقة، روى عن سليمان بن المغيرة، وشعبة، والمسعودي، وابن أبي ذئب، وحريز بن عثمان، وزهير بن معاوية، ومحمد بن طلحة بن مصرف، وأبي جعفر الرازي،','13315','3571','0');
INSERT INTO [t9351] VALUES (3681,'قراد أبو نوح مولى عبد الله بن مالك، وكان ثقة، روى عن شعبة، والحجاج رواية كثيرة','13316','3571','0');
INSERT INTO [t9351] VALUES (3682,'أبو قطن واسمه: عمرو بن الهيثم بن قطن بن كعب القطعي','13317','3571','0');
INSERT INTO [t9351] VALUES (3683,'شاذان واسمه الأسود بن عامر وكان أصله من الشام، وكان صالح الحديث، ونزل بغداد، فلم يزل بها حتى مات سنة ثمان ومائتين','13318','3571','0');
INSERT INTO [t9351] VALUES (3684,'عفان بن مسلم بن عبد الله مولى عزرة بن ثابت الأنصاري، ويكنى أبا عثمان، وكان ثقة كثير الحديث، صحيح الكتاب، وكان من أهل البصرة فقدم بغداد فلم يزل بها حتى توفي سنة عشرين ومائتين، وصلى عليه عاصم بن علي بن عاصم، وامتحن وسئل عن القرآن، فأبى أن يقول:','13319','3571','0');
INSERT INTO [t9351] VALUES (3685,'محمد بن الحسن ويكنى أبا عبد الله، مولى لبني شيبان، وكان أصله من أهل الجزيرة، وكان أبوه في جند أهل الشام، فقدم واسط، فولد محمد بها في سنة اثنتين وثلاثين ومائة، ونشأ بالكوفة، وطلب الحديث، وسمع سماعا كثيرا من مسعر، ومالك بن مغول، وعمر بن ذر،','13320','3571','0');
INSERT INTO [t9351] VALUES (3686,'يوسف بن يعقوب بن إبراهيم القاضي وكان قد سمع الحديث وروى الرأي عن أبيه أبي يوسف، وولي قضاء بغداد في الجانب الغربي في حياة أبيه، وصلى بالناس الجمعة في مدينة أبي جعفر بأمر هارون أمير المؤمنين، ولم يزل قاضيا له بها إلى أن توفي في رجب سنة اثنتين وتسعين','13322','3571','0');
INSERT INTO [t9351] VALUES (3687,'أبو كامل مظفر بن مدرك وكان من أبناء أهل خراسان، وكان ثقة، روى عن حماد بن سلمة وغيره','13323','3571','0');
INSERT INTO [t9351] VALUES (3688,'يونس بن محمد المؤدب ويكنى أبا محمد، وكان ثقة صدوقا، توفي ببغداد يوم السبت لسبع ليال خلون من صفر سنة ثمان ومائتين','13324','3571','0');
INSERT INTO [t9351] VALUES (3689,'الحسن بن موسى الأشيب من أبناء أهل خراسان، ويكنى أبا علي، ولي قضاء حمص والموصل لهارون أمير المؤمنين، ثم قدم بغداد في خلافة المأمون، فلم يزل ببغداد إلى أن ولاه المأمون قضاء طبرستان، فتوجه إليها فمات بالطريق بالري في شهر ربيع سنة تسع ومائتين، وكان ثقة','13325','3571','0');
INSERT INTO [t9351] VALUES (3690,'حسين بن محمد بن بهرام المروزي ويكنى أبا أحمد، وكان ثقة، روى عن شعبة، وجرير بن حازم، وذكر أنه سمع منه بجرجان أيام سليمان بن راشد، وروى عن ابن أبي ذئب، وشيبان بن عبد الرحمن التفسير، وغيره، وروى عن أبي معشر المغازي ومات ببغداد في آخر خلافة المأمون','13326','3571','0');
INSERT INTO [t9351] VALUES (3691,'حجير بن المثنى ويكنى أبا عمرو، وكان أصله من أهل اليمامة، وقدم بغداد، فنزلها، وكان صاحب لؤلؤ وجوهر، لزم السوق ببغداد، وكان ثقة، روى عن ليث بن سعد، وعبد العزيز بن عبد الله الماجشون فأكثر، ومات ببغداد','13327','3571','0');
INSERT INTO [t9351] VALUES (3692,'علي بن الجعد مولى أم سلمة المخزومية امرأة أبي العباس أمير المؤمنين أخبرني عبد الرحمن بن إسحاق القاضي قال: جاءني علي بن الجعد بسجل أبيه بعتقه من أم سلمة، فيه شهادة جدي إبراهيم بن سلمة، ورجل آخر معه ممن كان يدخل عليها قال علي بن الجعد: ولدت سنة ست','13328','3571','0');
INSERT INTO [t9351] VALUES (3693,'هوذة بن خليفة بن عبد الله بن أبي بكرة ويكنى أبا الأشهب، وأمه الزهرة بنت عبد الرحمن بن يزيد بن أبي بكرة، وأمها هولة بنت عبد الرحمن بن يزيد بن أبي بكرة، وولد هوذة سنة خمس وعشرين ومائة، وطلب الحديث، وكتب عن يونس، وهشام، وعوف، وابن عون، وابن جريج،','13329','3571','0');
INSERT INTO [t9351] VALUES (3694,'يحيى بن سعيد بن أبان بن سعيد بن العاص بن سعيد بن العاص بن أمية بن عبد شمس ويكنى أبا أيوب، وكان من أهل الكوفة، فقدم بغداد فنزلها، وكان ثقة كثير الحديث، روى عن هشام بن عروة، ويحيى بن سعيد الأنصاري، والأعمش، وإسماعيل بن أبي خالد، وعبد الملك بن أبي','13330','3571','0');
INSERT INTO [t9351] VALUES (3695,'أبو زكريا السيلحيني واسمه: يحيى بن إسحاق البجلي، ذكر أنه من أنفسهم، وكان ثقة، روى عن يحيى بن أيوب، وابن لهيعة، وغيرهما، وقد كتب الناس عنه، وكان حافظا لحديثه، وكان ينزل بغداد في دار الرقيق، ومات بها في سنة عشر ومائتين، في خلافة المأمون','13331','3571','0');
INSERT INTO [t9351] VALUES (3696,'سعيد بن سليمان الواسطي يكنى أبا عثمان، وهو سعدويه، وكان ثقة كثير الحديث، روى عن سليمان بن المغيرة، والمبارك بن فضالة، وليث بن سعد، وأبي معشر، وغيرهم، ونزل بغداد وتجر بها، وكان منزله بالكرخ نحو درب أصحاب القراطيس، وتوفي بها يوم الثلاثاء بالعشي،','13332','3571','0');
INSERT INTO [t9351] VALUES (3697,'أبو نصر التمار واسمه: عبد الملك بن عبد العزيز، من أبناء أهل خراسان من أهل نسا، ذكر أنه ولد بعد قتل أبي مسلم الداعية بستة أشهر، ونزل بغداد في ربض أبي العباس الطوسي، ثم في درب النسابية، وتجر بها في التمر وغيره، وكان ثقة فاضلا، خيرا ورعا، وقد روى','13333','3571','0');
INSERT INTO [t9351] VALUES (3698,'شريح بن النعمان ويكنى أبا الحسين، صاحب اللؤلؤ، وكان ثقة، روى عن حماد بن سلمة، وفليح بن سليمان، وأبي عوانة، وكان منزله بعسكر المهدي على سيب القاضي، وتوفي يوم الأضحى سنة سبع عشرة في خلافة المأمون','13334','3571','0');
INSERT INTO [t9351] VALUES (3699,'يحيى بن غيلان بن عبد الله بن أسماء بن حارثة من خزاعة، وكان ثقة نزل بغداد ثم خرج إلى البصرة في حاجة له، فمات هناك سنة عشر ومائتين، وقد روى عن البصريين','13335','3571','0');
INSERT INTO [t9351] VALUES (3700,'معاوية بن عمرو الأزدي ويكنى أبا عمرو، روى عن زائدة بن قدامة كتبه ومصنفه، وروى عن أبي إسحاق الفزاري كتاب السيرة في دار الحرب، ونزل بغداد فسمع منه أهل بغداد، وتوفي ببغداد في سنة خمس عشرة أو أربع عشرة ومائة في خلافة المأمون','13336','3571','0');
INSERT INTO [t9351] VALUES (3701,'المعلى بن منصور الرازي ويكنى أبا يعلى، نزل بغداد، وطلب الحديث، وكان صدوقا صاحب حديث ورأي وفقه، فمن أصحاب الحديث من يروي عنه، ومنهم من لا يروي عنه الرأي، وكان ينزل الكرخ في قطيعة الربيع، وتوفي سنة إحدى عشرة ومائتين','13337','3571','0');
INSERT INTO [t9351] VALUES (3702,'محمد بن الصباح البزاز وهو الدولابي، ويكنى أبا جعفر، كان ينزل باب الكرخ، ومات في آخر المحرم سنة سبع وعشرين ومائتين','13338','3571','0');
INSERT INTO [t9351] VALUES (3703,'بشر بن الحارث رضي الله عنه، ويكنى أبا نصر، وكان من أبناء أهل خراسان من أهل مرو ونزل بغداد وطلب الحديث، وسمع من حماد بن زيد، وشريك، وعبد الله بن المبارك، وهشيم وغيرهم سماعا كثيرا، ثم أقبل على العبادة واعتزل الناس فلم يحدث، ومات ببغداد يوم الأربعاء','13339','3571','0');
INSERT INTO [t9351] VALUES (3704,'الهيثم بن خارجة ويكنى أبا أحمد، من أبناء أهل خراسان، من أهل مرو الروذ، نزل بغداد، وكان أتى الشام، فكتب من الشاميين وليث بن سعد، ثم رجع إلى بغداد، فلم يزل بها إلى أن مات يوم الاثنين لثماني ليال بقين من ذي الحجة سنة سبع وعشرين ومائتين','13340','3571','0');
INSERT INTO [t9351] VALUES (3705,'إسحاق بن عيسى الطباع','13341','3571','0');
INSERT INTO [t9351] VALUES (3706,'سعد بن إبراهيم بن سعد بن إبراهيم بن عبد الرحمن بن عوف الزهري ويكنى أبا إسحاق، وولي قضاء واسط في خلافة هارون، ثم ولي قضاء عسكر المهدي في أول خلافة المأمون وهو بخراسان، وكان يروي كتب أبيه، وسمع منه بعض البغداديين، ثم عزل عن القضاء ببغداد، فلحق بالحسن','13342','3571','0');
INSERT INTO [t9351] VALUES (3707,'وأخوه يعقوب بن إبراهيم بن سعد بن إبراهيم بن عبد الرحمن بن عوف الزهري ويكنى أبا يوسف، وكان ثقة مأمونا، وكان يروي عن أبيه المغازي وغيرها، وسمع منه البغداديون، وكان يقدم على أخيه في الفضل والورع والحديث، ولم يزل ببغداد، ثم خرج إلى الحسن بن سهل وهو بفم','13343','3571','0');
INSERT INTO [t9351] VALUES (3708,'سليمان بن داود بن علي بن عبد الله بن العباس بن عبد المطلب بن هاشم بن عبد مناف بن قصي، ويكنى أبا أيوب، وكان ثقة، سمع من إبراهيم بن سعد، وعبد الرحمن بن أبي الزناد، وغيرهما، وكتب عنه البغداديون ورووا عنه، توفي ببغداد سنة تسع عشرة ومائتين','13344','3571','0');
INSERT INTO [t9351] VALUES (3709,'قران بن تمام الأسدي ويكنى أبا تمام، وكان من أهل الكوفة، وقدم فنزل بغداد، وكان يتنخس في الدواب، وقد سمع منه، وكان ضعيفا','13345','3571','0');
INSERT INTO [t9351] VALUES (3710,'عمر بن حفص ويكنى أبا حفص العبدي، روى عن ثابت البناني، ويزيد الرقاشي، وأبان بن أبي عياش، وأم شبيب العبدية، ومالك بن أنس، وغيرهم، وكان ضعيفا عندهم في الحديث، كتبوا عنه ثم تركوه، ومات ببغداد سنة ثمان وتسعين ومائة، في أول خلافة المأمون','13346','3571','0');
INSERT INTO [t9351] VALUES (3711,'مصعب بن عبد الله بن مصعب بن ثابت بن عبد الله بن الزبير بن العوام، ويكنى أبا عبد الله، نزل بغداد، وروى عن مالك بن أنس الموطأ، وروى عن الدراوردي، وإبراهيم بن سعد، وعبد العزيز بن أبي حازم، وعن أبيه وغيرهم، وكان إذا سئل عن القرآن يقف ويعيب من لا يقف','13347','3571','0');
INSERT INTO [t9351] VALUES (3712,'نصر بن زيد بن المجدر ويكنى أبا الحسن، وكان ثقة صاحب حديث، سمع من جرير بن حازم، ومن أبي هلال، ووهيب، وغيرهم، ومات قديما قبل أن يحدث، وكان أصله من سجستان، وهو مولى جعفر الأكبر ابن أبي جعفر المنصور','13348','3571','0');
INSERT INTO [t9351] VALUES (3713,'عنبسة بن سعيد بن أبان بن سعيد بن العاص ويكنى أبا خالد، وكان ثقة صاحب حديث، وكان قدم بغداد، فأقام بها، وسمع منه البغداديون','13349','3571','0');
INSERT INTO [t9351] VALUES (3714,'منصور بن سلمة ويكنى أبا سلمة، وكان ثقة، سمع من غير واحد، وكان يتمنع بالحديث ثم حدث أياما، ثم خرج إلى الثغر، فمات هناك بالمصيصة سنة عشر ومائتين، في خلافة عبد الله المأمون','13350','3571','0');
INSERT INTO [t9351] VALUES (3715,'نصر بن باب الخراساني ويكنى أبا سهل، سمع من داود بن أبي هند، وعوف الأعرابي، والحجاج بن أرطأة، وغيرهم، ونزل بغداد فسمعوا منه ورووا عنه، ثم حدث عن إبراهيم الصائغ فاتهموه، فتركوا حديثه، وتوفي ببغداد في عسكر المهدي','13351','3571','0');
INSERT INTO [t9351] VALUES (3716,'موسى بن داود الضبي ويكنى أبا عبد الله، وكان ثقة، صاحب حديث، سمع من سفيان الثوري، وزهير، وغيرهما، وكان قد نزل بغداد، ثم ولي قضاء طرسوس، فخرج إلى ما هناك، فلم يزل قاضيا بها إلى أن مات بها','13352','3571','0');
INSERT INTO [t9351] VALUES (3717,'إبراهيم بن العباس ويكنى أبا إسحاق، ويعرف بالسامري، روى عن أبي أويس، وشريك وغيرهما وكان قد اختلط في آخر عمره، فحجبه أهله في منزله حتى مات','13353','3571','0');
INSERT INTO [t9351] VALUES (3718,'الحكم بن موسى البزاز ويكنى أبا صالح، ثقة كثير الحديث، وكان من أهل خراسان، من أهل نسا، وروى عن الشاميين، عن يحيى بن حمزة، وفضل بن زياد، وغيرهما من أهل الشام، وكان رجلا صالحا، ثبتا في الحديث، وتوفي ببغداد في شوال سنة اثنتين وثلاثين ومائتين','13354','3571','0');
INSERT INTO [t9351] VALUES (3719,'هشام بن سعيد البزاز ويكنى أبا أحمد، وكان راوية لابن لهيعة، وحماد بن زيد، وكان ثقة، فمات قبل أن يسمع منه الناس','13355','3571','0');
INSERT INTO [t9351] VALUES (3720,'محمد بن الحجاج المصفر ويكنى أبا جعفر، وكان قد سمع من شعبة، وابن أبي ذئب، وغيرهما، وهو ضعيف عندهم في الحديث','13356','3571','0');
INSERT INTO [t9351] VALUES (3721,'سعد بن عبد الحميد بن جعفر بن الحكم بن أبي الحكم حلفاء الأنصار، ويكنى أبا معاذ، ذكر أنه سمع من مالك بن أنس وغيره','13357','3571','0');
INSERT INTO [t9351] VALUES (3722,'خالد بن خداش بن عجلان ويكنى أبا الهيثم، مولى آل المهلب بن أبي صفرة، وكان ثقة، روى عن حماد بن زيد، وأبي عوانة وغيرهما، وتوفي في سنة ثلاث أو أربع وعشرين ومائتين','13358','3571','0');
INSERT INTO [t9351] VALUES (3723,'منصور بن بشير وهو ابن أبي مزاحم، ويكنى أبا نصر، مولى الأزد، وكان من سبي الترك، وكان له ديوان فتركه، وقد كتبوا عنه وكان ثقة صاحب سنة، وتوفي ببغداد في ذي القعدة سنة خمس وثلاثين ومائتين، وهو ابن ثمانين سنة أو أكثر','13359','3571','0');
INSERT INTO [t9351] VALUES (3724,'محمد بن بكار ويكنى أبا عبد الله، روى عن أبي معشر، ومحمد بن طلحة، وقيس بن الربيع، وعنبسة بن عبد الواحد وغيرهم، وتوفي ببغداد في ربيع الآخر سنة ثمان وثلاثين ومائتين','13360','3571','0');
INSERT INTO [t9351] VALUES (3725,'محمد بن جعفر الوركاني ويكنى أبا عمران، روى عن إبراهيم بن سعد، وأبي معشر، وشريك، والمعافى بن عمران، وابن أبي الزناد، وأبي عقيل صاحب بهية، وغيرهم، وتوفي ببغداد في شهر رمضان سنة ثمان وعشرين ومائتين','13361','3571','0');
INSERT INTO [t9351] VALUES (3726,'يحيى بن يوسف الرقي ويكنى أبا زكريا، وكان يروي عن عبيد الله بن عمرو الرقي وغيره، وتوفي ببغداد في خلافة هارون الواثق','13362','3571','0');
INSERT INTO [t9351] VALUES (3727,'خلف بن هشام البزار ويكنى أبا محمد، سمع من شريك، وأبي عوانة، وحماد بن زيد، وغيرهم، وهو صاحب قرآن وحروف، وقرأ على سليم صاحب حمزة، ومات ببغداد يوم السبت لسبع ليال خلون من جمادى الآخرة سنة تسع وعشرين ومائتين، ودفن في مقابر الكناسة','13363','3571','0');
INSERT INTO [t9351] VALUES (3728,'الحسين بن إبراهيم بن الحر بن زعلان ويكنى أبا علي، ويلقب إشكاب، وهو من أبناء أهل خراسان من أهل نسا، وكان أبوه ممن خرج في دعوة آل العباس مع أسيد بن عبد الرحمن الذي ظهر بنسا وسود، وولي أسيد أصبهان، فكان إبراهيم بن الحر معه في أصحابه، فولد له الحسين','13364','3571','0');
INSERT INTO [t9351] VALUES (3729,'ثابت بن الوليد بن عبد الله بن جميع','13365','3571','0');
INSERT INTO [t9351] VALUES (3730,'غسان بن المفضل الغلابي ويكنى أبا معاوية','13366','3571','0');
INSERT INTO [t9351] VALUES (3731,'داود بن عمرو بن زهير بن عمرو بن جميل بن الأعرج بن ربيعة بن مسعود بن منقذ بن كوز بن كعب بن بجالة بن دهل بن مالك بن بكر بن سعد بن ضبة بن أد بن طابخة بن إلياس بن مضر، ويكنى أبا سليمان، مات ببغداد في ربيع الأول سنة ثمان وعشرين ومائتين','13367','3571','0');
INSERT INTO [t9351] VALUES (3732,'داود بن رشيد نزل مدينة أبي جعفر، وهو من أبناء أهل خراسان من أهل خوارزم، روى عن الوليد بن مسلم، وبقية بن الوليد، وإسماعيل بن عباس، وغيرهم من الشاميين، وكتب عنه أهل بغداد، وهو ثقة كثير الحديث','13368','3571','0');
INSERT INTO [t9351] VALUES (3733,'فضيل بن عبد الوهاب القناد وهو أخو محمد بن عبد الوهاب الذي روى عنه هارون بن إسحاق الهمداني','13369','3571','0');
INSERT INTO [t9351] VALUES (3734,'عبد الجبار بن عاصم ويكنى أبا طالب، من أبناء أهل خراسان الذين كانوا بالجزيرة، وكان قد كتب عن عبيد الله بن عمرو، وإسماعيل بن عياش، وأبي المليح، وبقية، وغيرهم، وتوفي ببغداد في عسكر المهدي في ربيع الآخر سنة ثلاث وثلاثين ومائتين','13370','3571','0');
INSERT INTO [t9351] VALUES (3735,'عبيد الله بن عمر بن ميسرة القواريري ويكنى أبا سعيد، وهو من أهل البصرة، وقدم بغداد فنزلها، وقد روى عن حماد بن زيد، ويزيد بن زريع، وعبد الرحمن بن مهدي، وغيرهم، وكان كثير الحديث، ثقة، وتوفي ببغداد لثلاث عشرة ليلة خلت من ذي الحجة في أيام التشريق سنة','13371','3571','0');
INSERT INTO [t9351] VALUES (3736,'محمد بن أبي حفص المعيطي مولى لهم، ويكنى أبا عبد الله، واسم أبي حفص: عمر، وكان ثقة صاحب حديث، روى عن بقية، وعبد الله بن المبارك، وأبي الأحوص، وشريك، وهشيم وغيرهم، وكان من أهل بغداد، وصلى الجمعة وانصرف إلى منزله وأوى إلى فراشه ليلة السبت، فطرقه','13372','3571','0');
INSERT INTO [t9351] VALUES (3737,'عيسى بن هشام النخاس سمع سماعا كثيرا، وكان صاحب حديث، وتوفي قبل أن يحدث','13373','3571','0');
INSERT INTO [t9351] VALUES (3738,'سلم بن قادم ويكنى أبا الليث، روى عن بقية، ومحمد بن حرب وغيرهما، وتوفي ببغداد في ذي القعدة سنة ثمان وعشرين ومائتين','13374','3571','0');
INSERT INTO [t9351] VALUES (3739,'نعيم بن هيصم ويكنى أبا محمد، من أبناء أهل خراسان، روى عن حماد بن زيد وغيره، توفي ببغداد في شوال سنة ثمان وعشرين ومائتين','13375','3571','0');
INSERT INTO [t9351] VALUES (3740,'يحيى بن عثمان ويكنى أبا زكريا، من أبناء أهل خراسان، كان ينزل درب أبي الجهم، وروى عن الشاميين رشيد بن سعد، وهقل بن زياد، وبقية، وإسماعيل بن عياش، وغيرهم، وتوفي في ربيع الأول من سنة ثمان وثلاثين ومائتين','13376','3571','0');
INSERT INTO [t9351] VALUES (3741,'إبراهيم بن زياد سبلان ويكنى أبا إسحاق، توفي ببغداد، ودفن يوم الأربعاء لست ليال خلون من ذي الحجة سنة ثمان وعشرين ومائتين','13377','3571','0');
INSERT INTO [t9351] VALUES (3742,'بشار بن موسى الخفاف ويكنى أبا عثمان، توفي ببغداد في شهر رمضان سنة ثمان وعشرين ومائتين، ودفن يوم الجمعة بعد العصر','13378','3571','0');
INSERT INTO [t9351] VALUES (3743,'أبو الأحوص واسمه: محمد بن حيان البغي، وقد سمع سماعا كثيرا، وكان ثقة، وتوفي في ذي الحجة سنة تسع وعشرين ومائتين','13379','3571','0');
INSERT INTO [t9351] VALUES (3744,'شجاع بن مخلد ويكنى أبا الفضل، من أبناء أهل خراسان، من البغيين، روى عن هشيم عامة كتبه، وعن إسماعيل ابن علية، وغيرهما، وهو ثقة ثبت، وتوفي ببغداد لعشر خلون من صفر سنة خمس وثلاثين ومائتين، وحضره بشر كثير، ودفن في مقبرة باب التبن','13380','3571','0');
INSERT INTO [t9351] VALUES (3745,'مهدي بن حفص ويكنى أبا أحمد، كان ينزل باب الكوفة','13381','3571','0');
INSERT INTO [t9351] VALUES (3746,'عباد بن موسى الختلي ويكنى أبا محمد، روى عن إبراهيم بن سعد، وطلحة بن يحيى الزرقي، وإسماعيل بن جعفر، وخرج إلى طرسوس فمات بها في أول سنة ثلاثين ومائتين','13382','3571','0');
INSERT INTO [t9351] VALUES (3747,'أحمد بن محمد بن أيوب ويكنى أبا جعفر، وكان وراقا يكتب للفضل بن يحيى بن جعفر بن برمك، فذكر أنه سمع المغازي من إبراهيم بن سعد مع الفضل بن يحيى، وذكر أنه سمع من أبي بكر بن عياش ما حدث به الفضل بن يحيى، ومات ببغداد ليلة الثلاثاء لأربع ليال بقين من ذي','13383','3571','0');
INSERT INTO [t9351] VALUES (3748,'سهل بن نصر وكان ينزل المطبخية','13384','3571','0');
INSERT INTO [t9351] VALUES (3749,'إسحاق بن إبراهيم بن كامجار ويكنى أبا يعقوب، وهو ابن أبي إسرائيل، من أبناء خراسان، من أهل مرو، وكان مخلطا متنقلا، وقف في القرآن ورجع مرارا، روى عن إبراهيم بن سعد، وحماد بن زيد، وعبد الرحمن بن أبي الزناد، وجعفر بن سليمان، وسليمان بن أخضر، وسمع','13385','3571','0');
INSERT INTO [t9351] VALUES (3750,'يحيى بن معين ويكنى أبا زكريا، وقد كان أكثر من كتاب الحديث، وعرف به، وكان لا يكاد يحدث، وتوفي بمدينة الرسول صلى الله عليه وسلم وهو متوجه إلى الحج','13386','3571','0');
INSERT INTO [t9351] VALUES (3751,'زهير بن حرب بن اشتال من أهل نسا، ثم عربت اشتال فجعلت شدادا، ويكنى أبا خيثمة، وهو مولى لبني حريش بن كعب بن عامر بن صعصعة العامري، روى عن جرير بن عبد الحميد، وهشيم، وسفيان بن عيينة، وابن علية، وعبد الله بن وهب، والوليد بن مسلم، وغيرهم من الكوفيين','13387','3571','0');
INSERT INTO [t9351] VALUES (3752,'خلف بن سالم المخرمي ويكنى أبا محمد، مولى المهالبة، وقد كان صنف المسند عن رسول الله صلى الله عليه وسلم، وكان كثير الحديث، وقد كتب الناس عنه، وتوفي ببغداد في شهر رمضان سنة إحدى وثلاثين ومائتين','13388','3571','0');
INSERT INTO [t9351] VALUES (3753,'أحمد بن محمد بن حنبل رضي الله عنه، ويكنى أبا عبد الله، وهو ثقة، ثبت، صدوق، كثير الحديث، وقد كان امتحن وضرب بالسياط، أمر بضربه أبو إسحاق أمير المؤمنين على أن يقول: القرآن مخلوق، فأبى أن يقول، وقد كان حبس قبل ذلك، فثبت على قوله، ولم يجبهم إلى','13389','3571','0');
INSERT INTO [t9351] VALUES (3754,'هارون بن معروف ويكنى أبا علي، توفي ببغداد في شهر رمضان سنة إحدى وثلاثين ومائتين','13390','3571','0');
INSERT INTO [t9351] VALUES (3755,'القاسم بن سلام ويكنى أبا عبيد، وهو من أبناء أهل خراسان، وكان مؤدبا، صاحب نحو وعربية، وطلب الحديث والفقه، وولي قضاء طرسوس أيام ثابت بن نصر بن مالك، ولم يزل معه ومع ولده، وقدم بغداد، ففسر بها غريب الحديث، وصنف كتبا، وسمع الناس منه، وحج فتوفي','13391','3571','0');
INSERT INTO [t9351] VALUES (3756,'بشر بن الوليد الكندي روى عن أبي يوسف القاضي كتبه وإملاءه، وروى عن شريك، وحماد بن زيد، ومالك بن أنس، وصالح المري وغيرهم، وروى عن محمد بن طلحة، وولي القضاء ببغداد في الجانبين جميعا، وكان يحدث ويفتي الناس ببغداد، وسعى به رجل فقال: إنه لا يقول','13392','3571','0');
INSERT INTO [t9351] VALUES (3757,'سهل بن محمد ويكنى أبا السري، مولى العباس بن عبد الله بن مالك، وكان ثقة','13393','3571','0');
INSERT INTO [t9351] VALUES (3758,'محمد بن سليم ويكنى أبا عبد الله العبدي، وقد سمع سماعا كثيرا، وولي القضاء ببادرايا وباكسابا أيام المأمون، ورأيت أصحاب الحديث يتقون حديثه والرواية عنه','13394','3571','0');
INSERT INTO [t9351] VALUES (3759,'بشر بن آدم سمع سماعا كثيرا، ورأيت أصحاب الحديث يتقون حديثه والكتابة عنه','13395','3571','0');
INSERT INTO [t9351] VALUES (3760,'عبد الرحمن بن يونس ويكنى أبا مسلم، من موالي أبي جعفر المنصور أخبرنا أنه ولد سنة أربع وستين ومائة، وطلب الحديث ورحل فيه، وسمع سماعا كثيرا، واستملى لسفيان بن عيينة، ويزيد بن هارون، وغيرهما، ومات يوم الأربعاء مع طلوع الشمس فجاءة في مسجد أسد بن','13396','3571','0');
INSERT INTO [t9351] VALUES (3761,'يحيى بن أيوب ويكنى أبا زكريا، مولى لأبي القاسم محرر','13397','3571','0');
INSERT INTO [t9351] VALUES (3762,'أبو القاسم زوج بنت أبي مسلم وهو جد حسين بن الفهم لأبيه، وكان ينزل عسكر المهدي، وكان ثقة، ورعا عالما، يقول بالسنة ويعيب من يقول بقول جهم وبخلاف السنة، وتوفي يوم الأحد لاثنتي عشرة ليلة خلت من ربيع الأول سنة أربع وعشرين ومائتين','13398','3571','0');
INSERT INTO [t9351] VALUES (3763,'إبراهيم بن حاتم بن عبد الله الهروي ويكنى أبا إسحاق','13399','3571','0');
INSERT INTO [t9351] VALUES (3764,'عبد الله بن عون الخراز ويكنى أبا محمد، توفي ببغداد في خلافة هارون الواثق بالله أمير المؤمنين','13400','3571','0');
INSERT INTO [t9351] VALUES (3765,'شريح بن يونس المروروذي ويكنى أبا الحارث، وهو زوج بنت قريش المستملي، وكان قد صنف كتبا، وأخرجها، وحدث بها، وكان ثقة، توفي في يوم الثلاثاء لسبع بقين من ربيع الأول سنة خمس وثلاثين ومائتين','13401','3571','0');
INSERT INTO [t9351] VALUES (3766,'أحمد بن داود ويكنى أبا سعيد الحداد الواسطي، وقد كان نزل بغداد، وكان ثقة، ومات قبل أن يحدث ويكتب عنه','13402','3571','0');
INSERT INTO [t9351] VALUES (3767,'إسماعيل بن إبراهيم بن بسام الترجماني ويكنى أبا إبراهيم، من أبناء أهل خراسان، ومنزله نحو صحراء أبي السري، روى عن هشيم، وعن العطاف بن خالد، وعبد العزيز الماجشون، وخلف بن خليفة، وصالح المري، وغيرهم، وقد روى عن شريك أيضا، وتوفي ببغداد لخمس ليال','13403','3571','0');
INSERT INTO [t9351] VALUES (3768,'عمرو الناقد ابن محمد بن بكير ويكنى أبا عثمان، وهو ثقة، صاحب حديث، ثبت، وقد كتب عنه أهل بغداد كتبا كثيرة، وكان من الحفاظ المعدودين، وكان فقيها، وتوفي ببغداد، وذلك يوم الخميس لأربع ليال خلون من ذي الحجة سنة اثنتين ومائتين','13404','3571','0');
INSERT INTO [t9351] VALUES (3769,'محمد بن عباد المكي صاحب سفيان بن عيينة، وتوفي بعسكر الخليفة بسامرا في سنة أربع وثلاثين ومائتين','13405','3571','0');
INSERT INTO [t9351] VALUES (3770,'حاجب بن الوليد الأعور المعلم ويكنى أبا أحمد، توفي ببغداد في شهر رمضان سنة ثمان وعشرين ومائتين','13406','3571','0');
INSERT INTO [t9351] VALUES (3771,'أبو معمر واسمه: إسماعيل بن إبراهيم بن معمر الهروي من هذيل من أنفسهم، صاحب سنة وفضل وخير، وهو ثقة ثبت، وتوفي ببغداد في جمادى الأولى سنة ست وثلاثين ومائتين، وشهده خلق كثير','13407','3571','0');
INSERT INTO [t9351] VALUES (3772,'محمد بن حاتم بن ميمون المروزي ويكنى أبا عبد الله، استخرج كتابا في تفسير القرآن كتبه الناس ببغداد، وكان ينزل قطيعة الربيع بالكرخ، وتوفي ببغداد يوم الخميس لأربع بقين من ذي الحجة سنة خمس وثلاثين ومائتين','13408','3571','0');
INSERT INTO [t9351] VALUES (3773,'أحمد بن حاتم الطويل','13409','3571','0');
INSERT INTO [t9351] VALUES (3774,'إبراهيم بن محمد بن عرعرة بن البرند من بني سامة بن لؤي، يكنى أبا إسحاق، وتوفي ببغداد في شهر رمضان سنة إحدى وثلاثين ومائتين بعد انصرافه من العسكر عسكر الخليفة بسامرا','13410','3571','0');
INSERT INTO [t9351] VALUES (3775,'أحمد بن محمد الصفار ويكنى أبا حفص','13411','3571','0');
INSERT INTO [t9351] VALUES (3776,'عبد الرحمن بن صالح الأزدي ويكنى أبا محمد، وهو من أهل الكوفة، ونزل بغداد، وكان يحدث عن شريك، وابن أبي زائدة، وأبي بكر بن عياش، وغيرهم، وعن ملازم بن عمرو، وتوفي ببغداد يوم الإثنين انسلاخ ذي الحجة سنة خمس وثلاثين ومائتين','13412','3571','0');
INSERT INTO [t9351] VALUES (3777,'أحمد بن إبراهيم ويكنى أبا علي، ويعرف بالموصلي، روى عن حماد بن زيد، وشريك، وأبي عوانة، وغيرهم، وتوفي ببغداد في شهر ربيع الأول سنة ست وثلاثين ومائتين','13413','3571','0');
INSERT INTO [t9351] VALUES (3778,'إبراهيم بن أبي الليث ويكنى أبا إسحاق، وهو صاحب الأشجعي، ونزل بغداد في عسكر المهدي، وكان صاحب سنة، ويضعف في الحديث','13414','3571','0');
INSERT INTO [t9351] VALUES (3779,'يعقوب بن إبراهيم بن كثير العبدي ويكنى أبا يوسف، وهو ابن الدورقي','13415','3571','0');
INSERT INTO [t9351] VALUES (3780,'وأخوه أحمد بن إبراهيم بن كثير ويكنى أبا عبد الله','13416','3571','0');
INSERT INTO [t9351] VALUES (3781,'عبد المنعم بن إدريس بن سنان ويكنى أبا عبد الله، وهو ابن ابنة وهب بن منبه، وروى كتب وهب من أحاديث الأنبياء والعباد، وأحاديث بني إسرائيل عن أبيه، عن وهب بن منبه، وذكر أنه قد لقي معمر بن راشد باليمن وسمع منه، وكان قارئا لكتب وهب بن منبه وحكمته، مات','13417','3571','0');
INSERT INTO [t9351] VALUES (3782,'محمد بن مصعب ويكنى أبا جعفر، كان قارئا لكتاب الله، وقد سمع الحديث وجالس الناس، وكان ثقة إن شاء الله، مات ببغداد في ذي القعدة سنة ثمان وعشرين ومائتين','13418','3571','0');
INSERT INTO [t9351] VALUES (3783,'محرز بن عون بن أبي عون ويكنى أبا الفضل قال: أخبرني أبي قال: ولدت سنة أربع وأربعين ومائة قال: وفي هذه السنة حج أبو جعفر المنصور بالناس وتوفي ببغداد سنة إحدى وثلاثين ومائتين، وهو ابن ثمان وثمانين سنة، وقد كان حدث وكتب الناس عنه كتابا كبيرا، وكان ثقة','13419','3571','0');
INSERT INTO [t9351] VALUES (3784,'الوليد بن صالح النحاس ويكنى أبا محمد، روى عن عبيد الله بن عمرو، وأبي معشر، وبقية بن الوليد، وحماد بن سلمة، وعيسى بن يونس','13420','3571','0');
INSERT INTO [t9351] VALUES (3785,'العباس بن غالب الوراق روى مصنف وكيع وغير ذلك، وتوفي ببغداد في صفر سنة ثلاث وثلاثين ومائتين','13421','3571','0');
INSERT INTO [t9351] VALUES (3786,'رباح بن الجراح ويكنى أبا الوليد، من أهل الموصل، وقدم بغداد، وروى عن المعافى بن عمران، وعفيف بن سالم','13422','3571','0');
INSERT INTO [t9351] VALUES (3787,'الوليد بن شجاع بن الوليد ويكنى أبا همام السكوني، روى عن بقية بن الوليد وغيره من الشاميين، والعراقيين','13423','3571','0');
INSERT INTO [t9351] VALUES (3788,'نوح بن يزيد المؤدب ويكنى أبا محمد، وكان صاحب إبراهيم بن سعد، وكان ثقة، فيه عسر','13424','3571','0');
INSERT INTO [t9351] VALUES (3789,'عبد العزيز بن بحر المؤدب روى عن إسماعيل بن جعفر وغيره','13425','3571','0');
INSERT INTO [t9351] VALUES (3790,'كامل بن طلحة الجحدري من أهل البصرة، ويكنى أبا يحيى، وتوفي بالبصرة سنة اثنتين وثلاثين ومائتين','13426','3571','0');
INSERT INTO [t9351] VALUES (3791,'يوسف بن موسى القطان وكان من أهل الكوفة ونزل الري وتجر بها، وسمع من جرير بن عبد الحميد وغيره، وقدم بغداد فنزل دار القطن','13427','3571','0');
INSERT INTO [t9351] VALUES (3792,'مردويه الصائغ واسمه: عبد الصمد بن يزيد، ولقبه مردويه، ويكنى أبا عبد الله، روى عن الفضيل بن عياض، وابن عيينة، وغيرهما، وكان ثقة، من أهل السنة والورع، وقد كتب الناس عنه، وتوفي في آخر يوم من ذي الحجة سنة خمس وثلاثين ومائتين','13428','3571','0');
INSERT INTO [t9351] VALUES (3793,'يحيى بن إسماعيل الواسطي ويكنى أبا زكريا','13429','3571','0');
INSERT INTO [t9351] VALUES (3794,'أبو عمرو المقرئ وهو حفص بن عمر بن عبد العزيز بن صهبان الأزدي، وقد قرأ عليه الناس القرآن، وكان عالما بالقرآن وتفسيره، وقد كتب عن شريك وغيره من أهل العراق، وأهل المدينة، وأهل الشام','13430','3571','0');
INSERT INTO [t9351] VALUES (3795,'محمد بن سعد صاحب الواقدي، وهو مولى الحسين بن عبد الله بن عبيد الله بن العباس بن عبد المطلب الهاشمي، وتوفي ببغداد يوم الأحد لأربع خلون من جمادى الآخرة سنة ثلاثين ومائتين، ودفن في مقبرة باب الشام، وهو ابن اثنتين وستين سنة، وهو الذي ألف هذا الكتاب،','13431','3571','0');
INSERT INTO [t9351] VALUES (3796,'تسمية من كان بخراسان من أصحاب رسول الله صلى الله عليه وسلم ممن غزاها ومات بها','13432','3571','0');
INSERT INTO [t9351] VALUES (3797,'بريدة بن الحصيب بن عبد الله بن الحارث بن الأعرج بن سعد بن رزاح بن عدي بن سهم بن مازن بن الحارث بن سلامان بن أسلم بن أفصى، ويكنى أبا عبد الله، وأسلم حين مر به رسول الله صلى الله عليه وسلم إلى الهجرة، وأقرأه صدرا من سورة مريم، ثم قدم عليه المدينة','13433','3571','0');
INSERT INTO [t9351] VALUES (3798,'أبو برزة الأسلمي واسمه فيما ذكر محمد بن عمر، وبعض ولد أبي برزة: عبد الله بن نضلة، وقال غيرهم من العلماء: اسمه: نضلة بن عبد الله وقال آخرون: نضلة بن عبيد بن الحارث بن جناد بن ربيعة بن دعبل بن أنس بن خزيمة بن مالك بن سلامان بن أسلم بن أفصى أسلم أبو','13435','3571','0');
INSERT INTO [t9351] VALUES (3799,'الحكم بن عمرو بن مجدع بن حذيم بن الحارث بن نعيلة بن مليك بن ضمرة بن بكر بن عبد مناة بن كنانة، ونعيلة ثعلبة هو أخو غفار بن مليك، فقيل للحكم بن عمرو الغفاري وهو من ولد نعيلة أخي غفار، وقد صحب الحكم النبي صلى الله عليه وسلم حتى قبض، ثم تحول إلى البصرة','13436','3571','0');
INSERT INTO [t9351] VALUES (3800,'عبد الرحمن بن سمرة بن حبيب بن عبد شمس بن عبد مناف بن قصي، وأمه أروى بنت أبي الفرعة، واسم أبي الفرعة: حارثة بن كعب بن مطرف بن ضريس، من بني فراس بن غنم، تحول عبد الرحمن إلى البصرة ونزلها، وروى عن رسول الله صلى الله عليه وسلم أحاديث، وكان اسمه عبد','13437','3571','0');
INSERT INTO [t9351] VALUES (3801,'قثم بن العباس بن عبد المطلب بن هاشم بن عبد مناف وأمه أم الفضل وهي لبابة الكبرى بنت الحارث الهلالية، وكان قثم يشبه برسول الله صلى الله عليه وسلم، وغزا قثم خراسان، وكان عليها سعيد بن عثمان، فقال له: أضرب لك بألف سهم، فقال: لا، بل أخمس، ثم أعط','13438','3571','0');
INSERT INTO [t9351] VALUES (3802,'عبد الرحمن بن يعمر الدئلي روى عنه بكير بن عطاء، عن النبي صلى الله عليه وسلم أنه قال: " الحج عرفة، من أدرك عرفة قبل الصبح فقد أدرك الحج "','13439','3571','0');
INSERT INTO [t9351] VALUES (3803,'وكان بخراسان بعد هؤلاء من الفقهاء والمحدثين','13440','3571','0');
INSERT INTO [t9351] VALUES (3804,'يحيى بن يعمر الليثي من بني كنانة، وكان من أهل البصرة، وكان نحويا صاحب علم بالعربية والقرآن، ثم أتى خراسان، فنزل مرو وولي القضاء بها، فكان يقضي باليمين مع الشاهد، وكان ثقة','13441','3571','0');
INSERT INTO [t9351] VALUES (3805,'أبو مجلز لاحق بن حميد السدوسي وكان ثقة، له أحاديث، وكان قد أتى مرو، فنزلها وابتنى بها دارا، وولي بيت المال بها، وكان أعور، توفي في خلافة عمر بن عبد العزيز','13443','3571','0');
INSERT INTO [t9351] VALUES (3806,'يزيد بن أبي سعيد النحوي من أهل مرو، وله أحاديث','13444','3571','0');
INSERT INTO [t9351] VALUES (3807,'محمد النخعي ويكنى أبا يوسف، وكان ثقة إن شاء الله، وروى عن سعيد بن جبير، وولي القضاء بمرو','13445','3571','0');
INSERT INTO [t9351] VALUES (3808,'الضحاك بن مزاحم يكنى أبا القاسم، من أهل بلخ','13446','3571','0');
INSERT INTO [t9351] VALUES (3809,'عطاء الخراساني وكان ثقة، وأتى الشام، فروى عنه الشاميون، وروى عنه مالك بن أنس وغيره','13447','3571','0');
INSERT INTO [t9351] VALUES (3810,'أبو المنيب واسمه عيسى بن عبيد وله أحاديث، وقد روى عن عكرمة','13448','3571','0');
INSERT INTO [t9351] VALUES (3811,'أبو جرير قاضي سجستان واسمه عبد الرحمن بن حسين','13449','3571','0');
INSERT INTO [t9351] VALUES (3812,'الربيع بن أنس أخبرنا عمار بن نصر الخراساني قال: كان الربيع بن أنس من بكر بن وائل من أنفسهم، وكان من أهل البصرة، وقد لقي ابن عمر، وجابر بن عبد الله، وأنس بن مالك، وكان هرب من الحجاج، فأتى مرو، فسكن قرية منها يقال لها برز، ثم تحول إلى قرية أخرى','13450','3571','0');
INSERT INTO [t9351] VALUES (3813,'إبراهيم بن ميمون الصائغ كان هو ومحمد بن ثابت العبدي صديقين لأبي مسلم الداعية بخراسان، يجلسان إليه ويسمعان كلامه، فلما أظهر الدعوة بخراسان وقام بهذا الأمر دس إليهما من يسألهما عن نفسه، وعن الفتك به، فقال محمد بن ثابت: لا أرى أن يفتك به؛ لأن الأيمان','13451','3571','0');
INSERT INTO [t9351] VALUES (3814,'محمد بن ثابت العبدي وكان أصله من أهل البصرة، روى عن أبي المتوكل، وقد ولي قضاء مرو، وروى عنه عبد الله بن المبارك وغيره','13452','3571','0');
INSERT INTO [t9351] VALUES (3815,'يعقوب بن القعقاع وكان من أهل مرو، وكان قاضيا بها، وروى عن عطاء بن أبي رباح، وروى عنه الثوري، وعبد الله بن المبارك','13453','3571','0');
INSERT INTO [t9351] VALUES (3816,'منصور بن أبي سريرة روى عنه عبد الله بن المبارك','13454','3571','0');
INSERT INTO [t9351] VALUES (3817,'حسين بن واقد روى عن عبد الله بن بريدة، وكان حسن الحديث','13455','3571','0');
INSERT INTO [t9351] VALUES (3818,'خارجة بن مصعب السرخسي اتقى الناس حديثه فتركوه','13456','3571','0');
INSERT INTO [t9351] VALUES (3819,'نوح بن أبي مريم ويكنى أبا عصمة','13457','3571','0');
INSERT INTO [t9351] VALUES (3820,'أبو حمزة السكري من أهل مرو، وكان قديما','13458','3571','0');
INSERT INTO [t9351] VALUES (3821,'حفص بن عبد الرحمن البلخي ويكنى أبا عمرو، وكان ينزل نيسابور','13459','3571','0');
INSERT INTO [t9351] VALUES (3822,'عبيد الله السجزي وهو من أهل سجستان، وروى لسفيان الثوري وغيره، وكان متجره إلى نيسابور','13460','3571','0');
INSERT INTO [t9351] VALUES (3823,'نهشل بن سعيد بن وردان يروي عن الضحاك بن مزاحم','13461','3571','0');
INSERT INTO [t9351] VALUES (3824,'الفضل بن موسى السيناني وسينان قرية من قرى مرو، من ربع السقاذم، وكان الفضل ثقة، روى عنه وكيع بن الجراح وغيره','13462','3571','0');
INSERT INTO [t9351] VALUES (3825,'عبد الله بن المبارك ويكنى أبا عبد الرحمن، ولد سنة ثماني عشرة ومائة، وطلب العلم، فروى رواية كثيرة، وصنف كتبا كثيرة في أبواب العلم وصنوفه، حملها عنه قوم وكتبها الناس عنهم، وقال الشعر في الزهد والحث على الجهاد، وقدم العراق والحجاز والشام ومصر واليمن','13463','3571','0');
INSERT INTO [t9351] VALUES (3826,'النضر بن محمد المروزي وكان مقدما عندهم في العلم والفقه والعقل والفضل، وكان صديقا لعبد الله بن المبارك، وكان من أصحاب أبي حنيفة','13464','3571','0');
INSERT INTO [t9351] VALUES (3827,'مكي بن إبراهيم البلخي ويكنى أبا السكن، توفي ببلخ سنة خمس عشرة ومائتين، وكان ثقة، وقدم بغداد يريد الحج، فحج ورجع، وحدث الناس في ذهابه ورجوعه، فكتبوا عنه، وكان ثبتا في الحديث','13465','3571','0');
INSERT INTO [t9351] VALUES (3828,'النضر بن شميل المروزي وهو من أهل البصرة، من بني مازن، وكان ثقة إن شاء الله، صاحب حديث ورواية للشعر ومعرفة بالنحو وبأيام الناس، وتوفي بخراسان سنة ثلاث ومائتين في خلافة المأمون، وذلك قبل خروج المأمون من خراسان','13466','3571','0');
INSERT INTO [t9351] VALUES (3829,'مقاتل بن سليمان البلخي صاحب التفسير، روى عن الضحاك بن مزاحم، وعطاء، وأصحاب الحديث يتقون حديثه وينكرونه','13467','3571','0');
INSERT INTO [t9351] VALUES (3830,'أبو مطيع البلخي واسمه: الحكم بن عبد الله، وكان على قضاء بلخ، وكان مرجئا، وقد لقي عبد الرحمن بن حرملة وغيره، وهو ضعيف عندهم في الحديث، وكان مكفوفا','13468','3571','0');
INSERT INTO [t9351] VALUES (3831,'عمرو بن هاون البلخي روى عن ابن جريج وغيره، وقد كتب الناس عنه كتابا كبيرا، وتركوا حديثه','13469','3571','0');
INSERT INTO [t9351] VALUES (3832,'سلم بن سالم البلخي ويكنى أبا محمد، وكان مرجئا، ضعيفا في الحديث، ولكنه كان صارما، يأمر بالمعروف، وينهى عن المنكر، وكانت له رئاسة بخراسان، فبعث إليه هارون أمير المؤمنين، فأقدمه عليه فحبسه، فلم يزل محبوسا إلى أن مات هارون، ثم أخرجه محمد بن هارون','13470','3571','0');
INSERT INTO [t9351] VALUES (3833,'مقاتل بن حيان أبو معان البلخي وقد روي عنه','13471','3571','0');
INSERT INTO [t9351] VALUES (3834,'خلف بن أيوب ويكنى أبا سعيد، من أهل بلخ، وقد روي عنه','13472','3571','0');
INSERT INTO [t9351] VALUES (3835,'شداد بن حكيم ويكنى أبا عثمان البلخي، وقد روي عنه','13473','3571','0');
INSERT INTO [t9351] VALUES (3836,'أبو تميلة المروزي واسمه: يحيى بن واضح، وكان مولى للأنصار، لقي محمد بن إسحاق وروى عنه، وكان ثقة، يحدث عنه','13474','3571','0');
INSERT INTO [t9351] VALUES (3837,'الحسن بن سوار ويكنى أبا العلاء المروروذي، وكان ثقة، قدم بغداد يريد الحج فروى عنه الناس وكتبوا عنه، ثم رجع إلى خراسان، فمات بها في آخر خلافة المأمون','13475','3571','0');
INSERT INTO [t9351] VALUES (3838,'عبد الصمد بن حسان المروروذي وكان قاضيا بها وبنيسابور وهراة، وكان ثقة، وتوفي في خلافة المأمون','13476','3571','0');
INSERT INTO [t9351] VALUES (3839,'علي بن الحسن بن شقيق من أصحاب عبد الله بن المبارك، وقد لقي الحسين بن واقد وروى عنه، وهو من أهل مرو، وتوفي بمرو','13477','3571','0');
INSERT INTO [t9351] VALUES (3840,'عبد العزيز بن أبي رزمة المروزي روى عن حماد بن سلمة، وحماد بن زيد وغيرهما، وكان ثقة','13478','3571','0');
INSERT INTO [t9351] VALUES (3841,'نصر بن باب ويكنى أبا سهل، من أهل مرو، سمع من داود بن أبي هند، وعوف الأعرابي، والحجاج، وغيرهم، وقدم بغداد فسمعوا منه وروي عنه، ثم حدث عن إبراهيم الصائغ فاتهموه فتركوا حديثه','13479','3571','0');
INSERT INTO [t9351] VALUES (3842,'علي بن إسحاق الداركاني وهي قرية بمرو، وكان ينزلها الحجاج إذا خرجوا من مرو، وكان من أصحاب عبد الله بن المبارك، معروفا بصحبته، وكان ثقة، وقدم بغداد فسمعوا منه','13480','3571','0');
INSERT INTO [t9351] VALUES (3843,'الحسين بن الوليد ويكنى أبا عبد الله مولى لقريش','13481','3571','0');
INSERT INTO [t9351] VALUES (3844,'سهل بن مزاحم من أهل مرو، وكان فقيها مفتيا عابدا، ويكنى أبا بشر','13482','3571','0');
INSERT INTO [t9351] VALUES (3845,'وأخوه محمد بن مزاحم ويكنى أبا وهب، وكان خبيرا فاضلا، مات سنة إحدى عشرة ومائتين، وكان يروي عن عبد الله بن المبارك','13483','3571','0');
