INSERT INTO [t9351] VALUES (3849,'سلمة بن سليمان من أهل مرو، وهو صاحب عبد الله بن المبارك، معروف به','13487','3571','0');
INSERT INTO [t9351] VALUES (3850,'عياذ بن عثمان واسمه: عبد الله، وهو ابن ابنة عبد العزيز بن أبي رواد، وقد لقي شعبة، وعنده كتب عن عبد الله بن المبارك','13488','3571','0');
INSERT INTO [t9351] VALUES (3851,'محمد بن الفضل من أهل مرو، متروك الحديث','13489','3571','0');
