INSERT INTO [t9351] VALUES (3853,'وأخوه القاسم بن المغيرة من أهل سرخس','13491','3571','0');
INSERT INTO [t9351] VALUES (3854,'أبو سعيد الصاغاني وكان ثقة، واسمه: محمد بن ميسر، وكان مكفوفا','13492','3571','0');
INSERT INTO [t9351] VALUES (3855,'عصام بن يوسف من أهل بلخ','13493','3571','0');
INSERT INTO [t9351] VALUES (3856,'أبو إسحاق الزيات من أهل بلخ. واسمه: إبراهيم بن سليمان، وكان مرجئا','13494','3571','0');
INSERT INTO [t9351] VALUES (3857,'قتيبة بن سعيد ويكنى أبا رجاء البلخي، روى عن ليث بن سعد، وابن لهيعة','13495','3571','0');
INSERT INTO [t9351] VALUES (3858,'أبو معاذ النحوي من أهل مرو، روى عن عبد الله بن المبارك','13496','3571','0');
INSERT INTO [t9351] VALUES (3859,'يعمر بن بشر ويكنى أبا عمرو، صاحب عبد الله بن المبارك','13497','3571','0');
INSERT INTO [t9351] VALUES (3860,'وكان بالري من الفقهاء والمحدثين','13498','3571','0');
INSERT INTO [t9351] VALUES (3861,'أبو جعفر الرازي واسمه: عيسى بن ماهان، وكان أصله من أهل مرو، من قرية يقال لها برز، وهي القرية التي نزلها الربيع بن أنس أولا، وبها سمع أبو جعفر من الربيع بن أنس، ثم تحول أبو جعفر بعد ذلك إلى الري، فمات بها، فقيل له الرازي، وكان ثقة، وكان يقدم','13499','3571','0');
INSERT INTO [t9351] VALUES (3862,'يحيى بن ضريس كان قاضيا بالري ومات بها','13500','3571','0');
INSERT INTO [t9351] VALUES (3863,'سعيد بن سنان الشيباني من أنفسهم، وكان أصله من أهل الكوفة، ولكنه سكن الري بعد ذلك، وكان يحج كل سنة، وكان سيئ الخلق','13501','3571','0');
INSERT INTO [t9351] VALUES (3864,'جرير بن عبد الحميد ويكنى أبا عبد الله، ولد سنة سبع ومائة بالكوفة، ونشأ بها، وطلب الحديث، وسمع فأكثر، ثم نزل الري، فمات بها، وكان ثقة، كثير العلم، ترحل إليه','13502','3571','0');
INSERT INTO [t9351] VALUES (3865,'حكام بن سلم الرازي وكان ثقة إن شاء الله','13503','3571','0');
INSERT INTO [t9351] VALUES (3866,'سلمة الأبرش بن الفضل ويكنى أبا عبد الله، وكان ثقة صدوقا، وهو صاحب محمد بن إسحاق، روى عنه المغازي والمبتدأ، وتوفي بالري وقد أتى عليه مائة وعشر سنين، وكان مؤدبا، وكان يقال إنه من أخشع الناس في صلاته','13504','3571','0');
INSERT INTO [t9351] VALUES (3867,'إسحاق بن سليمان ويكنى أبا يحيى، مولى لعبد القيس، وكان ثقة، له فضل في نفسه وورع، وانتقل إلى الكوفة فأقام بها سنين، ثم رجع إلى الري فمات بها سنة تسع وتسعين ومائة','13505','3571','0');
INSERT INTO [t9351] VALUES (3868,'إسحاق بن إسماعيل الرازي ويلقب حيويه، توفي بالري، وكان قد حدث وروي عنه','13506','3571','0');
INSERT INTO [t9351] VALUES (3869,'وكان بهمدان من الفقهاء','13507','3571','0');
INSERT INTO [t9351] VALUES (3870,'أصرم بن حوشب الهمداني وكان قدم بغداد، فكتب عنه أهل بغداد، ثم رجع إلى همدان فمات بها','13508','3571','0');
INSERT INTO [t9351] VALUES (3871,'وكان بقم من المحدثين','13509','3571','0');
