INSERT INTO [t9351] VALUES (3873,'ويعقوب بن عبد الله الأشعري','13511','3571','0');
INSERT INTO [t9351] VALUES (3874,'وكان بالأنبار من المحدثين','13512','3571','0');
INSERT INTO [t9351] VALUES (3875,'محمد بن عبد الله الحذاء ويكنى أبا جعفر، وكانت عنده أحاديث، وكان ثقة','13513','3571','0');
INSERT INTO [t9351] VALUES (3876,'سويد بن سعيد ويكنى أبا محمد الأنباري، وكان ينزل الحديثة حديثة النورة على فراسخ من الأنبار','13514','3571','0');
INSERT INTO [t9351] VALUES (3877,'إسحاق بن البهلول ويكنى أبا يعقوب','13515','3571','0');
INSERT INTO [t9351] VALUES (3878,'تسمية من نزل الشام من أصحاب رسول الله صلى الله عليه وسلم','13516','3571','0');
INSERT INTO [t9351] VALUES (3879,'أبو عبيدة بن الجراح رضي الله عنه، واسمه: عامر بن عبد الله بن الجراح بن هلال بن أهيب بن ضبة بن الحارث بن فهر، وأمه أميمة بنت غنم بن جابر بن عبد العزى بن عامر بن عميرة، أسلم أبو عبيدة قبل دخول رسول الله صلى الله عليه وسلم دار الأرقم، وهاجر إلى أرض','13517','3571','0');
INSERT INTO [t9351] VALUES (3880,'بلال بن رباح مولى أبي بكر الصديق رضي الله عنه، ويكنى أبا عبد الله، وكان من مولدي السراة، واسم أمه: حمامة، وكانت أمة لبعض بني جمح','13521','3571','0');
INSERT INTO [t9351] VALUES (3881,'عبادة بن الصامت بن قيس بن أصرم بن فهر بن ثعلبة بن غنم بن عوف بن عمرو بن عوف بن الخزرج، من القواقلة، ويكنى أبا الوليد، وأمه قرة العين بنت عبادة بن نضلة بن مالك بن العجلان بن زيد بن غنم بن سالم بن عمرو بن عوف بن الخزرج، شهد عبادة العقبة مع السبعين من','13531','3571','0');
INSERT INTO [t9351] VALUES (3882,'معاذ بن جبل بن عمرو بن أوس بن عائذ بن عدي بن كعب بن عمرو بن أدي بن سعد أخي سلمة بن سعد بن علي بن أسد بن شاردة بن يزيد بن جشم بن الخزرج قال: ويكنى معاذ أبا عبد الرحمن، وأمه هند بنت سهل من جهينة، وأخوه لأمه عبد الله بن الجد بن قيس من أهل بدر، وشهد معاذ','13533','3571','0');
INSERT INTO [t9351] VALUES (3883,'سعد بن عبادة بن دليم بن حارثة بن أبي حزيمة بن ثعلبة بن طريف بن الخزرج بن ساعدة، من الأنصار، ويكنى أبا ثابت، وأمه عمرة بنت مسعود بن قيس بن عمرو بن زيد مناة بن عدي بن عمرو بن مالك بن النجار، وهو ابن خالة مسعود بن زيد الأشهلي، من أهل بدر، وكان سعد بن','13541','3571','0');
INSERT INTO [t9351] VALUES (3884,'أبو الدرداء واسمه عويمر بن زيد بن قيس بن عائشة بن أمية بن مالك بن عامر بن عدي بن كعب بن الخزرج بن الحارث بن الخزرج، وأمه محبة بنت واقد بن عمرو بن الإطنابة بن عامر بن زيد مناة بن مالك بن ثعلبة بن كعب وكان أبو الدرداء آخر أهل داره إسلاما، فجاء عبد الله','13545','3571','0');
INSERT INTO [t9351] VALUES (3885,'شرحبيل ابن حسنة وهي أمه، وهي عدوية، وهو ابن عبد الله بن المطاع بن عمرو، من كندة، حليف لبني زهرة، ويكنى أبا عبد الله، وأسلم قديما بمكة، وهو من مهاجرة الحبشة في الهجرة الثانية، وكان من علية أصحاب رسول الله صلى الله عليه وسلم، وغزا معه غزوات، وهو','13556','3571','0');
INSERT INTO [t9351] VALUES (3886,'خالد بن الوليد بن المغيرة بن عبد الله بن عمير بن مخزوم ويكنى أبا سليمان، وأمه عصماء: وهي لبابة الصغرى بنت الحارث بن حرب بن بجير بن الهزم بن رويبة بن عبد الله بن هلال بن عامر بن صعصعة، وهي أخت أم الفضل بن الحارث أم بني العباس بن عبد المطلب، وكان خالد','13557','3571','0');
INSERT INTO [t9351] VALUES (3887,'عياض بن غنم بن زهير بن أبي شداد بن ربيعة بن هلال بن أهيب بن ضبة بن الحارث بن فهر، أسلم قديما قبل الحديبية، وشهد الحديبية مع رسول الله صلى الله عليه وسلم، وكان رجلا صالحا سمحا، وكان مع أبي عبيدة بن الجراح بالشام، فلما حضرت أبا عبيدة الوفاة ولي عياض','13566','3571','0');
INSERT INTO [t9351] VALUES (3888,'سعيد بن عامر بن حذيم بن سلامان بن ربيعة بن سعد بن جمح بن عمرو بن هصيص، أسلم قبل خيبر، وهاجر إلى المدينة، وشهد مع النبي صلى الله عليه وسلم خيبر، وما بعد ذلك من المشاهد، ولا نعلم له بالمدينة دارا، وولاه عمر بن الخطاب عمل عياض بن غنم حين مات عياض،','13568','3571','0');
INSERT INTO [t9351] VALUES (3889,'الفضل بن العباس بن عبد المطلب بن هاشم بن عبد مناف بن قصي، ويكنى أبا محمد، وكان أسن ولد العباس، وغزا مع رسول الله صلى الله عليه وسلم مكة وحنينا، وثبت يومئذ مع رسول الله صلى الله عليه وسلم حين ولى الناس، وشهد معه حجة الوداع، وأردفه رسول الله صلى الله','13570','3571','0');
INSERT INTO [t9351] VALUES (3890,'أبو مالك الأشعري أسلم، وصحب النبي صلى الله عليه وسلم، وغزا معه، وروى عنه','13571','3571','0');
INSERT INTO [t9351] VALUES (3891,'عوف بن مالك الأشجعي أسلم قبل حنين، وشهد حنينا، وكانت راية أشجع معه يوم فتح مكة، وتحول إلى الشام في خلافة أبي بكر، فنزل حمص، وبقي إلى أول خلافة عبد الملك بن مروان، ومات سنة ثلاث وسبعين، وكان يكنى أبا عمرو','13573','3571','0');
INSERT INTO [t9351] VALUES (3892,'ثوبان، مولى رسول الله صلى الله عليه وسلم ويكنى أبا عبد الله، وهو من أهل السراة، قال: يذكرون أنه من حمير، أصابه سبا، فاشتراه رسول الله صلى الله عليه وسلم، فأعتقه، فلم يزل مع رسول الله صلى الله عليه وسلم حتى قبض رسول الله صلى الله عليه وسلم، فتحول','13574','3571','0');
INSERT INTO [t9351] VALUES (3893,'سهل بن الحنظلية وهو سهل بن عمرو بن عدي بن زيد بن جشم بن حارثة، وأمه من بني تميم، ثم من بني حنظلة، فنسب إلى أمه، فقيل ابن الحنظلية، شهد أحدا والخندق والمشاهد مع رسول الله صلى الله عليه وسلم، ثم تحول إلى الشام، فنزل دمشق حتى مات بها','13575','3571','0');
INSERT INTO [t9351] VALUES (3894,'شداد بن أوس بن ثابت بن المنذر بن حرام بن عمرو بن زيد مناة بن عامر بن عمرو بن مالك بن النجار، وهو ابن أخي حسان بن ثابت الشاعر، وتحول إلى فلسطين، فنزلها، ومات بها سنة ثمان وخمسين، في آخر خلافة معاوية بن أبي سفيان، وكان يوم مات ابن خمس وتسعين سنة،','13576','3571','0');
INSERT INTO [t9351] VALUES (3895,'فضالة بن عبيد بن نافذ بن قيس بن صهبية بن الأصرم بن جحجبا بن كلفة بن عوف بن عمرو بن عوف، من الأنصار، شهد أحدا والخندق والمشاهد كلها مع رسول الله صلى الله عليه وسلم، ثم خرج إلى الشام، فنزل دمشق، وبنى بها دارا، وكان قاضيا بها في زمن معاوية بن أبي','13577','3571','0');
INSERT INTO [t9351] VALUES (3896,'أبو أبي ابن امرأة عبادة بن الصامت واسمه عبد الله بن عمرو بن قيس بن زيد بن سواد بن مالك بن غنم بن مالك بن النجار، من الأنصار، من الخزرج، شهد أبوه وأخوه قيس بن عمرو بدرا، ولم يشهدها أبو أبي، وأمه أم حرام بنت ملحان خالة أنس بن مالك، وتحول أبو أبي إلى','13578','3571','0');
INSERT INTO [t9351] VALUES (3897,'عبد الرحمن بن شبل بن عمرو بن زيد بن نجدة من بني عمرو بن عوف، من الأنصار، نزل الشام، وروى عن رسول الله صلى الله عليه وسلم أنه نهى عن نقرة الغراب، وافتراش السبع','13580','3571','0');
INSERT INTO [t9351] VALUES (3898,'عمير بن سعد بن عبيد بن النعمان بن قيس بن عمرو بن زيد بن أمية من بني عمرو بن عوف، وأبوه ممن شهد بدرا، وهو سعد القارئ، وصحب عمير بن سعد النبي صلى الله عليه وسلم، وروى عنه، وولاه عمر بن الخطاب حمص بعد سعيد بن عامر بن حذيم','13581','3571','0');
INSERT INTO [t9351] VALUES (3899,'عمرو بن عبسة بن خالد بن حذيفة بن عمرو بن خلف بن مازن بن مالك بن ثعلبة بن بهثة بن سليم بن منصور بن عكرمة بن خصفة بن قيس بن عيلان بن مضر، يكنى أبا نجيح','13582','3571','0');
INSERT INTO [t9351] VALUES (3900,'الحارث بن هشام بن المغيرة بن عبد الله بن عمر بن مخزوم أسلم يوم فتح مكة، وشهد مع رسول الله صلى الله عليه وسلم حنينا، وأعطاه رسول الله صلى الله عليه وسلم من غنائم حنين مائة من الإبل، ولم يزل مقيما بمكة بعد أن أسلم حتى توفي رسول الله صلى الله عليه وسلم،','13584','3571','0');
INSERT INTO [t9351] VALUES (3901,'عكرمة بن أبي جهل واسم أبي جهل: عمرو بن هشام بن المغيرة بن عبد الله بن عمرو بن مخزوم، أسلم يوم فتح مكة، واستعمله رسول الله صلى الله عليه وسلم عام حج على صدقات هوازن، فقبض رسول الله صلى الله عليه وسلم وعكرمة بتبالة واليا على هوازن، وخرج عكرمة إلى','13585','3571','0');
INSERT INTO [t9351] VALUES (3902,'سهيل بن عمرو بن عبد شمس بن عبد ود بن نصر بن مالك بن حسل بن عامر بن لؤي، ويكنى أبا يزيد، وخرج إلى حنين مع رسول الله صلى الله عليه وسلم وهو على شركه حتى أسلم بالجعرانة منصرف رسول الله صلى الله عليه وسلم من حنين، فأعطاه رسول الله صلى الله عليه وسلم يومئذ','13586','3571','0');
INSERT INTO [t9351] VALUES (3903,'أبو جندل بن سهيل بن عمرو بن عبد شمس بن عبد ود بن نصر بن مالك بن حسل بن عامر بن لؤي، أسلم قديما بمكة، فحبسه أبوه وأوثقه في الحديد، ومنعه الهجرة، ثم أفلت بعد الحديبية، فخرج إلى أبي بصير بالعيص، فلم يزل معه حتى مات أبو بصير، فقدم أبو جندل ومن كان معه','13588','3571','0');
INSERT INTO [t9351] VALUES (3904,'يزيد بن أبي سفيان بن حرب بن أمية بن عبد شمس بن عبد مناف بن قصي، وأمه زينب بنت نوفل بن حلف بن قوالة، من بني كنانة، أسلم يزيد يوم فتح مكة، وشهد مع رسول الله صلى الله عليه وسلم حنينا، وأعطاه رسول الله صلى الله عليه وسلم من غنائم حنين مائة من الإبل،','13589','3571','0');
INSERT INTO [t9351] VALUES (3905,'معاوية بن أبي سفيان بن حرب بن أمية بن عبد شمس بن عبد مناف بن قصي، وأمه هند بنت عتبة بن ربيعة بن عبد شمس بن عبد مناف بن قصي، ويكنى معاوية: أبا عبد الرحمن، وله عقب، وكان يذكر أنه أسلم عام الحديبية، وكان يكتم إسلامه من أبي سفيان قال: " فدخل رسول الله','13591','3571','0');
INSERT INTO [t9351] VALUES (3906,'أبو هاشم بن عتبة بن ربيعة بن عبد شمس بن عبد مناف بن قصي أسلم يوم فتح مكة، وخرج إلى الشام، فنزلها إلى أن مات بها، وكان ينزل دمشق','13593','3571','0');
INSERT INTO [t9351] VALUES (3907,'عبد الله بن السعدي واسم السعدي: عمرو بن وقدان بن عبد شمس بن عبد ود بن نصر بن مالك بن حسل بن عامر بن لؤي، أسلم يوم فتح مكة، وصحب النبي صلى الله عليه وسلم، وروى عنه، وقدم إلى الشام، فنزل دمشق، فمات هناك','13594','3571','0');
INSERT INTO [t9351] VALUES (3908,'ضرار بن الخطاب بن مرداس بن كبير بن عمرو بن حبيب بن عمرو بن شيبان بن محارب بن فهر، وكان شاعرا، أسلم يوم فتح مكة، وكان فارسا، وصحب النبي صلى الله عليه وسلم وحسن إسلامه، وخرج إلى الشام مجاهدا، فمات هناك','13595','3571','0');
INSERT INTO [t9351] VALUES (3909,'واثلة بن الأسقع بن عبد العزى بن عبد ياليل بن ناشب بن عنزة بن سعد بن ليث بن بكر، من بني كنانة، ويكنى أبا قرصافة، كان ينزل ناحية المدينة، ثم وقع الإسلام في قلبه، فقدم على رسول الله صلى الله عليه وسلم وهو يتجهز إلى تبوك، فأسلم، وخرج مع رسول الله صلى','13596','3571','0');
INSERT INTO [t9351] VALUES (3910,'تميم الداري وهو تميم بن أوس بن خارجة بن سود بن جذيمة بن دارع بن عدي بن الدار بن هانئ بن حبيب بن نمارة بن لخم بن كعب، وفد على رسول الله صلى الله عليه وسلم، ومعه أخوه نعيم بن أوس، فأسلما، وأقطعهما رسول الله صلى الله عليه وسلم حبرى وبيت عينون بالشام،','13601','3571','0');
INSERT INTO [t9351] VALUES (3911,'بسر بن أبي أرطأة واسمه: عمير بن عويمر بن عمران بن الجليس بن سيار بن نزار بن معيص بن عامر بن لؤي، قال محمد بن عمر: " قبض رسول الله صلى الله عليه وسلم وبسر بن أبي أرطأة صغير، ولم يرو عنه أحد من المدنيين أنه سمع من النبي صلى الله عليه وسلم، تحول، فنزل','13602','3571','0');
INSERT INTO [t9351] VALUES (3912,'حبيب بن مسلمة الفهري ابن مالك الأكبر بن وهب بن ثعلبة بن واثلة بن عمرو بن شيبان بن محارب بن فهر','13603','3571','0');
INSERT INTO [t9351] VALUES (3913,'الضحاك بن قيس بن خالد الأكبر بن وهب بن ثعلبة بن واثلة بن عمرو بن شيبان بن محارب بن فهر، قال محمد بن عمر: في روايتنا أن رسول الله صلى الله عليه وسلم قبض والضحاك بن قيس غلام لم يبلغ، وفي رواية غيره أنه أدرك النبي صلى الله عليه وسلم، وسمع منه','13605','3571','0');
INSERT INTO [t9351] VALUES (3914,'قباث بن أشيم بن عامر بن الملوح بن يعمر وهو الشداخ بن عوف بن كعب بن عامر بن ليث بن بكر بن عبد مناة بن كنانة، شهد بدرا مع المشركين، وكان له فيها ذكر، ثم أسلم بعد ذلك، وشهد مع النبي صلى الله عليه وسلم بعض المشاهد، وكان على مجنبة أبي عبيدة بن الجراح يوم','13607','3571','0');
INSERT INTO [t9351] VALUES (3915,'أبو أمامة الباهلي واسمه: الصدي بن عجلان، وروى عن سليمان','13609','3571','0');
