INSERT INTO [t9351] VALUES (3919,'عتبة بن عبد السلمي وكان ينزل بالشام، قال الهيثم بن عدي: " توفي سنة إحدى أو اثنتين وتسعين "، وقال محمد بن عمر: " توفي سنة سبع وثمانين، وهو ابن أربع وتسعين سنة "','13618','3571','0');
INSERT INTO [t9351] VALUES (3920,'عبد الله بن بسر المازني مازن بن منصور أخو سليم بن منصور، ويكنى أبا صفوان','13619','3571','0');
INSERT INTO [t9351] VALUES (3921,'عبد الله بن حوالة ويكنى أبا حوالة، قال الهيثم بن عدي: " هو من الأزد " وقال محمد بن عمر: هو من بني معيص بن عامر بن لؤي ويكنى أبا محمد، وكان يسكن الأردن، ومات سنة ثمان وخمسين، في آخر خلافة معاوية، وهو ابن اثنتين وسبعين سنة أخبرنا يزيد بن هارون قال:','13623','3571','0');
INSERT INTO [t9351] VALUES (3922,'كعب بن مرة البهزي وبهز من بني سليم، وكان يسكن الأردن، وهو الذي روى عن النبي صلى الله عليه وسلم في عثمان مثل ما روى عبد الله بن حوالة، ومات كعب سنة سبع وخمسين','13624','3571','0');
