INSERT INTO [t9351] VALUES (3932,'نعيم بن هبار الغطفاني هكذا أخبرنا معن بن عيسى، عن معاوية بن صالح، عن أبي الزاهرية، عن كثير بن مرة، عن نعيم بن هبار، قال: " وكان الوليد بن مسلم يقول فيما يحدث به: نعيم بن هبار "، وقال غيرهم: " نعيم بن حمار، وكان نعيم قد صحب النبي صلى الله عليه','13640','3571','0');
