INSERT INTO [t9351] VALUES (4159,'عمرو بن ميمون بن مطران وكان ثقة إن شاء الله، وكان ينزل الرقة، قال محمد بن عمر: مات سنة خمس وأربعين ومائة، في خلافة أبي جعفر المنصور','13988','4133','0');
INSERT INTO [t9351] VALUES (4160,'جعفر بن برقان الكلابي وكان ثقة صدوقا، له رواية وفقه وفتوى في دهره، وكان كثير الخطأ في حديثه، وكان ينزل الرقة، ومات بها سنة أربع وخمسين ومائة، في خلافة أبي جعفر','13989','4133','0');
INSERT INTO [t9351] VALUES (4161,'النضر بن عربي العامري وكان ضعيف الحديث، توفي في خلافة المهدي','13990','4133','0');
INSERT INTO [t9351] VALUES (4162,'غالب بن عبيد الله الجزري العقيلي كان ضعيفا ليس بذاك، توفي في خلافة أبي جعفر','13991','4133','0');
INSERT INTO [t9351] VALUES (4163,'عبد الله بن محرر العامري كان ضعيفا ليس بذاك، توفي في خلافة أبي جعفر','13992','4133','0');
INSERT INTO [t9351] VALUES (4164,'موسى بن أعين ويكنى أبا سعيد، مولى لبني أمية، وكان صدوقا، مات بحران سنة سبع وسبعين ومائة، في خلافة هارون','13993','4133','0');
INSERT INTO [t9351] VALUES (4165,'سليمان بن عبد الله بن علاثة الكلابي وكان قليل الحديث، وكان ينزل حران، وكان على قضائها','13994','4133','0');
INSERT INTO [t9351] VALUES (4166,'محمد بن عبد الله بن علاثة الكلابي ويكنى أبا اليسر، وكان ثقة إن شاء الله، وكان على قضاء المهدي','13995','4133','0');
INSERT INTO [t9351] VALUES (4167,'زياد بن عبد الله بن علاثة الكلابي وكان على خلافة أخيه على القضاء مع المهدي','13996','4133','0');
INSERT INTO [t9351] VALUES (4168,'بجير بن أبي أنيسة كان يسكن الرها، ومات بها، وكان أحدث من أخيه زيد، وكان ضعيفا، وأصحاب الحديث لا يكتبون حديثه','13997','4133','0');
INSERT INTO [t9351] VALUES (4169,'أبو المليح واسمه: الحسن بن عمر','13998','4133','0');
INSERT INTO [t9351] VALUES (4170,'عبيد الله بن عمرو بن أبي الوليد الأسدي مولى لهم، ويكنى أبا وهب، وكان ثقة، صدوقا، كثير الحديث، وربما أخطأ، وكان أحفظ من روى عن عبد الكريم الجزري، ولم يكن أحد ينازعه في الفتوى في دهره، ومات بالرقة سنة ثمانين ومائة، في خلافة هارون','14001','4133','0');
INSERT INTO [t9351] VALUES (4171,'أبو العطوف واسمه: الجراح بن المنهال، وكان ضعيفا في الحديث','14002','4133','0');
INSERT INTO [t9351] VALUES (4172,'مروان بن شجاع ويكنى أبا عمرو، مولى مروان بن محمد بن مروان بن الحكم، وكان من أهل حران، وكان ثقة، صدوقا، راوية لخصيف، وهو الذي كان يقال له الخصيفي، وكان قدم بغداد مؤدبا مع موسى أمير المؤمنين وولده، ومات ببغداد سنة أربع وثمانين ومائة، في خلافة هارون','14003','4133','0');
INSERT INTO [t9351] VALUES (4173,'عتاب بن بشير ويكنى أبا الحسن، مولى لبني أمية، وكان يسكن حران، وكان صدوقا، ثقة إن شاء الله، راوية لخصيف، وليس هو بذاك في الحديث، ومات بحران سنة تسعين ومائة، في خلافة هارون','14004','4133','0');
INSERT INTO [t9351] VALUES (4174,'محمد بن سلمة ويكنى أبا عبد الله، مولى لباهلة، وكان يسكن حران وكان صدوقا، ثقة إن شاء الله، وكان له فضل ورواية وفتوى، مات في آخر سنة إحدى وتسعين ومائة، في خلافة هارون','14005','4133','0');
INSERT INTO [t9351] VALUES (4175,'أبو قتادة الحراني واسمه: عبد الله بن واقد، مولى لبني حمان، وكان له فضل وعبادة، ولم يكن في الحديث بذاك','14006','4133','0');
INSERT INTO [t9351] VALUES (4176,'الفيض بن إسحاق ويكنى أبا يزيد، من أهل الرقة، وكان صاحب حديث وخير وغزو، مات بالرقة سنة ست عشرة ومائتين، في خلافة عبد الله بن هارون','14007','4133','0');
INSERT INTO [t9351] VALUES (4177,'معمر بن سليمان الرقي النخعي مات في شعبان سنة إحدى وتسعين ومائة، في خلافة هارون','14008','4133','0');
INSERT INTO [t9351] VALUES (4178,'خالد بن حيان ويكنى أبا يزيد الخزاز، وكان ثقة ثبتا، مات بالرقة في ذي القعدة سنة إحدى وتسعين ومائة، في خلافة هارون، وكان يوم مات قد دخل في سبعين سنة، ولم يستكملها','14009','4133','0');
INSERT INTO [t9351] VALUES (4179,'عبد الله بن جعفر بن غيلان، يكنى أبا عبد الرحمن مولى آل أبي معيط، وكان راوية لأبي المليح، وعبيد الله بن عمرو، وكان ضعيف البصر، يخضب بالحناء، ومات بالرقة لتسع ليال بقين من شعبان سنة عشرين ومائتين، في خلافة أبي إسحاق بن هارون','14010','4133','0');
INSERT INTO [t9351] VALUES (4180,'يحيى بن عبد الله بن الضحاك بن باب لت الحراني ويكنى أبا سعيد، وكان باب لت من أهل طخارستان، من الملوك الكبار، روى عن أبي بكر بن أبي مريم، وصفوان بن عمرو','14011','4133','0');
INSERT INTO [t9351] VALUES (4181,'عبد الله بن محمد بن علي بن نفيل الحراني صاحب زهير بن معاوية، ويكنى أبا جعفر، وكان بالموصل','14012','4133','0');
INSERT INTO [t9351] VALUES (4182,'المغيرة بن زياد','14013','4133','0');
INSERT INTO [t9351] VALUES (4183,'المعافى بن عمران بن محمد بن عمران بن نفيل بن جابر بن وهب بن عبيد الله بن لبيد بن جبلة بن غنم بن دوس بن محاسن بن سلمة بن فهم، من الأزد، قال: وكان ثقة، فاضلا، خيرا، صاحب سنة','14014','4133','0');
INSERT INTO [t9351] VALUES (4184,'وكان بالعواصم والثغور','14016','4133','0');
INSERT INTO [t9351] VALUES (4185,'أبو عمرو الأوزاعي واسمه عبد الرحمن بن عمرو، والأوزاع بطن من همدان، وهو من أنفسهم، ولد سنة ثمان وثمانين، وكان ثقة مأمونا، صدوقا فاضلا، خيرا، كثير الحديث والعلم والفقه، حجة، وكان مكتبه باليمامة؛ فلذلك سمع من يحيى بن أبي كثير وغيره من مشايخ أهل','14017','4133','0');
INSERT INTO [t9351] VALUES (4186,'أبو إسحاق الفزاري واسمه: إبراهيم بن محمد الحارث بن أسماء بن خارجة بن حصن بن حذيفة بن بدر، وكان ثقة، فاضلا، صاحب سنة وغزو، كثير الخطأ في حديثه، ومات بالمصيصة سنة ثمان وثمانين ومائة، في خلافة هارون','14018','4133','0');
INSERT INTO [t9351] VALUES (4187,'عيسى بن يونس بن أبي إسحاق السبيعي من همدان، ويكنى أبا عمرو، وهو من أهل الكوفة، تحول إلى الثغر، فنزل بالحدث، وكان ثقة ثبتا، ومات بالحدث في أول سنة إحدى وتسعين ومائة، في خلافة هارون','14019','4133','0');
INSERT INTO [t9351] VALUES (4188,'مخلد بن الحسين ويكنى أبا محمد، وكان من أهل البصرة، وهو ابن امرأة هشام بن حسان، وكان راوية عنه، وكان ثقة فاضلا، فتحول، فنزل بالمصيصة، ومات بها سنة إحدى وتسعين ومائة، في خلافة هارون','14020','4133','0');
INSERT INTO [t9351] VALUES (4189,'محمد بن كثير ويكنى أبا يوسف، وكان من أهل صنعاء، ونشأ بالشام، ونزل المصيصة، وكان ثقة، روى عن معمر والأوزاعي وغيرهما، ويذكرون أنه اختلط في آخر عمره، ومات في آخر سنة ست عشرة ومائتين، في خلافة عبد الله بن هارون','14021','4133','0');
INSERT INTO [t9351] VALUES (4190,'الحجاج بن محمد الأعور ويكنى أبا محمد، مولى لسليمان بن مجالد مولى أبي جعفر المنصور أمير المؤمنين، وكان من أهل بغداد، فتحول إلى المصيصة بعياله، فنزلها سنين كثيرة، ثم رجع إلى بغداد، فمات بها سنة ست ومائتين، في خلافة عبد الله بن هارون، وكان ثقة،','14022','4133','0');
INSERT INTO [t9351] VALUES (4191,'محمد بن يوسف الفريابي ويكنى أبا عبد الله، وهو صاحب سفيان الثوري رحمه الله','14023','4133','0');
INSERT INTO [t9351] VALUES (4192,'الحنيني المدني واسمه: إسحاق بن إبراهيم','14024','4133','0');
INSERT INTO [t9351] VALUES (4193,'آدم بن أبي إياس ويكنى أبا الحسن، وكان من أبناء أهل خراسان، من أهل مرو الروذ، طلب الحديث ببغداد، وسمع من شعبة سماعا كثيرا صحيحا، ثم انتقل فنزل عسقلان، فلم يزل هناك حتى مات بها في جمادى الآخرة سنة عشرين ومائتين، في خلافة أبي إسحاق بن هارون، وهو ابن','14025','4133','0');
INSERT INTO [t9351] VALUES (4194,'الهيثم بن جميل','14026','4133','0');
INSERT INTO [t9351] VALUES (4195,'علي بن بكار البصري ويكنى أبا الحسن، وكان عالما فقيها، توفي بالمصيصة سنة ثمان ومائتين، في خلافة عبد الله بن هارون','14028','4133','0');
INSERT INTO [t9351] VALUES (4196,'حارث بن عطية البصري ويكنى أبا عبد الله، توفي في المصيصة سنة تسع وتسعين ومائة، في خلافة المأمون، وكان عالما','14029','4133','0');
INSERT INTO [t9351] VALUES (4197,'خلف بن تميم الكوفي وكان عالما، توفي بالمصيصة سنة ثلاث عشرة ومائتين، في خلافة عبد الله بن هارون','14030','4133','0');
INSERT INTO [t9351] VALUES (4198,'محمد بن عيينة الفزاري ويكنى أبا عبد الله، وكان عالما، توفي بالمصيصة سنة سبع عشرة ومائتين، في خلافة عبد الله بن هارون','14031','4133','0');
INSERT INTO [t9351] VALUES (4199,'أبو عثمان سعيد القارئ الصياد وكان من أهل خراسان، سكن الثغر، وكان فقيها، عالما، زاهدا، توفي بالمصيصة سنة إحدى وعشرين ومائتين، في خلافة أبي إسحاق بن هارون','14032','4133','0');
INSERT INTO [t9351] VALUES (4200,'أبو الموفق وكان فقيها، وكان ينزل كفربيا، توفي بالمصيصة في سنة عشرين ومائتين، في خلافة أبي إسحاق أمير المؤمنين','14033','4133','0');
INSERT INTO [t9351] VALUES (4201,'أبو المنذر وكان قاضيا بالمصيصة، وكان عالما، فقيها، توفي بالمصيصة سنة اثنتين وعشرين ومائتين، في خلافة المعتصم: أبي إسحاق بن هارون','14034','4133','0');
INSERT INTO [t9351] VALUES (4202,'منصور بن هارون ويكنى أبا الحسن، وكان عالما، فقيها، توفي بالمصيصة سنة اثنتين وعشرين ومائتين، في خلافة أبي إسحاق','14035','4133','0');
INSERT INTO [t9351] VALUES (4203,'أبو زكريا الطحان وكان عالما، توفي بالمصيصة سنة خمس وعشرين ومائتين، في خلافة أبي إسحاق بن هارون','14036','4133','0');
INSERT INTO [t9351] VALUES (4204,'تسمية من نزل مصر من أصحاب رسول الله صلى الله عليه وسلم','14037','4133','0');
INSERT INTO [t9351] VALUES (4205,'عمرو بن العاص بن وائل بن هاشم بن سعيد بن سهم ويكنى أبا عبد الله، أسلم بأرض الحبشة عند النجاشي، ثم قدم المدينة على رسول الله صلى الله عليه وسلم مهاجرا في هلال صفر سنة ثمان من الهجرة، وصحب رسول الله صلى الله عليه وسلم، واستعمله على غزوة ذات السلاسل،','14038','4133','0');
INSERT INTO [t9351] VALUES (4206,'عبد الله بن عمرو بن العاص بن وائل بن هاشم بن سعيد بن سهم قال محمد بن عمر: أسلم عبد الله بن عمرو قبل أبيه، وصحب النبي صلى الله عليه وسلم، وكان خيرا فاضلا "','14041','4133','0');
INSERT INTO [t9351] VALUES (4207,'خارجة بن حذافة بن غانم بن عامر بن عبد الله بن عبيد بن عويج بن عدي بن كعب، أسلم قديما، وصحب النبي صلى الله عليه وسلم، ثم خرج، فنزل مصر، وكان قاضيا بها لعمرو بن العاص، فلما كان صبيحة يوم وافى الخارجي ليضرب عمرو بن العاص، ولم يخرج عمرو يومئذ، وأمر','14049','4133','0');
INSERT INTO [t9351] VALUES (4208,'عبد الله بن سعد بن أبي سرح بن الحارث بن حبيب بن جذيمة بن مالك بن حسل بن عامر بن لؤي، وكان قد أسلم قديما، وكتب لرسول الله صلى الله عليه وسلم الوحي، ثم افتتن، وخرج من المدينة إلى مكة مرتدا، فأهدر رسول الله صلى الله عليه وسلم دمه يوم الفتح، فجاء عثمان','14051','4133','0');
INSERT INTO [t9351] VALUES (4209,'محمية بن جزء بن عبد يغوث بن عويج بن عمرو بن زبيد بن مذحج، وكان حليفا لبني سهم، وأسلم محمية بمكة قديما، وهاجر إلى أرض الحبشة في الهجرة الثانية، وأول مشاهده المريسيع، وهي غزوة بلمصطلق، واستعمله رسول الله صلى الله عليه وسلم على الخمس وسهمان المسلمين','14052','4133','0');
INSERT INTO [t9351] VALUES (4210,'عبد الله بن الحارث بن جزء الزبيدي صحب النبي صلى الله عليه وسلم، ونزل بمصر، وروى عنه المصريون','14053','4133','0');
INSERT INTO [t9351] VALUES (4211,'عقبة بن عامر بن عبس الجهني، ويكنى أبا عمرو صحب النبي صلى الله عليه وسلم، فلما قبض رسول الله صلى الله عليه وسلم، وندب أبو بكر الناس إلى الشام خرج عقبة بن عامر، فشهد فتوح الشام ومصر، وشهد مع معاوية صفين، ثم تحول إلى مصر، فنزلها، وابتنى بها دارا،','14055','4133','0');
