INSERT INTO [t9351] VALUES (4225,'معاذ بن أنس الجهني صحب النبي صلى الله عليه وسلم، وروى عنه أحاديث، وسكن مصر، وهو أبو سهل بن معاذ، الذي روى عنه زبان بن فائد وغيره من الشاميين والمصريين','14080','4133','0');
INSERT INTO [t9351] VALUES (4226,'أبو اليقظان صاحب رسول الله صلى الله عليه وسلم','14081','4133','0');
INSERT INTO [t9351] VALUES (4227,'معاوية بن حديج صحب النبي صلى الله عليه وسلم، وروى عنه، وقد لقي عمر بن الخطاب، وروى عنه حديثا في المسح، وكان عثمانيا','14083','4133','0');
INSERT INTO [t9351] VALUES (4228,'زياد بن الحارث الصدائي وهو الذي كان مع رسول الله صلى الله عليه وسلم في بعض أسفاره، فسار مع رسول الله صلى الله عليه وسلم، ولزم غرزه، فلما كان في السحر قال النبي صلى الله عليه وسلم: " أذن يا أخا صداء " فأذن، ثم جاء بلال يقيم، فقال رسول الله صلى الله','14085','4133','0');
INSERT INTO [t9351] VALUES (4229,'مسلمة بن مخلد بن الصامت بن نيار بن لوذان بن عبد ود بن زيد بن ثعلبة بن الخزرج بن ساعدة، من الأنصار، ويكنى أبا معمر','14086','4133','0');
