INSERT INTO [t9351] VALUES (4310,'رقية بنت رسول الله صلى الله عليه وسلم وأمها خديجة بنت خويلد بن أسد بن عبد العزى بن قصي كان تزوجها عتبة بن أبي لهب بن عبد المطلب قبل النبوة فلما بعث رسول الله وأنزل الله تبت يدا أبي لهب قال له أبوه أبو لهب: رأسي من رأسك حرام إن لم تطلق ابنته ففارقها ولم','14307','4282','0');
INSERT INTO [t9351] VALUES (4311,'أم كلثوم بنت رسول الله صلى الله عليه وسلم وأمها خديجة بنت خويلد بن أسد بن عبد العزى بن قصي تزوجها عتيبة بن أبي لهب بن عبد المطلب قبل النبوة فلما بعث رسول الله وأنزل الله تبت يدا أبي لهب قال له أبوه أبو لهب: رأسي من رأسك حرام إن لم تطلق ابنته ففارقها ولم','14310','4282','0');
