INSERT INTO [t9351] VALUES (4321,'ضباعة بنت الزبير بن عبد المطلب بن هاشم بن عبد مناف بن قصي وأمها عاتكة بنت أبي وهب بن عمرو بن عائذ بن عمران بن مخزوم زوجها رسول الله صلى الله عليه وسلم المقداد بن عمرو بن ثعلبة بن بهراء وكان حليفا للأسود بن عبد يغوث الزهري فتبناه وكان يقال له المقداد بن','14339','4282','0');
INSERT INTO [t9351] VALUES (4322,'أم الحكم بنت الزبير بن عبد المطلب وأمها عاتكة بنت أبي وهب بن عمرو بن عائذ بن عمران بن مخزوم تزوجها ربيعة بن الحارث بن عبد المطلب بن هاشم فولدت له محمدا وعبد الله وعباسا والحارث وعبد شمس وعبد المطلب وأمية رجلا وأروى الكبرى وأطعم رسول الله صلى الله عليه','14340','4282','0');
