INSERT INTO [t9351] VALUES (4324,'أم الزبير بنت الزبير بن عبد المطلب بن هاشم بن عبد مناف بن قصي وأمها عاتكة بنت أبي وهب بن عمرو بن عائذ بن عمران بن مخزوم أطعمها رسول الله صلى الله عليه وسلم في خيبر أربعين وسقا','14342','4282','0');
INSERT INTO [t9351] VALUES (4325,'أم هانئ واسمها فاختة ابنة أبي طالب بن عبد المطلب بن هاشم بن عبد مناف بن قصي وأمها فاطمة بنت أسد بن هاشم بن عبد مناف بن قصي تزوجها هبيرة بن أبي وهب المخزومي ولدت له جعدة بن هبيرة وأطعمها رسول الله صلى الله عليه وسلم بخيبر أربعين وسقا.','14343','4282','0');
INSERT INTO [t9351] VALUES (4326,'أم طالب بنت أبي طالب بن عبد المطلب بن هاشم بن عبد مناف بن قصي لم يذكرها هشام بن الكلبي في كتاب النسب في أولاد أبي طالب وذكر أنه كان لأبي طالب من البنات أم هانئ وجمانة وريطة ولعل ريطة هي أم طالب كما سماها محمد بن عمر في كتاب طعم النبي صلى الله عليه وسلم','14344','4282','0');
INSERT INTO [t9351] VALUES (4327,'جمانة بنت أبي طالب بن عبد المطلب وأمها فاطمة بنت أسد بن هاشم بن عبد مناف بن قصي تزوجها أبو سفيان بن الحارث بن عبد المطلب بن هاشم بن عبد مناف بن قصي فولدت له جعفر بن أبي سفيان وأطعمها رسول الله صلى الله عليه وسلم في خيبر ثلاثين وسقا','14345','4282','0');
