INSERT INTO [t9351] VALUES (4332,'أم عمرو بنت المقوم بن عبد المطلب بن هاشم وأمها قلابة بنت عمرو بن جعونة تزوجها مسعود بن معتب الثقفي فولدت له عبد الله بن مسعود ثم تزوجها أبو سفيان بن الحارث بن عبد المطلب بن هاشم فولدت له عاتكة بنت أبي سفيان','14350','4282','0');
INSERT INTO [t9351] VALUES (4333,'أروى بنت الحارث بن عبد المطلب بن هاشم بن عبد مناف بن قصي وأمها غزية بنت قيس بن طريف بن عبد العزى بن عامرة بن عميرة بن وديعة بن الحارث بن فهر تزوجها أبو وداعة بن صبرة بن سعيد بن سعد بن سهم فولدت له المطلب وأبا سفيان وأم جميل وأم حكيم والربعة بني أبي وداعة','14351','4282','0');
INSERT INTO [t9351] VALUES (4334,'درة بنت أبي لهب بن عبد المطلب بن هاشم بن عبد مناف بن قصي وأمها أم جميل بنت حرب بن أمية بن عبد شمس تزوجها الحارث بن عامر بن نوفل بن عبد مناف بن قصي فولدت له الوليد وأبا الحسن ومسلما ثم قتل يوم بدر كافرا فخلف عليها دحية بن خليفة بن فروة الكلبي.','14352','4282','0');
INSERT INTO [t9351] VALUES (4335,'عزة بنت أبي لهب بن عبد المطلب بن هاشم وأمها أم جميل بنت حرب بن أمية بن عبد شمس تزوجها أوفى بن حكيم بن أمية بن حارثة بن الأوقص السلمي فولدت له عبيدة وسعيدا وإبراهيم بني أوفى.','14353','4282','0');
INSERT INTO [t9351] VALUES (4336,'خالدة بنت أبي لهب بن عبد المطلب بن هاشم وأمها أم جميل بنت حرب بن أمية تزوجها عثمان بن أبي العاص بن بشر بن عبد دهمان الثقفي فولدت له','14354','4282','0');
INSERT INTO [t9351] VALUES (4337,'فاطمة بنت أسد بن هاشم بن عبد مناف بن قصي وأمها فاطمة بنت هرم بن رواحة بن حجر بن عبد بن معيص بن عامر بن لؤي تزوجها أبو طالب بن عبد المطلب بن هاشم فولدت له عليا، وجعفرا، وعقيلا، وطالبا وهو أسنهم وأم هانئ، وجمانة، وريطة بني أبي طالب','14355','4282','0');
INSERT INTO [t9351] VALUES (4338,'رقيقة بنت أبي صيفي بن هاشم بن عبد مناف بن قصي وأمها هالة بنت كلدة بن عبد الدار بن قصي تزوجها نوفل بن أهيب بن عبد مناف بن زهرة فولدت له مخرمة بن نوفل','14356','4282','0');
INSERT INTO [t9351] VALUES (4339,'ذكر أزواج رسول الله صلى الله عليه وسلم','14359','4282','0');
INSERT INTO [t9351] VALUES (4340,'خديجة بنت خويلد بن أسد بن عبد العزى بن قصي وهي أول امرأة تزوجها رسول الله صلى الله عليه وسلم، فقد حكينا أمرها وكتبنا نسبها وخبرها وتزويج رسول الله صلى الله عليه وسلم إياها قبل النبوة، وإسلامها وولدها ووفاتها في أول الكتاب، وتزوج رسول الله صلى الله','14360','4282','0');
INSERT INTO [t9351] VALUES (4341,'سودة بنت زمعة بن قيس بن عبد شمس بن ود بن نصر بن مالك بن حسل بن عامر بن لؤي وأمها الشموس بنت قيس بن عمرو بن زيد بن لبيد بن خداش بن عامر بن غنم بن عدي بن النجار من الأنصار تزوجها السكران بن عمرو بن عبد شمس بن عبد ود بن نصر بن مالك بن حسل بن عامر بن لؤي','14361','4282','0');
