INSERT INTO [t9351] VALUES (4364,'أم شريك واسمها غزية بنت جابر بن حكيم كان محمد بن عمر يقول: هي من بني معيص بن عامر بن لؤي وكان غيره يقول هي دوسية من الأزد أخبرنا محمد بن عمر، حدثنا موسى بن محمد بن إبراهيم التيمي، عن أبيه، قال: كانت أم شريك امرأة من بني عامر بن لؤي معيصية وإنها وهبت','14779','4282','0');
INSERT INTO [t9351] VALUES (4365,'خولة بنت حكيم بن أمية بن حارثة بن الأوقص بن مرة بن هلال بن فالج بن ثعلبة بن ذكوان بن امرئ القيس بن بهثة بن سليم وأمها ضعيفة بنت العاص بن أمية بن عبد شمس، وكان مرة بن هلال قدم مكة فحالف عبد مناف بن قصي نفسه وتزوج عبد مناف ابنته عاتكة بنت مرة فهي أم هاشم','14789','4282','0');
INSERT INTO [t9351] VALUES (4366,'أمامة بنت حمزة بن عبد المطلب بن هاشم بن عبد مناف بن قصي وأمها سلمى بنت عميس بن معد بن تيم بن مالك بن قحافة من خثعم أخت أسماء بنت عميس هكذا سماها هشام بن محمد بن السائب الكلبي وقال غيره هي عمارة بنت حمزة وقال هشام: عمارة رجل وهو ابن حمزة وبه كان يكنى','14791','4282','0');
