INSERT INTO [t9351] VALUES (4374,'ذكر ما كان قبل الحجاب','14847','4282','0');
INSERT INTO [t9351] VALUES (4375,'ذكر من كان يصلح له الدخول على أزواج النبي صلى الله عليه وسلم أخبرنا محمد بن عمر، حدثنا معمر، عن الزهري قال: قيل له: من كان يدخل على أزواج النبي صلى الله عليه وسلم؟ ، فقال: كل ذي رحم محرم من نسب أو رضاع قيل: فسائر الناس، قال: كن يحتجبن منه حتى','14854','4282','0');
INSERT INTO [t9351] VALUES (4376,'ذكر ما هجر فيه رسول الله صلى الله عليه وسلم نساءه وتخييره إياهن','14860','4282','0');
INSERT INTO [t9351] VALUES (4377,'ذكر المرأتين اللتين تظاهرتا على رسول الله صلى الله عليه وسلم وتخييره نساءه','14864','4282','0');
INSERT INTO [t9351] VALUES (4378,'ذكر ما أعطي رسول الله صلى الله عليه وسلم من القوة على الجماع','14886','4282','0');
INSERT INTO [t9351] VALUES (4379,'باب الاستتار وغيره','14893','4282','0');
INSERT INTO [t9351] VALUES (4380,'ذكر من قال: إن النبي صلى الله عليه وسلم لم يمت حتى أحل له جميع النساء','14898','4282','0');
INSERT INTO [t9351] VALUES (4381,'ذكر من قال إن النبي صلى الله عليه وسلم حبس على نسائه','14903','4282','0');
INSERT INTO [t9351] VALUES (4382,'باب تفسير الآيات التي في ذكر أزواج رسول الله صلى الله عليه وسلم','14922','4282','0');
INSERT INTO [t9351] VALUES (4383,'ذكر ضرب النساء','14940','4282','0');
INSERT INTO [t9351] VALUES (4384,'ذكر حج رسول الله صلى الله عليه وسلم بأزواجه','14951','4282','0');
INSERT INTO [t9351] VALUES (4385,'ذكر مارية أم إبراهيم ابن رسول الله صلى الله عليه وسلم','14978','4282','0');
INSERT INTO [t9351] VALUES (4386,'ذكر عدد أزواج النبي صلى الله عليه وسلم أخبرنا محمد بن عمر، حدثنا محمد بن عبد الله، عن الزهري، قال: وحدثنا كثير بن زيد، عن المطلب بن عبد الله بن حنطب قالا: كانت أول امرأة تزوجها رسول الله صلى الله عليه وسلم قبل النبوة خديجة بنت خويلد بن أسد بن عبد','14996','4282','0');
INSERT INTO [t9351] VALUES (4387,'ذكر عدد أزواج النبي صلى الله عليه وسلم','15004','4282','0');
INSERT INTO [t9351] VALUES (4388,'تسمية النساء المسلمات المبايعات من قريش وحلفائهم ومواليهم وغرائب نساء العرب','15008','4282','0');
INSERT INTO [t9351] VALUES (4389,'فاطمة بنت أسد بن هاشم بن عبد مناف بن قصي وأمها فاطمة بنت قيس بن هرم بن رواحة بن حجر بن عبد بن معيص بن عامر بن لؤي، وهي ابنة عم زائدة بن الأصم بن هرم بن رواحة جد خديجة بنت خويلد بن أسد بن عبد العزى بن قصي زوج رسول الله صلى الله عليه وسلم من قبل أمها،','15009','4282','0');
INSERT INTO [t9351] VALUES (4390,'رقيقة بنت أبي صيفي بن هاشم بن عبد مناف بن قصي وأمها هالة ويقال تماضر بنت كلدة بنت عبد مناف بن عبد الدار بن قصي وكانت عند نوفل بن أهيب بن عبد مناف بن قصي بن زهرة بن كلاب، فولدت له مخرمة وصفوان وأمية','15010','4282','0');
INSERT INTO [t9351] VALUES (4391,'أم أيمن واسمها بركة مولاة رسول الله وحاضنته. قال: وكان رسول الله صلى الله عليه وسلم ورثها من أبيه وخمسة أجمال أوارك وقطعة غنم فأعتق رسول الله صلى الله عليه وسلم أم أيمن حين تزوج خديجة بنت خويلد فتزوج عبيد بن زيد من بني الحارث بن الخزرج أم أيمن فولدت له','15013','4282','0');
INSERT INTO [t9351] VALUES (4392,'سلمى مولاة رسول الله صلى الله عليه وسلم وقد سمعت من يقول إنها مولاة صفية بنت عبد المطلب وكانت سلمى امرأة أبي رافع مولى رسول الله وأم أولاده , وهي التي كانت تقبل خديجة بنت خويلد بن أسد في ولادتها إذا ولدت من رسول الله وتعد قبل ذلك ما تحتاج إليه , وهي قبلت','15027','4282','0');
INSERT INTO [t9351] VALUES (4393,'خديجة بنت الحصين بن الحارث بن المطلب بن عبد مناف بن قصي أسلمت وبايعت رسول الله صلى الله عليه وسلم , وأطعمها رسول الله وأختها هندا بخيبر مائة وسق','15028','4282','0');
INSERT INTO [t9351] VALUES (4394,'هند بنت الحصين بن الحارث بن المطلب بن عبد مناف بن قصي أسلمت وبايعت رسول الله , وأطعمها رسول الله وأختها خديجة بخيبر مائة وسق','15029','4282','0');
INSERT INTO [t9351] VALUES (4395,'أم رمثة ويقال أم رميثة بنت عمرو بن هاشم بن المطلب بن عبد مناف بن قصي أسلمت وبايعت رسول الله وأطعمها رسول الله بخيبر أربعين وسقا تمرا وخمسة أوسق شعير , وهي أم حكيم أبي القعقاع بن حكيم وهو من الأزد حليف لبني المطلب بن عبد مناف بن قصي','15030','4282','0');
INSERT INTO [t9351] VALUES (4396,'بحينة واسمها عبدة بنت الحارث وهو الأرت بن المطلب بن عبد مناف بن قصي , وأمها أم صيفي بنت الأسود بن المطلب بن أسد بن عبد العزى بن قصي تزوجها مالك رجل من الأزد حليف لهم فولدت له عبد الله ابن بحينة وجبير ابن بحينة وقد صحبها النبي صلى الله عليه وسلم وأسلمت','15031','4282','0');
INSERT INTO [t9351] VALUES (4397,'هند بنت أثاثة بن عباد بن المطلب بن عبد مناف بن قصي وأمها أم مسطح بنت أبي رهم بن المطلب بن عبد مناف بن قصي أسلمت هند وبايعت رسول الله صلى الله عليه وسلم , وأطعمها رسول الله مع أخيها مسطح بن أثاثة بخيبر ثلاثين وسقا واغتربت هند عند أبي جندب فولدت له ريطة','15032','4282','0');
INSERT INTO [t9351] VALUES (4398,'أم مسطح بنت أبي رهم بن المطلب بن عبد مناف بن قصي وأمها ريطة بنت صخر بن عامر بن كعب بن سعد بن تيم بن مرة تزوجها أثاثة بن عباد بن المطلب بن عبد مناف فولدت له مسطحا من أهل بدر وهندا , وأسلمت أم مسطح فحسن إسلامها وكانت من أشد الناس على مسطح حين تكلم مع أهل','15033','4282','0');
INSERT INTO [t9351] VALUES (4399,'أروى بنت كريز بن ربيعة بن حبيب بن عبد شمس بن عبد مناف بن قصي وأمها أم حكيم البيضاء بنت عبد المطلب بن هاشم بن عبد مناف بن قصي تزوجها عفان بن أبي العاص بن أمية فولدت له عثمان وآمنة ابني عفان , ثم تزوجها عقبة بن أبي معيط فولدت له الوليد وعمارة وخالدا وأم','15034','4282','0');
INSERT INTO [t9351] VALUES (4400,'أم كلثوم بنت عقبة بن أبي معيط بن أبي عمرو بن أمية بن عبد شمس بن عبد مناف بن قصي وأمها أروى بنت كريز بن ربيعة بن حبيب بن عبد شمس بن عبد مناف بن قصي، أسلمت بمكة وبايعت قبل الهجرة وهي أول من هاجر من النساء بعد أن هاجر رسول الله صلى الله عليه وسلم إلى','15037','4282','0');
INSERT INTO [t9351] VALUES (4401,'أمامة بنت أبي العاص بن الربيع بن عبد العزى بن عبد شمس بن عبد مناف بن قصي وأمها زينب بنت رسول الله صلى الله عليه وسلم','15039','4282','0');
INSERT INTO [t9351] VALUES (4402,'أم خالد وهي أمة بنت خالد بن سعيد بن العاص بن أمية بن عبد شمس , وأمها همينة بنت خلف بن أسعد بن عامر بن بياضة بن سبيع بن جعثمة بن سعد بن مليح بن عمرو من خزاعة , وكان خالد بن سعيد قد هاجر إلى أرض الحبشة ومعه امرأته همينة بنت خلف فولدت له هناك أمة بنت خالد','15048','4282','0');
INSERT INTO [t9351] VALUES (4403,'هند بنت عتبة بن ربيعة بن عبد شمس بن عبد مناف وأمها صفية بنت أمية بن حارثة بن الأوقص بن مرة بن هلال بن فالج بن ذكوان بن ثعلبة بن بهثة بن سليم. تزوج هندا حفص بن المغيرة بن عبد الله بن عمر بن مخزوم فولدت له أبانا','15051','4282','0');
INSERT INTO [t9351] VALUES (4404,'أم كلثوم بنت عتبة بن ربيعة بن عبد شمس وأمها بنت حارثة بن الأوقص تزوجها عبد الرحمن بن عوف فولدت له سالما الأكبر قبل الإسلام','15060','4282','0');
INSERT INTO [t9351] VALUES (4405,'فاطمة بنت عتبة بن ربيعة بن عبد شمس بن عبد مناف وأمها صفية بنت أمية بن حارثة بن الأوقص بن مرة بن هلال بن فالج بن ذكوان بن ثعلبة بن بهثة بن سليم بن منصور , تزوجها قرظة بن عبد عمرو بن نوفل بن عبد مناف بن قصي فولدت له الوليد وهشاما وأبيا وآمنة وعتبة ومسلما','15061','4282','0');
INSERT INTO [t9351] VALUES (4406,'رملة بنت شيبة بن ربيعة بن عبد شمس بن عبد مناف وأمها أم شراك بنت وقدان بن عبد شمس بن عبد ود من بني عامر بن لؤي تزوج رملة عثمان بن عفان فولدت له عائشة وأم أبان وأم عمرو بنات عثمان , وكان أبو الزناد واسمه عبد الله بن ذكوان مولى رملة بنت شيبة بن ربيعة أسلمت','15064','4282','0');
INSERT INTO [t9351] VALUES (4407,'أميمة بنت أبي سفيان بن حرب بن أمية بن عبد شمس بن عبد مناف وأمها صفيا بنت أبي العاص بن أمية بن عبد شمس تزوجها حويطب بن عبد العزى بن أبي قيس بن عبد ود بن نضر بن مالك بن حسل بن عامر بن لؤي فولدت له أبا سفيان ثم خلف عليها صفوان بن أمية بن خلف فولدت له عبد','15065','4282','0');
INSERT INTO [t9351] VALUES (4408,'جويرية بنت أبي سفيان بن حرب بن أمية بن عبد شمس بن عبد مناف وأمها هند بنت عتبة بن ربيعة تزوجها السائب بن أبي حبيش بن المطلب بن أسد بن عبد العزى بن قصي , ثم خلف عليها عبد الرحمن بن الحارث بن أمية الأصغر بن عبد شمس','15066','4282','0');
INSERT INTO [t9351] VALUES (4409,'أم الحكم بنت أبي سفيان بن حرب بن أمية وأمها هند بنت عتبة بن ربيعة , تزوجها عبد الله بن عثمان بن عبد الله بن ربيعة بن الحارث بن حبيب بن الحارث بن مالك بن حطيط بن جشم الثقفي، فولدت له عبد الرحمن فكان يقال له: ابن أم الحكم','15067','4282','0');
INSERT INTO [t9351] VALUES (4410,'هند بنت أبي سفيان بن حرب بن أمية وأمها صفية بنت أبي عمرو بن أمية بن عبد شمس تزوجها الحارث بن نوفل بن الحارث فولدت له عبد الله ومحمدا الأكبر وربيعة وعبد الرحمن ورملة وأم الزبير وهي أم المغيرة وظريبة','15068','4282','0');
INSERT INTO [t9351] VALUES (4411,'صخرة بنت أبي سفيان بن حرب بن أمية وأمها صفية بنت أبي عمرو بن أمية تزوجها سعيد بن الأخنس بن شريق الثقفي فولدت له','15069','4282','0');
INSERT INTO [t9351] VALUES (4412,'ميمونة بنت أبي سفيان بن حرب بن أمية وأمها لبابة بنت أبي العاص بن أمية , تزوجها عروة بن مسعود الثقفي فولدت له ثم خلف عليها المغيرة بن شعبة الثقفي','15070','4282','0');
INSERT INTO [t9351] VALUES (4413,'حمنة بنت جحش بن رياب بن يعمر بن صبرة بن مرة بن كبير بن غنم بن دودان بن أسد وأمها أميمة بنت عبد المطلب بن هاشم بن عبد مناف بن قصي , وكان جحش بن رياب حليف حرب بن أمية بن عبد شمس، وكانت حمنة عند مصعب بن عمير بن هاشم بن عبد مناف الدار فولدت له ابنة وقتل','15071','4282','0');
INSERT INTO [t9351] VALUES (4414,'حبيبة وهي أم حبيب بنت جحش بن رياب بن يعمر بن صبرة بن مرة بن كبير بن غنم بن دودان بن أسد , وأمها أميمة بنت عبد المطلب بن هاشم بن عبد مناف، وحبيبة وهي المستحاضة وبعض أصحاب الحديث يقلب اسمها فيقول: أم حبيبة وإنما هي أم حبيب واسمها حبيبة.','15073','4282','0');
INSERT INTO [t9351] VALUES (4415,'أم قيس بنت محصن بن حرثان بن قيس بن مرة بن بكير بن غنم بن دودان بن أسد وهي أخت عكاشة بن محصن من أهل بدر حلفاء حرب بن أمية , وقد روت عن رسول الله صلى الله عليه وسلم وأسلمت قديما بمكة وهاجرت إلى المدينة مع أهل بيتها.','15075','4282','0');
INSERT INTO [t9351] VALUES (4416,'آمنة بنت رقيش بن رياب بن يعمر بن صبرة بن مرة بن كبير بن غنم بن دودان بن أسد وهي أخت يزيد بن رقيش من أهل بدر أسلمت قديما بمكة وهاجرت إلى المدينة مع أهل بيتها','15077','4282','0');
INSERT INTO [t9351] VALUES (4417,'جدامة بنت جندل الأسدية أسلمت قديما بمكة وبايعت وهاجرت إلى المدينة مع أهلها أخبرنا محمد بن عمر، أخبرنا عمر بن عثمان الجحشي، عن أبيه قال: كان بنو غنم بن دودان بن أسد وهم حلفاء حرب بن أمية أهل إسلام أسلموا بمكة وأوعبوا في الهجرة رجالهم ونساءهم حتى غلقت','15078','4282','0');
INSERT INTO [t9351] VALUES (4418,'أم حبيبة بنت نباتة الأسدية أسلمت بمكة وبايعت رسول الله وهاجرت إلى المدينة مع من هاجر من قومها.','15080','4282','0');
INSERT INTO [t9351] VALUES (4419,'نفيسة بنت أمية بن أبي عبيدة بن همام بن الحارث بن بكر بن زيد بن مالك بن حنظلة بن مالك بن زيد مناة بن تميم وأمها منية بنت جابر بن وهب بن نسيب بن زيد بن مالك بن الحارث بن عوف بن مازن بن منصور، ومنية عمة عتبة بن غزوان بن جابر وهم جميعا حلفاء الحارث بن نوفل','15081','4282','0');
INSERT INTO [t9351] VALUES (4420,'الحولاء بنت تويت بن حبيب بن أسد بن عبد العزى بن قصي أسلمت وبايعت رسول الله صلى الله عليه وسلم بعد الهجرة','15082','4282','0');
INSERT INTO [t9351] VALUES (4421,'فاطمة بنت أبي حبيش بن المطلب بن أسد بن عبد العزى بن قصي تزوجها عبد الله بن جحش بن رياب فولدت له محمد بن عبد الله بن جحش.','15083','4282','0');
INSERT INTO [t9351] VALUES (4422,'بسرة بنت صفوان بن نوفل بن أسد بن عبد العزى بن قصي وأمها سالمة بنت أمية بن حارثة بن الأوقص بن مرة بن هلال بن فالج بن ذكوان بن ثعلبة بن بهثة بن سليم , وأخوها لأمها عقبة بن أبي معيط بن أبي عمرو بن أمية، وكانت بسرة عند المغيرة بن أبي العاص فولدت له معاوية','15085','4282','0');
INSERT INTO [t9351] VALUES (4423,'بركة بنت يسار وهي أخت أبي تجراة مولى بني عبد الدار وهم يقولون نحن من أهل اليمن من الأزد حلفاء لبني عبد الدار. أسلمت بركة بمكة قديما , وبايعت وهاجرت إلى أرض الحبشة الهجرة الثانية مع زوجها قيس بن عبد الله الأسدي وكان يسار يكنى أبا فكيهة.','15087','4282','0');
INSERT INTO [t9351] VALUES (4424,'وأختها فكيهة بنت يسار ويكنى أبا فكيهة أسلمت بمكة قديما وبايعت وهاجرت إلى أرض الحبشة الهجرة الثانية مع زوجها حطاب بن الحارث بن معمر بن حبيب الجمحي','15088','4282','0');
INSERT INTO [t9351] VALUES (4425,'برة بنت أبي تجراة بن أبي فكيهة واسمه يسار ويقولون إنهم من الأزد حلفاء بني عبد الدار , ولهم فيهم ولادات وقد روت برة عن رسول الله صلى الله عليه وسلم','15089','4282','0');
INSERT INTO [t9351] VALUES (4426,'وأختها حبيبة بنت أبي تجراة وقد روت عن رسول الله صلى الله عليه وسلم حديثا.','15091','4282','0');
INSERT INTO [t9351] VALUES (4427,'عاتكة بنت عوف بن عبد عوف بن عبد الحارث بن زهرة بن كلاب أخت عبد الرحمن بن عوف لأبيه وأمه , وأمهما الشفاء بنت عوف بن عبد بن الحارث بن زهرة تزوجها مخرمة بن نوفل بن أهيب بن عبد مناف بن زهرة بن كلاب فولدت له المسور وصفوان الأكبر والصلت الأكبر وأم صفوان بني','15093','4282','0');
INSERT INTO [t9351] VALUES (4428,'الشفاء بنت عوف بن عبد بن الحارث بن زهرة بن كلاب وأمها سلمى بنت عامر بن بياضة بن سبيع بن جعثمة بن سعد بن مليح من خزاعة تزوجها عوف بن عبد عوف بن عبد بن الحارث بن زهرة , فولدت له عبد الرحمن شهد بدرا والأسود أسلم وهاجر قبل الفتح، وعاتكة وأمة بني عوف،','15094','4282','0');
INSERT INTO [t9351] VALUES (4429,'خالدة بنت الأسود بن عبد يغوث بن وهب بن عبد مناف بن زهرة وأمها آمنة بنت نوفل بن وهيب بن عبد مناف بن زهرة أسلمت خالدة بنت الأسود بالمدينة , وبايعت رسول الله وتزوجها عبد الله بن الأرقم بن عبد يغوث بن عبد مناف بن زهرة','15095','4282','0');
INSERT INTO [t9351] VALUES (4430,'أم فروة بنت أبي قحافة واسمه عثمان بن عامر بن عمرو بن كعب بن سعد بن تيم بن مرة، وأمها هند بنت نقيد بن بجير بن عبد بن قصي زوجها أبو بكر الصديق من الأشعث بن قيس الكندي فولدت له محمدا وإسحاق وإسماعيل وحبابة وقريبة.','15097','4282','0');
INSERT INTO [t9351] VALUES (4431,'قريبة بنت أبي قحافة عثمان بن عامر بن عمرو بن كعب بن سعد بن تيم وأمها هند بنت نقيد بن بجير بن عبد بن قصي تزوجها قيس بن سعد بن عبادة بن دليم الساعدي فلم تلد له شيئا','15098','4282','0');
INSERT INTO [t9351] VALUES (4432,'أم عامر بنت أبي قحافة عثمان بن عامر بن عمرو بن كعب بن سعد بن تيم وأمها هند بنت نقيد بن بجير بن عبد بن قصي تزوجها عامر بن أبي وقاص فولدت له ضعيفة','15099','4282','0');
INSERT INTO [t9351] VALUES (4433,'أسماء بنت أبي بكر الصديق بن أبي قحافة عثمان بن عامر بن عمرو بن كعب بن سعد بن تيم وأمها قتيلة بنت عبد العزى بن أسعد بن جابر بن مالك بن حسل بن عامر بن لؤي، وهي أخت عبد الله بن أبي بكر الصديق لأبيه وأمه أسلمت قديما بمكة , وبايعت رسول الله وهي ذات النطاقين','15100','4282','0');
INSERT INTO [t9351] VALUES (4434,'ريطة بنت الحارث بن جبيلة بن عامر بن كعب بن سعد بن تيم وأمها زينب بنت عبد الله بن ساعدة بن مشنوء بن عبد بن حبتر من خزاعة وهي أخت صبيحة بن الحارث , وأسلمت بمكة قديما وبايعت وهاجرت إلى أرض الحبشة الهجرة الثانية مع زوجها الحارث بن خالد بن صخر بن عامر بن كعب','15130','4282','0');
INSERT INTO [t9351] VALUES (4435,'أميمة بنت رقيقة وهي التي روى عنها محمد بن المنكدر , وروت عن رسول الله صلى الله عليه وسلم حديثا في بيعته النساء. وهي أميمة بنت عبد الله بن بجاد بن عمير بن الحارث بن حارثة بن سعد بن تيم بن مرة، وأمها رقيقة بنت خويلد بن أسد بن عبد العزى بن قصي أخت خديجة','15131','4282','0');
INSERT INTO [t9351] VALUES (4436,'جارية بنت عمرو بن مؤمل أسلمت بمكة قديما وكانت ممن يعذب في الله , وكان عمر بن الخطاب قبل أن يسلم هو الذي يعذبها ليردها عن الإسلام فيعذبها حتى يفتر ثم يدعها , ويقول: والله ما أدعك إلا سآمة. فتقول: كذلك يفعل بك ربك.','15133','4282','0');
INSERT INTO [t9351] VALUES (4437,'بريرة مولاة عائشة بنت أبي بكر الصديق','15134','4282','0');
INSERT INTO [t9351] VALUES (4438,'فاطمة بنت الوليد بن المغيرة بن عبد الله بن عمر بن مخزوم وأمها حنتمة بنت شيطان , وهو عبد الله بن عمرو بن كعب بن وائلة بن الأحمر بن الحارث بن عبد مناة بن كنانة تزوجها الحارث بن هشام بن المغيرة فولدت له عبد الرحمن بن الحارث وأم حكيم أخبرنا محمد بن عمر،','15157','4282','0');
INSERT INTO [t9351] VALUES (4439,'أم حكيم بنت الحارث بن هشام بن المغيرة بن عبد الله بن عمر بن مخزوم وأمها فاطمة بنت الوليد بن المغيرة بن عبد الله بن عمر بن مخزوم أخبرنا محمد بن عمر، حدثني أبو بكر بن عبد الله بن أبي سبرة، عن موسى بن عقبة، عن أبي حبيبة، مولى آل الزبير، عن عبد الله بن','15158','4282','0');
INSERT INTO [t9351] VALUES (4440,'جويرية بنت أبي جهل بن هشام بن المغيرة بن عبد الله بن عمر بن مخزوم وأمها أروى بنت أبي العيص بن أمية بن عبد شمس. أسلمت وبايعت وتزوجها عتاب بن أسيد بن أبي العيص بن أمية , ثم تزوجها أبان بن سعيد بن العاص بن أمية فلم تلد له شيئا، وجويرية هي التي خطبها علي','15159','4282','0');
INSERT INTO [t9351] VALUES (4441,'الحنفاء بنت أبي جهل بن هشام بن المغيرة بن عبد الله بن عمر بن مخزوم وأمها أروى بنت أبي العيص بن أمية بن عبد شمس. أسلمت وبايعت وتزوجها سهيل بن عمرو بن عبد شمس بن عبد ود بن نصر بن مالك بن حسل بن عامر بن لؤي , فولدت له هندا ويذكرون أن أسامة بن زيد بن حارثة','15160','4282','0');
INSERT INTO [t9351] VALUES (4442,'قريبة الصغرى بنت أبي أمية بن المغيرة بن عبد الله بن عمر بن مخزوم وأمها عاتكة بنت عتبة بن ربيعة بن عبد شمس وهي أخت أم سلمة بنت أبي أمية زوج النبي صلى الله عليه وسلم لأبيها. أسلمت وبايعت وتزوجها عبد الرحمن بن أبي بكر الصديق فولدت له عبد الله وأم حكيم وحفصة','15161','4282','0');
INSERT INTO [t9351] VALUES (4443,'فاطمة بنت الأسود بن عبد الأسد بن هلال بن عبد الله بن عمر بن مخزوم أسلمت وبايعت وهي التي سرقت فقطع النبي صلى الله عليه وسلم يدها','15163','4282','0');
INSERT INTO [t9351] VALUES (4444,'سمية بنت خباط مولاة أبي حذيفة بن المغيرة بن عبد الله بن عمر بن مخزوم , وهي أم عمار بن ياسر أسلمت قديما بمكة , وكانت ممن يعذب في الله لترجع عن دينها فلم تفعل وصبرت حتى مر بها أبو جهل يوما فطعنها بحربة في قبلها فماتت رحمها الله , وهي أول شهيد في الإسلام','15166','4282','0');
INSERT INTO [t9351] VALUES (4445,'عاتكة بنت زيد بن عمرو بن نفيل بن عبد العزى بن رياح بن عبد الله بن قرط بن رزاح بن عدي بن كعب وأمها أم كرز بنت الحضرمي بن عمار بن مالك بن ربيعة بن لكيز بن مالك بن عوف أسلمت فبايعت وهاجرت','15168','4282','0');
INSERT INTO [t9351] VALUES (4446,'فاطمة بنت الخطاب بن نفيل بن عبد العزى بن رياح بن عبد الله بن قرط بن رزاح بن عدي بن كعب وهي أخت عمر بن الخطاب , وأمها حنتمة بنت هاشم بن المغيرة بن عبد الله بن عمر بن مخزوم تزوجها سعيد بن زيد بن عمرو بن نفيل، وأسلمت هي وزوجها قبل عمر بن الخطاب وقبل دخول','15176','4282','0');
INSERT INTO [t9351] VALUES (4447,'ليلى بنت أبي حثمة بن حذيفة بن غانم بن عامر بن عبد الله بن عبيد بن عويج بن عدي بن كعب وأمها أم ولد من تنوخ من سبايا العرب أسلمت قديما وبايعت وهاجرت إلى أرض الحبشة الهجرتين جميعا مع زوجها عامر بن ربيعة العنزي حليف الخطاب بن نفيل وولدت لعامر بن ربيعة وتزوج','15177','4282','0');
INSERT INTO [t9351] VALUES (4448,'الشفاء بنت عبد الله بن عبد شمس بن خلف بن صداد بن عبد الله بن قرط بن رزاح بن عدي بن كعب وأمها فاطمة بنت وهب بن عمرو بن عائذ بن عمران بن مخزوم. أسلمت الشفاء قبل الهجرة قديما وبايعت النبي صلى الله عليه وسلم وتزوجها أبو حثمة بن حذيفة بن غانم بن عامر بن عبد','15178','4282','0');
INSERT INTO [t9351] VALUES (4449,'رملة بنت أبي عوف بن صبيرة بن سعيد بن سعد بن سهم وأمها أم عبد الله وهي صرماء بنت الحارث بن عوف بن عمرو بن يربوع بن ناضرة بن غاضرة بن حطيط وهو راعي الشمس أسلمت رملة بمكة قديما قبل دخول رسول الله صلى الله عليه وسلم دار الأرقم وبايعت وهاجرت إلى أرض الحبشة','15179','4282','0');
INSERT INTO [t9351] VALUES (4450,'ريطة بنت منبه بن الحجاج بن عامر بن حذيفة بن سعد بن سهم وأمها من خثعم وتزوجها عمرو بن العاص بن وائل السهمي فولدت له عبد الله بن عمرو','15180','4282','0');
INSERT INTO [t9351] VALUES (4451,'زينب بنت عثمان بن مظعون بن حبيب بن وهب بن حذافة بن جمح','15182','4282','0');
INSERT INTO [t9351] VALUES (4452,'التوأمة بنت أمية بن خلف بن وهب بن حذافة بن جمح وأمها ليلى بنت حبيب بن عمرو بن الحارث من بني تميم من البراجم اغتربت التوأمة عند عاصم بن الجعد الفزاري وولدت له، وكانت التوأمة ولدت هي وأخت لها في بطن فسميت تلك باسم وسميت هذه التوأمة','15184','4282','0');
INSERT INTO [t9351] VALUES (4453,'سهلة بنت سهيل بن عمرو بن عبد شمس بن عبد ود بن نصر بن مالك بن حسل بن عامر بن لؤي وأمها فاطمة بنت عبد العزى بن أبي قيس بن عبد ود بن نصر بن مالك بن حسل بن عامر بن لؤي. أسلمت قديما بمكة وبايعت وهاجرت إلى أرض الحبشة الهجرتين جميعا مع زوجها أبي حذيفة بن عتبة','15186','4282','0');
INSERT INTO [t9351] VALUES (4454,'أم كلثوم بنت سهيل بن عمرو بن عبد شمس بن عبد ود بن نصر بن مالك بن حسل بن عامر بن لؤي وأمها فاختة بنت عامر بن نوفل بن عبد مناف بن قصي. أسلمت قديما بمكة وبايعت وهاجرت إلى أرض الحبشة مع زوجها أبي سبرة بن أبي رهم بن عبد العزى بن أبي قيس بن عبد ود بن نصر بن','15190','4282','0');
INSERT INTO [t9351] VALUES (4455,'فاطمة وهي أم جميل بنت المجلل بن عبد بن أبي قيس بن عبد ود بن نصر بن مالك بن حسل بن عامر بن لؤي وأمها أم حبيب بنت العاص بن أمية بن عبد شمس أخت أبي أحيحة سعيد بن العاص بن أمية. أسلمت فاطمة قديما بمكة وبايعت وهاجرت إلى أرض الحبشة الهجرة الثانية مع زوجها','15191','4282','0');
INSERT INTO [t9351] VALUES (4456,'فاطمة وهي أم قهطم بنت علقمة بن عبد الله بن أبي قيس بن عبد ود بن نصر بن مالك بن حسل بن عامر بن لؤي وأمها عاتكة بنت أسعد بن عامر بن بياضة بن سبيع بن جعثمة بن سعد بن مليح من خزاعة أسلمت قديما بمكة وبايعت وهاجرت إلى أرض الحبشة الهجرة الثانية مع زوجها سليط بن','15192','4282','0');
INSERT INTO [t9351] VALUES (4457,'عميرة بنت السعدي واسمه عمرو بن وقدان بن عبد شمس بن عبد ود بن نصر بن مالك بن حسل بن عامر بن لؤي. أسلمت قديما بمكة وبايعت وهاجرت إلى أرض الحبشة الهجرة الثانية مع زوجها مالك بن زمعة بن قيس من بني عامر بن لؤي وهو أخو سودة بنت زمعة زوج رسول الله صلى الله','15193','4282','0');
INSERT INTO [t9351] VALUES (4458,'فاطمة بنت قيس أخت الضحاك بن قيس بن خالد الأكبر بن وهب بن ثعلبة بن وائلة بن عمرو بن شيبان بن محارب بن فهر , وأمها أميمة بنت ربيعة بن حذيم بن عامر بن مبذول بن الأحمر بن الحارث بن عبد مناة بن كنانة، وكانت فاطمة بنت قيس تحت أبي عمرو بن حفص بن المغيرة بن عبد','15194','4282','0');
INSERT INTO [t9351] VALUES (4459,'تسمية غرائب نساء العرب المسلمات المهاجرات المبايعات','15203','4282','0');
INSERT INTO [t9351] VALUES (4460,'أم رومان بنت عامر بن عويمر بن عبد شمس بن عتاب بن أذينة بن سبيع بن دهمان بن الحارث بن غنم بن مالك بن كنانة قال محمد بن سعد: وسمعت من ينسبها غير هذا فيقول أم رومان بنت عامر بن عميرة بن ذهل بن دهمان بن الحارث بن غنم بن مالك بن كنانة وكانت أم رومان امرأة','15204','4282','0');
INSERT INTO [t9351] VALUES (4461,'أم الفضل وهي لبابة الكبرى ابنة الحارث بن حزن بن البجير بن الهزم بن رويبة بن عبد الله بن هلال بن عامر بن صعصعة بن معاوية بن بكر بن هوازن بن منصور بن عكرمة بن خصفة بن قيس بن عيلان بن مضر , وأمها هند وهي خولة بنت عوف بن زهير بن الحارث بن حماطة بن ذي حليل من','15206','4282','0');
INSERT INTO [t9351] VALUES (4462,'لبابة الصغرى وهي العصماء بنت الحارث بن حزن بن البجير بن الهزم بن رويبة بن عبيد الله بن هلال بن عامر بن صعصعة، وأمها فاختة بنت عامر بن معتب بن مالك الثقفي تزوجها الوليد بن المغيرة بن عبد الله بن عمر بن مخزوم بمكة فولدت له خالد بن الوليد سيف الله , ثم','15214','4282','0');
INSERT INTO [t9351] VALUES (4463,'هزيلة بنت الحارث بن حزن بن بجير بن الهزم بن رويبة أسلمت بعد الهجرة وبايعت رسول الله صلى الله عليه وسلم.','15215','4282','0');
INSERT INTO [t9351] VALUES (4464,'عزة بنت الحارث بن حزن بن بجير بن الهزم بن رويبة بن عبد الله بن هلال بن عامر بن صعصعة تزوجها عبد الله بن مالك بن الهزم بن رويبة فولدت له زيادا وعبد الرحمن وبرزة فولدت برزة للأصم البكائي يزيد بن الأصم صاحب عبد الله بن العباس بن عبد المطلب وفي رواية أخرى أن','15216','4282','0');
INSERT INTO [t9351] VALUES (4465,'أسماء بنت عميس بن معد بن تيم بن الحارث بن كعب بن مالك بن قحافة بن عامر بن ربيعة بن عامر بن معاوية بن زيد بن مالك بن نسر بن وهب الله بن شهران بن عفرس بن أفتل وهو جماع خثعم، وأمها هند وهي خولة بنت عوف بن زهير بن الحارث بن حماطة من جرش. أخبرنا محمد بن عمر','15217','4282','0');
INSERT INTO [t9351] VALUES (4466,'سلمى بنت عميس بن معد بن تميم بن الحارث بن كعب بن مالك بن قحافة بن عامر بن ربيعة بن عامر بن معاوية بن زيد بن مالك بن نسر بن وهب الله بن شهران بن عفرس بن أفتل وهو جماع خثعم , وأمها هند وهي خولة بنت عوف بن زهير بن الحارث بن حماطة بن جرش، أسلمت قديما مع','15244','4282','0');
INSERT INTO [t9351] VALUES (4467,'همينة بنت خلف بن أسعد بن عامر بن بياضة بن سبيع بن جعثمة بن سعد بن مليح بن عمرو من خزاعة أسلمت بمكة قديما وهاجرت إلى أرض الحبشة الهجرة الثانية مع زوجها خالد بن سعيد بن العاص بن أمية، فولدت له هناك سعيد بن خالد وأمة بنت خالد فتزوج أمة بنت خالد الزبير بن','15246','4282','0');
INSERT INTO [t9351] VALUES (4468,'حرملة بنت عبد بن الأسود بن جذيمة بن أقيش بن عامر بن بياضة بن سبيع بن جعثمة بن سعد بن مليح بن عمرو من خزاعة أسلمت بمكة قديما وبايعت وهاجرت إلى أرض الحبشة الهجرة الثانية مع زوجها جهم بن قيس بن عبد شرحبيل بن هاشم بن عبد مناف بن عبد الدار بن قصي فهلكت حرملة','15247','4282','0');
INSERT INTO [t9351] VALUES (4469,'فاطمة بنت صفوان بن محرث بن خمل بن شق أسلمت بمكة قديما وبايعت وهاجرت إلى أرض الحبشة الهجرة الثانية مع زوجها عمرو بن سعيد بن العاص بن أمية.','15248','4282','0');
INSERT INTO [t9351] VALUES (4470,'حسنة أم شرحبيل ابن حسنة وهو ابن عبد الله بن المطاع بن عمرو الكندي أسلمت بمكة قديما وبايعت وهاجرت إلى أرض الحبشة الهجرة الثانية مع ابنها شرحبيل ابن حسنة','15249','4282','0');
INSERT INTO [t9351] VALUES (4471,'خرنيق بنت الحصين بن عبيد بن خلف بن عبد نهم بن خريبة بن جهمة بن غاضرة بن حبشية بن كعب بن عمرو من خزاعة أسلمت فبايعت رسول الله صلى الله عليه وسلم وروت عنه.','15250','4282','0');
INSERT INTO [t9351] VALUES (4472,'سبيعة بنت الحارث الأسلمية كانت تحت سعد بن خولة فتوفي عنها.','15251','4282','0');
INSERT INTO [t9351] VALUES (4473,'أم معبد واسمها عاتكة بنت خالد بن خليف بن منقذ بن ربيعة بن أصرم بن ضبيس بن حرام بن حبشية بن سلول بن كعب بن عمرو من خزاعة كانت تحت ابن عمها ويقال له تميم بن عبد العزى بن منقذ بن ربيعة بن أصرم بن ضبيس بن حرام بن حبشية بن سلول بن كعب بن عمرو من خزاعة وكان','15255','4282','0');
INSERT INTO [t9351] VALUES (4474,'أم عبد الله بن مسعود وهي أم عبد بنت عبد ود بن سوي بن قريم بن صاهلة بن كاهل بن الحارث بن تميم بن سعد بن هذيل بن مدركة بن إلياس بن مضر , وأمها هند بنت عبد بن الحارث بن زهرة بن كلاب، أسلمت وبايعت رسول الله صلى الله عليه وسلم','15258','4282','0');
INSERT INTO [t9351] VALUES (4475,'ريطة بنت عبد الله امرأة عبد الله بن مسعود وأم ولده وكانت امرأة صناعا فقالت: يا رسول الله إني امرأة ذات صنعة أبيع منها وليس لي ولا لزوجي ولا لولدي شيء. وسألته عن النفقة عليهم، فقال: " لك في ذلك أجر ما أنفقت عليهم ".','15260','4282','0');
INSERT INTO [t9351] VALUES (4476,'زينب بنت أبي معاوية الثقفية امرأة عبد الله بن مسعود أسلمت وبايعت وروت عن رسول الله صلى الله عليه وسلم حديثا.','15261','4282','0');
