INSERT INTO [t9351] VALUES (4494,'أسماء بنت مخربة بن جندل بن أبير بن نهشل بن دارم من بني تميم , وأمها العناق بنت الجبار بن عوف بن أبي حارثة بن زيد بن عمرو بن غنم بن تغلب بن وائل تزوجها هشام بن المغيرة بن عبد الله بن مخزوم فولدت له أبا جهل والحارث ابني هشام ثم مات عنها هشام بن المغيرة','15303','4282','0');
INSERT INTO [t9351] VALUES (4495,'أسماء بنت سلامة بن مخربة بن جندل بن أبير بن نهشل بن دارم من بني تميم , وأمها سلمى بنت زهير بن أبير بن نهشل بن دارم من بني تميم أسلمت قديما بمكة وبايعت وهاجرت إلى أرض الحبشة الهجرة الثانية مع زوجها عياش بن أبي ربيعة بن المغيرة بن عبد الله بن عمر بن مخزوم','15305','4282','0');
