INSERT INTO [t9351] VALUES (4892,'أم كلثوم بنت علي بن أبي طالب بن عبد المطلب بن هاشم بن عبد مناف بن قصي وأمها فاطمة بنت رسول الله وأمها خديجة بنت خويلد بن أسد بن عبد العزى بن قصي تزوجها عمر بن الخطاب وهي جارية لم تبلغ فلم تزل عنده إلى أن قتل وولدت له زيد بن عمر ورقية بنت عمر ثم خلف على','15837','4282','0');
INSERT INTO [t9351] VALUES (4893,'زينب بنت علي بن أبي طالب بن عبد المطلب بن هاشم بن عبد مناف بن قصي وأمها فاطمة بنت رسول الله صلى الله عليه وسلم تزوجها عبد الله بن جعفر بن أبي طالب بن عبد المطلب فولدت له عليا وعونا الأكبر وعباسا ومحمدا وأم كلثوم','15847','4282','0');
INSERT INTO [t9351] VALUES (4894,'فاطمة بنت علي بن أبي طالب بن عبد المطلب بن هاشم بن عبد مناف وأمها أم ولد تزوجها محمد بن أبي سعيد بن عقيل بن أبي طالب فولدت له حميدة بنت محمد ثم خلف عليها سعيد بن الأسود بن أبي البحتري بن هشام بن الحارث بن أسد بن عبد العزى بن قصي فولدت له برزة وخالدا ابني','15849','4282','0');
