INSERT INTO [t9351] VALUES (4908,'أم سلمة بنت المختار بن أبي عبيد بن مسعود بن عمرو بن عمير بن عوف بن عقدة بن غيرة بن عوف بن قسي وهو ثقيف وأمها أم الوليد بنت عمير بن رباح بن عوف بن جابر بن سفيان بن عبد ياليل بن سالم بن مالك بن حطيط تزوجها عبد الله بن عبد الله بن عمر بن الخطاب فولدت له عمر','15880','4282','0');
INSERT INTO [t9351] VALUES (4909,'فاطمة بنت حسين بن علي بن أبي طالب بن عبد المطلب بن هاشم بن عبد مناف بن قصي وأمها أم إسحاق بنت طلحة بن عبيد الله بن عثمان بن عمرو بن كعب بن سعد بن تيم تزوجها ابن عمها حسن بن حسن بن علي بن أبي طالب فولدت له عبد الله وإبراهيم وحسنا وزينب ثم مات عنها فخلف','15882','4282','0');
