INSERT INTO [t9356] VALUES (1,'يأمرنا إذا تغيرتم نتغير عليكم - وحدثت عن أحمد بن صالح فيما قرئ على ابن وهب قال: حدثني','17','0','0');
INSERT INTO [t9356] VALUES (2,'إذا طابت المكسبة، زكت النفقة، وسترد فترى','47','0','0');
INSERT INTO [t9356] VALUES (3,'من ولي للمسلمين سلطانا أوقف يوم القيامة على جسر جهنم يتزلزل به الجسر، فإن كان محسنا نجا،','56','0','0');
INSERT INTO [t9356] VALUES (4,'ما من وال ولي للمسلمين سلطانا إلا أوقف يوم القيامة على جسر جهنم، فيتزلزل به الجسر، حتى','56','0','0');
INSERT INTO [t9356] VALUES (5,'من ولي شيئا من أمر الناس أقيم يوم القيامة على جسر في النار، ينتفض به ذلك الجسر حتى يزول','57','0','0');
INSERT INTO [t9356] VALUES (6,'ما من وال ولي من أمر المسلمين شيئا، فلم يحط من ورائهم بالنصيحة إلا أكبه الله على وجهه في','58','0','0');
INSERT INTO [t9356] VALUES (7,'إنك بين هؤلاء وبين الله، وليس بينك وبين الله أحد، انظر عن يمينك وعن شمالك، انظر بين يديك','58','0','0');
INSERT INTO [t9356] VALUES (8,'اخش الله في الناس، ولا تخش الناس في الله، وأحب لأهل الإسلام ما تحب لنفسك وأهل بيتك، وأقم','59','0','0');
INSERT INTO [t9356] VALUES (9,'لا يمنعن أحدكم مخافة الناس أن يتكلم بالحق إذا رآه» ، فذاك الذي حملني أن دخلت إلى فلان فملأت','60','0','0');
INSERT INTO [t9356] VALUES (10,'" ما من حاكم حكم إلا جيء به يوم القيامة، وملك آخذ بقفاه، حتى يوقفه على جهنم، ثم يرفع رأسه','63','0','0');
INSERT INTO [t9356] VALUES (11,'ليأتين على القاضي العدل يوم القيامة ما يتمنى أن يكون كان معلقا بالثريا، وأنه لم يقض بين','64','0','0');
INSERT INTO [t9356] VALUES (12,'ليأتين على أحدهم يوم يعني يوم القيامة ما ود أنه معلق بالنجم متذبذب، وأنه لم يتأمر على','64','0','0');
INSERT INTO [t9356] VALUES (13,'ليتمنين أقوام يوم القيامة أن ذوائبهم كانت معلقة بالثريا، يتذبذبون بين السماء والأرض،','65','0','0');
INSERT INTO [t9356] VALUES (14,'القاضي ليزل في مزلقة أبعد من عدن أبين في جهنم»','65','0','0');
INSERT INTO [t9356] VALUES (15,'«من كان قاضيا فقضى بجور كان من أهل النار، ومن كان قاضيا فقضى بالجهل كان من أهل النار، ومن','66','0','0');
INSERT INTO [t9356] VALUES (16,'«من لم يبال من أين جمع المال، لم يبال الله من أين أدخله النار» - وسمعت هارون بن عبد الله','92','0','0');
INSERT INTO [t9356] VALUES (17,'سيكون قوم يتفقهون في الدين، ويقرءون القرآن، يأتيهم الشيطان فيقول: لو أتيتم السلطان فأصبتم','94','0','0');
INSERT INTO [t9356] VALUES (18,'ستحرصون على الإمارة، وستصير ندامة وحسرة، فنعمت المرضعة، وبئست الفاطمة»','95','0','0');
INSERT INTO [t9356] VALUES (19,'«لا تسأل الإمارة» - سمعت أبا عبد الله يقول: حدثنا إبراهيم بن خالد، حدثنا رباح بن زيد، قال:','95','0','0');
INSERT INTO [t9356] VALUES (20,'السلطان على باب عنت، إلا من عصمه الله» ، قال الرجل عند ذاك: والله، لا أعمل ولا لغيرك','99','0','0');
INSERT INTO [t9356] VALUES (21,'كانت أنبياء، وسيكون بعدهم أمراء، يتركون بعض ما يؤمرون به، فمن ناوأهم نجا، ومن اعتزلهم','99','0','0');
INSERT INTO [t9356] VALUES (22,'أشد الناس عذابا يوم القيامة المنصور، قال: قيل: وما المنصور؟ قال: الكاتب يصانع العريف،','106','0','0');
INSERT INTO [t9356] VALUES (23,'يستريح من غفر له» - سمعت أبا عبد الله يقول: كانوا عند أنس قبل طلوع الشمس، فقال: هكذا نهار','131','0','0');
INSERT INTO [t9356] VALUES (24,'الله عز وجل جزأ الخلق عشرة أجزاء، فجعل الملائكة تسعة أجزاء، وجزأ سائر الخلق، وجزأ','132','0','0');
INSERT INTO [t9356] VALUES (25,'اثبت حراء، فما عليك إلا نبي، أو صديق، أو فاروق، أو شهيد مؤمن، يعني عثمان بن','136','0','0');
INSERT INTO [t9356] VALUES (26,'أبو بكر الصديق أصبتم اسمه، وعمر الفاروق قرن من حديد أصبتم اسمه وعثمان بن عفان ذو النورين','136','0','0');
INSERT INTO [t9356] VALUES (27,'المعلم الذي يأخذ على التعليم أجرا يعجل أجره في الدنيا، وإن أخذ ولم يعلم أخذ من حسناته يوم','155','0','0');
INSERT INTO [t9356] VALUES (28,'المقسطين عند الله عز وجل على منابر من نور، عن يمين الرحمن، وكلتا يديه يمين، الذين يعدلون','156','0','0');
