INSERT INTO [t9361] VALUES (1,'آتي يوم القيامة إلى الجنة فأستفتح، فيقول لي الخازن: من أنت؟ فأقول: محمد، فيقول: بك أمرت','1','0','0');
INSERT INTO [t9361] VALUES (2,'«هل قلت في أبي بكر شيئا؟» قال: نعم. قال: «قل وأنا أسمع»، قال: وثاني اثنين في الغار المنيف وقد ...','3','0','0');
INSERT INTO [t9361] VALUES (3,'ربك عز وجل باهى بأصحابي عشية عرفة، وباهى بك خاصة»','6','0','0');
INSERT INTO [t9361] VALUES (4,'ستكون فتن كأنها صياصي بقر». فمر بنا رجل متقنع، فقال: «هذا وأصحابه على الحق». فذهبت فنظرت','9','0','0');
INSERT INTO [t9361] VALUES (5,'أنت في الجنة، يا علي أنت في الجنة، يا علي أنت في الجنة»','10','0','0');
INSERT INTO [t9361] VALUES (6,'الله عز وجل بنى لإبراهيم خليله عليه السلام قصرا من ياقوتة خضراء في الجنة، وبنى لي قصرا من','14','0','0');
INSERT INTO [t9361] VALUES (7,'مثل أهل بيتي كمثل سفينة نوح، من ركب فيها نجا، ومن تخلف عنها غرق»','15','0','0');
INSERT INTO [t9361] VALUES (8,'فأقبل الحسن والحسين عليهما السلام، عليهما قميصان أحمران يعثران ويقومان، فنزل فأخذهما فصعد','17','0','0');
INSERT INTO [t9361] VALUES (9,'إذا كان يوم القيامة جاء أصحاب الحديث بأيديهم المحابر، فيأمر الله عز وجل جبريل عليه السلام','22','0','0');
INSERT INTO [t9361] VALUES (10,'«كيف أصبحت يا حارث؟»، قال: أصبحت مؤمنا حقا، قال: «انظر ما تقول فإن لكل قول حقيقة». قال: يا','26','0','0');
INSERT INTO [t9361] VALUES (11,'يبعث الله العباد يوم القيامة ثم يميز العلماء، فيقول: يا معشر العلماء، إنه لم أضع فيكم','29','0','0');
INSERT INTO [t9361] VALUES (12,'تفضل صلاة الجماعة على صلاة الرجل وحده خمسا وعشرين درجة»، قال: «وتجتمع ملائكة الليل وملائكة','31','0','0');
INSERT INTO [t9361] VALUES (13,'من سلك طريق علم سهل الله له به طريقا إلى الجنة، وإن الملائكة لتضع أجنحتها لطالب العلم،','35','0','0');
INSERT INTO [t9361] VALUES (14,'الصيام جنة، فإذا كان أحدكم صائما فلا يرفث ولا يجهل، وإن امرؤ قاتله أو شاتمه، فليقل إني','36','0','0');
INSERT INTO [t9361] VALUES (15,'اكفلوا لي بست أكفل لكم بالجنة: إذا حدث أحدكم فلا يكذب، وإذا ائتمن فلا يخن، وإذا وعد فلا','39','0','0');
INSERT INTO [t9361] VALUES (16,'من تطهر في بيته، ثم مشى إلى بيت من بيوت الله ليؤدي فريضة الله، فخطوتاه إحداهما تحط خطيئة،','41','0','0');
INSERT INTO [t9361] VALUES (17,'عشرة في الجنة: أبو بكر في الجنة، وعمر في الجنة، وعثمان في الجنة، وعلي في الجنة، وطلحة في','50','0','0');
INSERT INTO [t9361] VALUES (18,'يغدو يوم العيد إلى المصلى في الطريق الأعظم، فإذا رجع رجع من الطريق الأخرى على دار عمار بن','54','0','0');
INSERT INTO [t9361] VALUES (19,'سبحان الله والحمد لله ولا إله إلا الله والله أكبر، يأتين يوم القيامة مقدمات معقبات مجنبات','56','0','0');
INSERT INTO [t9361] VALUES (20,'من أبر؟ قال: «أمك». قلت: ثم من؟ قال: «أمك». قلت: ثم من؟ قال: «ثم أباك، ثم الأقرب','58','0','0');
INSERT INTO [t9361] VALUES (21,'من قتل نفسه بحديدة، فحديدته في يده يتوجأ بها في بطنه في نار جهنم خالدا مخلدا فيها أبدا،','60','0','0');
INSERT INTO [t9361] VALUES (22,'إذا كان أول يوم من شهر رمضان صفدت الشياطين ومردة الجن، وغلقت أبواب النار فلم يفتح منها','65','0','0');
INSERT INTO [t9361] VALUES (23,'مثل الصلوات الخمس مثل نهر جار عذب غمر، يجري على باب أحدكم يغتسل فيه كل يوم خمس مرات،','67','0','0');
INSERT INTO [t9361] VALUES (24,'من صلى الصبح فهو في ذمة الله، فانظر يا ابن آدم لا يطالبك الله بشيء من','68','0','0');
INSERT INTO [t9361] VALUES (25,'من أحب الأنصار أحبه الله، ومن أبغضهم أبغضه الله، لا يحبهم إلا مؤمن، ولا يبغضهم إلا','72','0','0');
INSERT INTO [t9361] VALUES (26,'السخي قريب من الله قريب من الناس بعيد من النار، والبخيل بعيد من الله بعيد من الناس بعيد','74','0','0');
INSERT INTO [t9361] VALUES (27,'أنزلت علي سورة، فقرأ بسم الله الرحمن الرحيم {إنا أعطيناك الكوثر فصل لربك وانحر إن شانئك','75','0','0');
