INSERT INTO [t9382] VALUES (1,'من قال: لا إله إلا الله وحده لا شريك له، له الملك وله الحمد وهو على كل شيء قدير بعدما','2','0','0');
INSERT INTO [t9382] VALUES (2,'إذا فرغ من الصلاة: «لا إله إلا الله وحده لا شريك له، له الملك والحمد وهو على كل شيء قدير،','3','0','0');
INSERT INTO [t9382] VALUES (3,'أيمنع أحدكم أن يكبر في دبر كل صلاة عشرا، ويسبح عشرا، ويحمد عشرا، وذلك في خمس صلوات خمسون','4','0','0');
INSERT INTO [t9382] VALUES (4,'لا تدعن في دبر كل صلاة، أن تقول: اللهم أعني على ذكرك وشكرك وحسن عبادتك ". هذا حديث أخرجه','5','0','0');
INSERT INTO [t9382] VALUES (5,'معقبات لا يخيب قائلهن، يسبح الله في دبر كل صلاة ثلاثا وثلاثين، ويحمده ثلاثا وثلاثين،','5','0','0');
INSERT INTO [t9382] VALUES (6,'مر بها رسول الله صلى الله عليه وسلم حين صلى بالغداة أو بعدما صلى بالغداة وهى تذكر الله،','6','0','0');
INSERT INTO [t9382] VALUES (7,'إذا رفعت المائدة، قال: «الحمد لله كثيرا طيبا مباركا فيه، غير ملقى ولا مودع ولا مستغنى عنه','6','0','0');
INSERT INTO [t9382] VALUES (8,'إذا رأى أحدكم أحدا في بلاء، فليقل: الحمد لله الذي عافاني مما ابتلاك به، وفضلني عليك وعلى','7','0','0');
INSERT INTO [t9382] VALUES (9,'يقول عند الكرب: «لا إله إلا الله العظيم الحليم، لا إله إلا الله رب العرش العظيم، لا إله','8','0','0');
INSERT INTO [t9382] VALUES (10,'إذا أراد سفرا وركب راحلته كبر ثلاثا، ثم قال: " سبحان الذي سخر لنا هذا وما كنا له مقرنين','8','0','0');
INSERT INTO [t9382] VALUES (11,'إذا سافر، يقول: «اللهم إني أعوذ بك من وعثاء السفر، وكآبة المنقلب، ومن الحور بعد الكور،','9','0','0');
INSERT INTO [t9382] VALUES (12,'إذا سافر وركب راحلته، قال بإصبعه هكذا، وقال: «اللهم أنت الصاحب في السفر، والخليفة في','10','0','0');
INSERT INTO [t9382] VALUES (13,'إذا خرج في سفره فمر بنشز أو فدفد كبر ثلاثا، ثم قال: " لا إله إلا الله وحده لا شريك له، له','10','0','0');
INSERT INTO [t9382] VALUES (14,'إن لله تسعة وتسعين اسما مائة غير واحد من أحصاها دخل الجنة، وهو وتر يحب الوتر، هو الله','11','0','0');
INSERT INTO [t9382] VALUES (15,'سبحان الله، والحمد لله، ولا إله إلا الله، والله أكبر تساقط الذنوب كما تساقط هذه الشجرة','12','0','0');
INSERT INTO [t9382] VALUES (16,'اللهم إني أعوذ بك من جهد البلاء، ودرك الشقاء، وشماتة الأعداء، وسوء القضاء» . هذا حديث صحيح','12','0','0');
INSERT INTO [t9382] VALUES (17,'من قال: لا إله إلا الله وحده لا شريك له، له الملك وله الحمد وهو على كل شيء قدير. في كل','13','0','0');
INSERT INTO [t9382] VALUES (18,'اللهم إني أعوذ بك من زوال نعمتك وتحويل عافيتك، وفجأة نعمتك، وجميع سخطك» . هذا حديث صحيح،','13','0','0');
INSERT INTO [t9382] VALUES (19,'ألا أدلك على كنز من كنوز الجنة؟ قلت: بلى يا رسول الله. قال: لا حول ولا قوة إلا بالله، ولا','14','0','0');
INSERT INTO [t9382] VALUES (20,'يتعوذ من علم لا ينفع، ودعاء لا يسمع، وقلب لا يخشع، ونفس لا تشبع» . هذا حديث حسن، أخرجه','14','0','0');
INSERT INTO [t9382] VALUES (21,'اللهم إني أعوذ بك من العجز والكسل، والجبن والبخل والهرم، وأعوذ بك من عذاب القبر، وأعوذ بك','15','0','0');
INSERT INTO [t9382] VALUES (22,'اللهم أعني ولا تعن على، وانصرني على من بغى على، رب اجعلني لك شاكرا، لك ذاكرا، لك راهبا،','15','0','0');
INSERT INTO [t9382] VALUES (23,'كلمتان خفيفتان على اللسان، ثقيلتان في الميزان، حبيبتان إلى الرحمن: سبحان الله وبحمده،','16','0','0');
INSERT INTO [t9382] VALUES (24,'لما نزلت: {إن الله وملائكته يصلون على النبي يأيها الذين آمنوا صلوا عليه وسلموا تسليما}','17','0','0');
INSERT INTO [t9382] VALUES (25,'عرفنا كيف نسلم عليك، فكيف نصلي عليك؟ قال: قولوا: «اللهم صل على محمد، وعلى آل محمد، كما','17','0','0');
INSERT INTO [t9382] VALUES (26,'من ذكرت عنده فليصل علي، فإنه من صلى علي مرة صلى الله عليه عشرا»','18','0','0');
INSERT INTO [t9382] VALUES (27,'أبخل الناس من ذكرت عنده، فلم يصل علي»','18','0','0');
INSERT INTO [t9382] VALUES (28,'إذا صليتم على النبي صلى الله عليه وسلم، فأحسنوا الصلاة عليه، فإنكم لا تدرون لعل ذلك يعرض','18','0','0');
INSERT INTO [t9382] VALUES (29,'أفضل ما قلت أنا والنبيون من قبلي يوم عرفة: لا إله إلا الله وحده لا شريك له ". فقيل لسفيان','19','0','0');
