INSERT INTO [t9383] VALUES (1,'ما اجتمع قوم يذكرون الله إلا حفتهم الملائكة، وغشيتهم الرحمة، ونزلت عليهم السكينة، وذكرهم','1','0','0');
INSERT INTO [t9383] VALUES (2,'الله عز وجل تمهل حتى إذا كانت ثلث الليل الآخر نزل إلى هذه السماء الدنيا فنادى: هل من مذنب','1','0','0');
INSERT INTO [t9383] VALUES (3,'كيف يحشر الكافر على وجهه يوم القيامة؟ قال: «الذي أمشاه على رجله في الدنيا قادر على أن','1','0','0');
INSERT INTO [t9383] VALUES (4,'وجعلت لي الأرض مسجدا أينما كنت، وإن لم أجد الماء تيممت بالصعيد ثم صليت وكانت لي مسجدا','2','0','0');
INSERT INTO [t9383] VALUES (5,'يدركه الفجر وهو جنب ثم يصوم يومئذ» ، قال: فأخبرت بذلك مروان بن الحكم، فقال: أقسمت لتخبرن','2','0','0');
INSERT INTO [t9383] VALUES (6,'ابن آدم اذكرني في نفسك أذكرك في نفسي، فإن ذكرتني في ملأ ذكرتك في ملأ من الملائكة أو قال','2','0','0');
INSERT INTO [t9383] VALUES (7,'خلفة فم الصائم أطيب عند الله من ريح المسك»','3','0','0');
INSERT INTO [t9383] VALUES (8,'إذا أدرك أحدكم الركعتين يوم الجمعة، فقد أدرك الجمعة، وإن أدرك ركعة فليركع إليها أخرى، وإن','3','0','0');
INSERT INTO [t9383] VALUES (9,'بعثت على أثر ثمانية آلاف نبي منهم أربعة آلاف من بني إسرائيل»','3','0','0');
INSERT INTO [t9383] VALUES (10,'إذا أردت أن تنام وأخذت مضجعك، فقل: هدأت العيون وغارت النجوم، وأنت الحي القيوم "، قال:','4','0','0');
INSERT INTO [t9383] VALUES (11,'إذا هم عبدي بالحسنة فاكتبوها له حسنة، فإن عملها فاكتبوها بعشر أمثالها، فإذا هم بالسيئة','4','0','0');
INSERT INTO [t9383] VALUES (12,'يقول لهلال رمضان: «إذا رأيتموه فصوموا، وإذا رأيتموه فأفطروا، فإن غم عليكم فاقدروا','5','0','0');
INSERT INTO [t9383] VALUES (13,'إذا صلى الجمعة انصرف فصلى في بيته ركعتين» ، وكان ابن عمر يفعل ذلك','5','0','0');
INSERT INTO [t9383] VALUES (14,'الواشمة والمستوشمة والواصلة والمستوصلة» ، يعني لعن النبي صلى الله عليه وسلم، رواه البخاري','5','0','0');
INSERT INTO [t9383] VALUES (15,'أي الأعمال أفضل؟ قال: «الصلاة لوقتها، ثم بر الوالدين، ثم الجهاد في سبيل','6','0','0');
INSERT INTO [t9383] VALUES (16,'ما بين بيتي ومنبري روضة من رياض الجنة، وقوائم منبري رواتب في الجنة» أخبرنا أبو عمرو عثمان','6','0','0');
INSERT INTO [t9383] VALUES (17,'يحتجم ولا يظلم أحدا أجره»','7','0','0');
INSERT INTO [t9383] VALUES (18,'اتقوا النار ولو بشق تمرة»','7','0','0');
INSERT INTO [t9383] VALUES (19,'لا زكاة في مال حتى يحول عليه الحول»','7','0','0');
INSERT INTO [t9383] VALUES (20,'من اطلع على قوم في بيتهم بغير إذن فقد حل لهم أن يفقئوا عينيه»','8','0','0');
INSERT INTO [t9383] VALUES (21,'يستحب أن يصلي على الحصير أو فروة مدبوغة»','8','0','0');
INSERT INTO [t9383] VALUES (22,'ليس فيما دون خمس ذود صدقة، وليس فيما دون خمس أواق صدقة، وليس فيما دون خمس أوساق','8','0','0');
INSERT INTO [t9383] VALUES (23,'إذا جلست في قوم فيهم عشرون رجلا أقل أو أكثر فتصفحت وجوههم فلم تر فيهم أحدا يهاب في الله','9','0','0');
INSERT INTO [t9383] VALUES (24,'اتركوني ما تركتكم فإنما هلك من كان قبلكم بكثرة مسائلهم واختلافهم على أنبيائهم فما نهيتكم','9','0','0');
INSERT INTO [t9383] VALUES (25,'أصبت أرضا من خيبر، ما أصبت مالا أنفس عندي منه، قال: «إن شئت حبست أصلها وتصدقت بها» ، فتصدق','9','0','0');
INSERT INTO [t9383] VALUES (26,'أتاكم أهل اليمن، أتاكم أهل اليمن، هم أرق أفئدة، الإيمان يمان والفقه يمان والحكمة','10','0','0');
INSERT INTO [t9383] VALUES (27,'الله تعالى مقمصك قميصا فإن أرادك المنافقون على خلعه فلا تخلعه لهم»','10','0','0');
INSERT INTO [t9383] VALUES (28,'لا يدخل الجنة مدمن خمر، ولا قاطع رحم، ولا ولد زنية، ولا عاق والديه، ولا من أتى ذات','10','0','0');
INSERT INTO [t9383] VALUES (29,'من أول من يحاسب الله يوم القيامة؟ قال: «أبو بكر الصديق» ، قال: ثم من؟ قال: «ثم عمر بن الخطاب» ،','10','0','0');
INSERT INTO [t9383] VALUES (30,'بينا أنا نائم رأيتني أتيت بقدح فشربت منه حتى إني أرى الري يخرج من أظفاري ثم أعطيت فضلي','11','0','0');
INSERT INTO [t9383] VALUES (31,'فأتى الخلاء ثم إنه رجع فأتي بطعام، فقيل: يا رسول الله ألا تتوضأ؟ قال: «لم أصل','11','0','0');
INSERT INTO [t9383] VALUES (32,'من نذر نذرا لم يسمه فكفارته كفارة يمين، ومن نذر نذرا في معصية الله فكفارته كفارة يمين،','11','0','0');
INSERT INTO [t9383] VALUES (33,'«يا أبا ذر لا تحقرن من المعروف شيئا ولو أن تلقى أخاك بوجه منبسط، ولو أن تفرغ من دلوك في','12','0','0');
INSERT INTO [t9383] VALUES (34,'لو كنت متخذا خليلا لاتخذت ابن أبي قحافة خليلا»','12','0','0');
INSERT INTO [t9383] VALUES (35,'كانت امرأة تغشى عائشة، قالت: فكانت تكثر تتمثل بهذا البيت: ويوم الوشاح من تعاجيب ربنا ...','12','0','0');
INSERT INTO [t9383] VALUES (36,'أخرجوا المخنثين من بيوتكم» ، فأخرج رسول الله صلى الله عليه وسلم مخنثا وأخرج عمر','13','0','0');
INSERT INTO [t9383] VALUES (37,'لا تزال طائفة من أمتي ظاهرين على الدين عزيزة إلى يوم القيامة»','13','0','0');
INSERT INTO [t9383] VALUES (38,'" يأيها الناس إن الله طيب لا يقبل إلا طيبا، وإن الله أمر المؤمنين مما أمر به المرسلين،','13','0','0');
INSERT INTO [t9383] VALUES (39,'«يا علي، يدك في يدي تدخل معي يوم القيامة حيث أدخل»','14','0','0');
INSERT INTO [t9383] VALUES (40,'يوم يموت عثمان تصلي عليه ملائكة السماء» ، قال: قلت: يا رسول الله، عثمان خاصة أو الناس عامة؟','14','0','0');
INSERT INTO [t9383] VALUES (41,'من يسو لي رحلي وهو في الجنة» ، فنزل طلحة فسواه له حتى ركب، فقال له النبي صلى الله عليه','14','0','0');
INSERT INTO [t9383] VALUES (42,'فجلس الزبير عند وجهه حتى استيقظ، فقال له: «أبا عبد الله، لم تزل؟» ، قال: لم أزل بأبي وأمي،','14','0','0');
INSERT INTO [t9383] VALUES (43,'أوتر قوسه أربع عشرة مرة، ويقول له: «ارم فداك أبي وأمي»','14','0','0');
INSERT INTO [t9383] VALUES (44,'في منزل فاطمة، والحسن والحسين يبكيان جوعا ويتضوران، فقال النبي صلى الله عليه وسلم: «من','14','0','0');
INSERT INTO [t9383] VALUES (45,'يستحلفان المعسر بالله ما يجد ما يقضيه من عرض ولا فرض ولئن وجدت من حيث لا يعلم ليقضينه ثم','14','0','0');
INSERT INTO [t9383] VALUES (46,'يصلي من الليل، قال: فقمت عن يساره أصلي بصلاته " قال: فأخذ بذؤاب كان لي أو برأسي فأقامني عن','15','0','0');
INSERT INTO [t9383] VALUES (47,'ما هذا يا أبا بكر ما أعرف هذا من فعلك؟» قال: يا رسول الله أذكر الرصد، فأكون أمامك، وأذكر','15','0','0');
INSERT INTO [t9383] VALUES (48,'المدينة حرم ما بين عير إلى ثور، فمن أحدث فيها حدثا أو آوى محدثا فعليه لعنة الله والملائكة','16','0','0');
INSERT INTO [t9383] VALUES (49,'الجاهر بالقرآن كالجاهر بالصدقة، والمسر بالقرآن كالمسر بالصدقة»','17','0','0');
INSERT INTO [t9383] VALUES (50,'من شرب الخمر فقد كفر بالله عز وجل»','17','0','0');
INSERT INTO [t9383] VALUES (51,'أي الأعمال أفضل؟ قال: «إيمان بالله وجهاد في سبيل الله» ، قال: فأي العتاق أفضل؟ قال: «أنفسها» ،','17','0','0');
INSERT INTO [t9383] VALUES (52,'الشهر هكذا وهكذا وهكذا» ، وقبض إبهامه في الثالثة','17','0','0');
INSERT INTO [t9383] VALUES (53,'يطوف على نسائه في غسل واحد.','18','0','0');
INSERT INTO [t9383] VALUES (54,'إذا أوى إلى فراشه، قال: «اللهم باسمك أحيا وباسمك أموت» وإذا أصبح حمد الله وقال: «الحمد لله','18','0','0');
INSERT INTO [t9383] VALUES (55,'إذا كان يوم القيامة نادى مناد يا معشر الخلائق طأطئوا رءوسكم حتى تجوز','18','0','0');
INSERT INTO [t9383] VALUES (56,'مثل المؤمن كمثل الزرع لا تزال الريح تفيئه ولا يزال المؤمن يصيبه البلاء، ومثل المنافق كمثل','18','0','0');
INSERT INTO [t9383] VALUES (57,'من قام رمضان إيمانا واحتسابا غفر له ما تقدم من ذنبه»','19','0','0');
INSERT INTO [t9383] VALUES (58,'من قام ليلة القدر إيمانا واحتسابا غفر له ما تقدم من ذنبه»','19','0','0');
INSERT INTO [t9383] VALUES (59,'لا تسافر المرأة سفرا ثلاثة أيام فصاعدا إلا مع زوجها أو أخيها أو ذوي','19','0','0');
INSERT INTO [t9383] VALUES (60,'إذا تفل أحدكم في صلاته فليتفل تحت قدمه اليسرى، فإن عجل به فبادره فليحملها هكذا في','19','0','0');
INSERT INTO [t9383] VALUES (61,'آتي يوم القيامة باب الجنة فأستفتح، فيقول الخازن: من أنت؟ فأقول: محمد، فيقول: بك أمرت أن','20','0','0');
INSERT INTO [t9383] VALUES (62,'سافرنا مع رسول الله صلى الله عليه وسلم في رمضان فمنا الصائم ومنا المفطر، فلم يعب الصائم','20','0','0');
INSERT INTO [t9383] VALUES (63,'إذا رأيتم الهلال فصوموا، وإذا رأيتموه فأفطروا، فإن غم عليكم فعدوا','20','0','0');
INSERT INTO [t9383] VALUES (64,'ليس أحد من أمة محمد صلى الله عليه وسلم، يصلي عليه صلاة إلا وهي تبلغه، يقول الملك: فلان','20','0','0');
INSERT INTO [t9383] VALUES (65,'من طلب القضاء واستعان عليه وكل إليه، ومن لم يطلبه ولم يستعن عليه أنزل الله إليه ملكا','21','0','0');
INSERT INTO [t9383] VALUES (66,'قد علمت أنكم كنتم تؤنسون أو ترون مني شدة وغلظة وذلك أني كنت مع رسول الله صلى الله عليه','21','0','0');
INSERT INTO [t9383] VALUES (67,'كلمة التقوى لا إله إلا الله والله أكبر»','21','0','0');
INSERT INTO [t9383] VALUES (68,'إذا قام إلى الصلاة فكبر ضرب بيده اليمنى على رسغه الأيسر، فلا يزال كذلك حتى يرفع إلا أن','22','0','0');
INSERT INTO [t9383] VALUES (69,'إني إمامكم فلا تسبقوني بالركوع، ولا بالسجود، ولا ترفعوا رءوسكم، فإني أراكم من خلفي، وايم','22','0','0');
INSERT INTO [t9383] VALUES (70,'إن موسى عليه السلام، كان إذا اغتسل اعتزل وحده، فقالت بنو إسرائيل: أومن قال منهم: من يفعل','22','0','0');
INSERT INTO [t9383] VALUES (71,'ينزل الله عز وجل كل ليلة إلى سماء الدنيا حين يبقى ثلث الليل الآخر، فيقول: من يدعوني','23','0','0');
INSERT INTO [t9383] VALUES (72,'ما منكم من نفس إلا وقد كتب مكانها من الجنة والنار» ، قالوا: يا رسول الله فبم نعمل؟ قال:','23','0','0');
INSERT INTO [t9383] VALUES (73,'استأذن النبي صلى الله عليه وسلم، في عمرة فأذن له، وقال: «لا تنسانا يا أخي من دعائك» ، قال:','23','0','0');
INSERT INTO [t9383] VALUES (74,'بكروا بالصلاة في يوم الغيم، فإنه من ترك الصلاة العصر حبط عمله»','24','0','0');
INSERT INTO [t9383] VALUES (75,'إذا لقيتم المشركين في الطريق فلا تبدءوهم بالسلام واضطروهم إلى أضيقه»','24','0','0');
INSERT INTO [t9383] VALUES (76,'ذكر عنده رجل نام فلم يستيقظ حتى أصبح، قال: «ذاك رجل بال الشيطان في أذنه، أو في','24','0','0');
INSERT INTO [t9383] VALUES (77,'الدينار بالدينار , والدرهم بالدرهم لا فضل بينهما، ولا يباع عاجل بآجل»','24','0','0');
INSERT INTO [t9383] VALUES (78,'تفضل صلاة الجمع على صلاة أحدكم وحده بخمس وعشرين جزءا، وتجتمع ملائكة الليل والنهار في صلاة','25','0','0');
INSERT INTO [t9383] VALUES (79,'حجة لمن لم يحج خير من عشرة غزوات، وغزوة لمن قد حج خير من عشرة حجج، وغزوة في البحر خير من','25','0','0');
INSERT INTO [t9383] VALUES (80,'ركب البحر فتاهت سفينتهم، فسقطوا إلى جزيرة، فخرجوا إليها يلتمسون الماء، فلقي إنسانا يجر','26','0','0');
INSERT INTO [t9383] VALUES (81,'العمرة إلى العمرة كفارة لما بينهن، والحج المبرور ليس له جزاء إلا الجنة»','27','0','0');
INSERT INTO [t9383] VALUES (82,'ما من بني آدم من مولود إلا مسه الشيطان فيستهل صارخا من مس الشيطان غير مريم وابنها» ، ثم','27','0','0');
INSERT INTO [t9383] VALUES (83,'لا تزال طائفة من أمتي يقاتلون على الحق ظاهرين إلى يوم القيامة» وأومأ بيده إلى','27','0','0');
INSERT INTO [t9383] VALUES (84,'إذا افتتح التكبير في الصلاة رفع يديه حين يكبر، حتى يجعلهما حذو منكبيه، ثم إذا كبر للركوع','28','0','0');
INSERT INTO [t9383] VALUES (85,'ما تأمرنا أن نلبس في الإحرام من الثياب؟ قال رسول الله صلى الله عليه وسلم: «لا تلبسوا','28','0','0');
INSERT INTO [t9383] VALUES (86,'المساجد بيوت الله تضيء لأهلها كما تضيء نجوم السماء لأهلها»','28','0','0');
INSERT INTO [t9383] VALUES (87,'المؤمنون تتكافأ دماؤهم ويسعى بذمتهم أدناهم، لا يقتل مؤمن بكافر ولا ذو عهد في','28','0','0');
INSERT INTO [t9383] VALUES (88,'أوصي امرأ بأمه ثلاث مرار، وامرأ بأبيه مرتين، أوصي امرأ بمواليه الذي يليه وإن كان عليه منه','29','0','0');
INSERT INTO [t9383] VALUES (89,'كلكم راع ومسئول عن رعيته، والمرأة في بيت زوجها راعية وهي مسئولة عن رعيتها، والخادم في مال','29','0','0');
INSERT INTO [t9383] VALUES (90,'خير الصفوف المقدم وشرها المؤخر»','29','0','0');
INSERT INTO [t9383] VALUES (91,'«كل ما نهى الله عنه كبيرة وقد ذكرت الطرفة»','30','0','0');
INSERT INTO [t9383] VALUES (92,'أفاض رسول الله صلى الله عليه وسلم من عرفات ورديفه أسامة بن زيد، فجالت ناقته وهو رافع يديه','30','0','0');
INSERT INTO [t9383] VALUES (93,'من لم يدع قول الزور والعمل به والجهل فليس لله حاجة في أن يدع طعامه','30','0','0');
INSERT INTO [t9383] VALUES (94,'من قرأ القرآن ثم مات قبل أن يستظهره أتاه ملك فعلمه في قبره ويلقى الله تعالى وقد استظهره»','30','0','0');
INSERT INTO [t9383] VALUES (95,'هذا الطاعون بقية رجز عذاب عذب به قوم، فإذا كان بأرض فلا تهبطوا عليه، وإذا وقع بأرض وأنتم','32','0','0');
INSERT INTO [t9383] VALUES (96,'يكون دجالون كذابون يأتونكم من الأحاديث، بما لم تعرفوا أنتم ولا آبائكم فإياكم وإياهم أن','32','0','0');
INSERT INTO [t9383] VALUES (97,'كيف أسري بك؟ فقال: " صليت لأصحابي صلاة العتمة بمكة معتما، فأتاني جبريل عليه السلام بدابة','33','0','0');
INSERT INTO [t9383] VALUES (98,'أركد في الأولتين، أحذف في الأخرتين، فقال عمر، رضي الله عنه: ذاك الظن بك يا أبا إسحاق،','34','0','0');
INSERT INTO [t9383] VALUES (99,'على أنقاب المدينة ملائكة لا يدخلها الطاعون ولا الدجال»','35','0','0');
INSERT INTO [t9383] VALUES (100,'ليس على المرء المسلم في فرسه ولا مملوكه صدقة»','35','0','0');
INSERT INTO [t9383] VALUES (101,'أيعجز أحدكم أن يعمل كل يوم عملا مثل أحد؟» قالوا: ومن يستطيع أن يعمل كل يوم عملا مثل أحد؟ ،','35','0','0');
INSERT INTO [t9383] VALUES (102,'{حتى إذا استيأس الرسل وظنوا أنهم قد كذبوا} [يوسف: ١١٠] قالت: بل كذبهم قومهم، قال: فقلت:','35','0','0');
INSERT INTO [t9383] VALUES (103,'أمر بضرب رجلين جعل أحدهما يقول: بسم الله، والآخر يقول: سبحان الله، فقال: ويحك خفف عن','36','0','0');
INSERT INTO [t9383] VALUES (104,'لا تبتدئوا اليهود والنصارى بالسلام، وإذا لقيتموهم في طريق فاضطروهم إلى','36','0','0');
INSERT INTO [t9383] VALUES (105,'الرؤيا جزء من خمسة وأربعين جزءا من النبوة»','36','0','0');
INSERT INTO [t9383] VALUES (106,'ألا أعلمك كلمات ينفعك الله بهن؟» قلت: بلى، قال: «احفظ الله يحفظك، احفظ الله تجده أمامك،','37','0','0');
INSERT INTO [t9383] VALUES (107,'كنت ردف النبي صلى الله عليه وسلم»','37','0','0');
INSERT INTO [t9383] VALUES (108,'عن صيد المعراض، فقال: «ما أصاب بحده فكل وما أصاب بعرضه فهو وقيذ» وسألته عن صيد الكلب، فقال:','37','0','0');
INSERT INTO [t9383] VALUES (109,'ما كرم عبد على الله عز وجل قط إلا ازداد البلاء عليه شدة، وما أعطى رجل زكاة فنقصت من ماله','38','0','0');
INSERT INTO [t9383] VALUES (110,'سلام على من اتبع الهدى»','38','0','0');
INSERT INTO [t9383] VALUES (111,'رجلا من بني إسرائيل كان يسلف الناس إذا أتاه رجل، فأتاه رجل فقال: يا فلان، أسلفني ست مائة','39','0','0');
INSERT INTO [t9383] VALUES (112,'يقول لرجل: أدب ابنك، فإنك مسئول عن ولدك ما علمته، وهو مسئول عن تركه','40','0','0');
INSERT INTO [t9383] VALUES (113,'لا تدخل الملائكة بيتا فيه كلب، ولا صورة تماثيل»','40','0','0');
INSERT INTO [t9383] VALUES (114,'من صلى علي صلاة واحدة صلى الله عليه عشرة صلوات، وحطت عنه عشر خطيات»','40','0','0');
INSERT INTO [t9383] VALUES (115,'يوشك أن يخرج قبل يوم القيامة نار من قبل حضرموت، أو من حضرموت، تحشر الناس، فقالوا: يا رسول','41','0','0');
INSERT INTO [t9383] VALUES (116,'إذا وطئ أحدكم نجسة» ، أو قال: «بنعليه الأذى، فطهروها التراب»','41','0','0');
INSERT INTO [t9383] VALUES (117,'الكافر يأكل في سبعة أمعاء، والمؤمن يأكل في معاء واحد»','41','0','0');
INSERT INTO [t9383] VALUES (118,'لا تنبذوا الرطب والزهو جميعا، ولا تنبذوا التمر والزبيب جميعا، وانبذوا كل واحد منهما على','42','0','0');
INSERT INTO [t9383] VALUES (119,'لم يكن رسول الله صلى الله عليه وسلم يحجبه عن قراءة القرآن شيء ليس','42','0','0');
INSERT INTO [t9383] VALUES (120,'البزاق في المسجد خطيئة، وكفارتها دفنه»','42','0','0');
INSERT INTO [t9383] VALUES (121,'أصبت أرضا بخيبر، والله ما أصبت مالا قط هو أنفس عندي منها، فما تأمرني يا رسول الله؟ قال:','43','0','0');
INSERT INTO [t9383] VALUES (122,'يقاتلكم يهود فتسلطون عليهم حتى يقول الحجر: يا مسلم، هذا يهودي ورائي','43','0','0');
INSERT INTO [t9383] VALUES (123,'اعتدلوا في السجود، ولا يبسط أحدكم ذراعيه كالكلب، وإذا بزق فلا يبزق بين يديه، ولا عن','43','0','0');
INSERT INTO [t9383] VALUES (124,'لا تسأل الناس شيئا، فإن الله تعالى مسئول»','43','0','0');
INSERT INTO [t9383] VALUES (125,'التصفيح للنساء والتسبيح للرجال، فإذا فات أحدكم شيء في صلاته، فليقل: سبحان الله، سبحان','44','0','0');
INSERT INTO [t9383] VALUES (126,'يعلمنا التشهد والخطبة، كما يعلمنا السورة من القرآن: «التحيات لله والصلوات الطيبات لله،','44','0','0');
INSERT INTO [t9383] VALUES (127,'ولا صاحب غنم لا يؤدي حقها إلا بطح لها بقاع قرقر يوم القيامة، ليس فيها عقصاء ولا جماء،','45','0','0');
INSERT INTO [t9383] VALUES (128,'توضئوا مما مست النار»','45','0','0');
INSERT INTO [t9383] VALUES (129,'ما كان رسول الله يصوم في شهر ما كان يصوم في شعبان، كان يصومه كله إلا قليلا، بل كان يصومه','45','0','0');
INSERT INTO [t9383] VALUES (130,'مر على قدر فيها لحم غنم انتهبوها، فأمر بها فأكفئت، وقال: «إن النهبة لا','46','0','0');
INSERT INTO [t9383] VALUES (131,'من أكرم الناس؟ قال: «أتقاهم» ، قالوا: ليس عن هذا نسألك يا رسول الله، قال: «فأكرم الناس يوسف','46','0','0');
INSERT INTO [t9383] VALUES (132,'الندم توبة»','46','0','0');
INSERT INTO [t9383] VALUES (133,'خلل أصابعك وأسبغ الوضوء، وإذا استنشقت» ، قال: «إلا أن تكون صائما»','47','0','0');
INSERT INTO [t9383] VALUES (134,'متى الساعة؟ قال: «وماذا أعددت لها؟» قال: لا والذي نفسي بيده ما أعددت لها من كثير صلاة ولا','47','0','0');
INSERT INTO [t9383] VALUES (135,'لو شئنا أن نشبع شبعنا، ولكن محمدا صلى الله عليه وسلم كان يؤثر على نفسه»','47','0','0');
INSERT INTO [t9383] VALUES (136,'لو أن امرأ اطلع عليك بغير إذن فحذفته بحصاة ففقأت عينه، ما كان عليك','47','0','0');
INSERT INTO [t9383] VALUES (137,'صامتا تطوعا، فأهديت لهما هدية فأفطرتا، فلما دخل النبي صلى الله عليه وسلم، سألته حفصة،','48','0','0');
INSERT INTO [t9383] VALUES (138,'إذا شرب الخمر فاجلدوه، فإن عاد فاجلدوه، فإن عاد فاجلدوه، فإن عاد فاقتلوه» ، فأتي رسول الله','48','0','0');
INSERT INTO [t9383] VALUES (139,'لا تهجر امرأة فراش زوجها إلا لعنتها ملائكة الله عز وجل»','48','0','0');
INSERT INTO [t9383] VALUES (140,'ما من يوم اثنين ولا خميس إلا ترفع فيهما الأعمال، إلا عمل المهاجرين»','49','0','0');
INSERT INTO [t9383] VALUES (141,'عن ليلة القدر، فحلف لا يستثني أنها ليلة سبع وعشرين، قلت: بم تقول يا أبا المنذر؟ قال:','49','0','0');
INSERT INTO [t9383] VALUES (142,'يسلم عن يمينه، ويساره حتى يرى بياض خديه، وكان يقول: السلام عليكم ورحمة الله عن يمينه، ثم','49','0','0');
INSERT INTO [t9383] VALUES (143,'كره السدل» ، ورفع ذلك إلى النبي صلى الله عليه وسلم','50','0','0');
INSERT INTO [t9383] VALUES (144,'ولم تذكر المدينة وأهلها وحرمتها؟ وقد حرم رسول الله صلى الله عليه وسلم ما بين لابتيها،','50','0','0');
INSERT INTO [t9383] VALUES (145,'عن غسلها من الحيض، فأمرها كيف تغتسل، قال: «خذي فرصة من مسك فتطهري بها» ، فقالت: كيف أتطهر','50','0','0');
INSERT INTO [t9383] VALUES (146,'نقول على عهد رسول الله صلى الله عليه وسلم: أبو بكر، وعمر، وعثمان، رضي الله','51','0','0');
INSERT INTO [t9383] VALUES (147,'نهى أن يسافر بالقرآن إلى أرض العدو مخافة أن يناله العدو','51','0','0');
INSERT INTO [t9383] VALUES (148,'فقاتل إنسانا فعض أحدهما يد صاحبه فانتزع أصبعه، فسقطت ثنيته، فجاء إلى النبي صلى الله عليه','51','0','0');
INSERT INTO [t9383] VALUES (149,'أعرابيا بال في المسجد، فأمر رسول الله صلى الله عليه وسلم بذنوب من ماء فصبه على','52','0','0');
INSERT INTO [t9383] VALUES (150,'ما الإيمان؟ قال: «الصبر والسماحة» ، قال: يا رسول الله، فأي الإسلام أفضل؟ قال: «من سلم','52','0','0');
INSERT INTO [t9383] VALUES (151,'من أخف الناس صلاة في تمام','53','0','0');
