INSERT INTO [t9385] VALUES (1,'يدعو عند الكرب: لا إله إلا الله العظيم الحليم، لا إله إلا الله رب العرش الكريم، لا إله','2','0','0');
INSERT INTO [t9385] VALUES (2,'يدعو عند الكرب: لا إله إلا الله العظيم الحليم، لا إله إلا الله رب العرش الكريم، لا إله','6','0','0');
INSERT INTO [t9385] VALUES (3,'يقول عند الكرب هؤلاء الكلمات: «لا إله إلا الله العظيم الحليم، لا إله إلا الله رب العرش','7','0','0');
INSERT INTO [t9385] VALUES (4,'إن نزل بي كرب أو شدائد أقولهن: «لا إله إلا الله الكريم الحليم، وسبحانه، وتبارك الله رب','8','0','0');
INSERT INTO [t9385] VALUES (5,'إن نزل بي كرب أن أقولهن: «لا إله إلا الله الحليم الكريم، سبحانه، وتبارك الله رب العرش','9','0','0');
INSERT INTO [t9385] VALUES (6,'" إذا دخل بك فقولي: لا إله إلا الله الحليم الكريم، سبحان الله رب العرش العظيم، الحمد لله رب','10','0','0');
INSERT INTO [t9385] VALUES (7,'" إذا دخل عليك فقولي: لا إله إلا الله العلي العظيم، سبحان الله رب العرش العظيم، والحمد لله','12','0','0');
INSERT INTO [t9385] VALUES (8,'كلمات أقولها عند الكرب: الله ربي لا أشرك به شيئا "','13','0','0');
INSERT INTO [t9385] VALUES (9,'إذا راعه أمر قال: «الله ربي لا أشرك به شيئا»','14','0','0');
INSERT INTO [t9385] VALUES (10,'إذا راعه أمر، قال: «هو الله لا شريك له» .','15','0','0');
INSERT INTO [t9385] VALUES (11,'" ما أصاب أحدا قط هم ولا حزن، فقال: اللهم إني عبدك، وابن عبدك، وابن أمتك، ناصيتي بيدك، ماض','16','0','0');
INSERT INTO [t9385] VALUES (12,'اللهم إني أسألك بأن لك الحمد لا إله إلا أنت، المنان يا بديع السماوات والأرض، يا ذا الجلال','19','0','0');
INSERT INTO [t9385] VALUES (13,'اللهم إني أسألك بأن لك الحمد لا إله إلا أنت، يا منان، يا بديع السماوات والأرض، ذا الجلال،','22','0','0');
INSERT INTO [t9385] VALUES (14,'اللهم إني أسألك بأن لك الحمد، لا إله إلا أنت وحدك لا شريك لك، أنت المنان بديع السماوات','24','0','0');
INSERT INTO [t9385] VALUES (15,'دعوت الله باسمه الذي إذا دعي به أجاب، وإذا سئل به أعطى، ولو دعوت لجميع أمة محمد أن يغفر','25','0','0');
INSERT INTO [t9385] VALUES (16,'اللهم إني أسألك بأنك أنت الله، لا إله إلا أنت الأحد الصمد، الذي لم تلد ولم تولد، ولم يكن','26','0','0');
INSERT INTO [t9385] VALUES (17,'كلمة لا يقولها مكروب إلا فرج الله كربه، كلمة أخي يونس عليه السلام، {فنادى في الظلمات أن','29','0','0');
INSERT INTO [t9385] VALUES (18,'" اللهم إني أسألك بالله الواحد الصمد الذي لم يلد ولم يولد ولم يكن له كفوا أحد، أن تغفر لي','30','0','0');
INSERT INTO [t9385] VALUES (19,'دعوة ذي النون، إذا نادى ربه في بطن الحوت: {لا إله إلا أنت سبحانك إني كنت من الظالمين}','32','0','0');
INSERT INTO [t9385] VALUES (20,'" لو دعي به على كل شيء بين المشرق والمغرب، في ساعة من يوم الجمعة لاستجيب لصاحبه: سبحانك لا','34','0','0');
INSERT INTO [t9385] VALUES (21,'«يا بديع السماوات والأرض، يا ذا الجلال والإكرام»','35','0','0');
INSERT INTO [t9385] VALUES (22,'دعاء تدعو به لو كان عليك مثل جبل دينا لأداه الله عنك قل يا معاذ اللهم مالك الملك، تؤتي','36','0','0');
INSERT INTO [t9385] VALUES (23,'كلمات علمنيهن رسول الله صلى الله عليه وسلم لو كان عليك مثل جبل صير دنانير لأداه الله عنك،','37','0','0');
INSERT INTO [t9385] VALUES (24,'«اللهم عافني في بدني، اللهم عافني في سمعي، اللهم عافني في بصري، لا إله إلا أنت، تعيدها','39','0','0');
INSERT INTO [t9385] VALUES (25,'" دعوات المكروب: اللهم رحمتك أرجو، فلا تكلني إلى نفسي طرفة عين، وأصلح لي شأني كله، لا إله','39','0','0');
INSERT INTO [t9385] VALUES (26,'" اسم الله الأعظم في ما بين الآيتين: {وإلهكم إله واحد} [البقرة: ١٦٣] و {الم {١} الله لا إله','41','0','0');
INSERT INTO [t9385] VALUES (27,'" اسم الله الأعظم في ما بين الآيتين: {وإلهكم إله واحد} [البقرة: ١٦٣] و {الم {١} الله لا إله','42','0','0');
INSERT INTO [t9385] VALUES (28,'" من كانت له حاجة إلى الله تعالى، أو إلى أحد من بني آدم فليتوضأ، فليحسن الوضوء، ثم ليصل','43','0','0');
INSERT INTO [t9385] VALUES (29,'" ائت الميضاة فتوضأ، ثم ائت المسجد فصلي فيه ركعتين، ثم قل: اللهم إني أسألك وأتوجه إليك','45','0','0');
INSERT INTO [t9385] VALUES (30,'لكلمات سمعتهن من رسول الله صلى الله عليه وسلم، من قالها أول النهار لم تصبه مصيبة حتى','49','0','0');
INSERT INTO [t9385] VALUES (31,'اللهم رب الأرواح الفانية، والأجساد البالية، أسألك بطاعة الأرواح الراجعة إلى أجسادها،','51','0','0');
INSERT INTO [t9385] VALUES (32,'يا ودود، يا ذا العرش المجيد، يا فعال لما تريد، أسألك بعزك الذي لا يرام، وملكك الذي لا','53','0','0');
INSERT INTO [t9385] VALUES (33,'" ما أنعم الله على عبد نعمة في أهل، أو مال، أو ولد، فقال: ما شاء الله لا قوة إلا بالله،','55','0','0');
INSERT INTO [t9385] VALUES (34,'إذا طلبت حاجة فأحببت أن تنجح، فقل: " لا إله إلا الله وحده لا شريك له العلي العظيم، لا إله','56','0','0');
INSERT INTO [t9385] VALUES (35,'أقول إذا أصبحت: آمنت بالله العظيم، وكفرت بالجبت والطاغوت، واستمسكت بالعروة الوثقى لا','59','0','0');
INSERT INTO [t9385] VALUES (36,'«آمنت بالله العظيم وحده، وكفرت بالجبت والطاغوت، واعتصمت بالعروة الوثقى، التي لا انفصام','60','0','0');
INSERT INTO [t9385] VALUES (37,'رجلا أصابه ذلك الداء يعني الجذام، حتى ظن أهله أنه ليس عليهم بأس أن يخرجوه، فظن هو أنه لا','61','0','0');
INSERT INTO [t9385] VALUES (38,'«يا علي يا عظيم، يا حليم يا عليم.','62','0','0');
INSERT INTO [t9385] VALUES (39,'" رأيت رجلا في المنام قائما، فقال لي: قل. قلت: ما أقول؟ .','63','0','0');
INSERT INTO [t9385] VALUES (40,'" هذه الكلمات لو دعوتهن على حلقة حديد لفصمتهن: يا بادئ لا بدء لك، يا دائم لا نفاد لك، يا حي','64','0','0');
INSERT INTO [t9385] VALUES (41,'" خرجت يوما لحاجة، فأسمع قائلا يقول: ورائي ولا أرى أحدا: اللهم أنت المليك، وأنت على كل شيء','65','0','0');
INSERT INTO [t9385] VALUES (42,'«اللهم رب السماوات السبع، ورب الأرضين السبع، ورب العرش العظيم، بألا إله إلا أنت، بما مننت','66','0','0');
INSERT INTO [t9385] VALUES (43,'" من كانت له إلى الله حاجة فليصم الأربعاء والخميس والجمعة، فإذا كان يوم الجمعة تطهر، وراح','67','0','0');
INSERT INTO [t9385] VALUES (44,'هل من الدعاء شيء لا يرد؟ قال: نعم، تقول: «أسألك باسم الأعلى الأعز الأجل','69','0','0');
INSERT INTO [t9385] VALUES (45,'قال لقمان لابنه: " إذا أصابتك شدة فاذهب إلى فلاة من الأرض، وصل ركعتين، ثم ضع وجهك على','70','0','0');
INSERT INTO [t9385] VALUES (46,'من الدعاء الذي لا يرد، أن يصلي العبد ثنتي عشرة ركعة، يقرأ في كل ركعة بأم القرآن، وآية','71','0','0');
INSERT INTO [t9385] VALUES (47,'" اسم الله الأعظم هو الله عز وجل، ألم تروا أنه يبدأ به في القرآن قبل الأشياء','72','0','0');
INSERT INTO [t9385] VALUES (48,'" فرأى النبي صلى الله عليه وسلم في المنام، يقول له: أتكتب إلى مخلوق تسأله حاجتك، وتدع أن','73','0','0');
INSERT INTO [t9385] VALUES (49,'" يا أول قبل كل شيء، ويا آخر بعد كل شيء، ويا من ليس لأوله عنصر، ويا من ليس لآخره فناء، ويا','74','0','0');
INSERT INTO [t9385] VALUES (50,'جار علي السلطان فحبسني، فلما كان في الليل أتاني آت، فقال: يا فضل، أمغموم أنت؟ قلت: نعم،','75','0','0');
INSERT INTO [t9385] VALUES (51,'في المطبق وقد خفت على بصري، فأتاني آت في منامي، فقال لي: يا يعقوب كيف ترى مكانك؟ قلت: وما','76','0','0');
INSERT INTO [t9385] VALUES (52,'«اللهم أسألك بأن لك الحمد لا إله إلا أنت المنان، بديع السماوات والأرض، ذو الجلال والإكرام،','79','0','0');
INSERT INTO [t9385] VALUES (53,'أدفع إلى شدة الفاقة أياما كثيرة، وربما كنت أسقط مغشيا علي، وكنت حينئذ قليل الدراية، وكنت','80','0','0');
INSERT INTO [t9385] VALUES (54,'" إني لأعلم حين يذكرني ربي عز وجل. قال: ففزعوا من ذلك، فقالوا: تعلم حين يذكرك ربك؟ ! ، قال:','81','0','0');
INSERT INTO [t9385] VALUES (55,'" تسوقت من حمص، فخرجت عن الطريق لأخبار اتصلت بنا، فأتاني آت في النوم، فقال: يا فضل، إذا','83','0','0');
INSERT INTO [t9385] VALUES (56,'«اللهم إني أعوذ باسمك الأجل الأعز، وأدعوك اللهم باسمك الأحد الصمد، وأدعوك اللهم باسمك','84','0','0');
INSERT INTO [t9385] VALUES (57,'داء عيسى عليه السلام الذي يدعو به للمرضى، والزمنى، والعميان، والمجانين وغيرهم: اللهم أنت','85','0','0');
INSERT INTO [t9385] VALUES (58,'" دعاؤك لنفسك خير من دعائي لك، أما سمعت قول الله تعالى حيث، يقول: {أمن يجيب المضطر إذا','86','0','0');
INSERT INTO [t9385] VALUES (59,'آدم عليه السلام صلى ركعتين تحت مثعب البيت، ثم قال: اللهم رب كل شيء، وخالق كل شيء، ناصية','87','0','0');
