INSERT INTO [t9390] VALUES (1,'دماءكم وأموالكم عليكم حرام كحرمة يومكم هذا، في شهركم هذا، وفي بلدكم هذا إلى يوم تلقون','2','0','0');
INSERT INTO [t9390] VALUES (2,'دماءكم وأموالكم وأعراضكم عليكم حرام كحرمة يومكم هذا في شهركم هذا» ، ثم أعادها مرارا، ثم رفع','4','0','0');
INSERT INTO [t9390] VALUES (3,'دماءكم وأموالكم وأعراضكم عليكم حرام كحرمة يومكم هذا في بلدكم هذا في شهركم، ألا لا يجني','5','0','0');
INSERT INTO [t9390] VALUES (4,'لا ترجعوا بعدي كفارا يضرب بعضكم رقاب بعض "','7','0','0');
INSERT INTO [t9390] VALUES (5,'«لا ترجعوا بعدي كفارا يضرب بعضكم رقاب بعض»','9','0','0');
INSERT INTO [t9390] VALUES (6,'«إذا التقى المسلمان بسيفيهما، فالقاتل والمقتول في النار»','11','0','0');
INSERT INTO [t9390] VALUES (7,'" إذا تواجه المسلمان بسيفيهما فقتل أحدهما الآخر، فالقاتل والمقتول في النار، فقيل: هذا','13','0','0');
INSERT INTO [t9390] VALUES (8,'" لا يحل دم امرئ مسلم إلا بإحدى ثلاث: كفر بعد إسلامه، أو زنا بعد إحصانه، أو قتل نفسا بغير','14','0','0');
INSERT INTO [t9390] VALUES (9,'" لا يحل دم امرئ مسلم يشهد أن لا إله إلا الله وأني رسول الله، إلا بإحدى ثلاث: النفس بالنفس،','15','0','0');
INSERT INTO [t9390] VALUES (10,'لا يحل دم رجل يشهد أن لا إله إلا الله وأني رسول الله إلا أحد ثلاثة نفر: النفس بالنفس،','17','0','0');
INSERT INTO [t9390] VALUES (11,'ابني هذا سيد، وإن الله يصلح به بين فئتين من المؤمنين عظيمتين»','18','0','0');
INSERT INTO [t9390] VALUES (12,'«كل ذنب عسى الله أن يغفره إلا الرجل يموت كافرا، أو الرجل يقتل مؤمنا','20','0','0');
INSERT INTO [t9390] VALUES (13,'«كل ذنب عسى الله أن يغفره إلا من مات مشركا، أو مؤمنا قتل مؤمنا متعمدا»','21','0','0');
INSERT INTO [t9390] VALUES (14,'إن لقيت مشركا فاختلفنا ضربتين، فأبان إحدى يدي بضربته، ثم قدرت على قتله، فقال حين أردت أن','23','0','0');
INSERT INTO [t9390] VALUES (15,'إن اختلفت أنا ورجل من المشركين ضربتين فقطع يدي، فلما أهويت إليه لأضربه قال: لا إله إلا','24','0','0');
INSERT INTO [t9390] VALUES (16,'«لم قتلته؟» فقال: يا رسول الله، أوجع في المسلمين، وقتل فلانا وفلانا، وسمى نفرا وإني حملت','26','0','0');
INSERT INTO [t9390] VALUES (17,'الله، عز وجل، أبى علي لمن قتل مؤمنا» .','28','0','0');
INSERT INTO [t9390] VALUES (18,'«لا يعجبك رحب الذراعين بالدم، فإن له عند الله قاتلا لا يموت، ولا يعجبك امرؤ كسب مالا من','29','0','0');
INSERT INTO [t9390] VALUES (19,'هي أربع: لا تشركوا بالله شيئا ولا تقتلوا النفس التي حرم الله إلا بالحق، ولا تزنوا، ولا','30','0','0');
INSERT INTO [t9390] VALUES (20,'«إذا تواجه المسلمان بسيفيهما فقتل أحدهما الآخر، فالقاتل والمقتول في النار» ، فقيل: هذا','31','0','0');
INSERT INTO [t9390] VALUES (21,'" يأتي المقتول متعلقا رأسه بإحدى يديه متلببا قاتله بيده الأخرى، تشخب أوداجه دما حتى يأتي','31','0','0');
INSERT INTO [t9390] VALUES (22,'جيء بالرجل القاتل في نسعة يقاد، فقال رسول الله، صلى الله عليه وسلم: «أتعفو؟» قال: لا، قال:','33','0','0');
INSERT INTO [t9390] VALUES (23,'«القاتل والمقتول في النار» فانطلق إليه رجل، وذكر له ما قال رسول الله، صلى الله عليه وسلم،','34','0','0');
INSERT INTO [t9390] VALUES (24,'بين يدي الساعة فتنا كأنها قطع الليل المظلم، يصبح الرجل فيها مؤمنا ثم يمسي كافرا، ويمسي','36','0','0');
INSERT INTO [t9390] VALUES (25,'ما تقول في القتال في الفتنة، والله يقول: {وقاتلوهم حتى لا تكون فتنة} [البقرة: ١٩٣] ؟ فقال','37','0','0');
INSERT INTO [t9390] VALUES (26,'«ستكون فتنة تستنظف العرب، قتلاها في النار، اللسان فيها أشد من وقع','38','0','0');
INSERT INTO [t9390] VALUES (27,'ناسا من المسلمين كانوا مع المشركين يكثرون سواد المشركين على رسول الله، صلى الله عليه','39','0','0');
INSERT INTO [t9390] VALUES (28,'ما أردت قتله، فقال رسول الله، صلى الله عليه وسلم، للوالي: " أما إنه إن كان صادقا ثم قتلته','40','0','0');
INSERT INTO [t9390] VALUES (29,'«ستكون فتن، ثم تكون فتنة، ألا والماشي فيها خير من الساعي إليها، ألا والقاعد فيها خير من','41','0','0');
INSERT INTO [t9390] VALUES (30,'" اغزوا بني فلان مع فلان، قال: فصفت الرجال، وكانت النساء من وراء الرجال، ثم لما رجعوا قال','42','0','0');
INSERT INTO [t9390] VALUES (31,'«وما الذي صنعت؟» مرة أو مرتين وأخبره بالذي صنع، فقال رسول الله، صلى الله عليه وسلم: «فهلا','45','0','0');
INSERT INTO [t9390] VALUES (32,'«كل ذنب عسى الله أن يغفره إلا من مات مشركا، أو مؤمن قتل مؤمنا متعمدا»','46','0','0');
INSERT INTO [t9390] VALUES (33,'«من قتل مؤمنا فاعتبط بقتله لم يقبل الله منه صرفا ولا عدلا»','47','0','0');
INSERT INTO [t9390] VALUES (34,'«لا يزال المؤمن معنقا صالحا ما لم يصب دما حراما، فإذا أصاب دما حراما بلح» . وحدث هانئ بن','47','0','0');
INSERT INTO [t9390] VALUES (35,'«إذا سل أحدكم سيفه فنظر إليه، فإذا كان يناوله أخاه فليغمده ثم يناوله','48','0','0');
INSERT INTO [t9390] VALUES (36,'«إذا أشار الرجل على أخيه بالسلام فهما على حرف جهنم، فإذا قتله وقعا فيه','49','0','0');
INSERT INTO [t9390] VALUES (37,'فإذا سللتم السيف فليغمده الرجل ثم ليعطه كذلك» .','50','0','0');
INSERT INTO [t9390] VALUES (38,'ستكون أحداث واختلاف وفرقة، فإذا كان كذلك؛ فإن استطعت أن تكون المقتول لا القاتل، قال موسى','51','0','0');
INSERT INTO [t9390] VALUES (39,'«من أشار على أخيه بحديدة لعنته الملائكة»','52','0','0');
INSERT INTO [t9390] VALUES (40,'بين يدي الساعة فتنا كقطع الليل المظلم، يصبح الرجل فيها مؤمنا ويمسي كافرا، ويمسي مؤمنا','54','0','0');
INSERT INTO [t9390] VALUES (41,'ستكون فتن، النائم فيها خير من اليقظان، والقاعد فيها خير من القائم، والقائم فيها خير من','55','0','0');
INSERT INTO [t9390] VALUES (42,'«ستكون من بعدي فتنة، النائم فيها خير من اليقظان، والقاعد فيها خير من القائم، والقائم خير','56','0','0');
INSERT INTO [t9390] VALUES (43,'ستكون فتنة، القاعد فيها خير من القائم، والقائم فيها خير من الماشي، والماشي خير من الساعي» ،','57','0','0');
INSERT INTO [t9390] VALUES (44,'ستكون فتنة، المضطجع فيها خير من الجالس، والجالس خير من القائم، والقائم فيها خير من','58','0','0');
INSERT INTO [t9390] VALUES (45,'«الإيمان قيد الفتك، لا يفتك مؤمن» .','59','0','0');
INSERT INTO [t9390] VALUES (46,'«من قتل تحت راية عمية ينصر العصبة ويغضب للعصبة، فقتل جاهلية» .','60','0','0');
INSERT INTO [t9390] VALUES (47,'«لا يشير أحدكم بسلاح على أخيه، فإنه لا يدري لعل الشيطان ينزع من يده فيضعه في حفرة من','61','0','0');
INSERT INTO [t9390] VALUES (48,'" من شرك في دم حرام جاء يوم القيامة مكتوب بين عينيه: آيس من رحمة الله ".','62','0','0');
INSERT INTO [t9390] VALUES (49,'«لزوال الدنيا أهون على الله، عز وجل، من قتل رجل مسلم» وبه، ثنا محمد بن بشار، ثنا محمد بن','63','0','0');
INSERT INTO [t9390] VALUES (50,'«قتل المؤمن عند الله أعظم من زوال الدنيا» .','65','0','0');
INSERT INTO [t9390] VALUES (51,'لقتل المؤمن أعظم عند الله يوم القيامة من زوال الدنيا»','65','0','0');
INSERT INTO [t9390] VALUES (52,'«لقتل المؤمن أعظم عند الله من زوال الدنيا» .','66','0','0');
INSERT INTO [t9390] VALUES (53,'لو أن أهل السماء والأرض أجمعوا على قتل امرئ مسلم؛ لعذبهم الله بلا عدد ولا','67','0','0');
INSERT INTO [t9390] VALUES (54,'«لو أجمع أهل السماء وأهل الأرض على قتل امرئ مؤمن؛ لعذبهم الله، عز وجل» .','68','0','0');
INSERT INTO [t9390] VALUES (55,'«لا يشهدن أحدكم قتيلا لعله أن يكون قتل ظلما، فينزل عليه السخط» .','69','0','0');
INSERT INTO [t9390] VALUES (56,'«لا يعجبنك رحب الذراعين يسفك الدماء، فإن له عند الله قاتلا أو قتيلا لا يموت، ولا يعجبنك','70','0','0');
INSERT INTO [t9390] VALUES (57,'«قاتل به المشركين ما قوتلوا، فإذا رأيت المسلمين، قد أقبل بعضهم على بعض فامش بسيفك إلى أحد،','71','0','0');
INSERT INTO [t9390] VALUES (58,'«لا يزال المسلم معنقا صالحا ما لم يصب دما حراما، فإذا أصاب دما حراما','72','0','0');
INSERT INTO [t9390] VALUES (59,'«أول ما يقضى بين الناس في الدماء، وأول ما يحاسب به العبد الصلاة» .','73','0','0');
INSERT INTO [t9390] VALUES (60,'حملت على رجل من المشركين، فلما غشيته بالرمح؛ قال: إني مسلم، فظننت أنه متعوذ، فقتلته،','74','0','0');
INSERT INTO [t9390] VALUES (61,'«كيف أنت ولا إله إلا الله؟» فقال: يا رسول الله: إنما قالها متعوذا، فقال رسول الله، صلى الله','75','0','0');
INSERT INTO [t9390] VALUES (62,'يأيها الذين آمنوا إذا ضربتم في سبيل الله فتثبتوا أنزلت في رجل من بني مرة بن عوف بن سعد بن','77','0','0');
INSERT INTO [t9390] VALUES (63,'أضرب رقبته؟ قال: قلت نعم، والآن إن أمرتني فعلت، قال: فقال أبو بكر: " ليس تلك لأحد بعد رسول','78','0','0');
INSERT INTO [t9390] VALUES (64,'أضرب عنقه؟ قال: لا، ليست لأحد بعد رسول الله، صلى الله عليه وسلم.','78','0','0');
INSERT INTO [t9390] VALUES (65,'الله، عز وجل، لم يحل في الفتنة شيئا حرمه قبل ذلك، ما بال أحدكم يأتي أخاه فيسلم عليه، ثم','79','0','0');
INSERT INTO [t9390] VALUES (66,'" يجيء المقتول آخذا قاتله وأوداجه تشخب دما عند ذي العزة، فيقول: رب، سل هذا فيم قتلني؟','80','0','0');
INSERT INTO [t9390] VALUES (67,'«لا ترجعوا بعدي كفارا يضرب بعضكم رقاب بعض، ولا يؤخذ أحدكم بجريرة ابنه ولا بجريرة','82','0','0');
INSERT INTO [t9390] VALUES (68,'أعتى الناس على الله من قتل في حرم الله، أو قتل غير قاتله، أو قتل بذحول','83','0','0');
INSERT INTO [t9390] VALUES (69,'«ستكون بعدي فتن شداد، خير الناس فيها مسلمو أهل البوادي، الذين لا يندون من دماء الناس ولا','84','0','0');
INSERT INTO [t9390] VALUES (70,'«لا يحل لمسلم أن يروع مسلما» .','85','0','0');
INSERT INTO [t9390] VALUES (71,'أمرني: إذا كان قتال بين فئتين من المسلمين أن أتخذ سيفا من خشب، واستل وهو في حجزه، فقال:','86','0','0');
INSERT INTO [t9390] VALUES (72,'ستكون فتنة وفرقة واختلاف، فإذا كان ذلك، فإن استطعت أن تكون المقتول لا القاتل','88','0','0');
INSERT INTO [t9390] VALUES (73,'«قاتل به ما قوتل العدو، فإذا رأيت الناس يقتل بعضهم بعضا، فاعمد به إلى صخرة فاضربه بها، ثم','89','0','0');
INSERT INTO [t9390] VALUES (74,'«سيكون من بعدي فتنة، النائم فيها خير من اليقظان، والقاعد خير من القائم، والقائم فيها خير','89','0','0');
INSERT INTO [t9390] VALUES (75,'«قاتل به ما قوتل العدو، فإذا رأيت الناس يقتل بعضهم بعضا، فاعمد به إلى صخرة فاضربه بها، ثم','90','0','0');
INSERT INTO [t9390] VALUES (76,'إن أدركت شيئا من هذه الفتن فاعمد إلى أحد فاكسر به حد سيفك، ثم اقعد في بيتك، قال: فإن دخل','90','0','0');
INSERT INTO [t9390] VALUES (77,'كيف تفعل إذا جاع الناس حتى لا تستطيع أن تقوم من فراشك إلى مسجدك ومن مسجدك إلى فراشك؟»','91','0','0');
INSERT INTO [t9390] VALUES (78,'ستكون فتنة وفرقة، فاضرب بسيفك عرض أو عرض أحد، واكسر نبلك، واقطع وترك، واقعد في بيتك، قال:','92','0','0');
INSERT INTO [t9390] VALUES (79,'«تكون فتنة، النائم فيها خير من المضطجع، والمضطجع فيها خير من القاعد، والقاعد فيها خير من','94','0','0');
INSERT INTO [t9390] VALUES (80,'«لو أن أهل السماء وأهل الأرض اشتركوا في دم مؤمن؛ لأكبهم الله في النار»','95','0','0');
INSERT INTO [t9390] VALUES (81,'«الله أضن بدم عبده المؤمن من أحدكم بكريمة ماله حتى يقبضه على فراشه» .','96','0','0');
INSERT INTO [t9390] VALUES (82,'«ما من رجل استرعاه الله رعية إلا سأله عنها يوم القيامة؛ أقام أمر الله فيهم أم أضاعه؟ حتى','97','0','0');
INSERT INTO [t9390] VALUES (83,'كيف أصنع بأهل المشرق، والله ما أحب أنها دانت لي سبعين سنة، وأنه قتل في سببي رجل واحد،','99','0','0');
