INSERT INTO [t9393] VALUES (1,'نساؤنا ما نأتي منها وما نذر؟ قال: «حرثك فأت حرثك أنى شئت غير أن لا تضرب الوجه، ولا تقبح،','1','0','0');
INSERT INTO [t9393] VALUES (2,'في كل ذود خمس سائمة صدقة» . ولا أعلم حدث بهذا الحديث غير الزبير بن بكار، وهو عندي مما رواه','3','0','0');
INSERT INTO [t9393] VALUES (3,'بما بعثك إلينا ربنا عز وجل؟ . قال: بالإسلام. قال: وما الإسلام؟ قال: أن تقول: أسلمت وجهي','6','0','0');
INSERT INTO [t9393] VALUES (4,'عوراتنا ما نأتي منها وما نذر؟ قال: احفظ عورتك إلا من زوجتك أو ما ملكت','7','0','0');
INSERT INTO [t9393] VALUES (5,'من أبر؟ قال: أمك.','7','0','0');
INSERT INTO [t9393] VALUES (6,'إن كان أحدنا خاليا؟ قال: فالله أحق أن يستحيا منه الناس','7','0','0');
INSERT INTO [t9393] VALUES (7,'نساؤنا ما نأتي منهن وما نذر؟ ، قال: ائت حرثك أنى شئت غير أن لا تضرب الوجه، ولا تقبح، ولا','7','0','0');
INSERT INTO [t9393] VALUES (8,'إنكم محشورون رجالا وركبانا وتخرون على وجوهكم. قالت: يا رسول الله، إنا قوم نتساءل أموالنا','7','0','0');
INSERT INTO [t9393] VALUES (9,'ويل للذي يحدث ويكذب ليضحك به القوم، ويل له ويل له»','8','0','0');
INSERT INTO [t9393] VALUES (10,'لا يأتي الرجل مولاه فيسأله من فضل ما عنده فيمنعه، إلا دعاه الله عز وجل يوم القيامة شجاعا','8','0','0');
INSERT INTO [t9393] VALUES (11,'أنتم توفون سبعين أمة أنتم خيرها وأكرمها على الله عز وجل»','8','0','0');
INSERT INTO [t9393] VALUES (12,'في كل أربعين ابنة لبون لا تفرق إبل عن حسابها فمن أعطاها مؤتجرا فله أجرها ومن منعها فإنا','8','0','0');
INSERT INTO [t9393] VALUES (13,'رجلا آتاه الله عز وجل مالا وولدا، فكان لا يدين الله عز وجل دينا، فدعا بنيه، فقال: أي أب','8','0','0');
INSERT INTO [t9393] VALUES (14,'بما بعثك ربك إلينا؟ . قال: بالإسلام. قلت: وما آيات الإسلام؟ ، قال: أن تقول: أسلمت وجهي','9','0','0');
INSERT INTO [t9393] VALUES (15,'الإسلام شهادة أن لا إله إلا الله، وأن محمدا رسول الله، وتقيم الصلاة، وتؤتي الزكاة، أخوان','10','0','0');
INSERT INTO [t9393] VALUES (16,'ما أتيتك حتى حلفت أكثر من عدد هؤلاء أن لا آتيك وقد جئت ولا أعقل شيئا إلا علمتني» . ثم ذكر','11','0','0');
INSERT INTO [t9393] VALUES (17,'احفظ عورتك إلا من زوجك أو ما ملكت يمينك» . قلت: يا رسول الله، أرأيت إذا كان بعضنا في بعض؟','11','0','0');
INSERT INTO [t9393] VALUES (18,'احفظ عورتك إلا من زوجتك أو ما ملكت يمينك» . قلت: الرجل يكون في القوم فيكون بعضهم في بعض؟ ،','12','0','0');
INSERT INTO [t9393] VALUES (19,'أين تأمرني؟ قال: هاهنا. ونحا بيده نحو الشام " هذا حديث حسن صحيح، قاله الترمذي. وخرجه','15','0','0');
INSERT INTO [t9393] VALUES (20,'تحشرون رجالا وركبانا وتجرون على وجوهكم» هذا حديث حسن، قاله الترمذي. وخرجه النسائي، عن محمد','15','0','0');
INSERT INTO [t9393] VALUES (21,'بما بعثك ربك عز وجل؟ قال: بالإسلام. قال: قلت: يا نبي الله، وما آيات الإسلام؟ قال: أن','16','0','0');
INSERT INTO [t9393] VALUES (22,'يسأل الرجل الحاجة أو الفتق ليصلح به بين قومه فإذا بلغ أو كرب فليستعف»','16','0','0');
INSERT INTO [t9393] VALUES (23,'من أبر؟ . قال: أمك. قلت: ثم من؟ قال: أمك، ثم أباك، ثم الأقرب فالأقرب ". خرجه أبو داود، عن','17','0','0');
INSERT INTO [t9393] VALUES (24,'ويل للذي يحدث فيكذب ليضحك به القوم ويل له ويل له» . وخرجه الترمذي، عن محمد بن بشار بندار،','18','0','0');
INSERT INTO [t9393] VALUES (25,'لا يأتي رجل مولاه يسأله من فضل عنده فيمنعه إلا دعي له يوم القيامة شجاع أقرع يتلمظ فضله','19','0','0');
INSERT INTO [t9393] VALUES (26,'إنكم وفيتم سبعين أمة أنتم خيرها وأكرمها على الله عز وجل» . وقال أيضا قبل هذا: حدثنا عيسى بن','19','0','0');
INSERT INTO [t9393] VALUES (27,'في كل سائمة إبل في أربعين بنت لبون لا يفرق إبل عن حسابها من أعطها مؤتجرا» ، قال ابن العلاء:','21','0','0');
INSERT INTO [t9393] VALUES (28,'رجلا أتاه الله مالا وولدا، وكان لا يدين دينا، فمكث حتى إذا ذهب عمر، وتبقى عمر تذكر فعلم','22','0','0');
