INSERT INTO [t9398] VALUES (1,'ما من غازية تغزو في سبيل الله، فيصيبوا غنيمة، إلا تعجلوا ثلثي أجرهم من الآخرة، ويبقى لهم','1','0','0');
INSERT INTO [t9398] VALUES (2,'تدري أين تذهب؟ قال: الله ورسوله أعلم.','1','0','0');
INSERT INTO [t9398] VALUES (3,'بني أبي سلمة في حجري، وليس لهم شيء إلا ما أنفقت عليهم، ولست بتاركتهم أفلي أجر إن أنفقت','2','0','0');
INSERT INTO [t9398] VALUES (4,'العين حق، ونهى عن الوشم»','2','0','0');
INSERT INTO [t9398] VALUES (5,'نعم الإدام الخل»','3','0','0');
INSERT INTO [t9398] VALUES (6,'من آوى إلى فراشه طاهرا، فذكر الله جل وعز، حتى يدركه النعاس لم يتقلب ساعة من الليل يسأل','3','0','0');
INSERT INTO [t9398] VALUES (7,'الذي يقرأ القرآن، وهو ماهر به، مع السفرة الكرام البررة، والذي يقرؤه وهو يشتد عليه فله','3','0','0');
INSERT INTO [t9398] VALUES (8,'يقول في ركوعه: " سبحان ربي العظيم.','4','0','0');
INSERT INTO [t9398] VALUES (9,'يسأل عن الإسلام، فقال رسول الله صلى الله عليه وسلم: خمس صلوات في اليوم والليلة. قال: هل','4','0','0');
INSERT INTO [t9398] VALUES (10,'إذا صليت المكتوبة وحرمت الحرام، وأحللت الحلال أدخل الجنة؟ فقال النبي صلى الله عليه وسلم:','5','0','0');
INSERT INTO [t9398] VALUES (11,'وما أحد أحق بديناره ولا درهمه من أخيه المسلم»','5','0','0');
INSERT INTO [t9398] VALUES (12,'أن يسافر بالقرآن إلى أرض العدو مخافة أن يناله العدو»','5','0','0');
INSERT INTO [t9398] VALUES (13,'أفضلكم من تعلم القرآن وعلمه»','5','0','0');
INSERT INTO [t9398] VALUES (14,'" خطبت امرأة، فقال رسول الله صلى الله عليه وسلم: أنظرت إليها؟ فقلت: لا. قال: فانظر إليها،','6','0','0');
INSERT INTO [t9398] VALUES (15,'ضحى رسول الله صلى الله عليه وسلم عن نسائه بالبقر»','6','0','0');
INSERT INTO [t9398] VALUES (16,'إني أرى رؤياكم قد تواطأت على هذا، فاطلبوها في العشر الأواخر»','6','0','0');
INSERT INTO [t9398] VALUES (17,'إنكم سترون ربكم لا تضامون في رؤيته، كما تنظرون إلى القمر ليلة البدر، فمن استطاع منكم أن','6','0','0');
INSERT INTO [t9398] VALUES (18,'سجد بنا النبي صلى الله عليه وسلم في: إذا السماء انشقت، وفي: اقرأ باسم','7','0','0');
INSERT INTO [t9398] VALUES (19,'إذا استجمرت فأوتر، وإذا توضأت فانثر»','7','0','0');
INSERT INTO [t9398] VALUES (20,'إذا افتتح الصلاة رفع يديه، حتى يحاذي منكبيه، وإذا أراد أن يركع، وبعدما رفع من الركوع، ولا','7','0','0');
INSERT INTO [t9398] VALUES (21,'اعدلوا بين أبنائكم، اعدلوا بين أبنائكم»','7','0','0');
INSERT INTO [t9398] VALUES (22,'لا يخرج الرجل شيئا من الصدقة، حتى يفك عن لحي سبعين شيطانا»','8','0','0');
INSERT INTO [t9398] VALUES (23,'ليس على المختلس، ولا على المنتهب، ولا على الخائن قطع»','8','0','0');
INSERT INTO [t9398] VALUES (24,'ست لا ينجبون الملاح، والمكاري، والحمامي والحجام والبيطار والحائك»','8','0','0');
INSERT INTO [t9398] VALUES (25,'خصلتان من علامة الجهل: مشاورة النساء، واستكتام السر للنساء والصبيان "','8','0','0');
INSERT INTO [t9398] VALUES (26,'الدين لا ينسى، والبر لا يبلى، والديان لا يموت، فكن كما شئت كما تدين تدان» أخبرنا الحسن،','9','0','0');
INSERT INTO [t9398] VALUES (27,'لا تنزع الرحمة إلا من شقي»','9','0','0');
INSERT INTO [t9398] VALUES (28,'يقرأ بالطور في المغرب»','10','0','0');
INSERT INTO [t9398] VALUES (29,'من عشق فكتم وعف، فمات فهو شهيد»','10','0','0');
INSERT INTO [t9398] VALUES (30,'الأرواح جنود مجندة، فما تعارف منها ائتلف، وما تناكر منها اختلف»','10','0','0');
INSERT INTO [t9398] VALUES (31,'العائد في هبته، كالعائد في قيئه» أخبرنا الحسن، أنبأنا أحمد، حدثني أبو بكر محمد بن خلف بن','10','0','0');
INSERT INTO [t9398] VALUES (32,'من رضي الله مدبرا سره كل ما يقضي الله به عز وجل» سمعت أبا العباس ثعلبا، يقول: روي عن النبي','11','0','0');
INSERT INTO [t9398] VALUES (33,'صلى على النجاشي فكبر عليه خمسا»','12','0','0');
INSERT INTO [t9398] VALUES (34,'نعم الإدام الخل»','12','0','0');
INSERT INTO [t9398] VALUES (35,'من آوى إلى فراشه طاهرا فذكر الله جل وعز حتى يدركه النعاس لم يتقلب ساعة من الليل يسأل الله','13','0','0');
INSERT INTO [t9398] VALUES (36,'عن الغلام شاتان مكافأتان، وعن الجارية شاة»','13','0','0');
INSERT INTO [t9398] VALUES (37,'أعمار أمتي ما بين الستين إلى السبعين، وأقلهم من تجاوز ذلك»','13','0','0');
INSERT INTO [t9398] VALUES (38,'نزل نبي من الأنبياء تحت شجرة فلسعته نملة فأمر برحله فحول، ثم أحرق الشجرة بما فيها، فأوحى','13','0','0');
INSERT INTO [t9398] VALUES (39,'ألهتني أعلام هذه» ، قال: «اذهبوا بها وأتوني بأنبجانية أبي جهم»','14','0','0');
INSERT INTO [t9398] VALUES (40,'عن المسح على الخفين، فقال: كنا نمسح على عهد رسول الله صلى الله عليه وسلم ثلاثة أيام','14','0','0');
INSERT INTO [t9398] VALUES (41,'هذه الصلاة لا يصلح فيها شيء من كلام الناس، إنما هو التكبير والتسبيح، وقراءة القرآن','15','0','0');
INSERT INTO [t9398] VALUES (42,'الذي يقرأ القرآن وهو ماهر به فهو مع السفرة الكرام البررة، والذي يقرؤه وهو يشتد عليه فله','15','0','0');
INSERT INTO [t9398] VALUES (43,'عليكم بكتاب الله، وسترجعون إلى قوم يحبون الحديث عني، فمن قال علي ما لم أقل، فليتبوأ مقعده','15','0','0');
INSERT INTO [t9398] VALUES (44,'أيكم يحب أن يغدو في كل يوم إلى بطحان أو العقيق فيأتي منه بناقتين كوماوين في غير إثم ولا','16','0','0');
INSERT INTO [t9398] VALUES (45,'عليك بتقوى الله فإنها جماع كل خير، وعليك بالجهاد فإنها رهبانية المسلمين، وعليك بذكر الله،','16','0','0');
INSERT INTO [t9398] VALUES (46,'لله عز وجل أهلين من الناس» .','16','0','0');
INSERT INTO [t9398] VALUES (47,'اقرءوا القرآن إن شئتم فإنه يأتي يوم القيامة شافعا لأصحابه , اقرءوا الزهراوين البقرة، وآل','16','0','0');
INSERT INTO [t9398] VALUES (48,'أبهذا بعثتم، أم بهذا أمرتم، ألا لا ترجعوا بعدي كفارا يضرب بعضكم رقاب','17','0','0');
INSERT INTO [t9398] VALUES (49,'أنزل القرآن على سبعة أحرف»','17','0','0');
INSERT INTO [t9398] VALUES (50,'الله عز وجل يأمرك أن تقرئ أمتك القرآن على حرف» ، قال: «أسأل الله عفوه ومغفرته فإن أمتي لا','17','0','0');
INSERT INTO [t9398] VALUES (51,'مثل المؤمن الذي يقرأ القرآن كمثل الأترجة طعمها طيب وريحها طيب، ومثل المؤمن الذي لا يقرأ','18','0','0');
INSERT INTO [t9398] VALUES (52,'لو كان القرآن في إهاب ما أحرقته النار»','18','0','0');
INSERT INTO [t9398] VALUES (53,'لا تقوم الساعة حتى ترجع قبيلة من العرب إلى عبادة الأوثان» . وقال عقبة: «لو شئت أن أسميهم','18','0','0');
INSERT INTO [t9398] VALUES (54,'دخل الخلاء يوم الجمعة، فوضع له عكرمة وضوءا فلما خرج توضأ، فقال له عكرمة: ألا تغتسل فإن','18','0','0');
INSERT INTO [t9398] VALUES (55,'صلاة الرجل في الجميع تفضل صلاته وحده بخمس وعشرين صلاة»','19','0','0');
INSERT INTO [t9398] VALUES (56,'«لا أنام إلا على وتر، والغسل يوم الجمعة، وصيام ثلاثة أيام من الشهر»','19','0','0');
INSERT INTO [t9398] VALUES (57,'خبروني عن شجرة مثلها مثل المؤمن؟ قال: فجعل القوم يذكرون شجر البوادي وألقي في نفسي أو في','19','0','0');
INSERT INTO [t9398] VALUES (58,'بلغوا عني ولو آية، وحدثوا عن بني إسرائيل ولا حرج، ومن يكذب علي متعمدا فليتبوأ مقعده من','19','0','0');
INSERT INTO [t9398] VALUES (59,'إني أردت أن أخبركم بليلة القدر فتلاحى رجلان فاختلجت مني، ولعل ذلك خير لكم اطلبوها في','20','0','0');
INSERT INTO [t9398] VALUES (60,'ما أوان أشراط الساعة؟ ، فقال: أخبرني جبريل عليه السلام أن نارا تحشرهم من المشرق "، قال أبو','20','0','0');
INSERT INTO [t9398] VALUES (61,'ألا أخبركم بخير دور الأنصار؟ دار بني النجار، ثم دار بني الأشهل، ثم دار بني الحارث بن','20','0','0');
INSERT INTO [t9398] VALUES (62,'تحدثوا وليتبوأ من يكذب مقعده من جهنم» ، قال أبو جعفر: كذا ذكر بالحديث عنه لا','21','0','0');
INSERT INTO [t9398] VALUES (63,'لا تحروا طلوع الشمس، ولا تحروا غروبها فإن الشيطان يطلع قرناه مع طلوع الشمس، ويغربان مع','21','0','0');
