INSERT INTO [t9412] VALUES (14,'أعظم المسلمين من المسلمين جرما، من سأل عن شيء لم يحرم على المسلمين، فحرم على المسلمين من','6','0','0');
INSERT INTO [t9412] VALUES (15,'ألا يبسط أحدكم ثوبه حتى أفضي مقالتي هذه، ثم يجمع ثوبه إلى صدره، فلا ينسى من مقالتي شيئا','7','0','0');
INSERT INTO [t9412] VALUES (16,'سئل عما يترك المحرم من الثياب؟ قال: لا يلبس القميص، ولا العمامة، ولا السراويلات، ولا','7','0','0');
INSERT INTO [t9412] VALUES (17,'هذا الوباء رجز عذب الله به بعض الأمم قبلكم، فبقيت في الأرض منه بقايا، فيجيء أحيانا، ويذهب','7','0','0');
INSERT INTO [t9412] VALUES (18,'أي الأعمال أفضل؟ قال: إيمان بالله ورسوله. قال: ثم ماذا؟ قال: الجهاد في سبيل','8','0','0');
