INSERT INTO [t9563] VALUES (1,'إذا أرسلت كلبك المكلب وذكرت اسم الله فكل , قلت: فإن قتل , قال: وإن قتل، ما لم يشركه كلب','3','0','0');
INSERT INTO [t9563] VALUES (2,'«إذا أرسلت كلبك وسميت، فخالط كلابا أخر فأخذته جميعا فلا تأكل , فإنك لا تدري أيها أخذه','4','0','0');
INSERT INTO [t9563] VALUES (3,'نزل بعائشة ضيف، فأمرت له بملحفة لها فنام فيها، فاحتلم فيها، فاستحيا أن يرسل بها وفيها أثر','6','0','0');
INSERT INTO [t9563] VALUES (4,'«كنت أراه على ثوب النبي صلى الله عليه وسلم فأفركه» , تعني المني حدثنا أبو كريب، ثنا أبو','8','0','0');
INSERT INTO [t9563] VALUES (5,'إني حرمت فراشي , فقال له عبد الله: {يأيها الذين آمنوا لا تحرموا طيبات ما أحل الله لكم ولا','9','0','0');
INSERT INTO [t9563] VALUES (6,'" انشق القمر ونحن مع رسول الله صلى الله عليه وسلم، قال: حتى ذهب فرقة منه خلف الجبل , فقال','10','0','0');
INSERT INTO [t9563] VALUES (7,'" كنا مع رسول الله صلى الله عليه وسلم بمكة، فانشق القمر، فرفعت شقة وراء الجبل، فقال رسول','11','0','0');
INSERT INTO [t9563] VALUES (8,'" انشق القمر على عهد رسول الله صلى الله عليه وسلم فرقتين: فرقة وراء الجبل وفرقة دونه، فقال','12','0','0');
INSERT INTO [t9563] VALUES (9,'" {أولئك الذين يدعون يبتغون إلى ربهم الوسيلة أيهم أقرب} [الإسراء: ٥٧] , قال: كان قوم من','13','0','0');
INSERT INTO [t9563] VALUES (10,'" {قل ادعوا الذين زعمتم من دونه إلى قوله: أولئك الذين يدعون يبتغون إلى ربهم الوسيلة أيهم','13','0','0');
INSERT INTO [t9563] VALUES (11,'" {أولئك الذين يدعون يبتغون إلى ربهم الوسيلة} [الإسراء: ٥٧] , قال: ناس من الجن','14','0','0');
INSERT INTO [t9563] VALUES (12,'" {أولئك الذين يدعون يبتغون إلى ربهم الوسيلة أيهم أقرب} [الإسراء: ٥٧] , قال: كان رجال من','15','0','0');
INSERT INTO [t9563] VALUES (13,'لعن من حلق أو سلق أو خرق»','16','0','0');
INSERT INTO [t9563] VALUES (14,'إنا ندخل على أمرائنا فنقول قولا، فإذا خرجنا من عندهم قلنا غيره، فقال: «كنا نعد ذلك نفاقا','19','0','0');
INSERT INTO [t9563] VALUES (15,'القنوت في الفجر؟ فقال: وأي شيء القنوت؟ فقلت: إذا فرغ من القراءة قام ساعة , قال ابن عمر:','20','0','0');
INSERT INTO [t9563] VALUES (16,'القنوت؟ فقال: ما رأيت أحدا يفعله "','22','0','0');
INSERT INTO [t9563] VALUES (17,'«إن أولادكم من أطيب كسبكم، فكلوا من كسب أولادكم» , قيل لسفيان، فقال: أحفظه من الأعمش، كما','22','0','0');
INSERT INTO [t9563] VALUES (18,'«أطيب ما أكلتم من كسبكم وأولادكم من كسبكم»','23','0','0');
INSERT INTO [t9563] VALUES (19,'فأمرني أن آخذ من كل أربعين من البقر مسنة، ومن كل ثلاثين تبيعا أو تبيعة، ومن كل حالم','24','0','0');
INSERT INTO [t9563] VALUES (20,'«سنت لكم الركب فأمسكوا بالركب»','27','0','0');
INSERT INTO [t9563] VALUES (21,'" فضلنا على من كان قبلنا بثلاث: جعلت لنا الأرض مسجدا وطهورا وكان من قبلنا يصلون في المحراب،','29','0','0');
INSERT INTO [t9563] VALUES (22,'«ذروني ما تركتكم، فإنما هلك من كان قبلكم بسؤالهم واختلافهم على أنبيائهم، فإذا أمرتكم بشيء','31','0','0');
INSERT INTO [t9563] VALUES (23,'" كم مضى من الشهر؟ قالوا: يا رسول الله مضى ثنتان وعشرون وبقي ثمان , فقال: مضى ثنتان وعشرون','32','0','0');
INSERT INTO [t9563] VALUES (24,'" كم مضى من الشهر؟ قلنا: اثنان وعشرون وبقي ثمان , فقال: لا، بل بقي سبع، الشهر تسع وعشرون "','33','0','0');
INSERT INTO [t9563] VALUES (25,'" كم مضى من الشهر؟ قلنا: مضى اثنتان وعشرون وبقيت ثمان , فقال رسول الله صلى الله عليه وسلم:','33','0','0');
INSERT INTO [t9563] VALUES (26,'ليلة القدر في رمضان، فقال رسول الله صلى الله عليه وسلم: " كم مضى من الشهر؟ فقال بعضهم:','34','0','0');
INSERT INTO [t9563] VALUES (27,'" إذا كان أول ليلة من شهر رمضان صفدت الشياطين ومردة الجن، وغلقت أبواب النار فلم يفتح منها','35','0','0');
INSERT INTO [t9563] VALUES (28,'«إن لله عز وجل عتقاء من النار في كل ليلة، لكل عبد منهم دعوة مستجابة»','36','0','0');
INSERT INTO [t9563] VALUES (29,'إن لله تعالى ملائكة فضلاء عن كتاب الناس يطوفون في الطرق يلتمسون أهل الذكر، فإذا وجدوا','39','0','0');
INSERT INTO [t9563] VALUES (30,'أن نصوم يوم الجمعة، إلا أن نصوم قبله أو بعده» , حدثنا ابن إشكاب، ثنا عمر بن حفص الربالي','41','0','0');
INSERT INTO [t9563] VALUES (31,'«لا يصوم أحدكم يوم الجمعة، إلا أن يصوم قبله أو بعده»','42','0','0');
INSERT INTO [t9563] VALUES (32,'ما من حسنة يعملها ابن آدم إلا كتبت له عشر حسنات إلى سبع مائة ضعف , قال الله تعالى: «إلا','44','0','0');
INSERT INTO [t9563] VALUES (33,'إني أحدث نفسي بالشيء من شأن الرب عز وجل، ما يسرني أن أتكلم به، وإن لي الدنيا , قال: «ذاك','45','0','0');
INSERT INTO [t9563] VALUES (34,'" عمل ابن آدم يضاعف، إلا الصيام فإنه لي وأنا أجزي به، وللصائم فرحتان: فرحة عند إفطاره،','46','0','0');
INSERT INTO [t9563] VALUES (35,'«يبلى كل شيء من ابن آدم إلا عجب الذنب وفيه يركب الخلق يوم القيامة»','47','0','0');
INSERT INTO [t9563] VALUES (36,'«ما بين النفختين أربعون» , قالوا: يا أبا هريرة، أربعون يوما؟ قال: أبيت , قالوا: أربعون','47','0','0');
INSERT INTO [t9563] VALUES (37,'«إذا انقطع شسع أحدكم فلا يمش في النعل الواحدة»','50','0','0');
INSERT INTO [t9563] VALUES (38,'«إذا انقطع شسع أحدكم فلا يمش في نعل حتى يصلحها»','51','0','0');
INSERT INTO [t9563] VALUES (39,'«إذا انقطع شسع أحدكم فلا يمش في نعل واحدة» , حدثنا قاسم بن دينار، ثنا الحسين بن علي، عن','51','0','0');
INSERT INTO [t9563] VALUES (40,'«إذا انقطع شسع أحدكم فلا يمش في نعل واحدة»','51','0','0');
INSERT INTO [t9563] VALUES (41,'«ما من مولود إلا وهو على هذه الملة» , زاد أبو معاوية: «حتى يبين عنه لسانه، فأبواه يهودانه أو','53','0','0');
INSERT INTO [t9563] VALUES (42,'«ما من مولود إلا يولد على هذه الفطرة، فأبواه يهودانه وينصرانه أو يشركان به» , فقال رجل: " يا','54','0','0');
INSERT INTO [t9563] VALUES (43,'«إذا ولغ الكلب في إناء أحدكم فليهرقه، وليغسله سبع مرات»','55','0','0');
INSERT INTO [t9563] VALUES (44,'«إذا انقطع شسع أحدكم فلا يمش في نعل واحدة , وإذا ولغ الكلب في إناء أحدكم فليغسله سبع مرات»','56','0','0');
INSERT INTO [t9563] VALUES (45,'نزل بالنبي صلى الله عليه وسلم ضيف، فالتمس له شيئا يطعمه، فلم يجد له شيئا، ثم وجد لقمة','57','0','0');
INSERT INTO [t9563] VALUES (46,'انتهى أعرابي يسل النبي صلى الله عليه وسلم، فدخل يطلب له شيئا، قال: فأصاب لقمة بالية في','58','0','0');
INSERT INTO [t9563] VALUES (47,'«إذا صلى أحدكم ركعتي الفجر فليضطجع على يمينه» , فقال له مروان بن الحكم: " أما يكفي أحدنا','60','0','0');
INSERT INTO [t9563] VALUES (48,'مر بسعد وهو يدعو يشير بإصبعيه، فقال: «يا سعد أحد» , وأشار بإصبعه التي تلي','64','0','0');
INSERT INTO [t9563] VALUES (49,'«الصوم لي وأنا أجزي به، والسكينة مغنم وتركها مغرم، والصوم جنة، وليس من عمل ابن آدم شيء إلا','65','0','0');
INSERT INTO [t9563] VALUES (50,'ما من حسنة يعملها ابن آدم إلا كتبت له عشر حسنات إلى سبع مائة ضعف، قال الله تعالى: «إلا','66','0','0');
INSERT INTO [t9563] VALUES (51,'" لأن أقول: سبحان الله والحمد لله ولا إله إلا الله والله أكبر، أحب إلي مما طلعت عليه','67','0','0');
INSERT INTO [t9563] VALUES (52,'«أفضل الكلام سبحان الله والحمد لله ولا إله إلا الله والله أكبر»','68','0','0');
INSERT INTO [t9563] VALUES (53,'«إذا قام أحدكم من الليل، فلا يدخل يده في الإناء حتى يغسلها ثلاث مرات، فإنه لا يدري أين','68','0','0');
INSERT INTO [t9563] VALUES (54,'«استعيذوا بالله من عذاب القبر، واستعيذوا بالله من عذاب جهنم، واستعيذوا بالله من فتنة','70','0','0');
INSERT INTO [t9563] VALUES (55,'«ما نفعني مال أحد، ما نفعني مال أبي بكر» , قال: فبكى أبو بكر، وقال: هل أنا ومالي إلا لك يا','71','0','0');
INSERT INTO [t9563] VALUES (56,'قتل رجل في عهد رسول الله صلى الله عليه وسلم، فدفع القاتل إلى وليه، فقال القاتل: يا رسول','72','0','0');
INSERT INTO [t9563] VALUES (57,'«ليس المسكين بالطواف الذي ترده اللقمة واللقمتان والتمرة والتمرتان، ولكن المسكين المتعفف','73','0','0');
INSERT INTO [t9563] VALUES (58,'«طيبت رسول الله صلى الله عليه وسلم لإحرامه حين أحرم، ولحله حين أحل قبل أن يفيض من البيت»','74','0','0');
INSERT INTO [t9563] VALUES (59,'خرج رسول الله صلى الله عليه وسلم من عندي آنفا رجلت رأسه، فقال: «كيف تجدين أبا عبد الله؟»','75','0','0');
INSERT INTO [t9563] VALUES (60,'«من أتى هذا البيت فلم يرفث ولم يفسق رجع إلى أهله وقد برئ من الإثم مثل ما ولدته','77','0','0');
INSERT INTO [t9563] VALUES (61,'«إذا ولغ الكلب في إناء أحدكم فليغسله سبع مرات»','79','0','0');
INSERT INTO [t9563] VALUES (62,'«سافرنا مع رسول الله صلى الله عليه وسلم فأقام سبع عشرة يقصر الصلاة» , قال ابن عباس: «ونحن','80','0','0');
INSERT INTO [t9563] VALUES (63,'إذا جلس نصب قدميه وقعد على اليسرى، كراهية أن يسقط على شقه الأيسر»','81','0','0');
INSERT INTO [t9563] VALUES (64,'«أكبر الكبائر أن تجعل لله ندا وهو خلقك، وأن تقتل ولدك أن يأكل من طعامك، أو تزاني حليلة','82','0','0');
INSERT INTO [t9563] VALUES (65,'" تراصوا في الصف الأول، لا يتخللكم أولاد الحذف , قيل: يا رسول الله وما أولاد الحذف؟ قال:','83','0','0');
INSERT INTO [t9563] VALUES (66,'«بايعت النبي صلى الله عليه وسلم، فما مسست شيئا قط ألين من يده صلى الله عليه','86','0','0');
INSERT INTO [t9563] VALUES (67,'«من أتى يوم الجمعة فليغتسل»','87','0','0');
INSERT INTO [t9563] VALUES (68,'«ما زالت أكلة خيبر تعادني كل عام، حتى كان هذا أوان الزمان قطعت أبهري» , قال الجوهري: يعني','88','0','0');
INSERT INTO [t9563] VALUES (69,'«توضأ ومسح على خفيه»','90','0','0');
INSERT INTO [t9563] VALUES (70,'" جادل المشركون المسلمين، فقالوا: ما بال ما قتل الله لا تأكلونه، وما قتلتم أنتم تأكلونه،','91','0','0');
INSERT INTO [t9563] VALUES (71,'«من سأل الله الشهادة صادقا من قلبه أعطاه الله أجر شهيد، وإن مات على','92','0','0');
INSERT INTO [t9563] VALUES (72,'«كل مسكر خمر»','92','0','0');
INSERT INTO [t9563] VALUES (73,'«وصلت رسول الله صلى الله عليه وسلم جبة صوف رومية ضيقة الكمين، فأخرج نبي الله صلى الله عليه','92','0','0');
INSERT INTO [t9563] VALUES (74,'«من أراد بحبوحة الجنة فليلزم الجماعة، فإن الشيطان مع الفرد، وهو من الاثنين','93','0','0');
INSERT INTO [t9563] VALUES (75,'أن يأتيها منزلها فيصلي فيه فتتخذه مصلى، ففعل، فأتانا، فعمدت إلى حصير لهم فنضحته بماء،','94','0','0');
INSERT INTO [t9563] VALUES (76,'" نزلت هذه في نساء أهل خيبر، لما افتتح رسول الله صلى الله عليه وسلم خيبر، وأصاب المسلمون','103','0','0');
INSERT INTO [t9563] VALUES (77,'«المؤذن المحتسب كالشهيد يتشحط في دمه ما بين الأذان والإقامة، فيتمنى على الله ما','105','0','0');
INSERT INTO [t9563] VALUES (78,'" إن الرجل من بني إسرائيل كان إذا رأى أخاه على الذنب نهاه عنه تعذيرا، فإذا كان من الغد لم','106','0','0');
