INSERT INTO [t9608] VALUES (1,'إذا جاء أحدكم المسجد، فليركع ركعتين قبل أن يجلس» ، رواه البخاري، ومسلم في صحيحهما، من حديث','1','0','0');
INSERT INTO [t9608] VALUES (2,'الحلال بين، والحرام بين، وبين ذلك أمور متشابهات، وربما قال: مشتبهة، وسأضرب لكم في ذلك','2','0','0');
INSERT INTO [t9608] VALUES (3,'إذا دخل أهل الجنة الجنة، وأهل النار النار، نادى مناد: يا أهل الجنة، إن لكم عند الله','3','0','0');
INSERT INTO [t9608] VALUES (4,'لو صليتم حتى تكونوا كالحنايا، وصمتم حتى تكونوا كالأوتار، ثم كان الاثنان أحب إليكم من','4','0','0');
INSERT INTO [t9608] VALUES (5,'ما الإسلام؟ قال: شهادة أن لا إله إلا الله، وأني رسول الله، وتؤمن بالأقدار خيرها وشرها،','5','0','0');
INSERT INTO [t9608] VALUES (6,'أتاني جبريل عليه السلام، فقرأ: {بسم الله الرحمن الرحيم} [الفاتحة: ١] ، فجهر','6','0','0');
INSERT INTO [t9608] VALUES (7,'إذا كان الماء قلتين، لم ينجسه شيء "','7','0','0');
INSERT INTO [t9608] VALUES (8,'قنت شهرا يدعو عليهم، ثم تركه، وأما في الصبح، فلم يزل يقنت حتى فارق','8','0','0');
INSERT INTO [t9608] VALUES (9,'التحيات، المباركات، الصلوات، الطيبات لله، سلام عليك أيها النبي ورحمة الله وبركاته، سلام','9','0','0');
INSERT INTO [t9608] VALUES (10,'للحكمة أهلا، إن منعتها أهلها كنت جاهلا، فكن كالطبيب العالم يضع دواءه حيث','10','0','0');
INSERT INTO [t9608] VALUES (11,'إذا رأيت رجلا من أصحاب الحديث، فكأني رأيت رسول الله صلى الله عليه وسلم»','11','0','0');
INSERT INTO [t9608] VALUES (12,'أأنثر درا وسط سارحة النعم ... وأنظم منثورا لراعية الغنم لعمري لئن ضيعت في شر بلدة ...','11','0','0');
INSERT INTO [t9608] VALUES (13,'من أنفق زوجين في سبيل الله نودي في الجنة: يا عبد الله، هذا خير، فمن كان من أهل الصلاة دعي','12','0','0');
INSERT INTO [t9608] VALUES (14,'بينا أنا نائم رأيت الناس، عرضوا علي، وعليهم قمص، فمنها ما يبلغ الثدي، ومنها ما يبلغ دون','13','0','0');
INSERT INTO [t9608] VALUES (15,'وحض على جيش العسرة، فقام عثمان، رضي الله عنه، فقال: مائة بعير بأحلاسها وأقتابها في سبيل','14','0','0');
INSERT INTO [t9608] VALUES (16,'من يحمل رايتك يوم القيامة؟ قال: من عسى أن يحملها يوم القيامة إلا من كان يحملها في الدنيا','15','0','0');
INSERT INTO [t9608] VALUES (17,'رحم الله أبا بكر زوجني ابنته، وحملني إلى دار الهجرة، وأعتق بلالا من ماله، رحم الله عمر،','16','0','0');
INSERT INTO [t9608] VALUES (18,'أخذ بيد الحسن والحسين، رضي الله عنهما، فقال: من أحب هذين وأباهما وأمهما كان معي في درجتي','17','0','0');
INSERT INTO [t9608] VALUES (19,'" أعطيت عشر خصال لم تعطهن ذات خمار قبلي: صورت لرسول الله قبل أن أصور في رحم أمي، وتزوجني','18','0','0');
INSERT INTO [t9608] VALUES (20,'الله تعالى اختارني واختار لي أصحابا، فجعل منهم وزراء، وأنصارا، وأصهارا، فمن سبهم فعليه','19','0','0');
INSERT INTO [t9608] VALUES (21,'أصحابك عندي بمنزلة النجوم في السماء بعضها أضوأ من بعض، فمن أخذ بشيء مما هم عليه من','20','0','0');
INSERT INTO [t9608] VALUES (22,'يرحم الله خلفائي، قيل: ومن خلفاؤك يا رسول الله؟ قال: الذين يأتون من بعدي يروون أحاديثي،','21','0','0');
INSERT INTO [t9608] VALUES (23,'إن أنا نمت نهاري ضاعت الرعية، وإن نمت ليلا ضيعت نفسي، فكيف بالنوم','22','0','0');
INSERT INTO [t9608] VALUES (24,'يشهد أن لا إله إلا الله وحده لا شريك له وأن محمدا عبده ورسوله، وأن الجنة حق، وأن النار','23','0','0');
