INSERT INTO [t9620] VALUES (1,'«الراحمون يرحمهم الرحمن، ارحموا أهل الأرض يرحمكم من في السماء» . هذا حديث حسن غريب، تفرد به','2','0','0');
INSERT INTO [t9620] VALUES (2,'يسافر بالقرآن إلى أرض العدو» .','4','0','0');
INSERT INTO [t9620] VALUES (3,'الله لا يقبض العلم انتزاعا ينتزعه من الناس، ولكن يقبض العلماء حتى إذا لم يبق عالما اتخذ','5','0','0');
INSERT INTO [t9620] VALUES (4,'أيكم يعرف القس بن ساعدة الإيادي؟» ، قالوا: كلنا يا رسول الله نعرفه، قال صلى الله عليه','6','0','0');
INSERT INTO [t9620] VALUES (5,'لما جاء إلى مكة دخلها من أعلاها وخرج من أسفلها» .','8','0','0');
INSERT INTO [t9620] VALUES (6,'لا حسد إلا في اثنتين: رجل أتاه الله القرآن فهو يقوم به آناء الليل وآناء النهار، ورجل آتاه','9','0','0');
INSERT INTO [t9620] VALUES (7,'«من يقل علي ما لم أقل فليتبوأ مقعده من النار» .','12','0','0');
INSERT INTO [t9620] VALUES (8,'نصلي مع النبي صلى الله عليه وسلم المغرب إذا توارت بالحجاب» . وأخرجه مسلم، والترمذي، عن','13','0','0');
INSERT INTO [t9620] VALUES (9,'«نعم الإدام الخل» .','15','0','0');
INSERT INTO [t9620] VALUES (10,'أفرد بالحج. وأنبأنيه عاليا أبو الحسن علي بن البخاري، قال: أنا أبو حفص بن طبرزذ، قال: أنا','15','0','0');
INSERT INTO [t9620] VALUES (11,'«من كان وصلة لأخيه المسلم إلى ذي سلطان في منفعة بر، أو تيسير عسر، أعين على إجازة الصراط','16','0','0');
INSERT INTO [t9620] VALUES (12,'«هل عليه دين؟» قالوا: لا، قال: «فهل ترك شيئا؟» قالوا: لا، فصلى عليه، ثم أتي بجنازة أخرى،','19','0','0');
INSERT INTO [t9620] VALUES (13,'ألا تبايع؟» قلت: قد بايعت يا رسول الله، قال: «وأيضا» ، فبايعته الثانية، فقلت له: يا أبا مسلم،','20','0','0');
INSERT INTO [t9620] VALUES (14,'لا يزال هذا الأمر في قريش، ما بقي من الناس اثنان»','22','0','0');
INSERT INTO [t9620] VALUES (15,'هذا الأمر لا ينقضي حتى يمضي فيهم اثنا عشر خليفة، قال: ثم تكلم بكلام خفي علي، فقلت لأبي:','22','0','0');
INSERT INTO [t9620] VALUES (16,'ابنة النضر لطمت جارية فكسرت ثنيتها، فأتوا النبي صلى الله عليه وسلم فأمر','24','0','0');
INSERT INTO [t9620] VALUES (17,'«كتاب الله القصاص»','24','0','0');
INSERT INTO [t9620] VALUES (18,'من عباد الله، من لو أقسم على الله لأبره» سئل شيخنا هذا ابن الشحنة في رجب سنة ست وسبع مائة','24','0','0');
INSERT INTO [t9620] VALUES (19,'أحدكم إذا مات، عرض عليه مقعده بالغداة والعشي، فإن كان من أهل الجنة، فمن أهل الجنة، وإن','27','0','0');
INSERT INTO [t9620] VALUES (20,'نهى عن بيع وسلف»','27','0','0');
INSERT INTO [t9620] VALUES (21,'لا تسأل الإمارة، فإنك إن أعطيتها عن مسألة وكلت إليها، وإن أعطيتها عن غير مسألة أعنت','27','0','0');
INSERT INTO [t9620] VALUES (22,'كلكم راع، وكلكم مسئول عن رعيته، فالأمير الذي على الناس راع، وهو مسئول عن رعيته، والرجل','28','0','0');
INSERT INTO [t9620] VALUES (23,'من ضحى منكم فلا يصبحن بعد ثالثة وفي بيته منه شيء» ، فلما كان العام المقبل، قالوا: يا رسول','28','0','0');
INSERT INTO [t9620] VALUES (24,'«لا يبع بعضكم على بيع بعض» .','33','0','0');
INSERT INTO [t9620] VALUES (25,'الله ينهاكم أن تحلفوا بآبائكم، فمن كان حالفا فليحلف بالله أو ليصمت» . وأخرجه البخاري، عن','33','0','0');
INSERT INTO [t9620] VALUES (26,'أتي بالبراق ليلة أسري به ملجما مسرجا، فاستصعب عليه، فقال له جبريل عليه السلام: أبمحمد','36','0','0');
INSERT INTO [t9620] VALUES (27,'ما شممت عنبرا قط، ولا مسكا، ولا شيئا أطيب من ريح رسول الله صلى الله عليه وسلم. متفق عليه،','37','0','0');
INSERT INTO [t9620] VALUES (28,'يمر بالتمرة، فما يمنعه أن يأخذها إلا مخافة أن تكون صدقة» .','38','0','0');
INSERT INTO [t9620] VALUES (29,'«إذا مات الإنسان انقطع عمله إلا من صدقة جارية، أو علم ينتفع به، أو ولد صالح يدعو له» . صحيح،','39','0','0');
INSERT INTO [t9620] VALUES (30,'لم يبق من الدنيا إلا بلاء وفتنة» .','40','0','0');
INSERT INTO [t9620] VALUES (31,'" رأيت ربي الليلة في أحسن صورة، فقال لي: يا محمد، فيم يختصم الملأ الأعلى؟ قلت: لا أعلم،','40','0','0');
INSERT INTO [t9620] VALUES (32,'يصلي من الليل، فقمت عن يساره أصلي بصلاته، فأخذ بذؤابة كانت لي أو برأسي , فأقامني عن يمينه','44','0','0');
INSERT INTO [t9620] VALUES (33,'بينا أنا نائم رأيت الناس يعرضون علي وعليهم قمص، منها ما يبلغ الثدي، ومنها ما يبلغ دون','44','0','0');
INSERT INTO [t9620] VALUES (34,'القوم يقرون في قومهم» . أخرجه هكذا في الجهاد من صحيحه، وهو أحد ثلاثياته','48','0','0');
