INSERT INTO [t9621] VALUES (44,'نسخة الكتابين اللذين كتبا في بطن الكعبة اللذين شهد عليهما، ونسخة الشرط الذي كتبه محمد ابن أمير المؤمنين في بطن الكعبة بسم الله الرحمن الرحيم، هذا كتاب لعبد الله هارون أمير المؤمنين، كتبه له محمد بن هارون أمير المؤمنين، في صحة من بدنه وعقله وجواز من','372','0','0');
INSERT INTO [t9621] VALUES (45,'نسخة الشرط الذي كتبه عبد الله بن هارون أمير المؤمنين في بطن الكعبة بسم الله الرحمن الرحيم، هذا كتاب لعبد الله هارون أمير المؤمنين كتبه له عبد الله بن هارون أمير المؤمنين، في صحة من عقله وجواز من أمره وصدق نية، فيما كتب في كتابه ومعرفة ما فيه من الفضل','377','0','0');
