INSERT INTO [t9621] VALUES (180,'ما يقتل من دواب الحرم، وما رخص فيه','1183','0','0');
INSERT INTO [t9621] VALUES (181,'من كره أن يدخل شيئا من حجارة الحل في الحرم، أو يخرج شيئا من حجارة الحرم إلى الحل، أو يخلط بعضه ببعض','1195','0','0');
INSERT INTO [t9621] VALUES (182,'ما ذكر من أهل مكة أنهم أهل الله عز وجل','1200','0','0');
INSERT INTO [t9621] VALUES (183,'تذكر النبي صلى الله عليه وسلم وأصحابه مكة','1210','0','0');
