INSERT INTO [t9621] VALUES (187,'ذكر منزل النبي صلى الله عليه وسلم عام الفتح بعد الهجرة، وتركه دخول بيوت مكة بعد الهجرة','1238','0','0');
INSERT INTO [t9621] VALUES (188,'من كره كراء بيوت مكة، وما جاء في بيع رباعها ومنع تبويب دورها، وإخراج الرقيق والدواب منها','1247','0','0');
