INSERT INTO [t9621] VALUES (191,'سيول وادي مكة في الإسلام','1269','0','0');
INSERT INTO [t9621] VALUES (192,'ذكر سيل الجحاف وما جاء في ذلك قال أبو الوليد: وكان سيل الجحاف في سنة ثمانين في خلافة عبد الملك بن مروان، صبح الحاج يوما - وذلك يوم التروية - وهم آمنون غارون قد نزلوا في وادي مكة واضطربوا الأبنية، ولم يكن عليهم من المطر إلا شيء يسير، إنما كانت السماء','1272','0','0');
INSERT INTO [t9621] VALUES (193,'ما ذكر من أمر الوقود بمكة ليلة هلال شهر المحرم','1277','0','0');
INSERT INTO [t9621] VALUES (194,'ما جاء في منزل رسول الله صلى الله عليه وسلم بمنى وحدود منى','1280','0','0');
INSERT INTO [t9621] VALUES (195,'موضع منزل النبي صلى الله عليه وسلم بمنى، ومنازل أصحابه رضي الله عنهم','1284','0','0');
INSERT INTO [t9621] VALUES (196,'باب ما ذكر من النزول بمنى، وأين نزل النبي صلى الله عليه وسلم منها','1288','0','0');
INSERT INTO [t9621] VALUES (197,'ما ذكر من البناء بمنى، وما جاء في ذلك','1290','0','0');
INSERT INTO [t9621] VALUES (198,'ما جاء في مسجد الخيف وفضل الصلاة فيه','1293','0','0');
INSERT INTO [t9621] VALUES (199,'ما جاء في مسجد الكبش','1298','0','0');
INSERT INTO [t9621] VALUES (200,'من أول من رمى الجمار، وما جاء في ذلك','1301','0','0');
INSERT INTO [t9621] VALUES (201,'في أول من نصب الأصنام بمنى','1303','0','0');
INSERT INTO [t9621] VALUES (202,'في رفع حصى الجمار','1305','0','0');
INSERT INTO [t9621] VALUES (203,'في ذكر حصى الجمار كيف يرمى به','1309','0','0');
INSERT INTO [t9621] VALUES (204,'من أين ترمى الجمرة، وما يدعى عندها، وما جاء في ذلك','1313','0','0');
INSERT INTO [t9621] VALUES (205,'ما ذكر من اتساع منى أيام الحج، ولم سميت منى، وأسماء جبالها وشعابها','1320','0','0');
INSERT INTO [t9621] VALUES (206,'ما جاء في صفة مسجد منى وذرعه وأبوابه حدثنا أبو الوليد، قال: ذرع مسجد الخيف من وجهه في طوله من حدته التي تلي دار الإمارة إلى حدته التي تلي عرفة مائتا ذراع وثلاثة وتسعون ذراعا واثنتا عشر إصبعا، ومن حدته التي تلي الطريق السفلى في عرضه إلى حدته التي تلي','1323','0','0');
INSERT INTO [t9621] VALUES (207,'ذكر سعة مسجد منى، وتكسيره قال أبو الوليد: طول المسجد من حد الطاقات التي تلي القبلة إلى حد الطاقات التي تلي عرفة من وسطه مائة ذراع وأحد وثلاثون ذراعا، واثنتا عشر إصبعا، وعرضه من حد الظلة التي تلي الطريق إلى الظلة التي تلي الجبل مائة ذراع وستة وستون','1326','0','0');
INSERT INTO [t9621] VALUES (208,'صفة أبواب مسجد الخيف وذرعها قال أبو الوليد: فيه عشرون بابا، منها في الجدر الذي يلي الطريق تسعة أبواب شارعة في الرحبة على السوق، طول كل باب منها ثمانية أذرع واثنتا عشر إصبعا، وعرض كل باب خمسة أذرع، وبعضها يزيد وينقص في العرض، ومنها في الجدر الذي يلي','1327','0','0');
INSERT INTO [t9621] VALUES (209,'ذرع منى والجمار ومأزمي منى إلى محسر قال: ومن حد مسجد منى الذي يلي عرفات إلى وسط حياض الياقوتة ثلاثة آلاف وسبعمائة وثلاثة وخمسون ذراعا، ومن وسط حياض الياقوتة إلى حد محسر ألفا ذراع، ومن مسجد منى إلى قرين الثعالب ألف ذراع وخمسمائة وثلاثون ذراعا، وذرع ما','1329','0','0');
INSERT INTO [t9621] VALUES (210,'ذرع ما بين المزدلفة إلى منى وذرع مسجد المزدلفة، وصفة أبوابه قال: ومن حد مؤخر مسجد منى إلى مسجد مزدلفة ميلان، وذرع مسجد مزدلفة تسعة وخمسون ذراعا وشبر في مثله، ويكون مكسرا ثلاثة آلاف ذراع وخمسمائة ذراع، وأحد وأربعون ذراعا، والمسجد يدور حوله جدار ليس','1331','0','0');
INSERT INTO [t9621] VALUES (211,'ذرع ما بين مزدلفة إلى عرفة، ومأزمي عرفة، ومسجد عرفة وأبوابه، والحرم والموقف قال: وذرع ما بين مأزمي عرفة مائة ذراع وذراعان، واثنتا عشرة إصبعا، وذرع ما بين مسجد مزدلفة إلى مسجد عرفة ثلاثة أميال، وثلاثة آلاف وثلاثمائة وتسع عشرة ذراعا، وذرع سعة مسجد','1333','0','0');
INSERT INTO [t9621] VALUES (212,'عدد الأميال من المسجد الحرام إلى موقف الإمام بعرفة، وذكر مواضعها قال أبو الوليد: من باب المسجد الحرام - وهو الباب الكبير، باب بني عبد شمس، الذي يعرف اليوم ببني شيبة - إلى أول الأميال، وموضعه على جبل الصفا، والميل الثاني في حد جبل العيرة، والميل','1336','0','0');
INSERT INTO [t9621] VALUES (213,'ما جاء في ذكر المزدلفة وحدودها والوقوف بها، والنزول وقت الدفعة منها، والمشعر الحرام، وإيقاد النار عليه، ودفعة أهل الجاهلية','1338','0','0');
INSERT INTO [t9621] VALUES (214,'في ذكر طريق ضب ضب طريق مختصر من المزدلفة إلى عرفة، وهي في أصل المأزمين عن يمينك وأنت ذاهب إلى عرفة، وقد ذكروا أن النبي صلى الله عليه وسلم سلكها حين غدا من منى إلى عرفة قال ذلك بعض المكيين','1349','0','0');
INSERT INTO [t9621] VALUES (215,'منزل سيدنا رسول الله صلى الله عليه وسلم من نمرة','1352','0','0');
INSERT INTO [t9621] VALUES (216,'ذكر عرفة وحدودها، والموقف بها','1354','0','0');
INSERT INTO [t9621] VALUES (217,'ذكر منبر عرفة','1356','0','0');
INSERT INTO [t9621] VALUES (218,'ذكر الشعب الذي بال فيه رسول الله صلى الله عليه وسلم ليلة الدفعة','1364','0','0');
INSERT INTO [t9621] VALUES (219,'ذكر المواضع التي يستحب فيها الصلاة بمكة، وما فيها من آثار النبي صلى الله عليه وسلم، وما صح من ذلك قال أبو الوليد: مولد النبي أي البيت الذي ولد فيه النبي صلى الله عليه وسلم، وهو في دار محمد بن يوسف أخي الحجاج بن يوسف، كان عقيل بن أبي طالب أخذه حين','1371','0','0');
INSERT INTO [t9621] VALUES (220,'ذكر حراء وما جاء فيه','1379','0','0');
INSERT INTO [t9621] VALUES (221,'ذكر طريق النبي صلى الله عليه وسلم من حراء إلى ثور','1382','0','0');
INSERT INTO [t9621] VALUES (222,'باب ذكر ثور وما جاء فيه','1384','0','0');
INSERT INTO [t9621] VALUES (223,'ذكر مسجد البيعة وما جاء فيه','1386','0','0');
INSERT INTO [t9621] VALUES (224,'في مسجد الجعرانة','1388','0','0');
INSERT INTO [t9621] VALUES (225,'مسجد التنعيم وما جاء فيه','1392','0','0');
INSERT INTO [t9621] VALUES (226,'ما جاء في مقبرة مكة وفضائلها قال جدي: لا نعلم بمكة شعبا يستقبل ناحية من الكعبة ليس فيه انحراف إلا شعب المقبرة، فإنه يستقبل وجه الكعبة كله مستقيما','1398','0','0');
INSERT INTO [t9621] VALUES (227,'ما جاء في مقبرة المهاجرين التي بالحصحاص','1404','0','0');
INSERT INTO [t9621] VALUES (228,'ذكر الآبار التي بمكة قبل زمزم','1411','0','0');
INSERT INTO [t9621] VALUES (229,'باب الآبار التي حفرت بعد زمزم في الجاهلية قال أبو الوليد: الآبار التي حفرت في الجاهلية بعد زمزم بئر في دار محمد بن يوسف البيضاء، حفرها عقيل بن أبي طالب، ويقال: حفرها عبد شمس بن عبد مناف، ونثلها عقيل بن أبي طالب، يقال لها الطوي وبير الأسود بن','1423','0','0');
INSERT INTO [t9621] VALUES (230,'ذكر الآبار الإسلامية قال أبو الوليد: الياقوتة التي بمنى حفرها أبو بكر الصديق رضي الله عنه في خلافته، فعملها الحجاج بن يوسف بعد مقتل ابن الزبير، وضرب فيها وأحكمها. وبير عمر بن عثمان بن عفان التي بمنى في شعب آل عمرو وبير الشركاء بأجياد لبني مخزوم وبير','1425','0','0');
INSERT INTO [t9621] VALUES (231,'ما جاء في العيون التي أجريت في الحرم قال أبو الوليد: كان معاوية بن أبي سفيان رحمه الله قد أجرى في الحرم عيونا، واتخذ لها أخيافا، فكانت حوائط وفيها النخل والزرع، منها حائط الحمام، وله عين، وهو من حمام معاوية الذي بالمعلاة إلى موضع بركة أم جعفر،','1428','0','0');
INSERT INTO [t9621] VALUES (232,'ما ذكر من أمر الرباع رباع قريش وحلفائها','1432','0','0');
INSERT INTO [t9621] VALUES (233,'أولها رباع بني عبد المطلب بن هاشم قال أبو الوليد: الدار التي صارت لابن سليم الأزرق، وهي إلى جنب دار بني مرحب، صارت لإسماعيل بن إبراهيم الحجبي، وهي قبالة دار حويطب بن عبد العزى إلى منتهى دار إبراهيم بن محمد بن طلحة بن عبد الله، فلولده الحارث بن عبد','1433','0','0');
INSERT INTO [t9621] VALUES (234,'رباع حلفاء بني هاشم دار الأسود بن خلف الخزاعي، وهي دار طلحة الطلحات، باعها عبد الله بن القاسم بن عبيدة بن خلف الخزاعي من جعفر بن يحيى البرمكي بمائة ألف دينار، وهي دار الإمارة التي عند الحذائين، بناها حماد البربري للرشيد هارون أمير المؤمنين، ولهم','1435','0','0');
INSERT INTO [t9621] VALUES (235,'رباع بني عبد المطلب بن عبد مناف الدار التي بفوهة شعب ابن عامر يقال لها دار قيس بن مخرمة، كانت لهم جاهلية، وزعم بعض الناس أن دار عمرو بن سعيد بن العاص التي في ظهر دار سعيد كانت لهم، فخرجت من أيديهم وقال غير هؤلاء: بل كانت هذه الدار لقوم من بني بكر،','1437','0','0');
INSERT INTO [t9621] VALUES (236,'رباع حلفائهم لآل عتبة بن فرقد السلمي دارهم وربعهم التي عند المروة، وهو شق المروة السوداء دار الحرشي المنقوشة وزقاق آل أبي ميسرة يقال لها دار ابن فرقد','1438','0','0');
INSERT INTO [t9621] VALUES (237,'رباع بني عبد شمس بن عبد مناف لآل حرب بن أمية بن عبد شمس دار أبي سفيان بن حرب التي بين الدارين، يقال لها دار ريطة ابنة أبي العباس، وهي الدار التي قال النبي صلى الله عليه وسلم يوم الفتح: " من دخل دار أبي سفيان فهو آمن "','1439','0','0');
INSERT INTO [t9621] VALUES (238,'رباع آل سعيد بن العاص بن أمية قال أبو الوليد: دار أبي أحيحة سعيد بن العاص التي إلى جنب دار الحكم، وهي لهم ربع جاهلي، ولهم دار عمرو بن سعيد الأشدق، وهي شرى، كانت لقوم من بني بكر، وهم أخوال سعيد بن العاص','1447','0','0');
INSERT INTO [t9621] VALUES (239,'ربع آل أبي العاص بن أمية لآل عثمان بن عفان دار الحناطين التي يقال لها دار عمرو بن عثمان، ذكر بعض المكيين أنها كانت لآل السباق بن عبد الدار، وقال بعضهم: كانت لآل أمية بن المغيرة، ودار عمرو بن عثمان التي بالثنية يقال: إنها كانت لآل قدامة بن مظعون','1448','0','0');
INSERT INTO [t9621] VALUES (240,'ربع آل أسيد بن أبي العيص لهم دار عبد الله بن خالد بن أسيد التي كانت على الردم الأدنى، ردم آل عبد الله، وهي لهم ربع جاهلي، ولهم الدار التي فوقها على رأس الردم، بينها وبين دار عبد الله زقاق ابن هربذ، وهذه الدار لأبي عثمان بن عبد الله بن خالد بن أسيد،','1450','0','0');
INSERT INTO [t9621] VALUES (241,'ربع آل ربيعة بن عبد شمس لهم دار عتبة بن ربيعة بن عبد شمس التي بين دار أبي سفيان، ودار ابن علقمة، ثم كانت قد صارت للوليد بن عتبة بن أبي سفيان، فبناها بناءها الذي هو قائم إلى اليوم، ويقال: كان فيها حكيم بن أمية بن حارثة بن الأوقص السلمي الذي كانت قريش','1451','0','0');
INSERT INTO [t9621] VALUES (242,'ولآل عدي بن ربيعة بن عبد شمس الدار التي صارت لجعفر بن يحيى بن خالد بن برمك بفوهة أجياد الكبير، عمرها جعفر بن يحيى بالحجر المنقوش والساج، اشتراها جعفر بن يحيى من أم السائب بنت جميع الأموية بثمانين ألف دينار، وكانت هذه الدار لأبي العاص بن الربيع بن عبد','1452','0','0');
INSERT INTO [t9621] VALUES (243,'ربع آل عقبة بن أبي معيط الدار التي يقال لها دار الهرابذة من الزقاق الذي يخرج على النجارين يلي ربع كريز بن ربيعة بن حبيب بن عبد شمس إلى المسكن الذي صار لعبد المجيد بن عبد العزيز بن أبي رواد إلى الزقاق الآخر الأسفل الذي يخرج على البطحاء أيضا عند حمام ابن','1453','0','0');
INSERT INTO [t9621] VALUES (244,'ربع كريز بن ربيعة بن حبيب بن عبد شمس قال أبو الوليد: الدار التي في ظهر دار أبان بن عثمان مما يلي الوادي عند النجارين إلى زقاق ابن هربذ، وإلى ربع أبي معيط، فذلك الربع ربع كريز بن ربيعة بن حبيب بن عبد شمس في الجاهلية، ولعبد الله بن عامر بن كريز داره','1454','0','0');
INSERT INTO [t9621] VALUES (245,'ولولد أمية بن عبد شمس الأصغر الدار التي بأجياد الكبير عند الحواتين، يقال لها دار عبلة، في ظهرها دار الدومة، فهذه الدار للحارث بن أمية الأصغر بن عبد شمس، زعم بعض المكيين أنها كانت لأبي جهل بن هشام، فوهبها للحارث بن أمية على شعر قاله فيه وقال بعضهم:','1455','0','0');
INSERT INTO [t9621] VALUES (246,'رباع حلفاء بني عبد شمس دار جحش بن رياب الأسدي هي الدار التي بالمعلاة عند ردم عمر بن الخطاب رضي الله عنه، يقال لها دار أبان بن عثمان، عندها الرواسون، فلم تزل هذه الدار في أيدي ولد جحش، وهم بنو عمة رسول الله صلى الله عليه وسلم، أمهم أمية بنت عبد','1456','0','0');
INSERT INTO [t9621] VALUES (247,'ربع آل الأزرق بن عمرو بن الحارث بن أبي شمر الغساني حليف المغيرة بن أبي العاص بن أمية دار الأزرق دخلت في المسجد الحرام، كانت إلى جنب المسجد، جدرها وجدر المسجد واحد، وكان وجهها شارعا على باب بني شيبة، إذ كان المسجد متقدما لاصقا بالكعبة، وكانت على يسار','1459','0','0');
INSERT INTO [t9621] VALUES (248,'ربع أبي الأعور قال أبو الوليد: ربع أبي الأعور السلمي، واسمه عمرو بن سفيان بن قايف بن الأوقص، الدار التي تصل حق آل نافع بن عبد الحارث الخزاعي، وهذه الدار شارعة في السويقة البير التي في بطن السويقة بأصلها، يقال لها دار حمزة، وهي من دور معاوية، كان','1461','0','0');
INSERT INTO [t9621] VALUES (249,'ربع آل داود بن الحضرمي واسم الحضرمي عبد الله بن عمار حليف عتبة بن ربيعة قال أبو الوليد: لهم دارهم التي عند المروة، يقال لها دار طلحة، بين دار الأزرق بن عمرو الغساني، ودار عتبة بن فرقد السلمي، ولهم أيضا الدار التي إلى جنب هذه الدار عند باب دار الأزرق','1463','0','0');
INSERT INTO [t9621] VALUES (250,'رباع بني نوفل بن عبد مناف قال أبو الوليد: كانت لهم دار جبير بن مطعم عند موضع دار القوارير اللاصقة بالمسجد الحرام بين الصفا والمروة، اشتريت منهم في خلافة المهدي أمير المؤمنين حين وسع المسجد الحرام قال: فأقطعت تلك الرحبة جعفر بن يحيى في خلافة الرشيد','1464','0','0');
INSERT INTO [t9621] VALUES (251,'رباع حلفاء بني نوفل بن عبد مناف قال أبو الوليد: دار عتبة بن غزوان من بني مازن بن منصور، كانت إلى جنب المسجد الحرام، يقال لها ذات الوجهين، قد كتبت قصتها في رباع يعلى بن منبه، ودخلت هذه الدار في المسجد الحرام، ودار حجير بن أبي إهاب بن عزيز بن قيس بن','1465','0','0');
INSERT INTO [t9621] VALUES (252,'رباع بني الحارث بن فهر قال أبو الوليد: قال جدي: لهم ربع دبر قرن القرظ بين ربع آل مرة بن عمرو الجمحيين، وبين الطريق التي لآل وابصة مما يلي الخليج، وللضحاك بن قيس الفهري دار عند دار آل عفيف السهميين، بينها وبين حق آل المرتفع، وعلى ردم بني جمح دار','1466','0','0');
INSERT INTO [t9621] VALUES (253,'رباع ابن أسد بن عبد العزى قال أبو الوليد: كانت لهم دار حميد بن زهير اللاصقة بالمسجد الحرام في ظهر الكعبة، كانت تفيء على الكعبة بالعشي، وتفيء الكعبة عليها بالبكر، فدخلت في المسجد الحرام في خلافة أبي جعفر، ولهم دار أبي البختري بن هاشم بن أسد، وقد','1467','0','0');
INSERT INTO [t9621] VALUES (254,'رباع بني عبد الدار بن قصي كانت لهم دار الندوة، وهي دار قصي بن كلاب التي كانت قريش لا تشاور، ولا تناظر، ولا يعقدون لواء الحرب، ولا يبرمون إلا فيها، يفتحها لهم بعض ولد قصي، فإذا بلغت الجارية منهم أدخلت دار الندوة، فجاب عليها فيها درعها عامر بن هاشم','1469','0','0');
INSERT INTO [t9621] VALUES (255,'رباع حلفاء بني عبد الدار بن قصي قال أبو الوليد: رباع آل نافع بن عبد الحارث الخزاعيين، الربع المتصل بدار شيبة بن عثمان ودار الندوة إلى السويقة إلى دار حمزة التي بالسويقة، إلى ما دون السويقة، والزقاق الذي يسلك منه إلى دار عبد الله بن مالك، وإلى المروة','1472','0','0');
INSERT INTO [t9621] VALUES (256,'رباع بني زهرة قال أبو الوليد: كانت لهم بفناء المسجد الحرام دار دخلت في المسجد الحرام، كانت عند دار يعلى بن منبه ذات الوجهين، وكانت لهم دار مخرمة بن نوفل التي بين الصفا والمروة التي صارت لعيسى بن علي عند المروة، ولهم حق آل أزهر بن عبد عوف على فوهة','1473','0','0');
INSERT INTO [t9621] VALUES (257,'رباع حلفاء بني زهرة قال أبو الوليد: دار خيرة بنت سباع بن عبد العزى الخزاعية الملحية، كانت في أصل المسجد الحرام تصل دار جبير بن مطعم، ودار الأزرق بن الغساني، فدخلت في المسجد الحرام، وللغسانيين أيضا الدار التي تصل دار أوس، ودار عيسى بن علي فيها','1474','0','0');
INSERT INTO [t9621] VALUES (258,'ربع آل قارظ القاريين وهي الدار التي يقال لها دار الخلد على الصيادلة، بين الصفا والمروة، بناها بناءها هذا حماد البربري قال الأزرقي: وأما بناؤها هذا مما عمل لأم جعفر المقتدر بالله، وقد أقطعها في أيامه، واشتراها الرشيد هارون أمير المؤمنين بين دار آل','1475','0','0');
INSERT INTO [t9621] VALUES (259,'ربع آل أنمار القاريين الربع الشارع على المروة على أصحاب الأدم من ربع آل الحضرمي إلى رحبة عمر بن الخطاب رضي الله عنه مقابل زقاق الخرازين الذي يسلك على دار عبد الله بن مالك، ووجه هذا الربع بين الدارين مما يلي البرامين، فيه دار أم أنمار القارية، كانت','1476','0','0');
INSERT INTO [t9621] VALUES (260,'ربع آل الأخنس بن شريق دار الأخنس التي في زقاق العطارين من الدار التي بناها حماد البربري لهارون أمير المؤمنين إلى دار القدر التي للفضل بن الربيع، وهذا الربع لهم جاهلي، ولآل الأخنس أيضا الحق الذي بسوق الليل على الحدادين مقابل دار الحوار، شراء من بني','1478','0','0');
INSERT INTO [t9621] VALUES (261,'ربع آل عدي بن أبي الحمراء الثقفي لهم الدار التي في ظهر دار ابن علقمة في زقاق أصحاب الشيرق، يقال لها دار العاصميين، من دار القدر التي للفضل بن الربيع إلى بيت النبي صلى الله عليه وسلم الذي يقال له بيت خديجة، وهو لهم ربع جاهلي','1479','0','0');
INSERT INTO [t9621] VALUES (262,'ربع بني تيم قال أبو الوليد: دار أبي بكر الصديق في خط بني جمح، وفيها بيت أبي بكر رضي الله عنه الذي دخله عليه رسول الله صلى الله عليه وسلم، وهو على ذلك البناء إلى اليوم، ومنه خرج النبي صلى الله عليه وسلم وأبو بكر الصديق رضي الله عنه إلى ثور مهاجرا،','1480','0','0');
INSERT INTO [t9621] VALUES (263,'رباع بني مخزوم وحلفائهم قال أبو الوليد: لهم أجيادان الكبير والصغير ما قبل منهما على الوادي إلى منتهى آخرهما إلا حق بني جدعان، وآل عثمان التيمي، وأجيادان جميعا لبني المغيرة بن عبد الله بن عمر بن مخزوم، إلا دار السائب التي يقال لها سقيفة، ودار العباس','1481','0','0');
INSERT INTO [t9621] VALUES (264,'رباع بني عايذ من بني مخزوم قال أبو الوليد: دار أبي نهيك، وقد دخل أكثرها في الوادي، وبقيتها دار العباس بن محمد التي بفوهة أجياد الصغير على الصيارفة، باعها بعض ولد المتوكل بن أبي نهيك، ودار السائب بن أبي السائب العايذي، وقد دخل بعضها في الوادي،','1484','0','0');
INSERT INTO [t9621] VALUES (265,'ربع بني جمح لهم خط بني جمح عند الردم الذي ينسب إليهم، وكان يقال له ردم بني قراد، ودار أبي بن خلف، ودار السجن سجن مكة، كانت لصفوان بن أمية، فابتاعها منه نافع بن عبد الحارث الخزاعي، وهو أمير مكة، ابتاعها لعمر بن الخطاب رضي الله عنه بأربعة آلاف درهم','1489','0','0');
INSERT INTO [t9621] VALUES (266,'رباع بني سهم لهم دار عفيف التي في السويقة إلى قعيقعان إلى ما جاز سيل قعيقعان من دار عمرو بن العاص إلى دار غباة السهمي إلى ما جاز الزقاق الذي يخرج على دار أبي محذورة إلى الثنية، وكانت لهم دار العجلة، ومعهم لآل هبيرة الجشميين حق في سند جبل زرزر، ودار','1491','0','0');
INSERT INTO [t9621] VALUES (267,'رباع حلفاء بني سهم قال أبو الوليد: دار بديل بن ورقاء الخزاعي التي في طرف الثنية','1492','0','0');
INSERT INTO [t9621] VALUES (268,'رباع بني عامر بن لؤي قال أبو الوليد: لهم من وادي مكة على يسار المصعد في الوادي من دار العباس بن عبد المطلب التي في المسعى دار جعفر بن سليمان، ودار ابن حوار، مصعدا إلى دار أبي أحيحة سعيد بن العاص، ومعهم فيه حق لآل أبي طرفة الهذليين، وهو دار الربيع،','1493','0','0');
INSERT INTO [t9621] VALUES (269,'ذكر حد المعلاة وما يليها من ذلك قال أبو الوليد: حد المعلاة من شق مكة الأيمن ما جازت دار الأرقم بن أبي الأرقم، والزقاق الذي على الصفا يصعد منه إلى جبل أبي قبيس مصعدا في الوادي، فذلك كله من المعلاة، ووجه الكعبة، والمقام، وزمزم، وأعلى المسجد، وحد','1495','0','0');
INSERT INTO [t9621] VALUES (270,'حد المسفلة قال أبو الوليد: من الشق الأيمن من الصفا إلى أجيادين فما أسفل منه، فذلك كله من المسفلة وحد المسفلة من الشق الأيسر من زقاق البقر منحدرا إلى دار عمرو بن العاص، ودار ابن عبد الرزاق الجمحي، ودار زبيدة، فذلك كله من المسفلة، فهذه حدود المعلاة','1496','0','0');
INSERT INTO [t9621] VALUES (271,'ذكر أخشبي مكة قال أبو الوليد: أخشبا مكة أبو قبيس، وهو الجبل المشرف على الصفا إلى السويدا إلى الخندمة، وكان يسمى في الجاهلية الأمين، ويقال: إنما سمي الأمين؛ لأن الركن الأسود كان فيها مستودعا عام الطوفان، فلما بنى إبراهيم وإسماعيل عليهما السلام','1497','0','0');
INSERT INTO [t9621] VALUES (272,'ذكر شق معلاة مكة اليماني وما فيه مما يعرف اسمه من المواضع والجبال والشعاب مما أحاط به الحرم قال أبو الوليد: فاضح بأصل جبل أبي قبيس ما أقبل على المسجد الحرام والمسعى، كان الناس يتغوطون هنالك، فإذا جلسوا لذلك كشف أحدهم ثوبه؛ فسمي ما هنالك فاضحا وقال','1500','0','0');
INSERT INTO [t9621] VALUES (273,'الأثبرة ثبير غيناء، وهو المشرف على بئر ميمون، وقلته المشرفة على شعب علي عليه السلام، وعلى شعب الحضارمة بمنى، وكان يسمى في الجاهلية سميرا، ويقال لقلته ذات القتادة، وكان فوقه قتادة، ولها يقول الحارث بن خالد: إلى طرف الجمار فما يليها إلى ذات القتادة','1513','0','0');
INSERT INTO [t9621] VALUES (274,'ذكر شق معلاة مكة الشامي وما فيه مما يعرف اسمه من المواضع والجبال والشعاب مما أحاط به الحرم قال أبو الوليد: شعب قعيقعان: وهو ما بين دار يزيد بن منصور التي بالسويقة، يقال لها دار العروس إلى دور ابن الزبير إلى الشعب الذي منتهاه في أصل الأحمر إلى فلق ابن','1524','0','0');
INSERT INTO [t9621] VALUES (275,'ذكر شق مسفلة مكة اليماني وما فيه مما يعرف اسمه من المواضع والجبال والشعاب مما أحاط به الحرم قال أبو الوليد: أجياد الصغير الشعب الصغير اللاصق بأبي قبيس، ويستقبله أجياد الكبير، على فم الشعب دار هشام بن العاص بن هشام بن المغيرة، ودار زهير بن أبي أمية بن','1531','0','0');
INSERT INTO [t9621] VALUES (276,'ذكر شق مسفلة مكة الشامي وما فيه مما يعرف اسمه من المواضع والجبال والشعاب مما أحاط به الحرم قال أبو الوليد: الحزورة وهي كانت سوق مكة، كانت بفناء دار أم هانئ ابنة أبي طالب التي كانت عند الحناطين، فدخلت في المسجد الحرام، كانت في أصل المنارة إلى الحثمة','1537','0','0');
INSERT INTO [t9621] VALUES (277,'زقاق النار بأسفل مكة مما يلي دار بشر بن فاتك الخزاعي، وإنما سمي زقاق النار لما كان يكون فيه من الشرور','1539','0','0');
INSERT INTO [t9621] VALUES (278,'بيت الأزلام','1540','0','0');
